-- ----------------------------
-- View structure for b_vw_draft_news
-- ----------------------------
CREATE OR REPLACE VIEW "b_vw_draft_news" AS 
 SELECT n.count_visited,
    n.news_like,
    n.news_dislike,
    n.news_date,
    n.news_img,
    n.news_desc,
    n.news_path,
    n.news_title,
    n.news_status,
    c.cat_name,
    cp.parent_desc,
    ui.full_name,
    n.news_id,
    tbnewsdetail.news_content,
    n.news_draft_status,
    c.cat_code
   FROM ((((tbnews n
   JOIN tbcategory c ON (((n.cat_code)::text = (c.cat_code)::text)))
   JOIN tbcategoryparent cp ON ((c.parent_id = cp.parent_id)))
   JOIN tbuserinfo ui ON (((n.user_info_code)::text = (ui.user_info_code)::text)))
   JOIN tbnewsdetail ON ((tbnewsdetail.news_id = n.news_id)));

-- ----------------------------
-- View structure for b_vw_news_scrape
-- ----------------------------
CREATE OR REPLACE VIEW "b_vw_news_scrape" AS 
 SELECT n.count_visited,
    n.news_like,
    n.news_dislike,
    n.news_date,
    n.news_img,
    n.news_desc,
    n.news_path,
    n.news_title,
    n.news_status,
    c.cat_name,
    cp.parent_desc,
    ui.full_name,
    n.news_id,
    n.news_draft_status,
    n.news_published_date
   FROM ((((tbnews n
   JOIN tbcategory c ON (((n.cat_code)::text = (c.cat_code)::text)))
   JOIN tbcategoryparent cp ON ((c.parent_id = cp.parent_id)))
   JOIN tbuserinfo ui ON (((n.user_info_code)::text = (ui.user_info_code)::text)))
   LEFT JOIN tbnewsdetail ON ((tbnewsdetail.news_id = n.news_id)))
  WHERE (n.news_draft_status = false);

