-- ----------------------------
-- Function structure for e_search_news_filter_time
-- ----------------------------
CREATE OR REPLACE FUNCTION "e_search_news_filter_time"(nt varchar, c_code varchar, uicode varchar, time_filter varchar, lm int4, o int4)
  RETURNS SETOF "public"."re_vw_search_news" AS $BODY$BEGIN
IF time_filter = 'daily' THEN
	RETURN QUERY SELECT
		news_title,
    news_desc,
    news_img,
    news_path,
    news_published_date,
    full_name,
    count_visited,
			news_id
	FROM
		e_vw_search_news
	WHERE
		news_title LIKE '%' || nt || '%'
	AND cat_code LIKE '%' || c_code || '%'
	AND user_info_code LIKE '%' || uicode || '%'
	AND news_published_date = CURRENT_DATE
	LIMIT lm OFFSET o ;
	ELSEIF time_filter = 'weekly' THEN
		RETURN QUERY SELECT
			news_title,
    news_desc,
    news_img,
    news_path,
    news_published_date,
    full_name,
    count_visited,
			news_id
		FROM
			e_vw_search_news
		WHERE
			news_title LIKE '%' || nt || '%'
		AND cat_code LIKE '%' || c_code || '%'
		AND user_info_code LIKE '%' || uicode || '%'
		AND news_published_date > CURRENT_DATE - INTERVAL '7 DAYS'
		LIMIT lm OFFSET o ;
		ELSE
			RETURN QUERY SELECT
				news_title,
    news_desc,
    news_img,
    news_path,
    news_published_date,
    full_name,
    count_visited,
			news_id
			FROM
				e_vw_search_news
			WHERE
				news_title LIKE '%' || nt || '%'
			AND cat_code LIKE '%' || c_code || '%'
			AND user_info_code LIKE '%' || uicode || '%'
			AND news_published_date IN(SELECT news_published_date FROM e_vw_search_news WHERE news_published_date NOTNULL) ORDER BY news_published_date DESC
			LIMIT lm OFFSET o ;
			End
			IF ;
			END $BODY$
  LANGUAGE 'plpgsql' VOLATILE COST 100
 ROWS 1000
;

-- ----------------------------
-- Function structure for ee_search_news_filter_time
-- ----------------------------
CREATE OR REPLACE FUNCTION "ee_search_news_filter_time"(nt varchar, c_code varchar, uicode varchar, time_filter varchar, lm int4, o int4)
  RETURNS SETOF "public"."e_vw_search_news" AS $BODY$BEGIN
IF time_filter = 'daily' THEN
	RETURN QUERY SELECT
		*
	FROM
		e_vw_search_news
	WHERE
		news_title LIKE '%' || nt || '%'
	AND cat_code LIKE '%' || c_code || '%'
	AND user_info_code LIKE '%' || uicode || '%'
	AND news_published_date = CURRENT_DATE
	LIMIT lm OFFSET o ;
	ELSEIF time_filter = 'weekly' THEN
		RETURN QUERY SELECT
			*
		FROM
			e_vw_search_news
		WHERE
			news_title LIKE '%' || nt || '%'
		AND cat_code LIKE '%' || c_code || '%'
		AND user_info_code LIKE '%' || uicode || '%'
		AND news_published_date > CURRENT_DATE - INTERVAL '7 DAYS'
		LIMIT lm OFFSET o ;
		ELSE
			RETURN QUERY SELECT
				*
			FROM
				e_vw_search_news
			WHERE
				news_title LIKE '%' || nt || '%'
			AND cat_code LIKE '%' || c_code || '%'
			AND user_info_code LIKE '%' || uicode || '%'
			AND (
				SELECT
					EXTRACT (
						MONTH
						FROM
							news_published_date
					)
			) = (
				SELECT
					EXTRACT (MONTH FROM CURRENT_DATE)
			)
			LIMIT lm OFFSET o ;
			End
			IF ;
			END $BODY$
  LANGUAGE 'plpgsql' VOLATILE COST 100
 ROWS 1000
;

