-- ----------------------------
-- Function structure for s_search_news_filter_time(nt varchar, c_code varchar, fn varcha
-- ----------------------------
CREATE OR REPLACE FUNCTION "s_search_news_filter_time(nt varchar, c_code varchar, fn varcha"(nt varchar, c_code varchar, fn varchar, time_filter varchar)
  RETURNS SETOF "public"."s_vw_search_news" AS $BODY$
BEGIN
	IF time_filter = 'daily' THEN
		 RETURN QUERY 
			SELECT * FROM s_vw_search_news WHERE
	news_title LIKE '%'||nt||'%'
	AND cat_code LIKE '%'||c_code||'%'
	AND full_name LIKE '%'||fn||'%'
	AND news_published_date=CURRENT_DATE;

	ELSEIF time_filter = 'weekly' THEN
   RETURN QUERY 
			SELECT * FROM s_vw_search_news WHERE
	news_title LIKE '%'||nt||'%'
	AND cat_code LIKE '%'||c_code||'%'
	AND full_name LIKE '%'||fn||'%'
	AND news_published_date>CURRENT_DATE - INTERVAL '7 DAYS';

	ELSE
  RETURN QUERY 
			SELECT * FROM s_vw_search_news WHERE
	news_title LIKE '%'||nt||'%'
	AND cat_code LIKE '%'||c_code||'%'
	AND full_name LIKE '%'||fn||'%'
	AND (SELECT EXTRACT(MONTH FROM news_published_date)) = (SELECT EXTRACT(MONTH FROM CURRENT_DATE));

	End IF;

END $BODY$
  LANGUAGE 'plpgsql' VOLATILE COST 100
 ROWS 1000
;

