INSERT INTO "tbnews" VALUES ('24', 'B020201', 'B020000', 'មាស ច័ន្ទ​មាន​ ទទួល​បាន​ខ្សែ​ក្រវាត់​ K-Cement ក្នុងការ​ប្រដាល់​ប្រភេទទម្ងន់​៦៥គីឡូ​ក្រាម', 'រាជ​ធានី​ភ្នំពេញ​ ៖ សេចក្តី​រាយការណ៍​បាន​ឱ្យ​ដឹង​ថា ការ​ប្រកួត​ដណ្តើម​​ខ្សែ​ក្រវាត់ K-Cement ដ៏​កក្រើក​នៅ​ស្ថានីយ​ទូរ​ទស្សន៍​អាស៊ី​អាគ្នេយ៍​កាល​ពី​ល្ងាច​ថ្ងៃ​ទី​១ សីហា កីឡាករ​ឃឹម បូរ៉ា មិន​អាច​ទទួល​បាន​ខ្សែ​ក្រវាត់', 'http://kohsantepheapdaily.com.kh/article/125689.html', 'http://i.kspg.co/2015/8/b899a9ac-173a-4bae-a638-26ef9b3f5880.jpg?w=285&h=170&mode=carve', 'ថ្ងៃអាទិត្យ ទី២ ខែសីហា ឆ្នាំ២០១៥ ម៉ោង ០៩:៤៤', '0', '0', '0', 't', '2015-08-01', 'f');
INSERT INTO "tbnews" VALUES ('25', 'B020201', 'B020000', 'សេក រតនា ចាញ់​ពិន្ទុ​កីឡាករ​ថៃ នៅ​សង្វៀន​អាស៊ី​អាគ្នេយ៍ព្រឹក​នេះ', 'រាជ​ធានី​ភ្នំពេញ​ ៖ សេចក្តី​រាយការណ៍​បាន​ឱ្យ​ដឹង​ថា នៅ​ព្រឹក​ថ្ងៃ​ទី​២ កក្កដា នេះ​ស្ថានីយ​ទូរ​ទស្សន៍​អាស៊ី​អាគ្នេយ៍​បាន​រៀប​ចំ​ការ​ប្រកួត​កីឡា​ប្រដាល់​គូ​អន្តរ​ជាតិ​ដែល​ធ្វើ​ឡើង​រៀង​រាល់​២​សប្តាហ៍​ម្តង​។', 'http://kohsantepheapdaily.com.kh/article/125688.html', 'http://i.kspg.co/2015/8/aaa28263-a175-46ff-8d77-e6a31287d974.jpg?w=285&h=170&mode=carve', 'ថ្ងៃអាទិត្យ ទី២ ខែសីហា ឆ្នាំ២០១៥ ម៉ោង ០៩:៣៨', '0', '0', '23', 't', '2015-08-01', 'f');
INSERT INTO "tbnews" VALUES ('26', 'B020201', 'B020000', 'កីឡាករ​កម្ពុជា និង​បរទេស​10​រូប​ប៉ះ​គ្នា​នៅ​ថ្ងៃខានស្អែក​លើ​សង្វៀន​SEA-TV', 'រាជធានី​ភ្នំពេញ ៖ នៅ​ថ្ងៃអាទិត្យ ទី​២ កញ្ញា ស្អែក កីឡាករ​កម្ពុជា​ដល់​ទៅ​៥​រូប និង​បរទេស​៥​រូប​នឹង​ត្រូវ​ជួប​គ្នា​ដើម្បី​រំលេច​ព្រឹត្តិការណ៍​ប្រដាល់​អន្តរជាតិ​នៅ​លើ​សង្វៀន​ទូរទស្សន៍​អាស៊ី​អាគ្នេយ៍ (SEA-TV)​', 'http://kohsantepheapdaily.com.kh/article/125632.html', 'http://i.kspg.co/2015/7/f6d3338c-5d6e-4027-a536-328786ca7996.jpg?w=285&h=170&mode=carve', 'ថ្ងៃសុក្រ ទី៣១ ខែកក្កដា ឆ្នាំ២០១៥ ម៉ោង ១៤:៣៥', '1', '0', '3', 't', '2015-07-31', 'f');
INSERT INTO "tbnews" VALUES ('27', 'B020201', 'B020000', 'ផា​ន់ គ្រាន់ ឬ ឈោ​ក​ឆៃ កា​រ៉ា​វ៉េ​ត ជា​ម្ចាស់​ខ្សែក្រវាត់​ជើងឯក​គោ​ជល់ ?', 'រាជធានី​ភ្នំពេញ ៖ ការ​ប្រកួត​ប្រដាល់​ដណ្ដើម​ខ្សែក្រវាត់​គោ​ជល់​ក្នុង​ប្រភេទ​ទម្ងន់​៦៤​គីឡូក្រាម​នៅ​លើ​សង្វៀន​ស៊ី​ធី​អិន ថ្ងៃ​សៅរ៍ ទី​១ សីហា ខាង​មុខ​នេះ ឈាន​ចូល​ដល់​វគ្គ​ផ្ដាច់ព្រ័ត្រ​ហើយ ។ មក​ដល់​ពេល​នេះ​មានការ​ជជែក​គ្នា', 'http://kohsantepheapdaily.com.kh/article/125552.html', 'http://i.kspg.co/2015/7/1fba2e85-19ec-4f55-81df-c6d94b90e6ec.jpg?w=285&h=170&mode=carve', 'ថ្ងៃព្រហស្បត្ត៏ ទី៣០ ខែកក្កដា ឆ្នាំ២០១៥ ម៉ោង ១៥:២១', '12', '0', '0', 't', '2015-07-30', 'f');
INSERT INTO "tbnews" VALUES ('28', 'B020201', 'B020000', 'សន្និបាត​ប្រចាំឆ្នាំ​2015 របស់​សហព័ន្ធ​កីឡា​យានយន្ត​កម្ពុជា', 'រាជធានី​ភ្នំពេញ ៖ កាលពី​ថ្ងៃ​ទី​២៩ កក្កដា​កន្លង​មកនេះ សហព័ន្ធ​កីឡា​យានយន្ត​កម្ពុជា បាន​ធ្វើ​សន្និបាត​ប្រចាំឆ្នាំ​របស់​ខ្លួន​ដើម្បី​បូក​សរុប​របាយការណ៍​សកម្មភាព​ប៉ុន្មាន​ឆ្នាំ​កន្លង​មក ដែល​សហព័ន្ធ​រួម​សហការ​ជាមួយ​ដៃគូ​ផ្ដល់​ជំនួយ', 'http://kohsantepheapdaily.com.kh/article/125548.html', 'http://i.kspg.co/2015/7/f8feba86-15f9-4540-a98b-d0d476e53f4b.jpg?w=285&h=170&mode=carve', 'ថ្ងៃព្រហស្បត្ត៏ ទី៣០ ខែកក្កដា ឆ្នាំ២០១៥ ម៉ោង ១៥:១២', '0', '0', '0', 't', '2015-07-30', 'f');
INSERT INTO "tbnews" VALUES ('29', 'B020201', 'B020000', 'អន្តរជាតិ​2​គូ​វាយ​អម​ព្រឹត្តិការណ៍​ដណ្តើម​ខ្សែក្រវាត់​ថ្ងៃខានស្អែក', 'រាជធានី​ភ្នំពេញ ៖ នៅ​ល្ងាច​ថ្ងៃ​ទី​១ បើក​ឆាក​ខែកញ្ញា​ស្អែក ព្រឹត្តិការណ៍​ប្រដាល់​ដណ្ដើម​ខ្សែក្រវាត់ K.Cement ក្នុង​ប្រភេទ​ទម្ងន់​៦០​គីឡូក្រាម​នឹង​លេចឡើង​ហើយ​រវាង​កីឡា​ករ​ឃី​ម បូ​រ៉ា និង មាស ច័ន្ទ​មាន ។', 'http://kohsantepheapdaily.com.kh/article/125540.html', 'http://i.kspg.co/2015/7/1e369ec3-06c4-49a6-a645-651df641f918.jpg?w=285&h=170&mode=carve', 'ថ្ងៃព្រហស្បត្ត៏ ទី៣០ ខែកក្កដា ឆ្នាំ២០១៥ ម៉ោង ១៤:៤០', '1', '0', '1', 't', '2015-07-30', 'f');
