INSERT INTO "tbnews" VALUES ('31', 'B020201', 'B020000', 'អេ ភូ​ថង និង​ភរិយា បន្ត​លាតត្រដាង​ពី​ជីវិត​កម្សត់', 'រាជធានី​ភ្នំពេញ ៖ បើ​ទោះជា​បាន​ទទួល​ជំនួយ​ម៉ូតូ​មួយ​គ្រឿង និង​គ្រឿង​ឧបភោគបរិភោគ​ធ្វើ​ឲ្យ​ស្ថានភាព​រស់នៅ​បាន​ល្អ​ប្រសើរ​ជាង​មុន​បន្តិច​ក្ដី ក៏​អតីត​កីឡាករ​ប្រដាល់​ល្បីឈ្មោះ លោក អេ ភូ​ថង និង​ភរិយា', 'http://kohsantepheapdaily.com.kh/article/125453.html', 'http://i.kspg.co/2015/7/ba1c558c-ade6-4caa-997c-68865799a16c.jpg?w=285&h=170&mode=carve', 'ថ្ងៃពុធ ទី២៩ ខែកក្កដា ឆ្នាំ២០១៥ ម៉ោង ១៤:២៥', '0', '0', '0', 't', '2015-07-29', 'f');
INSERT INTO "tbnews" VALUES ('32', 'B020201', 'B020000', 'U16​កម្ពុជា​ឈ្នះ​ហ្វ៊ី​លី​ពី​ន​1-0​ខណៈ​មី​យ៉ា​ន់​ម៉ា​លត់​សិង្ហ​បុរី​3-1', 'រាជធានី​ភ្នំពេញ ៖ ក្រុម​ម្ចាស់ផ្ទះ​U16​កម្ពុជា​បាន​ធ្វើ​ឱ្យ​អ្នក​ទស្សនា​ទាំង​នៅ​លើ​ទីលាន និង​តាម​កញ្ចក់​ទូរទស្សន៍ មានការ​ចាប់អារម្មណ៍​យ៉ាង​ខ្លាំង​ក្រោយ​ពី​ក្រុម​ដែល​មាន​មាឌតូច​ៗ មួយ​នេះ​បាន​យកឈ្នះ​ក្រុម​U16​ហ្វ៊ី​លី​ពី​ន​១-០', 'http://kohsantepheapdaily.com.kh/article/125450.html', 'http://i.kspg.co/2015/7/c8e65f96-9530-4c31-b61e-b4ce6e0d0727.jpg?w=285&h=170&mode=carve', 'ថ្ងៃពុធ ទី២៩ ខែកក្កដា ឆ្នាំ២០១៥ ម៉ោង ១៤:២១', '12', '0', '0', 't', '2015-07-29', 'f');
INSERT INTO "tbnews" VALUES ('33', 'B020201', 'B020000', 'U 16 កម្ពុជា ​អាចយក៣ពិន្ទុពីហ្វីលីពីន​បានទេយប់នេះ ?', 'រាជធានីភ្នំពេញ ​៖ ​ក្រុមជម្រើសជាតិ​កម្ពុជាក្រោមអាយុ១៦ឆ្នាំ (U 16) ​នឹងត្រូវធ្វើការប្រកួត​លើកដំបូងរបស់ខ្លួន​ក្នុងពូល​ B​ ប្រឆាំង​នឹងក្រុម ​U16 ហ្វិលីពីន​', 'http://kohsantepheapdaily.com.kh/article/125369.html', 'http://i.kspg.co/news_pic_kh.png?w=285&h=170&mode=carve', 'ថ្ងៃអង្គារ ទី២៨ ខែកក្កដា ឆ្នាំ២០១៥ ម៉ោង ១៤:១៨', '0', '0', '1', 't', '2015-07-28', 'f');
