INSERT INTO "tbnews" VALUES ('38', 'B020302', 'B020000', 'កម្មវិធី​ប្រកួត​តារា​បង្ហាញ​ម៉ូ​ដ​ខ្មែរ​ស៊ូ​ភើ​ម៉ូ​ដែល​ជំនាន់​ទី​7​បាន​បញ្ចប់​ហើយ', 'រាជធានី​ភ្នំពេញ ៖ កម្មវិធី​ប្រកួត «​តារា​បង្ហាញ​ម៉ូ​ដ​ខ្មែរ​ស៊ូ​ភើ​ម៉ូ​ដែល​» ជំនាន់​ទី​៧​បន្ទាប់​ពី​បាន​ឆ្លងកាត់​ការ​ប្រកួត​វគ្គ​ជម្រុះ​អស់​ជា​ច្រើន​ខែ​មក​នោះ បាន​បញ្ចប់​ជា​ស្ថាពរ​ហើយ​កាលពី​រាត្រី​ថ្ងៃ​សុក្រ​ទី​៣១ កក្កដា ២០១៥', 'http://kohsantepheapdaily.com.kh/article/125772.html', 'http://i.kspg.co/2015/8/736b2e6c-34bd-4e7f-8198-52ab334f7312.jpg?w=285&h=170&mode=carve', 'ថ្ងៃចន្ទ ទី៣ ខែសីហា ឆ្នាំ២០១៥ ម៉ោង ០៩:១៩', '0', '0', '0', 't', '2015-08-03', 'f');
INSERT INTO "tbnews" VALUES ('39', 'B020302', 'B020000', 'អ្នក​នាង​ឱ​ក បុរី បង្ហាញមុខ​ស្វាមី ខណៈ​មានកូន​ស្រី​3​នាក់', 'រាជធានី​ភ្នំពេញ ៖ កន្លង​មក​ពេល​ចូលរួម​កម្មវិធី​អ្នកសិល្បៈ អតីត​តារាចម្រៀង​អ្នក​នាង​ឱ​ក បូ​រី មិន​ដែល​នាំ​ស្វាមី​ទៅ​ជាមួយ​ឲ្យ​មហាជន​បានឃើញ​ឡើយ ។ ទើបតែ​ក្នុង​ពិធី​រៀប​អាពាហ៍ពិពាហ៍​ប្អូនប្រុស​អ្នក​នាង​យុគ ចិន្តា​', 'http://kohsantepheapdaily.com.kh/article/125760.html', 'http://i.kspg.co/2015/8/e42f5783-8697-4dbb-a9e5-d71ee1ca5aa9.jpg?w=285&h=170&mode=carve', 'ថ្ងៃចន្ទ ទី៣ ខែសីហា ឆ្នាំ២០១៥ ម៉ោង ០៨:៤៦', '0', '0', '0', 't', '2015-08-03', 'f');
INSERT INTO "tbnews" VALUES ('40', 'B020302', 'B020000', 'ខ្សែភាពយន្ត​ហូ​លី​វូ​ដ​10​រឿង​រក​ចំណូល​បាន​ខ្ពស់​ជាងគេប្រចាំឆ្នាំ​2015 មួយ​ចំនួន​បាន​បញ្ចាំង​រួច​ហើយ​នៅ​ភ្នំពេញ', 'កោះសន្តិភាព​សូម​បង្ហាញ​ជូន​ខ្សែភាពយន្ត​ហូ​លី​វូ​ដ​ចំនួន​១០ រឿង​ក្នុង​នោះ​មានរឿង​មួយ​ចំនួន​បាន​បញ្ចាំង​នៅ​ប្រទេស​កម្ពុជា​ផង​ដែរ ដែល​រក​ចំណូល​បាន​ច្រើន​ជាងគេ​ប្រចាំឆ្នាំ ២០១៥ យោង​តាម​គេហទំព័រ​បរទេស Box Office Mojo ។', 'http://kohsantepheapdaily.com.kh/article/125685.html', 'http://i.kspg.co/2015/8/77f088dc-0cdc-4c68-b97b-0e92764d8e05.jpg?w=285&h=170&mode=carve', 'ថ្ងៃអាទិត្យ ទី២ ខែសីហា ឆ្នាំ២០១៥ ម៉ោង ០៩:២១', '0', '0', '0', 't', '2015-08-01', 'f');
INSERT INTO "tbnews" VALUES ('41', 'B020302', 'B020000', 'ខ្សែភាពយន្ត​កំប្លែង​រឿង​«​ខ្មោច​ប្រពន្ធ​ស្តេច​ល្បែង​»​ថត​ដល់​ក្រុង​ហុងកុង', 'រាជធានី​ភ្នំពេញ ៖ តារា​សម្តែង​លោក​ប៉ែន ចំ​រ៉ុ​ង ព្រមទាំង​តួ​សម្តែង និង​ផលិតករ បាន​ចាក​ចេញពី​កម្ពុជា​ទៅ​ទីក្រុង​ហុង ដើម្បី​ថត​បញ្ចប់​ខ្សែភាពយន្ត​បែប​កំប្លែង​រឿង «​ខ្មោច​ប្រពន្ធ​ស្តេច​ល្បែង​» ។ ដំណើរ​ទៅ​ហុងកុង​នេះ​', 'http://kohsantepheapdaily.com.kh/article/125684.html', 'http://i.kspg.co/2015/8/2b31331b-cbd8-4a6c-b41b-a8c3e8b5bd2a.jpg?w=285&h=170&mode=carve', 'ថ្ងៃអាទិត្យ ទី២ ខែសីហា ឆ្នាំ២០១៥ ម៉ោង ០៩:១៣', '12', '0', '0', 't', '2015-08-01', 'f');
INSERT INTO "tbnews" VALUES ('42', 'B020302', 'B020000', 'នាយ​ពែ​ក​មី​សងគុណ​លោកតា​វិច​ទ័​រ​ដោយ​ឱ្យចូលរួម​សម្តែង​ក្នុង​ភាពយន្ត​រឿង «​ឪ​2»', 'រាជធានី​ភ្នំពេញ ៖ លោក​សុវណ្ណ រិ​ទ្ធី ហៅ​លោកតា​វិច​ទ័​រ ដែល​ធ្លាប់​ដឹកនាំ​នាយ​ពែ​ក​មី សម្តែង​កំប្លែង​នោះ បាន​ចូលរួម​សម្តែង​ក្នុង​ខ្សែភាពយន្ត​ក្រោម​ការ​ដឹកនាំ​របស់​នាយ​ពែ​ក​មី​វិញ​ម្តង ។ ខ្សែភាពយន្ត​ដែល​លោក​សុវណ្ណ រិ​ទ្ធី ', 'http://kohsantepheapdaily.com.kh/article/125609.html', 'http://i.kspg.co/2015/7/2b3732f6-44ab-4c72-85d3-346f61722f8b.jpg?w=285&h=170&mode=carve', 'ថ្ងៃសុក្រ ទី៣១ ខែកក្កដា ឆ្នាំ២០១៥ ម៉ោង ១០:៣៤', '0', '0', '132', 't', '2015-07-31', 'f');
INSERT INTO "tbnews" VALUES ('43', 'B020302', 'B020000', 'ដើមទ្រូង​ធម្មជាតិ​មិន​គាប់ចិត្ត​កញ្ញា​មួយ​មួយ​កែ​ច្នៃ​បង្កើន​សម្រស់', 'រាជធានី​ភ្នំពេញ ៖ តារា​បង្ហាញ​ម៉ូ​ដ និង​សម្តែង​នាង​គី​ម ឡា​ង ហៅ​មួយ​មួយ បាត់​មុខ​មួយ​រយៈ​ពី​ការ​ពិភព​សិល្បៈ ងាក​ទៅ​ក្តាប់​មុខ​ជំនួញ​របស់​នាង​វិញ ។ នៅ​ពេល​ប្រកាស​ចូល​ពិភព​សិល្បៈ​វិញ តារា​ស្រី​រាង​ខ្ពស់ ', 'http://kohsantepheapdaily.com.kh/article/125592.html', 'http://i.kspg.co/2015/7/a9c88735-5ff9-40c9-8887-2095e02d1f01.jpg?w=285&h=170&mode=carve', 'ថ្ងៃសុក្រ ទី៣១ ខែកក្កដា ឆ្នាំ២០១៥ ម៉ោង ០៩:៥៥', '0', '0', '0', 't', '2015-07-31', 'f');
INSERT INTO "tbnews" VALUES ('44', 'B020302', 'B020000', 'យុវវ័យ​ជាង​100​នាក់​ចូលរួម​ប្រកួត​បង្ហាញ​ម៉ូ​ដ​ប្រាថ្នា​ចង់​ក្លាយទៅជា​តារា​ប្រចាំ​ក្រុមហ៊ុន​មួយ', 'រាជធានី​ភ្នំពេញ ៖ យុវវ័យ​ស្រស់​ស្អាត​ជា​ច្រើន​នាក់​បាន​ចូលរួម​ដើរ​បង្ហាញ​ម៉ូ​ដ​ម្នាក់​ម្តង​នៅ​ពីមុខ​គណៈកម្មការ​ក្នុង​កម្មវិធី​ប្រកួតប្រជែង​មួយ​ធ្វើ​ឡើង​នៅ​សណ្ឋាគារ​អាំង​ទែ​រ​កុង​ទី​ណ​ង់​តា​ល់ រាជធានី​ភ្នំពេញ កាលពី​ថ្ងៃ​ទី​២៩ កក្កដា ឆ្នាំ​២០១៥ ។', 'http://kohsantepheapdaily.com.kh/article/125527.html', 'http://i.kspg.co/2015/7/9fe031aa-e4b5-4c82-8e32-985471cf0499.jpg?w=285&h=170&mode=carve', 'ថ្ងៃព្រហស្បត្ត៏ ទី៣០ ខែកក្កដា ឆ្នាំ២០១៥ ម៉ោង ១០:៥៥', '0', '0', '0', 't', '2015-07-30', 'f');
INSERT INTO "tbnews" VALUES ('45', 'B020302', 'B020000', 'មាស សុខ​សោភា បដិសេធ​រឿង​កែ​មុខ តែ​សារភាព​ថា​បាន​កែ​ច្រមុះ', 'រាជធានី​ភ្នំពេញ ៖ ទម្រង់​មុខ​តារាចម្រៀង​មាស សុខ​សោភា ហាក់​ប្រែប្រួល ធ្វើ​ឱ្យ​យុវវ័យ​និយម​លេង​ហ្វេ​ស​ប៊ុ​ក​សង្ស័យ​ថា ជា​លទ្ធផល​នៃ​ការ​កែ​សម្រស់ ។  ស្រាយ​ច​ម្ង​ល់​របស់​យុវវ័យ​ជា​អ្នក​និយម​ប្រើប្រាស់​ហ្វេ​ស​ប៊ុ​ក ', 'http://kohsantepheapdaily.com.kh/article/125506.html', 'http://i.kspg.co/2015/7/0f55e28c-57f5-4934-9d09-c92afc043b69.jpg?w=285&h=170&mode=carve', 'ថ្ងៃព្រហស្បត្ត៏ ទី៣០ ខែកក្កដា ឆ្នាំ២០១៥ ម៉ោង ០៩:៥២', '2', '0', '0', 't', '2015-07-30', 'f');
INSERT INTO "tbnews" VALUES ('46', 'B020302', 'B020000', 'នាង Olivia Jordan តារា​ម៉ូ​ដែល​គ្រង​មកុដ​បវរកញ្ញា​អា​មេ​រិ​ក', 'តារា​ម៉ូ​ដែល និង​សម្តែង​វ័យ​២៦​ឆ្នាំ នាង Olivia Jordan គ្រង​មកុដ​បវរកញ្ញា​អា​មេ​រិ​ក​ឆ្នាំ​២០១៥ ក្រោយ​ពី​ផ្តួល​បេ​ក្ខ​នារី​៥០​នាក់ ផ្សេង​ទៀត​មក​ពី​រដ្ឋ ខុស​ៗ​គ្នា ។ ព្រឹត្តិការណ៍​នេះ​ធ្វើ​ឡើង​កាលពី​ថ្ងៃ​ទី​១២ កក្កដា', 'http://kohsantepheapdaily.com.kh/article/125418.html', 'http://i.kspg.co/2015/7/510abce4-55b1-465b-a303-25bfad0c8f5f.jpg?w=285&h=170&mode=carve', 'ថ្ងៃពុធ ទី២៩ ខែកក្កដា ឆ្នាំ២០១៥ ម៉ោង ១០:១៥', '0', '0', '3', 't', '2015-07-29', 'f');
INSERT INTO "tbnews" VALUES ('47', 'B020302', 'B020000', 'សុះ ម៉ាច មិន​សប្បាយចិត្ត​អ្នក​យក​បទ​ចម្រៀង​«​ខាន់ស្លា​ឥត​ន័យ​» របស់​ឪពុក​ទៅ​កែ​ទំនុកច្រៀង​ថ្មី', 'រាជធានី​ភ្នំពេញ ៖ តារាចម្រៀង​លោក សុះ ម៉ាច មាន​ប្រតិកម្ម​ចំពោះ​ផលិតកម្ម​ក្នុងស្រុក​មួយ​ដែល​យក​បទ​ចម្រៀង​ឪពុក​របស់​លោក គឺ​បទ «​ខាន់ស្លា​ឥត​ន័យ​» ទៅ​កែ​ទំនុកច្រៀង​ថ្មី ។', 'http://kohsantepheapdaily.com.kh/article/125413.html', 'http://i.kspg.co/2015/7/06762a57-83b3-4b5c-b420-51a027105b54.jpg?w=285&h=170&mode=carve', 'ថ្ងៃពុធ ទី២៩ ខែកក្កដា ឆ្នាំ២០១៥ ម៉ោង ១០:០២', '12', '0', '0', 't', '2015-07-29', 'f');
INSERT INTO "tbnews" VALUES ('48', 'B020302', 'B020000', 'យុគ ចិន្តា ស្រាយ​មន្ទិល​រឿង​គេ​ផ្អើល​ចូល​រោងការ​ជាមួយ​មន្ត្រី​ថ្នាក់​ធំ', 'រាជធានី​ភ្នំពេញ ៖ ពិធី​ការិនី​លំដាប់​កំពូល​ល្បីឈ្មោះ​ជិត​២០​ឆ្នាំ​អ្នក​នាង យុគ ចិន្តា កាលពី​ប៉ុន្មាន​ខែ​មុនគេ​ល្បី​ថា ត្រៀមខ្លួន​ចូល​រោងការ​ជាមួយ​មន្ត្រី​មានយស​ស័ក្តិ​ម្នាក់​បម្រើ​ការងារ​ក្នុង​ជួរ​រដ្ឋាភិបាល', 'http://kohsantepheapdaily.com.kh/article/125230.html', 'http://i.kspg.co/2015/7/186e6474-f005-45db-a86d-7fc15497b71e.jpg?w=285&h=170&mode=carve', 'ថ្ងៃចន្ទ ទី២៧ ខែកក្កដា ឆ្នាំ២០១៥ ម៉ោង ០៩:១៧', '0', '0', '21', 't', '2015-07-27', 'f');
INSERT INTO "tbnews" VALUES ('49', 'B020302', 'B020000', 'លោក​នួន សុខ​ចំរើន បង្ហាញ​ចំណុច​ជោគជ័យ​ក្នុង​អាជីព​សិល្បៈ', 'រាជធានី​ភ្នំពេញ ៖ លោក​នួន សុខ​ចំរើន​បាន​ជាប់​តំណែង​ជា​អ្នកតំណាង​ផលិតផល​ថែរក្សា​ស្បែក​ប្រចាំឆ្នាំ​២០១៤ បន្ទាប់​ពី​ឆ្លងកាត់​ការ​ប្រកួតប្រជែង​ជាមួយ​បេក្ខជន​ជា​ច្រើន​រូប ។ តំណែង​នេះ​ធ្វើ​ឱ្យ​លោក​ចំរើន', 'http://kohsantepheapdaily.com.kh/article/124902.html', 'http://i.kspg.co/2015/7/dca4daf7-9412-4067-a72f-16aa8167d36a.jpg?w=285&h=170&mode=carve', 'ថ្ងៃពុធ ទី២២ ខែកក្កដា ឆ្នាំ២០១៥ ម៉ោង ១៤:២៩', '12', '0', '1', 't', '2015-07-22', 'f');
