INSERT INTO "tbnews" VALUES ('51', 'B020302', 'B020000', 'ខៀវ សុខា​រ៉ា​វី ស្ថិត​ក្នុង​អាថ៌​កំបាំង​រឿង​បែកបាក់​ស្វាមី', 'រាជធានី​ភ្នំពេញ ៖ តារា​សម្តែង​ខៀវ សុខា​រ៉ា​វី ត្រូវ​គេ​សង្ស័យ​ថា​បែកបាក់​ជាមួយ​ស្វាមី​ដែល​មានបុណ្យ​ស័ក្តិធំ​គួរសម​ទើប​សម្រេចចិត្ត​ងាក​មក​ពិភព​សិល្បៈ​វិញ ។ ព័ត៌មាន​នេះ​កំពុង​តែ​បោកបក់​ខ្លាំង​លើ​តារា​ស្រស់​សោភា​រូប​នេះ​។', 'http://kohsantepheapdaily.com.kh/article/124786.html', 'http://i.kspg.co/2015/7/68f2384b-a02c-42b3-952a-45d8975dc099.jpg?w=285&h=170&mode=carve', 'ថ្ងៃអង្គារ ទី២១ ខែកក្កដា ឆ្នាំ២០១៥ ម៉ោង ០៩:១៧', '1', '0', '21', 't', '2015-07-21', 'f');
INSERT INTO "tbnews" VALUES ('52', 'B020302', 'B020000', 'កូនស្រី​អតីត​តារាភាពយន្ត​ខ្មែរ​ជាប់​ជម្រើស​ពី​រាប់​សិប​នាក់បាន​អាហារូបករណ៍​ទៅ​សិក្សា​ថ្នាក់​បរិញ្ញាបត្រ​នៅ​ប្រទេស​កូរ៉េ', 'រាជធានី​ភ្នំពេញ ៖ កញ្ញា​ត្រឡុង បញ្ញា​រ័​ត្ន អាយុ​២៤​ឆ្នាំ ត្រូវជា​កូនស្រី​របស់​អតីត​តារាភាពយន្ត និង​ផលិតករ​ភាពយន្ត​ខ្មែរ​លោក  ត្រឡុង បូ​រិ​ន ត្រូវ​បាន​ទីភ្នាក់ងារ KOICA របស់​កូរ៉េ​ផ្ដល់​អាហារូបករណ៍​តាម​រយៈ​ការ​ប្រឡង​ជ្រើសរើស​', 'http://kohsantepheapdaily.com.kh/article/124618.html', 'http://i.kspg.co/2015/7/b76bdc64-5c8c-4e33-8166-063aa1827a4b.jpg?w=285&h=170&mode=carve', 'ថ្ងៃអាទិត្យ ទី១៩ ខែកក្កដា ឆ្នាំ២០១៥ ម៉ោង ០៩:៤១', '0', '0', '0', 't', '2015-07-19', 'f');
INSERT INTO "tbnews" VALUES ('53', 'B020302', 'B020000', 'រឿង «​ក្រមុំ​សៅកែ​» របស់​មហាហង្ស​ធ្វើ​ឲ្យ​យុវវ័យ​ចាប់អារម្មណ៍​នា​ថ្ងៃ​ចាក់​បញ្ចាំង​នៅ​សៀមរាប', 'ខេត្តសៀមរាប ៖ បណ្តា​សិល្បករ​ល្បីឈ្មោះ និង​ផលិតករ​ប្រចាំ​ផលិតកម្ម​មហាហង្ស បាន​រួម​គ្នា​ទៅ​សម្តែង​នៅ​ទីក្រុង​សៀម​រាម ដើម្បី​រួម​គ្នា​ផ្សព្វផ្សាយ​ពី​ខ្សែភាពយន្ត​បែប​កំប្លែង​រឿង «​ក្រមុំ​សៅកែ​» ', 'http://kohsantepheapdaily.com.kh/article/124615.html', 'http://i.kspg.co/2015/7/029ce41a-ae1e-4dac-b2c9-975cada4ad56.jpg?w=285&h=170&mode=carve', 'ថ្ងៃអាទិត្យ ទី១៩ ខែកក្កដា ឆ្នាំ២០១៥ ម៉ោង ០៩:២៩', '0', '0', '1', 't', '2015-07-19', 'f');
INSERT INTO "tbnews" VALUES ('54', 'B020302', 'B020000', 'ឆៃ លី​ដា​ឡែ​ន និង​ស្វាមី​បើក​យុទ្ធនាការ​យក​កូន​ឱ្យ​បាន​ឆាប់​ៗ​នេះ', 'រាជធានី​ភ្នំពេញ ៖ អ្នក​នាង​ឆៃ លី​ដា​ឡែ​ន រៀប​ការ​ជាមួយ​ប្តី​ក្មេង​ជា​ច្រើន​ខែ​មក​ហើយ តែ​នៅ​មិន​ទាន់​មានកូន​ទេ ។ មិនតែប៉ុណ្ណោះ​លេចឮ​ថា ស្នេហា​ខុស​វ័យ​នេះ​រង្គោះរង្គើ​ទៀត​ផង ដូច្នេះ​ហើយ​នាង​ឆៃ លី​ដា​ឡែ​ន', 'http://kohsantepheapdaily.com.kh/article/124525.html', 'http://i.kspg.co/2015/7/62482879-654b-46cf-a6e0-45b494e69f58.jpg?w=285&h=170&mode=carve', 'ថ្ងៃសុក្រ ទី១៧ ខែកក្កដា ឆ្នាំ២០១៥ ម៉ោង ១០:០១', '0', '0', '3', 't', '2015-07-17', 'f');
INSERT INTO "tbnews" VALUES ('55', 'B020302', 'B020000', 'សមាគម​សិល្បករ​ខ្មែរ​បន្ទាប់​ពី​បន្ថែម​សមាសភាព​ថ្មី​6​នាក់ នាំ​យក​អំណោយ​ទៅ​ឧបត្ថម្ភ​លោក​ឈិ​ន ឈាង កំពុង​មាន​ជំងឺ', 'រាជធានី​ភ្នំពេញ ៖ សមាគម​សិល្បករ​ខ្មែរ កាលពី​ពេល ថ្មី​ៗ​កន្លង​ទៅ​បាន​ជួប​ប្រជុំ​គ្នា​ដើម្បី​ពិភាក្សា​ការងារ​ចាស់ និង​ការងារ​ថ្មី​មួយ​ចំនួន​ដើម្បី​ឱ្យ​សមាគម​ដើរទៅ​មុខ ព្រមទាំង​បន្ថែម​សមាសភាព​ថ្មី​ចំនួន​៦​នាក់​ទៀត ។ ', 'http://kohsantepheapdaily.com.kh/article/124415.html', 'http://i.kspg.co/2015/7/72c59bc0-3c37-4bb0-bed8-77e624316595.jpg?w=285&h=170&mode=carve', 'ថ្ងៃព្រហស្បត្ត៏ ទី១៦ ខែកក្កដា ឆ្នាំ២០១៥ ម៉ោង ០៩:២២', '0', '0', '0', 't', '2015-07-16', 'f');
INSERT INTO "tbnews" VALUES ('56', 'B020107', 'B020000', 'ព្រះរាជ​ជីវប្រវត្តិ និង​ស្នា​ព្រះ​ហស្ត​នៃ​សម្តេចព្រះសង្ឃរាជ​ជួន ណាត', 'លោ​កន​ង លន់ លេខាធិការ​នៃ​ពុទ្ធិក​សមាគម​កម្ពុជរដ្ឋ ក្នុង​កំណត់​ត្រា​ចុះ​ថ្ងៃ​ទី​៧ ខែវិច្ឆិកា ឆ្នាំ​១៩៦៩ បាន​រៀបរៀង ចងក្រង​ព្រះរាជ​ជីវប្រវត្តិ និង​ស្នា​ព្រះ​ហស្ត​នៃ​សម្តេចព្រះ​មហាសុមេធាធិបតី​ជួន ណាត ', 'http://kohsantepheapdaily.com.kh/article/125336.html', 'http://i.kspg.co/2015/7/db8b45b4-7ff6-4dc6-8cdd-0b7e186dce93.jpg?w=285&h=170&mode=carve', 'ថ្ងៃអង្គារ ទី២៨ ខែកក្កដា ឆ្នាំ២០១៥ ម៉ោង ០៩:៥៩', '0', '0', '0', 't', '2015-07-28', 'f');
INSERT INTO "tbnews" VALUES ('57', 'B020107', 'B020000', 'អាជ្ញាធរ​ជាតិ​អប្សរា​នឹង​រៀបចំ​បាឋកថា​ស្តី​ពី «​ទំនាក់ទំនង​សិល្បៈ​ខ្មែរ និង​ចាម​»', 'ខេត្តសៀមរាប ៖ អាជ្ញាធរ​ជាតិ​អប្សរា​នឹង​រៀបចំ​ធ្វើ​បាឋកថា ស្តី​ពី​«​ទំនាក់ទំនង​សិល្បៈ​ខ្មែរ និង​ចាម​» នៅ​សាល​សិក្សា​នៃ​មជ្ឈមណ្ឌល​បណ្តុះបណ្តាល​អង្គរ របស់​អាជ្ញាធរ​ជាតិ​អប្សរា នា​ថ្ងៃ​ទី​៣០ ខែកក្កដា ឆ្នាំ​២០១៥ ។ ', 'http://kohsantepheapdaily.com.kh/article/125233.html', 'http://i.kspg.co/2015/7/768fd79a-b563-40fb-9c34-4fd2dadcf96a.jpg?w=285&h=170&mode=carve', 'ថ្ងៃចន្ទ ទី២៧ ខែកក្កដា ឆ្នាំ២០១៥ ម៉ោង ០៩:៣១', '0', '0', '0', 't', '2015-07-27', 'f');
INSERT INTO "tbnews" VALUES ('58', 'B020107', 'B020000', 'ព្រះបាទ​ជ័យ​វរ្ម័ន​ទី​7 ជា​ស្តេច​ចក្រពត្តិ​ដ៏​ខ្លាំងពូកែ មាន​ជំនាញ​ខាង​ប្រើ​នយោបាយ​ចិញ្ចឹម​សត្រូវ​ឱ្យ​វាយប្រហារ​សត្រូវ', 'ជីវប្រវត្តិ និង​ស្នាដៃ​ដ៏​អស្ចារ្យ​លើ​ការ​ដឹកនាំ​ប្រទេស​ឱ្យ​រីក​ចម្រើន និង​មាន​អំណាច​ខ្លាំងពូកែ​របស់​ព្រះបាទ​ជ័យ​វរ្ម័ន​ទី​៧ លាតត្រដាង​តាម​រយៈ​អត្ថបទ​រៀបរៀង​ដោយ​លោក​មី​សែ​ល ត្រា​ណេ ហើយ​ទន្ទឹម​នេះ​លោក​វង់ សុធា​រ៉ា ', 'http://kohsantepheapdaily.com.kh/article/124245.html', 'http://i.kspg.co/2015/7/7ea9a72c-89dd-4a04-9d92-b73bd13dcc17.jpg?w=285&h=170&mode=carve', 'ថ្ងៃអង្គារ ទី១៤ ខែកក្កដា ឆ្នាំ២០១៥ ម៉ោង ១១:០៥', '0', '1', '0', 't', '2015-07-14', 'f');
INSERT INTO "tbnews" VALUES ('59', 'B020107', 'B020000', 'ល្បែង​កីឡា​ប្រជាប្រិយ​ខ្មែរ «​ប្រណាំង​រនាស់​»', 'យោង​តាម​សៀវភៅ «​ប្រពៃណី​និង​ទំនៀមទម្លាប់​ខ្មែរ​» រៀបរៀង​ដោយ​ព្រឹទ្ធាចារ្យ​មៀ​ច ប៉ុណ្ណ អធិប្បាយ​ថា មិន​តែ​នៅ​រដូវប្រាំង​ទេ ទោះបីជា​រដូវវស្សា​ដែល​គេ​ជាប់ដៃ​យ៉ាង​មមាញឹក​ក្នុង​ការងារ​ស្រែ​ចម្ការ​ក៏​ដោយ ', 'http://kohsantepheapdaily.com.kh/article/123610.html', 'http://i.kspg.co/2015/7/2def01a5-2bf2-446c-a3c4-6597be4ac940.jpg?w=285&h=170&mode=carve', 'ថ្ងៃចន្ទ ទី៦ ខែកក្កដា ឆ្នាំ២០១៥ ម៉ោង ០៩:៥៣', '0', '0', '21', 't', '2015-07-06', 'f');
INSERT INTO "tbnews" VALUES ('60', 'B020107', 'B020000', 'ជួប​គ្រោះ​រាំង​ស្ងួត​អ្នក​ភូមិ​អង្គរ​ប្រារព្ធ​ពិធី «​ហៅ​ទឹកភ្លៀង​»ខណៈ​ព្រះសង្ឃ​កំពុង​សូត្រធម៌ ភ្លៀង​បង្អុរ​មក​យ៉ាង​ស័ក្តិសិទ្ធិ', 'រាជធានី​ភ្នំពេញ ៖ អ្នក​ភូមិ​នៅ​តំបន់​អង្គរ​នៃ​ខេត្តសៀមរាប​តាំងពី​បុរាណ​មក​ដល់​បច្ចុប្បន្ន​នៅ​តែ​មាន​ជំនឿ​ប្រារព្ធ​ពិធី «​ហៅ​ទឹកភ្លៀង​» ខណៈ​ជួប​ភាពរាំងស្ងួត​ដើម្បី​បាន​ទឹកភ្លៀង​ប្រកប​របរ​កសិកម្ម និង​ប្រើប្រាស់​ក្នុង​ជីវភាព​ប្រចាំ​ថ្ងៃ ។ ', 'http://kohsantepheapdaily.com.kh/article/123095.html', 'http://i.kspg.co/2015/6/51767338-c0b1-402d-a9b8-d1c58de0ca28.jpg?w=285&h=170&mode=carve', 'ថ្ងៃចន្ទ ទី២៩ ខែមិថុនា ឆ្នាំ២០១៥ ម៉ោង ១៣:៥៦', '0', '0', '0', 't', '2015-06-29', 'f');
INSERT INTO "tbnews" VALUES ('61', 'B020107', 'B020000', 'របៀបរបប​រស់នៅ​ជនជាតិភាគតិច « រ​អូ​ង »', 'ខេត្តមណ្ឌលគិរី ៖ មិន​ត្រឹមតែ​សម្បូរ​ទៅ​ដោយ​សម្បត្តិ​ធម្មជាតិ​សន្ធឹកសន្ធាប់​ទេ ខេត្តមណ្ឌលគិរី​មាន​ប្រពៃណី ប្លែក​ៗ​របស់​ជនជាតិ​ដើម​ភាគតិច​ជា​ច្រើន​អម្បូរ ។ កោះសន្តិភាព​សូម​នាំ​យក​ប្រពៃណី​ស្លៀកពាក់', 'http://kohsantepheapdaily.com.kh/article/121630.html', 'http://i.kspg.co/2015/6/11221342-1ae1-4808-8d60-b9ba1911df24.jpg?w=285&h=170&mode=carve', 'ថ្ងៃអង្គារ ទី៩ ខែមិថុនា ឆ្នាំ២០១៥ ម៉ោង ១០:៤២', '1', '2', '1', 't', '2015-06-09', 'f');
INSERT INTO "tbnews" VALUES ('62', 'B020107', 'B020000', 'ពិធី​សែន​ចាស់ទុំ​តាម​ជំនឿ​របស់​ជនជាតិ​ខ្មែរ​យើង', 'យោង​តាម​សៀវភៅ « កម្រង​ឯកសារ​ស្តី​ពី​ប្រពៃណី និង​ទំនៀមទម្លាប់​ខ្មែរ » របស់​ព្រឹទ្ធាចារ្យ​មៀ​ច ប៉ុណ្ណ អធិប្បាយ​ថា ពិធី​សែន​ចាស់ទុំ ជា​ផ្នែក​មួយ​នៃ​លទ្ធិ​ព្រលឹង​និយម (​ព្រលឹង​និយម ឬ​ជី​វច​ល​និយម ។', 'http://kohsantepheapdaily.com.kh/article/121120.html', 'http://i.kspg.co/2015/6/41e0cc2e-c6b2-4ab1-9ce7-5039cd7447b8.jpg?w=285&h=170&mode=carve', 'ថ្ងៃអង្គារ ទី២ ខែមិថុនា ឆ្នាំ២០១៥ ម៉ោង ០៩:២៥', '0', '0', '0', 't', '2015-06-01', 'f');
INSERT INTO "tbnews" VALUES ('63', 'B020107', 'B020000', 'អា​មេ​រិ​កប្រគល់​វត្ថុ​បុរាណ រូបចម្លាក់ហនុមាន មក​ឲ្យ​កម្ពុជា​វិញ', 'រាជធានី​ភ្នំពេញ ៖ សហរដ្ឋអាមេរិក​បាន​យល់ព្រម​ប្រគល់​វត្ថុ​បុរាណ​ដែល​ជា រូបចម្លាក់ ហនុមាន មក​អោយ​កម្ពុជា​វិញ បន្ទាប់​ពី​រូបចម្លាក់ ដ៍​មាន​តម្លៃ នេ​ហ ត្រូវ​បាន​គេ​ដាក់​តាំង​អស់​រយៈពេល​៣៣​ឆ្នាំ នា​សារមន្ទី​ប្រទេស​អា​មេ​រិ​ក ។ រូបចម្លាក់ ហនុមាន', 'http://kohsantepheapdaily.com.kh/article/119709.html', 'http://i.kspg.co/2015/5/9e1b826b-e547-4009-bb11-f22c559fafd4.jpg?w=285&h=170&mode=carve', 'ថ្ងៃចន្ទ ទី១១ ខែឧសភា ឆ្នាំ២០១៥ ម៉ោង ១៤:០៥', '0', '123132', '1', 't', '2015-05-11', 'f');
INSERT INTO "tbnews" VALUES ('64', 'B020107', 'B020000', 'ពិធី​បំបួស​កុលបុត្រ កុលធីតា​ខ្មែរ​ចំនួន​700​រូប​នៅ​សៀមរាប', 'ខេត្តសៀមរាប ៖ កាលពី​រសៀល​ថ្ងៃ​ទី​២៩ ខែមេសា ឆ្នាំ​២០១៥ នៅ​វត្ត​អង្គរ​ត្បូង ខេត្តសៀមរាប បាន​រៀបចំ​ពិធី​បំបួស​កុលបុត្រ​-​កុលធីតា​ខ្មែរ​ចំនួន​៧០០​នាក់ ក្នុង​ចេតនា​អប់រំ​សីលធម៌ ចរិយា​ធម៌ ', 'http://kohsantepheapdaily.com.kh/article/119119.html', 'http://i.kspg.co/2015/5/28df444f-1f51-4bfc-92e5-8450d16359ea.jpg?w=285&h=170&mode=carve', 'ថ្ងៃសុក្រ ទី១ ខែឧសភា ឆ្នាំ២០១៥ ម៉ោង ០៩:០៦', '2', '2', '23', 't', '2015-05-01', 'f');
INSERT INTO "tbnews" VALUES ('65', 'B020107', 'B020000', 'ព្រះ​វិហារ​បុរាណ​អាយុ​ជាង​300​ឆ្នាំ​នៅ​វត្ត​សំរោង​ក្នុង​គ្រោង​អភិរក្ស​ជា​បេតិកភណ្ឌ​ជាតិ', 'ខេត្តបាត់ដំបង ៖ ព្រះ​វិហារ​ដ៏​ចំណាស់ ដែល​មាន​អាយុកាល​ជាង​៣០០​ឆ្នាំ នៅ​វត្ត​សំរោង​ក្នុង ស្ថិត​ក្នុងភូមិ​សំរោង​ក្នុង ឃុំ​សំរោង​ក្នុង ស្រុក​ឯក​ភ្នំ ខេត្តបាត់ដំបង អាច​នឹង​រៀបចំ​អភិរក្ស', 'http://kohsantepheapdaily.com.kh/article/118448.html', 'http://i.kspg.co/2015/4/e3740c58-e381-4a40-9353-57a8576efa59.jpg?w=285&h=170&mode=carve', 'ថ្ងៃចន្ទ ទី២០ ខែមេសា ឆ្នាំ២០១៥ ម៉ោង ០៩:៣៤', '1', '1', '1', 't', '2015-04-20', 'f');
INSERT INTO "tbnews" VALUES ('66', 'B020107', 'B020000', 'ជោគជតារាសី​អ្នក​កើត​ឆ្នាំមមែ', 'លោក​អ្នក​កើត​ឆ្នាំមមែ ពង្ស​ទេវតា​ស្រី នាម​ជា​សត្វ​ពពែ ធាតុមាស អង្សាកំណើត​នៅ​ថ្ងៃអាទិត្យ សិរី​ធ្លាក់​មក​ពី​ដៃ​ឆ្វេង​របស់​ព្រះព្រហ្ម មូល​ព្រលឹង​នៅ​នឹង​ដើម​បា​រិ​ក​ជាតិ និង​ដើម​ឫស្សី ។ ', 'http://kohsantepheapdaily.com.kh/article/118309.html', 'http://i.kspg.co/2015/4/33ad3fca-2d61-4a74-9842-db125c4b4e23.jpg?w=285&h=170&mode=carve', 'ថ្ងៃអាទិត្យ ទី១២ ខែមេសា ឆ្នាំ២០១៥ ម៉ោង ០៩:១២', '121', '0', '3', 't', '2015-04-12', 'f');
INSERT INTO "tbnews" VALUES ('67', 'B020107', 'B020000', 'មហាសង្ក្រាន្ត​ឆ្នាំមមែ និង​រណ្តាប់​ទេវតាឆ្នាំថ្មី', 'ព្រះពុទ្ធ​សករាជ​ព្រះ​សាសនា អតិ​ក្កន្តា ​កន្លង​ទៅ​ហើយ​បាន​២៥៥៨ ត្រឹម​ថ្ងៃ​១៥​កើត ខែ​ពិសាខ លុះដល់​ថ្ងៃ​១​រោច ខែ​ពិសាខ ឆ្នាំមមែ សប្តស័ក តទៅ ចូល​ពុទ្ធសករាជ​២៥៥៩ ។ នឹង​គណនា​ឆ្នាំមមែ ឥឡូវនេះ​ស​ង្ក្រា​ន​ចូល​មក​នៅ​ថ្ងៃ​អង្គារ', 'http://kohsantepheapdaily.com.kh/article/118281.html', 'http://i.kspg.co/2015/4/55e8117c-faee-4fe4-9b4b-8836bb18b62f.jpg?w=285&h=170&mode=carve', 'ថ្ងៃសុក្រ ទី១០ ខែមេសា ឆ្នាំ២០១៥ ម៉ោង ១៧:១៦', '0', '2', '0', 't', '2015-04-10', 'f');
INSERT INTO "tbnews" VALUES ('68', 'B020107', 'B020000', 'និស្សិត​ខ្មែររៀននៅ​ថៃ​ រៀបចំ​សង្ក្រាន្ត​ខ្មែរ​​ ចង់​ឱ្យ​ឃើញ​ភាព​ប្លែកគ្នារវាង​សង្ក្រាន្ត​ខ្មែរ និងថៃ', 'នៅ​ថ្ងៃ​ទី​5-6 ខែមេសា ឆ្នាំ​2015 ក្លឹ​ប​ភាសា​ និង​វប្បធម៌​ខ្មែរ សាកលវិទ្យាល័យ​ប៊ូ​រ​ផា (Cambodian Language and Culture Club, CLCC) បាន​រៀបចំ​កម្មវិធី "CLCC Songkran Khmer 2015" ដើម្បី​អោយ​និស្សិត​ខ្មែរ​ដែល​កំពុង​រៀន​នៅ​ប្រទេស​ថៃ', 'http://kohsantepheapdaily.com.kh/article/118219.html', 'http://i.kspg.co/2015/4/eb09617e-f599-46cc-9420-3bb153050800.jpg?w=285&h=170&mode=carve', 'ថ្ងៃព្រហស្បត្ត៏ ទី៩ ខែមេសា ឆ្នាំ២០១៥ ម៉ោង ១៧:៥៤', '0', '21', '1', 't', '2015-04-09', 'f');
INSERT INTO "tbnews" VALUES ('69', 'B020107', 'B020000', 'ទំនាយ​ជោគជតារាសី​ក្នុង​ឆ្នាំមមែ សម្រាប់​អ្នក​កើត​ឆ្នាំ​ផ្សេង​ៗ', 'លោក​អ្នក​កើត​ឆ្នាំជូត បើជា​មនុស្ស​ប្រុស​ទំនាយ​ថា ៖ ក្នុងឆ្នាំនេះ អ្នក​តោង​កែកុន​ប្រើ​សេចក្តី​ពិនិត្យពិច័យ​ឱ្យ​ល្អិតល្អន់​បន្តិច សូ​ម្បី​តែ​អ្នក​ដែល​ធ្លាប់​រាប់រក​អ្នក​យ៉ាងណា ឬ​អ្នកធ្វើការ​ងារ​រួម​នឹង​គេ អ្នក​កុំ​សង្ឃឹម​ឡើយ​ថា​នឹង​បាន​', 'http://kohsantepheapdaily.com.kh/article/118177.html', 'http://i.kspg.co/2015/4/a5ac49ad-6584-4fea-9b9b-9a76b7cc921e.jpg?w=285&h=170&mode=carve', 'ថ្ងៃព្រហស្បត្ត៏ ទី៩ ខែមេសា ឆ្នាំ២០១៥ ម៉ោង ១០:០៥', '0', '0', '0', 't', '2015-04-09', 'f');
INSERT INTO "tbnews" VALUES ('70', 'B020107', 'B020000', 'ប្រវត្តិ​នៃ​ពិធី​បុណ្យចូលឆ្នាំ​ថ្មី​ប្រពៃណី​ខ្មែរ', 'មានរឿង​តំណាល​ថា នៅ​ដើម​នៃ​ភទ្ទកប្ប​សេដ្ឋី​ម្នាក់​មាន​បុត្រ​មួយ​ឈ្មោះ​ធម្មបាល​កុមារ ជា​អ្នកមាន​ចំណេះវិជ្ជា​ដ៏​វិសេស រៀន​ចេះ​ចប់​ត្រៃវេទ​តាំងពី​អាយុ​បាន​៧​ឆ្នាំ ។ សេដ្ឋី​បិតា​បាន​សាង​ប្រាសាទ', 'http://kohsantepheapdaily.com.kh/article/118089.html', 'http://i.kspg.co/2015/4/04ac0e07-2945-4150-b138-5c08d7e9f832.jpg?w=285&h=170&mode=carve', 'ថ្ងៃពុធ ទី៨ ខែមេសា ឆ្នាំ២០១៥ ម៉ោង ០៩:៤៦', '0', '0', '1', 't', '2015-04-08', 'f');
INSERT INTO "tbnews" VALUES ('71', 'B020107', 'B020000', 'លោក​អឹុម សុង​សឺ​ម ស្លាប់​ទាំង​វ័យក្មេង ខណៈ​កំពុង​ល្បីឈ្មោះ​ក្នុង​អាជីព​ចម្រៀងច្រៀង​ចំអក​ឱ្យ​អ្នកស្រី​ហួយ មាស សិស្ស​រួម​គ្រូ', 'ប្រភព​ដើម លោក​អឹុម សុង​សឺ​ម ចាប់កំណើត​នៅ​ឆ្នាំ​១៩៤៣ នៅ​ឃុំ​កំពង់ព្រះ ស្រុក​សង្កែ ខេត្តបាត់ដំបង ។ លោក​អឹុម ស៊ាន និង​អ្នកស្រី​តាន់ សឿ​ន ជា​អ្នក​ផ្តល់​កំណើត​ឱ្យ​លោក​​អឹុម សុង​សឺ​ម ។ លោក​អឹុម សុង​សឺ​ម ជា​អតីត​សិស្ស​ថ្នាក់​ទី​៣​នៃ​វិទ្យាល័យ​ព្រះ​មុនី​វង្ស ខេត្តបាត់ដំបង ។', 'http://kohsantepheapdaily.com.kh/article/118020.html', 'http://i.kspg.co/2015/4/2a7e9e53-7a3d-40be-9020-b269f7537206.jpg?w=285&h=170&mode=carve', 'ថ្ងៃអង្គារ ទី៧ ខែមេសា ឆ្នាំ២០១៥ ម៉ោង ០៩:៤៥', '0', '0', '0', 't', '2015-04-07', 'f');
INSERT INTO "tbnews" VALUES ('72', 'B020107', 'B020000', 'ល្បែង​បោះអង្គញ់​និយម​លេង​រដូវ​បុណ្យចូលឆ្នាំ', 'យោង​តាម​សៀវភៅ «​ល្បែង​ប្រជាប្រិយ​ខ្មែរ​» លើក​ឡើង​ថា ល្បែង​បោះអង្គញ់ គេ​ឲ្យ​ឈ្មោះ​តាម​វត្ថុ​ដែល​គេ​យក​មក​លេង គឺ​ផ្លែ​អង្គញ់​ដែល​ក្រៀម​ស្ងួត ។ ល្បែង​នេះ គេ​លេង​បាន​ទាំងប្រុស​ទាំងស្រី', 'http://kohsantepheapdaily.com.kh/article/117882.html', 'http://i.kspg.co/2015/4/2476a7a9-7119-4e5a-8943-c9ae43e407f4.jpg?w=285&h=170&mode=carve', 'ថ្ងៃអាទិត្យ ទី៥ ខែមេសា ឆ្នាំ២០១៥ ម៉ោង ០៨:៤៩', '1', '0', '11', 't', '2015-04-05', 'f');
INSERT INTO "tbnews" VALUES ('73', 'B020107', 'B020000', 'ល្បែង​ប្រជាប្រិយ​ខ្មែរ​«​ចាប់កូនខ្លែង​»', 'ល្បែង «​ចាប់កូនខ្លែង​» ជា​ល្បែង​របស់​កុមា​រា​កុមារី ជំទង់​ៗ តែងតែ​លេង​ក្នុង​វេលា​យប់​ខែភ្លឺ នៅ​រដូវ​ចូល​ឆ្នាំ ឬ​នៅ​ពេលទំនេរ ។ ល្បែង​នេះ​ពេល​លេង​គេ​ចាត់​ម្នាក់​ដែល​មាន​មាឌ​មាំ​ធំ ឱ្យធ្វើ​ជា​', 'http://kohsantepheapdaily.com.kh/article/117587.html', 'http://i.kspg.co/2015/3/2bbc2554-41ce-4d8c-99ef-f46606599b4e.jpg?w=285&h=170&mode=carve', 'ថ្ងៃអង្គារ ទី៣១ ខែមីនា ឆ្នាំ២០១៥ ម៉ោង ១០:១៨', '1', '1', '1', 't', '2015-01-31', 'f');
INSERT INTO "tbnews" VALUES ('74', 'B020301', 'B020000', 'ភ្នំ​ជ្រាវ ឬ​បារាំង​ដោត​ទង់​មាន​ទឹកធ្លាក់​ខួប​ប្រាំង​វស្សា​គួរ​គាប់​កម្សាន្តទឹកថ្លា​ត្រជាក់​អាច​ក្លា​ស្សេ​ស្រា​បៀរ​ជំនួស​ទឹកកក​បាន', 'ខេត្តកំពង់ស្ពឺ ៖ ភ្នំ​ជ្រាវ ឬ​បារាំង​ដោត​ទង់​មាន​ទឹកធ្លាក់​ខួប​ប្រាំង វស្សា​មិន​ដែល​ដាច់​ទេ ។ នៅ​ទីនោះ​មាន​ទឹកជ្រោះ​៧​ជាន់ មានព្រៃ​ព្រឹក្សា លតា វល្លិ ពណ៌​បៃតងខ្ចី និង​មាន​សំឡេង​សត្វ​យំ​ប្រគំ​ជា​តន្ត្រី​ធម្មជាតិ​លាយឡំ​សូរ​ទឹក​ហូរ ', 'http://kohsantepheapdaily.com.kh/article/125055.html', 'http://i.kspg.co/2015/7/9e4f0f07-bd3a-4832-8a4f-8a1943364f19.jpg?w=285&h=170&mode=carve', 'ថ្ងៃសុក្រ ទី២៤ ខែកក្កដា ឆ្នាំ២០១៥ ម៉ោង ១០:០៦', '0', '2', '231', 't', '2015-07-24', 'f');
INSERT INTO "tbnews" VALUES ('75', 'B020301', 'B020000', 'ក្រុង​បាងកក​ជា​គោលដៅ​ភ្ញៀវ​ទេសចរ​ចង់ទៅ​កម្សាន្ត​ទី​1​នៅ​អាស៊ី', 'រាជធានី​ភ្នំពេញ ៖ វិស័យ​ទេសចរណ៍​គឺជា​ប្រភព​ចំណូល​មួយ​ដ៏​សំខាន់ និង​ជា​ឧស្សាហកម្ម​គ្មាន​ផ្សែង​ដែល​បាន​ចូលរួម​ចំណែក​ក្នុង​វិស័យសេដ្ឋកិច្ច នយោបាយ និង​ទំនាក់​ទំនង​អន្តរជាតិ​របស់​ប្រទេស​ថៃ ។ ដោយហេតុ​នេះ​ហើយ', 'http://kohsantepheapdaily.com.kh/article/124418.html', 'http://i.kspg.co/2015/7/9fc30519-c1d0-4198-bccc-bac6779009d2.jpg?w=285&h=170&mode=carve', 'ថ្ងៃព្រហស្បត្ត៏ ទី១៦ ខែកក្កដា ឆ្នាំ២០១៥ ម៉ោង ០៩:៣០', '0', '12', '1', 't', '2015-07-16', 'f');
INSERT INTO "tbnews" VALUES ('76', 'B020301', 'B020000', 'ជប៉ុន​ភ្ជាប់ Wi-Fi នៅ​ភ្នំ​ហ៊្វូ​ជី ទាក់ទាញ​អ្នកទេសចរ', 'សារព័ត៌មាន «​អា​សាហ៊ី» របស់​ជប៉ុន ផ្សាយ​ថា កាលពី​ថ្ងៃ​ទី​១០ កក្កដា ២០១៥ អាជ្ញាធរ​ជប៉ុន បើក​បម្រើ​អីុន​ធើ​រ​ណេ​ត​អត់​ខ្សែ (Wi-Fi)  ដោយ​មិន​គិត​ប្រាក់​នៅ​លើ​ភ្នំភ្លើង​ហ៊្វូ​ជី (Fuji) ជា​លើក​ដំបូង ', 'http://kohsantepheapdaily.com.kh/article/124135.html', 'http://i.kspg.co/2015/7/73f6560a-1bf3-4e4d-bf71-df906db52729.jpg?w=285&h=170&mode=carve', 'ថ្ងៃចន្ទ ទី១៣ ខែកក្កដា ឆ្នាំ២០១៥ ម៉ោង ១០:៤៣', '12', '3', '1', 't', '2015-07-13', 'f');
INSERT INTO "tbnews" VALUES ('77', 'B020301', 'B020000', 'ភ្នំ​ខៀវស្រងាត់​របស់​ហ្សា​ម៉ា​អីុកបាន​ចុះបញ្ជី​ជា​បេតិកភណ្ឌ​ពិភពលោក', 'ភ្នំ​ខៀវស្រងាត់​របស់​ហ្សា​ម៉ា​អីុ​ក «Blue and John Crow Mountians» បាន​ចុះបញ្ជី​ជា​បេតិកភណ្ឌ​ពិភពលោក​លើក​ដំបូង​របស់​ប្រទេស​ហ្សា​ម៉ា​អីុ​ក តាម​សេចក្តី​ប្រកាស​របស់​អង្គការ​យូ​ណេ​ស្កូ​។', 'http://kohsantepheapdaily.com.kh/article/123949.html', 'http://i.kspg.co/2015/7/9ddccbad-7141-4f2e-8989-0418580c2996.jpg?w=285&h=170&mode=carve', 'ថ្ងៃសុក្រ ទី១០ ខែកក្កដា ឆ្នាំ២០១៥ ម៉ោង ០៩:២៥', '0', '0', '0', 't', '2015-07-10', 'f');
INSERT INTO "tbnews" VALUES ('78', 'B020301', 'B020000', '«​អាណាចក្រ​សត្វ​ព្រាប​» មុខ​ព្រះ​បរមរាជវាំង​ទាក់ទាញ​អ្នកទេសចរ​ច្រើន​ឡើ​​ង​ទៅ​កម្សាន្ត', 'រាជធានី​ភ្នំពេញ ៖ ខ្លះ​ដើរ​នៅ​លើ​ដី ខ្លះ​ហោះហើរ ទាប​ៗ សត្វ​ព្រាប​រាប់រយ​ក្បាល​នៅ​មុខ​ព្រះ​បរមរាជវាំង បង្កើត​សោភ័ណ​ភាព​គាប់​ភ្នែក​អ្នកទេសចរ ។ អ្នកទេសចរ​និយម​ទៅ​បោះ​ចំណី និង​ប្រលែង​លេង​នឹង​សត្វ​ព្រាប', 'http://kohsantepheapdaily.com.kh/article/123943.html', 'http://i.kspg.co/2015/7/edb64155-1cf3-4143-b3dd-ef7b7366b685.jpg?w=285&h=170&mode=carve', 'ថ្ងៃសុក្រ ទី១០ ខែកក្កដា ឆ្នាំ២០១៥ ម៉ោង ០៩:១៥', '2', '1', '0', 't', '2015-07-10', 'f');
INSERT INTO "tbnews" VALUES ('79', 'B020301', 'B020000', '​ចំណូល​​ពី​ការ​លក់​សំបុត្រ​ចូល​ទស្សនា​អង្គរ​វត្ត​​ក្នុង​ឆមាស​ទី​១​​ថយ​ចុះ​​ ខណៈ​​ភ្ញៀវ​មក​ទស្សនា​មានការ​កើនឡើង', 'ខេត្តសៀមរាប​ ៖ អាជ្ញាធរ​ជាតិ​អប្សរាបាន​ឲ្យ​ដឹង​ថា​ ចំណូល​បាន​ពី​ការ​លក់​សំបុត្រ​ចូល​ទស្សនាអង្គរវត្ដ​ក្នុង​ឆមាស​ទី​១ ​ឆ្នាំ​២០១៥​នេះ​ គឺ​ទទួល​បាន​ជាង​៣០​លាន​ដុល្លារ ហើយ​ថយ​ចុះ​ជិត​១​ភាគរយ​បើ​ធៀប​នឹង​រយៈ​', 'http://kohsantepheapdaily.com.kh/article/123744.html', 'http://i.kspg.co/2015/7/b25f5b0c-de12-4761-b255-38305ee28a7c.jpg?w=285&h=170&mode=carve', 'ថ្ងៃអង្គារ ទី៧ ខែកក្កដា ឆ្នាំ២០១៥ ម៉ោង ១៤:៥៤', '0', '0', '0', 't', '2015-07-07', 'f');
INSERT INTO "tbnews" VALUES ('80', 'B020301', 'B020000', 'ការ​សិក្សា​ថ្មី​បង្ហាញ​ថា ព្រៃ​ឡង់​ជា​តំបន់​ជីវចម្រុះ​លំដាប់​អន្តរជាតិ', 'រាជធានី​ភ្នំពេញ ថ្ងៃ​ទី​៣០ ខែមិថុនា ឆ្នាំ​២០១៥ ៖ ការ​សិក្សា​ថ្មី​មួយ​ដែល​ត្រូវ​បាន​រៀបចំ​ផ្សព្វផ្សាយ​នៅ​ថ្ងៃនេះ​នៅ​ក្នុង​ទីក្រុង​ភ្នំពេញ ដោយ​ទីភ្នាក់ងារ​សហរដ្ឋអាមេរិក​សម្រាប់​អភិវឌ្ឍន៍​អន្តរជាតិ (USAID) តាម​រយៈ​គម្រោង​គាំទ្រ​ព្រៃឈើ', 'http://kohsantepheapdaily.com.kh/article/123325.html', 'http://i.kspg.co/2015/7/fd2ee576-230e-4e84-94df-4806937432a6.jpg?w=285&h=170&mode=carve', 'ថ្ងៃព្រហស្បត្ត៏ ទី២ ខែកក្កដា ឆ្នាំ២០១៥ ម៉ោង ០៩:៣៣', '0', '1', '0', 't', '2015-07-01', 'f');
INSERT INTO "tbnews" VALUES ('81', 'B020301', 'B020000', 'ប្រែក្លាយ​តំបន់​អភិវឌ្ឍន៍​ទេសចរណ៍ធម្មជាតិ​គិរី​រម្យ​ឱ្យ​កាន់តែ​មាន​សក្តានុពល', 'ខេត្ត​កំពង់់​ស្ពឺ ៖ រាជរដ្ឋាភិបាល បាន​សម្ពោធ​ដាក់​ឲ្យ​ប្រើប្រាស់​សមិទ្ធផល​នានា នៅ​រមណីយដ្ឋាន​វី​គិរី​រម្យ ផា​ញ​រី​សត និង​បើក​ការដ្ឋាន​សាងសង់​ជា​ផ្លូវការ​ក្នុង​តំបន់​អភិវឌ្ឍ​ទេសចរណ៍ធម្មជាតិ​គិរី​រម្យ​ក្រោម​ការ​អភិវឌ្ឍ​របស់​ក្រុមហ៊ុន A 2 A Town Cambodia LTd', 'http://kohsantepheapdaily.com.kh/article/123182.html', 'http://i.kspg.co/2015/6/e097a246-8af4-455f-b8c5-61761dd4f16e.jpg?w=285&h=170&mode=carve', 'ថ្ងៃអង្គារ ទី៣០ ខែមិថុនា ឆ្នាំ២០១៥ ម៉ោង ១០:១៤', '1', '1', '0', 't', '2015-06-30', 'f');
INSERT INTO "tbnews" VALUES ('82', 'B020301', 'B020000', 'ខេត្តមណ្ឌលគិរីបំពេញ​បាន​ស្តង់ដារ និង​ទទួលស្គាល់​ចំណាត់​ថ្នាក់​ផ្កាយ០១ និង​ផ្កាយ​០៣', 'ខេត្ត​មណ្ឌល​គិរី​ ៖ បន្ទាប់​ពី​ការ​ចុះ​ពិនិត្យ និង​វាយតម្លៃ​ចំណាត់ថ្នាក់​សណ្ឋាគារ​របស់​គណៈកម្មការ ក្រសួងទេសចរណ៍ ឃើញ​ថា ​សណ្ឋាគារ និង​រមណីយដ្ឋាន​ចំនួន០៤​ក្នុង​ខេត្តមណ្ឌលគិរីបំពេញ​បាន​ស្តង់ដារ ', 'http://kohsantepheapdaily.com.kh/article/123150.html', 'http://i.kspg.co/2015/6/c095a9ed-3445-4f54-b685-f48ac1f691a0.jpg?w=285&h=170&mode=carve', 'ថ្ងៃអង្គារ ទី៣០ ខែមិថុនា ឆ្នាំ២០១៥ ម៉ោង ០៨:៣៧', '0', '21', '0', 't', '2015-06-30', 'f');
INSERT INTO "tbnews" VALUES ('83', 'B020301', 'B020000', 'សត្វ​ដំរី​ញី​ឈ្មោះ​សំបូរ​កំពុង​រស់​នៅ​យ៉ាង​សុខ​សាន្ត​ក្នុង​តំបន់​ព្រៃ​អភិរក្ស​ជីវ​ចម្រុះ​សីមា', 'មណ្ឌលគិរី​៖ បន្ទាប់​ពី​បាន​ចូល​និវត្ត​ន៍បាន​ជិត​មួយ​ឆ្នាំ សំបូរ​​​ជា​ឈ្មោះ​របស់​សត្វ​ដំរី​ញី​មួយ​ក្បាល ដែល​បាន​សាង​កេរ្តិ៍ឈ្មោះ​​​ល្បីល្បាញ​នៅ​រាជធានី​ភ្នំពេញ បច្ចុប្បន្ន​កំពុង​តែ​រស់នៅ​យ៉ាង​សុខសាន្ត​ជាមួយ​សត្វ​ដំរី​ដទៃ​', 'http://kohsantepheapdaily.com.kh/article/121855.html', 'http://i.kspg.co/2015/6/ec3e1a1e-f10c-412b-b384-39474020caa1.jpg?w=285&h=170&mode=carve', 'ថ្ងៃព្រហស្បត្ត៏ ទី១១ ខែមិថុនា ឆ្នាំ២០១៥ ម៉ោង ១៦:៣១', '0', '0', '0', 't', '2015-06-11', 'f');
INSERT INTO "tbnews" VALUES ('84', 'B020301', 'B020000', 'ក្រុង​ឡុ​ង​ដ៍​ជាប់​ចំណាត់ថ្នាក់​លេខ​1 មាន​អ្នកទេសចរ​ច្រើន​បំផុត​ឆ្នាំ​2015', 'ទីក្រុង​នៅ​អាស៊ី​ចំនួន​៥​បាន​ជាប់​ចំណាត់ថ្នាក់​ជាទី​ក្រុង​ស្ថិត​ក្នុង​ចំណាប់អារម្មណ៍​របស់​អ្នកទេសចរ​នៅ​លើ​ពិភពលោក ក្រោម​ការ​រៀបចំ​របស់ «MasterCard Global Destination Cities Index» ', 'http://kohsantepheapdaily.com.kh/article/121624.html', 'http://i.kspg.co/2015/6/d15d7ec1-e12c-4374-9f65-6b882b6e049b.jpg?w=285&h=170&mode=carve', 'ថ្ងៃអង្គារ ទី៩ ខែមិថុនា ឆ្នាំ២០១៥ ម៉ោង ១០:២៧', '12', '231', '0', 't', '2015-06-09', 'f');
INSERT INTO "tbnews" VALUES ('85', 'B020301', 'B020000', 'កោះ​ទ្រី​ស្តា​ន​ដា​គុន​ហា នៅ​កណ្តាល​មហាសមុទ្រ​អា​ត្ល​ង់​ទិ​ក​មាន​ធម្មជាតិ​ស្រស់​ប្រណីត​តែ​កម្រ​មាន​អ្នកទេសចរ​ទៅ​ដល់', 'កោះ​ទ្រី​ស្តា​ន ដា​គុន​ហា (Tristan  Da Cunha) គឺជា​កោះ​មួយ​ដែល​ប្រទះ​ឃើញ​ដោយ​អ្នក​ដើរ​សមុទ្រ​ជនជាតិ​ព័រ​ទុ​យ​ហ្គា​ល់​ឈ្មោះ​ទ្រី​ស្តា​ន​ដា គុន​ហា​នៅ​ក្នុង​ឆ្នាំ​១៥០៦ ។ កោះ​នោះ​កកើត​ពី​ការ​ផ្ទុះ​ភ្នំភ្លើង​', 'http://kohsantepheapdaily.com.kh/article/120880.html', 'http://i.kspg.co/2015/5/a74611a1-0cef-48d0-a627-81d77bb9ab94.jpg?w=285&h=170&mode=carve', 'ថ្ងៃសុក្រ ទី២៩ ខែឧសភា ឆ្នាំ២០១៥ ម៉ោង ០៩:៣៧', '2', '1', '0', 't', '2015-05-29', 'f');
INSERT INTO "tbnews" VALUES ('86', 'B020301', 'B020000', 'ឆ្នេរសមុទ្រ​ជាប់​ចំណាត់ថ្នាក់​ទាំង​10​នៅ​អា​មេ​រិ​ក​ប្រចាំឆ្នាំ​2015', 'សារព័ត៌មាន​បរទេស​កាលពី​ពេល ថ្មី​ៗ​កន្លង​មក ផ្សាយ​ថា លោក​សាស្ត្រាចារ្យ​ស្ទី​ហ្វេ​ន ធើ​មេន ប្រចាំការ​នៅ​មហាវិទ្យាល័យ​អន្តរជាតិ​ផ្ល​រី​ដា សហ​រដ្ឋ​អាមេរិក បាន​រៀប​ចំណាត់ថ្នាក់​ឆ្នេរសមុទ្រ', 'http://kohsantepheapdaily.com.kh/article/120803.html', 'http://i.kspg.co/2015/5/ac65923b-7465-462b-a59b-984e94561ecf.jpg?w=285&h=170&mode=carve', 'ថ្ងៃព្រហស្បត្ត៏ ទី២៨ ខែឧសភា ឆ្នាំ២០១៥ ម៉ោង ១០:១៨', '12', '1', '0', 't', '2015-05-28', 'f');
INSERT INTO "tbnews" VALUES ('87', 'B020301', 'B020000', 'រមណីយដ្ឋាន​ធម្មជាតិ​ដ៏​ប្រណីត «វាល​រំ​ផ្ល​ង់» នៅ​ក្រុង​បាន​លុងមានរឿង​និទាន​ពី​អូរ​មួយ​កកើត​ពី​ខ្លែង​ដាច់​ធ្លាក់​នៅ​ទីនោះ', 'ខេត្តរតនគិរី ៖ ស្ថិត​នៅ​តាម​បណ្តោយ​ផ្លូវ​ពី​ក្រុង​បាន​លុង​ឆ្ពោះទៅ​ទីរួមស្រុក​តា​វែង មាន​ចម្ងាយ​ប្រមាណ​៧​គីឡូម៉ែត្រ ក្នុងភូមិ​តង​លាង​ឡិ​ក ឃុំ​អូរ​ជុំ ស្រុក​អូរ​ជុំ «​វាល​រំ​ផ្ល​ង់​» ជា​រមណីយដ្ឋាន​ធម្មជាតិ', 'http://kohsantepheapdaily.com.kh/article/120373.html', 'http://i.kspg.co/2015/5/d1aea215-b710-4a2e-930d-b69f74feddf6.jpg?w=285&h=170&mode=carve', 'ថ្ងៃសុក្រ ទី២២ ខែឧសភា ឆ្នាំ២០១៥ ម៉ោង ០៩:១១', '0', '123', '0', 't', '2015-05-22', 'f');
INSERT INTO "tbnews" VALUES ('88', 'B020301', 'B020000', 'កន្លែង​សក្តិ​សិទ្ធិ​«​ភ្នំ​ស្វាយ​»​ក្លាយជា​កន្លែង​ទស្សនា​ថ្ងៃ​រះ​នៅ​ក្រុង​បាន​លុង', 'ខេត្តរតនគិរី​៖ រមណីយដ្ឋាន​«​ឥសី​ប​តមៈ​» ហៅ​«​ភ្នំ​ស្វាយ​»​ជាទី​កន្លែង​សក្តិ​សិទ្ធិ ។ ឥសី​ប​តមៈ ស្ថិត​នៅ​លើ​កំពូលភ្នំ​ស្វាយ មាន​ចម្ងាយ​ប្រហែល​២​គីឡូម៉ែត្រ នា​ភាគ​ខាងលិច​នៃ​ក្រុង​បាន​លុង', 'http://kohsantepheapdaily.com.kh/article/119822.html', 'http://i.kspg.co/2015/5/2d3037b3-e20a-4457-b8c1-e1c5e1c7344d.jpg?w=285&h=170&mode=carve', 'ថ្ងៃពុធ ទី១៣ ខែឧសភា ឆ្នាំ២០១៥ ម៉ោង ០៩:៤៦', '1', '32', '0', 't', '2015-05-13', 'f');
INSERT INTO "tbnews" VALUES ('89', 'B020301', 'B020000', 'អំឡុង​បុណ្យចូលឆ្នាំ​ថ្មី​អ្នកទេសចរ​កើនឡើង​នៅ​នៅ​មណ្ឌលគិរី​និង​រតនគិរី', 'ខេត្តរតនគិរី​+​មណ្ឌលគិរី ៖ ក្នុង​អំឡុង​បុណ្យចូលឆ្នាំ​ថ្មី​ប្រពៃណី​ជាតិ ក្នុង​រយៈពេល​៣​ថ្ងៃ​ពី​ថ្ងៃ​ទី​១៤​ដល់ទី​១៦ មេ​សា ឆ្នាំ​២០១៥ មាន​អ្នកទេសចរ​ច្រើន​ទៅ​ទស្សនា​កម្សាន្ត​នៅ​តាម​រមណីយដ្ឋាន​', 'http://kohsantepheapdaily.com.kh/article/119054.html', 'http://i.kspg.co/2015/4/31ddff86-18da-4a2d-b310-bb5941e9714d.jpg?w=285&h=170&mode=carve', 'ថ្ងៃព្រហស្បត្ត៏ ទី៣០ ខែមេសា ឆ្នាំ២០១៥ ម៉ោង ០៩:៥៧', '0', '0', '0', 't', '2015-04-30', 'f');
INSERT INTO "tbnews" VALUES ('90', 'B020504', 'B020000', 'លោក ឱម យ៉ិនទៀង​ ​​៖ ACU​ ត្រូវការជួបនិង​ស្តាប់ការបំភ្លឺពីលោក​ ​អំាង មាលតី​ ​ច្រើនដងទៀត', 'រាជធានីភ្នំពេញ ៖ ​ទេសរដ្ឋមន្រ្តីទទួល​បន្ទុកបេសកកម្មពិសេស ​និងជាប្រធាន​អង្គភាពប្រឆាំង​អំពើពុករលួយ(ACU) ​លោក ឱម យ៉ិនទៀង ​បានឲ្យដឹង​តាម​', 'http://kohsantepheapdaily.com.kh/article/118142.html', 'http://i.kspg.co/news_pic_kh.png?w=285&h=170&mode=carve', 'ថ្ងៃពុធ ទី៨ ខែមេសា ឆ្នាំ២០១៥ ម៉ោង ១៧:១៨', '0', '1', '0', 't', '2015-04-08', 'f');
INSERT INTO "tbnews" VALUES ('91', 'B020504', 'B020000', 'ចោរ​លួច​​ឡាន​ផ្អើល​ទាំងព្រឹក​​សមត្ថកិច្ច​​ស្ទាក់​ចាប់​បាន​វិញក្តៅ​ៗ​ឃាត់​ខ្លួន៣នាក់​ប្រុស​២ស្រី១​', 'ខេត្តបាត់ដំបង ៖ បុរស២នាក់ និងស្ត្រីម្នាក់​ដែលគេ​សង្ស័យថា ​ជា«ចោរ» ​ត្រូវ​សមត្ថកិច្ច​តាមប្រមាញ់​​ស្ទាក់​ចាប់​បានភ្លាមៗ បន្ទាប់ពី​ពួកគេ​សម​គំនិត​​គ្នា​លួច​យក​រថយន្ត​កាមរី​មួយគ្រឿង កាលពីវេលាម៉ោង​៨ និង​៤០នាទី​ព្រឹកថ្ងៃទី​១ កុម្ភៈ ២០១៣ ខណៈ​ម្ចាស់ធ្វេស​ប្រហែស​ចតចោល​មិន​ដកសោ​នៅ​លើ​ផ្លូវ​​ខាងមុខផ្ទះ ក្នុងភូមិ​ព្រែក​ព្រះស្តេច​​ សង្កាត់​ព្រែក​​ព្រះស្តេច ​ក្រុង​បាត់ដំបង ។', 'http://kohsantepheapdaily.com.kh/article/63213.html', 'http://i.kspg.co/2013/2/2d8d6311-75d7-47bc-a1d9-9bec5f7b2bae.jpg?w=285&h=170&mode=carve', 'ថ្ងៃសុក្រ ទី១ ខែកុម្ភៈ ឆ្នាំ២០១៣ ម៉ោង ១៤:១៥', '0', '0', '0', 't', '2013-02-01', 'f');
INSERT INTO "tbnews" VALUES ('92', 'B020504', 'B020000', 'ដួង​ជតារាសី​ក្នុង​ឆ្នាំខាល (​ខ្លា​)', 'ក្នុង​ឆ្នាំ​ថ្មី​នៃ​ចន្ទគតិ​របស់​ជនជាតិ​ចិន​នឹង ស្ថិត​នៅ​លើ​អំឡុង​ឆ្នាំ​ទាំង​១២ ផ្ដើម​ចេញពី ឆ្នាំជូត ឆ្លូវ ខាល ថោះ រោង ម្សាញ់ មមី មមែ វក រកា ច និង​កុរ ហើយ​សំរាប់​ឆ្នាំ​នេះ​ឆ្នាំខាល ចាប់ផ្ដើម​នៅ​ថ្ងៃ​ទី​១៤ កុម្ភៈ ឆ្នាំ​២០១០ រហូត ទៅ​ដល់​ថ្ងៃ​ទី​៥​កុម្ភៈ ឆ្នាំ​២០១១ ។ សំរាប់ ទារក​ដែល​កើត​ក្នុង​រយៈពេល​នេះ គឺ​មាន ជោគជតា​អ្នក​កើត​ក្នុង​ឆ្នាំខាល (​ខ្លា​) ។មិន​ទាន់​ដឹង​ថា ជោគជតារាសី​មាន​ស្ថាន ភាព​បែប​ណា​ឡើយ ប៉ុន្ដែ​ជនជាតិ​ចិន​ក្នុង អំឡុង​ពេល​ផ្ដើម​នៃ​ឆ្នាំ​ថ្មី​នេះ', 'http://kohsantepheapdaily.com.kh/article/11884.html', 'http://i.kspg.co/2010/2/6db30540-efb4-4443-92c9-c171909da762.jpg?w=285&h=170&mode=carve', 'ថ្ងៃចន្ទ ទី១៥ ខែកុម្ភៈ ឆ្នាំ២០១០ ម៉ោង ១៤:៣១', '0', '21', '0', 't', '2010-02-15', 'f');
INSERT INTO "tbnews" VALUES ('93', 'B020504', 'B020000', 'ឆ្នាំទន្សាយជាឆ្នាំផ្តល់នូវសិរីមង្គលសួស្តី និងជាឆ្នាំដ៏ល្អតាមជំនឿរបស់ចិនបុរាណ', 'តាម​ប្រតិ​ទិន​ចន្ទ​គតិ នៃ​ពិធី​ចូល​ឆ្នាំ​ចិន​​ ត្រូវ​នឹង​ថ្ងៃ​ទី​៣ ​កុម្ភៈ ​ឆ្នាំ​២០១១ គឺ​ជា​ថ្ងៃ​ដំបូង​នៃ​ឆ្នាំ​ថោះ ឬ​ឆ្នាំ​ទន្សាយ ជា​ឆ្នាំ​ទី​៤ ក្នុង​ចំណោម​​ឆ្នាំ​ទាំង​១២មាន​​ ជូត ឆ្លូវ ខាល ថោះ រោង ម្សាញ់ មមី មមែ វក រកា ច កុរ។ ប្រជា​ជន​ចិន​នៅ​មាន​​ជំនឿ​ដ៏​ជ្រាល​ជ្រៅ​​ទាក់​ទិន​នឹង​ព្រះ​ចន្ទ​ថា ​ជា​អាស្រម​នៃ​សត្វ​ទន្សាយ​​ពណ៌​ស របស់​ទេព​ធីតា «ឆាង​អ័រ» និង​មាន​ពេល​ខ្លះ​បង្ហាញ​ជា​លក្ខណៈ​គីង្គក់​កំពុង​ផ្សំ​ថ្នាំ​ដោយ​មាន​​​ឆ្នាំង​ថ្នាំ​ដាក់​នៅ​ក្បែរ​ខ្លួន។ តាម​​ជំនឿ​របស់​​ជន​ជាតិ​ចិន​ខាង', 'http://kohsantepheapdaily.com.kh/article/1156.html', 'http://i.kspg.co/2011/2/0607cbdd-7891-45a0-965e-b614aa3d837a.jpg?w=285&h=170&mode=carve', 'ថ្ងៃសុក្រ ទី៤ ខែកុម្ភៈ ឆ្នាំ២០១១ ម៉ោង ១០:៥៦', '0', '0', '0', 't', '2012-02-04', 'f');
INSERT INTO "tbnews" VALUES ('94', 'B020501', 'B020000', 'វិធីសាស្ត្រ​ប្រើ​លេខ​1​បើក​ឡើង​ចំណោត​កុំឱ្យ​រលត់​ម៉ាស៊ីន', 'ការ​បើកបរ​រថយន្ត​លេខ​ដៃ​អាច​ជួប​ការ​លំបាក​ក្នុង​ការ​អនុវត្ត​ជាង​លេខ​អូ​តូ ពិសេស​ខណៈ​ពេល​លោក​អ្នក​បំ​លាស់​ទី​រថយន្ត​ឡើង​ទីទួល ដែល​អ្នក​កាន់​ចង្កូតរថយន្ត​ជា​ច្រើន​អាច​គាប់​ជួន​ត្រូវ​រលត់​ម៉ាស៊ីន', 'http://kohsantepheapdaily.com.kh/article/125467.html', 'http://i.kspg.co/2015/7/8bb8245c-1424-479d-8989-82a1adedfba2.jpg?w=285&h=170&mode=carve', 'ថ្ងៃពុធ ទី២៩ ខែកក្កដា ឆ្នាំ២០១៥ ម៉ោង ១៥:០៧', '0', '0', '0', 't', '2015-07-29', 'f');
