INSERT INTO "tbnews" VALUES ('149', 'B020505', 'B020000', 'ទប់ស្កាត់​មហារីក​សុដន់​តាម​រយៈ​របប​អាហារ​ទទួលទាន​ប្រចាំ​ថ្ងៃ', 'បច្ចុប្បន្ន​មហារីក​សុដន់​បាន​ក្លាយទៅជា​ប្រធានបទ​មួយ​ដែល​គួរ​ឱ្យ​ព្រួយបារម្ភ​សម្រាប់​សុខភាព​ស្ត្រី​នៅ​ទូ​ទាំង​ពិភពលោក ។ អាច​និយាយ​បាន​ថា វា​ជា​ឃាតករ​ដ៏​សាហាវ​បន្ទាប់​ពី​មហារីក​ស្បូន ។', 'http://kohsantepheapdaily.com.kh/article/125241.html', 'http://i.kspg.co/2015/7/3c867f8d-f275-4d23-9ee0-f0de2a689d4e.jpg?w=285&h=170&mode=carve', 'ថ្ងៃចន្ទ ទី២៧ ខែកក្កដា ឆ្នាំ២០១៥ ម៉ោង ១០:០២', '0', '0', '0', 't', '2015-07-27', 'f');
