INSERT INTO "tbnews" VALUES ('201', 'B020401', 'B020000', 'ការ​វិនិយោគ​លើ​វិស័យ​ស្រូវ​អង្ករ​ប្រើ​ទុន​អស់​ច្រើន តែ​ភាគរយ​នៃ​ប្រាក់​ចំណេញ​ទទួល​បាន​តិច', 'រាជធានី​ភ្នំពេញ ៖ ការ​ដាក់​ទុន​វិនិយោគ​ទៅ​លើ​វិស័យ​ស្រូវ​អង្ករ គឺ​ត្រូវ​ចំណាយ​អស់​ដើមទុន​ច្រើន​ទៅ​លើ​ការ​សាងសង់​រោង​ម៉ាស៊ីនកិនស្រូវ ឃ្លាំង​ស្តុក​ស្រូវ និង​ប្រាក់​បម្រុង​ទុក​សម្រាប់​ទិញ​ស្រូវ​ជាដើម ។ រីឯ​ភាគរយ​នៃ​ការ​ទទួល​', 'http://kohsantepheapdaily.com.kh/article/124643.html', 'http://i.kspg.co/2015/7/fd1b5663-3e82-4310-82de-94c410b6397c.jpg?w=285&h=170&mode=carve', 'ថ្ងៃអាទិត្យ ទី១៩ ខែកក្កដា ឆ្នាំ២០១៥ ម៉ោង ១៣:៤៧', '1', '0', '0', 't', '2015-07-19', 'f');
