INSERT INTO "tbnews" VALUES ('207', 'B020401', 'B020000', 'តម្លៃ​មាស​នៅ​កម្ពុជា​ធ្លាក់​ចុះ ខណៈ​ប្រាក់​ដុល្លារ​អា​មេ​រិ​ក​កើនឡើង​ខ្ពស់​ធៀប​នឹង​ប្រាក់​រៀល', 'រាជធានី​ភ្នំពេញ ៖ មួយ​រយៈពេល​ចុង​ក្រោយ​នេះ បើ​សង្កេត​មើល​ពី​តម្លៃ​មាស​នៅ​លើ​ទីផ្សារ​ប្រទេស​កម្ពុជា បាន​ធ្លាក់​ចុះ​ជា​បន្តបន្ទាប់​តាំងពី​តម្លៃ​ជិត​២.០០០​ដុល្លារ​អា​មេ​រិ​ក​ក្នុង​១​តម្លឹង ធ្លាក់​នៅ​សល់​តែ​ជាង​១.៤០០​ដុល្លារ​', 'http://kohsantepheapdaily.com.kh/article/124075.html', 'http://i.kspg.co/2015/7/cfeaa17f-7892-4bab-8ef2-4b2039e8af48.jpg?w=285&h=170&mode=carve', 'ថ្ងៃអាទិត្យ ទី១២ ខែកក្កដា ឆ្នាំ២០១៥ ម៉ោង ១០:២២', '23', '0', '0', 't', '2015-07-12', 'f');
INSERT INTO "tbnews" VALUES ('208', 'B020401', 'B020000', 'ពាណិជ្ជកម្ម​កម្ពុជា​-​កូរ៉េ​កើនឡើង​3​ឆ្នាំ​ជាប់​គ្នា', 'រាជធានី​ភ្នំពេញ ៖ នៅ​ក្នុង​រយៈពេល​៣​ឆ្នាំ​ចុង​ក្រោយ​នេះ ការ​ធ្វើ​ពាណិជ្ជកម្ម​រវាង​កម្ពុជា​និង​កូរ៉េ មានការ​រីក​ចម្រើន​គួរ​ឲ្យ​កត់សម្គាល់ ។ ជាក់ស្តែង​ទំហំ​ពាណិជ្ជកម្ម​រវាង​ប្រទេស​ទាំង​២ នៅ​ឆ្នាំ​២០១២ មាន​ទំហំ​ទឹកប្រាក់​៧១៩​លាន​ដុល្លារ ', 'http://kohsantepheapdaily.com.kh/article/124074.html', 'http://i.kspg.co/2015/7/fdd757d2-f9bf-46a8-90dd-66844d763476.jpg?w=285&h=170&mode=carve', 'ថ្ងៃអាទិត្យ ទី១២ ខែកក្កដា ឆ្នាំ២០១៥ ម៉ោង ១០:១៩', '0', '0', '0', 't', '2015-07-12', 'f');
INSERT INTO "tbnews" VALUES ('209', 'B020401', 'B020000', 'សាំង​វៀតណាម​ចុះថ្លៃ​331​ដុ​ង​ក្នុង​មួយ​លី​ត្រ', 'ហាណូយ ៖ តាម​សេចក្តី​ប្រកាស​របស់​ក្រសួង​ឧស្សាហកម្ម​និង​ពាណិជ្ជកម្ម និង​ក្រសួង​ហិរញ្ញវត្ថុ តម្លៃ​លក់រាយ​ចំពោះ​ផលិតផល​សាំង​ក្នុងស្រុក​ដូច​ជា ប្រភេទ​សាំង RON 92 និង E5 ត្រូវ​បាន​បញ្ចុះតម្លៃ​ចំនួន​៣៣១​ដុ​ង ', 'http://kohsantepheapdaily.com.kh/article/123561.html', 'http://i.kspg.co/news_pic_kh.png?w=285&h=170&mode=carve', 'ថ្ងៃអាទិត្យ ទី៥ ខែកក្កដា ឆ្នាំ២០១៥ ម៉ោង ១៥:១៥', '3', '0', '0', 't', '2015-07-05', 'f');
INSERT INTO "tbnews" VALUES ('210', 'B020103', 'B020000', 'បក្សនយោបាយ​ថ្មី លោក យ៉េ​ង វី​រៈ បាន​ក្លាយជា​ប្រធាន​គណបក្ស​ប្រជាធិបតេយ្យ​មូលដ្ឋាន', 'រាជធានី​ភ្នំពេញ ៖ លោក យ៉េ​ង វី​រៈ អតីតមន្ត្រី​សង្គម​ស៊ី​វិល នៅ​រសៀល​ថ្ងៃ​ទី​២ សីហា ត្រូវ​បាន​ជ្រើសរើស​ជា​ប្រធាន​គណបក្ស​ប្រជាធិបតេយ្យ​មូលដ្ឋាន (​គ​បម​) និង​លោក សាម ស៊ុន​ឌឿ​ន ជា​អនុប្រធាន​គណបក្ស ។ ការ​ជ្រើសរើស​នេះ ', 'http://kohsantepheapdaily.com.kh/article/125783.html', 'http://i.kspg.co/2015/8/63515836-2b80-419d-aa1f-a84b812dc3cd.jpg?w=285&h=170&mode=carve', 'ថ្ងៃចន្ទ ទី៣ ខែសីហា ឆ្នាំ២០១៥ ម៉ោង ០៩:៤០', '12', '0', '0', 't', '2015-08-03', 'f');
INSERT INTO "tbnews" VALUES ('211', 'B020103', 'B020000', 'សមាជិក​បក្ស​សង្គ្រោះ​ជាតិ​ជាង​500​នាក់​មក​ចូលរួម​ជាមួយ​បក្ស​ប្រជាជន', 'ខេត្តឧត្តរមានជ័យ ៖ សមាជិក​គណ​ប្ស​សង្គ្រោះ​ជាតិ​មក​ពី​ស្រុក​ចុងកាល និង​ក្រុង​សំរោង បាន​ដង្ហែ​ជា​ក្បួន​តាម​គោ​យន្ត​កន្ត្រៃ​មក​ចូលរួម​ជាមួយ​គណបក្ស​ប្រជាជន​កម្ពុជា ប្រមាណ​ជាង​៥០០​នាក់ កាលពី​ព្រឹក​ថ្ងៃ​ទី​១ សីហា ដែល​បាន​ធ្វើ​ឡើង​នៅ​ក្នុង​', 'http://kohsantepheapdaily.com.kh/article/125770.html', 'http://i.kspg.co/2015/8/e6fb89b9-71ec-469e-ae17-27db860bb5b2.jpg?w=285&h=170&mode=carve', 'ថ្ងៃចន្ទ ទី៣ ខែសីហា ឆ្នាំ២០១៥ ម៉ោង ០៩:០៨', '0', '0', '0', 't', '2015-08-03', 'f');
INSERT INTO "tbnews" VALUES ('212', 'B020103', 'B020000', 'មួយថ្ងៃមុនពេលដាក់ពាក្យ​សុំទទួលស្គាល់ពី​ក្រសួងមហាផ្ទៃ​ ថ្នាក់ដឹកនំា​គណបក្សប្រជាធិបតេយ្យ​មូលដ្ឋានជួបជុំគ្នា​ព្រឹកនេះ', 'រាជធានីភ្នំពេញ ​៖ ថ្នាក់ដឹកនំានិង​សកម្មជនគណបក្សប្រជាធិបតេយ្យមូលដ្ឋាន ​បានប្រមូលផ្តុំជួបជុំគ្នា​លើកដំបូង ​មុនពេលដាក់​ពាក្យសុំទទួលស្គាល់ពី​ក្រសួង​មហាផ្ទៃ​នៅថ្ងៃស្អែកនេះ ។ ', 'http://kohsantepheapdaily.com.kh/article/125680.html', 'http://i.kspg.co/news_pic_kh.png?w=285&h=170&mode=carve', 'ថ្ងៃអាទិត្យ ទី២ ខែសីហា ឆ្នាំ២០១៥ ម៉ោង ០៨:៣៥', '1', '0', '0', 't', '2015-08-01', 'f');
INSERT INTO "tbnews" VALUES ('213', 'B020103', 'B020000', 'លោក​ សម រង្ស៊ី ​ស្នើឲ្យវៀតណាម​គោរពកិច្ចព្រមព្រៀង​ក្រុងប៉ារីស', 'រាជធានីភ្នំពេញ ​៖ ប្រធាន​គណបក្សសង្គ្រោះជាតិ​ លោក សម រង្ស៊ី ​បានស្នើឲ្យវៀតណាម​គោរពកិច្ចព្រមព្រៀង​សន្តិភាពក្រុងប៉ារីស​ឆ្នាំ១៩៩១ ព្រោះ​', 'http://kohsantepheapdaily.com.kh/article/125564.html', 'http://i.kspg.co/2015/7/dbc7ef7f-84e1-46bf-bbbc-9f6279f80a82.jpg?w=285&h=170&mode=carve', 'ថ្ងៃព្រហស្បត្ត៏ ទី៣០ ខែកក្កដា ឆ្នាំ២០១៥ ម៉ោង ១៦:២៣', '1', '0', '0', 't', '2015-07-30', 'f');
INSERT INTO "tbnews" VALUES ('214', 'B020103', 'B020000', 'បដិសេធ​សំណើ​បក្ស​ប្រឆាំង ប្រមុខ​រាជរដ្ឋាភិបាល​ច្រាន​ចោលការ​ប្តឹង​ទៅតុលាការអន្តរជាតិ ​ប្រឆាំង​វៀតណាម', 'រាជធានី​ភ្នំពេញ ៖ បន្ទាប់​ពី​តំណាងរាស្ត្រ​ខាង​គណបក្ស​សង្គ្រោះ​ជាតិ ស្នើ​សុំ​ឲ្យ​រាជរដ្ឋាភិបាល​កម្ពុជា​យក​បញ្ហា​ព្រំដែន​ជាមួយ​វៀតណាម​ប្តឹង​ទៅ​តុលាការ​យុត្តិធម៌​អន្តរជាតិ (ICJ) តាម​រយៈ​សម្តេច​អគ្គ​មហា​ពញា​ចក្រី ហេង សំ​រិ​ន ប្រធានរដ្ឋសភា​នា​ពេល​កន្លង​មកនេះ', 'http://kohsantepheapdaily.com.kh/article/125526.html', 'http://i.kspg.co/2015/7/c6186f05-6e67-415e-9004-6f37660611fc.jpg?w=285&h=170&mode=carve', 'ថ្ងៃព្រហស្បត្ត៏ ទី៣០ ខែកក្កដា ឆ្នាំ២០១៥ ម៉ោង ១០:៥០', '1', '0', '0', 't', '2015-07-30', 'f');
INSERT INTO "tbnews" VALUES ('215', 'B020103', 'B020000', 'ព្រះរាជវាំង​បដិសេធ ព្រះមហាក្សត្រ​មិន​មាន​ព្រះរាជ​តំរិះ​ហៅ​មេដឹកនាំ​នយោបាយ​សួរនាំ​ពី​ព្រំដែន​ទេ', 'រាជធានី​ភ្នំពេញ ៖ សម្តេច​អគ្គមហាសេនាបតី​តេ​ជោ ហ៊ុន សែន នាយក​រដ្ឋមន្ត្រី​នៃ​ព្រះរាជាណាចក្រ​កម្ពុជា បាន​មាន​ប្រសាសន៍​អង្វរករ​អ្នក​ទាំងឡាយ ទាំង​រាជវង្សានុវង្ស ទាំង​ប្រជារាស្ត្រ សូមកុំ​យក​ព្រះមហាក្សត្រ​ធ្វើ​ជា​ការ​លេងសើច', 'http://kohsantepheapdaily.com.kh/article/125318.html', 'http://i.kspg.co/2015/7/a6659beb-2897-4605-8d81-c2c2b9893d18.jpg?w=285&h=170&mode=carve', 'ថ្ងៃអង្គារ ទី២៨ ខែកក្កដា ឆ្នាំ២០១៥ ម៉ោង ០៨:៥៤', '0', '0', '0', 't', '2015-07-28', 'f');
