INSERT INTO "tbnews" VALUES ('224', 'B020103', 'B020000', 'រិះគន់​លោក​សម រង្ស៊ី ឱ្យ​តម្លៃ​ផែនទី​ទិញ​តាម​ផ្សារ មន្ត្រី​គណបក្សប្រឆាំង​ច្រាន​ចោលការ​ចោទប្រកាន់​នេះ', 'រាជធានី​ភ្នំពេញ ៖ អ្នកនាំពាក្យ​អង្គភាព​ព័ត៌មាន​និង​ប្រតិកម្ម​រហ័ស​នៃ​ទីស្តីការគណៈរដ្ឋមន្ត្រី បាន​ប្រតិកម្ម​ចំពោះ​លោក សម រ​ង្ស៊ី ប្រធាន​គណបក្ស​សង្គ្រោះ​ជាតិ និង​ជា​ប្រធាន​ក្រុម​តំណាងរាស្ត្រ​ភាគតិច​ក្នុង​សភា ដែល​បាន​ប្រាប់​ពី​តម្លៃ​ផែនទី​ដែល​លោក​បាន​ទិញ​ពី​ផ្សារ​នៅ​ប្រទេស​បារាំង', 'http://kohsantepheapdaily.com.kh/article/124685.html', 'http://i.kspg.co/news_pic_kh.png?w=285&h=170&mode=carve', 'ថ្ងៃចន្ទ ទី២០ ខែកក្កដា ឆ្នាំ២០១៥ ម៉ោង ០៩:០១', '2', '0', '0', 't', '2015-07-20', 'f');
