INSERT INTO "tbnews" VALUES ('248', 'B020503', 'B020000', 'មន្ត្រី​ពាក់ព័ន្ធ​រួម​គ្នា​សម្អាត កាយ​សំរាម​និង​កាក​សំណល់​ចេញ ពី​បឹង​ព្រែក​ទប់​អាង​ស្តុក​ទឹក​ស្អាត', 'ខេត្ត​ព្រះ​សីហ​នុ ៖ បន្ទាប់​ពី​មានការ​ផ្សព្វផ្សាយ​ព័ត៌មាន​ជុំវិញ​ជន​មិន​ស្គាល់​មុខ​យក​សំរាម​ដ៏​ច្រើន​ទៅ​បោះចោល​ក្នុង​បឹង​ព្រែក​ទប់​ដែល​ជា​អាង​ស្តុក​ទឹកសាប​សម្រាប់​ផ្គត់ផ្គង់​ក្រុងព្រះសីហនុ​រួច​មក ក្រុម​មន្ត្រី​ពាក់ព័ន្ធ​មួយចំនួន​នៅ​ខេត្ត​ព្រះ​សីហ​នុ​បាន​ចុះ​ទៅ​ពិនិត្យ​មើល', 'http://kohsantepheapdaily.com.kh/article/125249.html', 'http://i.kspg.co/2015/7/20464fd4-84f6-4e5e-9dec-459c26707f2b.jpg?w=285&h=170&mode=carve', 'ថ្ងៃចន្ទ ទី២៧ ខែកក្កដា ឆ្នាំ២០១៥ ម៉ោង ១០:៣០', '3', '0', '0', 't', '2015-07-27', 'f');
INSERT INTO "tbnews" VALUES ('249', 'B020503', 'B020000', 'បឹង​ព្រែក​ទប់​អាង​ស្តុក​ទឹកសាប​សម្រាប់​ផ្គត់ផ្គង់​ក្រុងព្រះសីហនុ​ត្រូវ​គេ​ចាក់សំរាម​ចូល​ជះក្លិន​ស្អុយ​អសោចិ៍', 'ខេត្ត​ព្រះ​សីហ​នុ ៖ អ្នក​រស់នៅ​ក្រុងព្រះសីហនុ​ខ្លះ​ដែល​បានឃើញ​ផ្ទាល់​ភ្នែក បាន​សម្តែង​ការ​ព្រួយបារម្ភ​យ៉ាង​ខ្លាំង​ពាក់ព័ន្ធ​ទៅ​នឹង​បញ្ហា​សុខភាព​របស់​ខ្លួន ខណៈ​ពេល​បានឃើញ​សំរាម​ជា​ច្រើន​កំពុង​ត្រូវ​បាន​គេ​ចាក់​ចូល​បឹង​ព្រែក​ទប់ ', 'http://kohsantepheapdaily.com.kh/article/125067.html', 'http://i.kspg.co/2015/7/ea562377-8ac6-4765-8b89-a1ade823ba6a.jpg?w=285&h=170&mode=carve', 'ថ្ងៃសុក្រ ទី២៤ ខែកក្កដា ឆ្នាំ២០១៥ ម៉ោង ១០:៥២', '0', '0', '0', 't', '2015-07-24', 'f');
INSERT INTO "tbnews" VALUES ('250', 'B020503', 'B020000', 'កំណើន​វត្តមាន​ប្រ​ភ​ទ​សត្វ​សមុទ្រ​កម្រ​នៅ​ដែនសមុទ្រ​កម្ពុជា​​បង្ហាញ​នូវ​ការ​ត្រឡប់​មក​វិញ​នូវ​និរន្តរភាព​បរិស្ថាន​ល្អ​ប្រសើរ​', 'ខេត្ត​កំពត ​៖ មន្ត្រី​ជំនាញ​ជលផល​បាន​និយាយ​ថា កំណើន​វត្តមាន​ប្រ​ភ​ទ​សត្វ​សមុទ្រ​កម្រ​នៅ​ដែនសមុទ្រ​កម្ពុ​ជានា​ពេល​ថ្មី​ៗ​នេះ បាន​បង្ហាញ​នូវ​ការ​ត្រឡប់​មក​វិញ​នូវ​និរន្តរភាព​បរិស្ថាន​ល្អ​ប្រសើរ​ក្នុង​ដែនសមុទ្រ​កម្ពុជា', 'http://kohsantepheapdaily.com.kh/article/123881.html', 'http://i.kspg.co/2015/7/765cdeb7-e0c5-4f5e-a0bf-a43a6a72c2cd.jpg?w=285&h=170&mode=carve', 'ថ្ងៃព្រហស្បត្ត៏ ទី៩ ខែកក្កដា ឆ្នាំ២០១៥ ម៉ោង ០៩:៣៩', '3', '0', '0', 't', '2015-07-09', 'f');
INSERT INTO "tbnews" VALUES ('251', 'B020503', 'B020000', 'មន្ទីរ​បរិស្ថាន​ធ្វើ​ថ្នាល​បណ្តុះ​កូន​ឈើ​ត្រៀម​ដាំ​នៅ​ក្រុង ​និង​តាម​បណ្តោយ​ផ្លូវ​វាង​ក្រុង', 'ខេត្តកំពត ​៖ ​​ក្នុង​ការ​​ចូលរួម​ប្រឡងប្រណាំង​ទីក្រុង​ស្អាត និង​កែ​លម្អ​សោ​ភ​ណ័​ភាព​ក្រុង ​ដើម្បី​អបអរសាទរ​បុណ្យ​សមុទ្រ​នា​ខែធ្នូ ចុងឆ្នាំ​២០១៥​ ​ខាង​មុខ​នេះ លោក​ ស៊ុ​យ ធា ប្រធាន​មន្ទីរ​​បរិស្ថាន​ខេត្ត​​ ', 'http://kohsantepheapdaily.com.kh/article/122531.html', 'http://i.kspg.co/2015/6/a5065626-3ec3-4199-bfb2-f62658933120.jpg?w=285&h=170&mode=carve', 'ថ្ងៃអាទិត្យ ទី២១ ខែមិថុនា ឆ្នាំ២០១៥ ម៉ោង ១៣:៥៥', '3', '0', '0', 't', '2015-06-21', 'f');
INSERT INTO "tbnews" VALUES ('252', 'B020503', 'B020000', 'ទៅ​លេង​ឆ្នេរ​ខ្ពើម​ទឹក​សំអុយ​ដែល​បង្ហូរ​ចូល​សមុទ្រ', 'ខេត្ត​ព្រះ​សីហ​នុ ​៖ លោក នូ សុផល ប្រធាន​មន្ទីរ​ទេសចរណ៍​ខេត្ត បាន​មាន​ប្រសាសន៍​ឱ្យ​ដឹង​តាម​ទូរស័ព្ទ​កាលពី​ព្រឹក​ថ្ងៃ​ទី​១៥ ឧសភា ដែល​ជា​ថ្ងៃ​ចុងបញ្ចប់​នៃ ព្រះ​រាជពិធី​បុណ្យចម្រើនព្រះជន្ម​គម្រប់​៦២​យាង​ចូល​៦៣​ព្រះ​វស្សា​របស់​ព្រះករុណា​ព្រះបាទ​សម្តេច', 'http://kohsantepheapdaily.com.kh/article/120101.html', 'http://i.kspg.co/2015/5/9df9b65a-97d2-42ff-8161-4ec2548069b5.jpg?w=285&h=170&mode=carve', 'ថ្ងៃចន្ទ ទី១៨ ខែឧសភា ឆ្នាំ២០១៥ ម៉ោង ០៩:២២', '0', '0', '0', 't', '2015-05-18', 'f');
INSERT INTO "tbnews" VALUES ('253', 'B020503', 'B020000', 'ភ្លៀង​ធ្លាក់​ស្ទះលូ​ទឹក កាក​សំណល់​ស្អុយរលួយ​ជះក្លិន​អសោច នៅ​ផ្សារ​បាន​លុង', 'ខេត្តរតនគិរី ៖ មាន​ភ្លៀង​ធ្លាក់​២ទៅ៣​មេ កាក​សំណល់​សល់​ស្អុយរលួយ និង​ស្ទះលូ​​បង្ហូរទឹក​មួយ​កន្លែង ​ត្រង់​ចំណុច​ខាងកើត​ផ្សារ​ក្រុង​បាន​លុង​(​ទីប្រជុំជន​) ស្ថិត​នៅ​ភូមិ​ជ័យ​ជំនៈ សង្កាត់​ឡា​បាន​សៀ​ក ក្រុង​បាន​លុង ​។ ក្រុម​អាជីវករ ​និង​ភ្លៀង​ជាតិ', 'http://kohsantepheapdaily.com.kh/article/119796.html', 'http://i.kspg.co/2015/5/f9050768-84b6-4a9e-a717-88a08cfe564f.jpg?w=285&h=170&mode=carve', 'ថ្ងៃអង្គារ ទី១២ ខែឧសភា ឆ្នាំ២០១៥ ម៉ោង ១៧:១៦', '3', '0', '0', 't', '2015-05-12', 'f');
INSERT INTO "tbnews" VALUES ('254', 'B020503', 'B020000', 'ក្រសួងទេសចរណ៍​ព្រមាន​ម្ចាស់​សណ្ឋាគារ​និង​អាជីវកម្ម​ផ្សេង​ៗ​ដែល​បង្ហូរទឹក​សំអុយ​ចូល​សមុទ្រ', 'រាជធានី​ភ្នំពេញ ៖ រដ្ឋមន្ត្រី​ក្រសួងទេសចរណ៍ បាន​ធ្វើការ​ព្រមាន​ថា នឹង​ចាត់វិធានការ​ចំពោះ​ម្ចាស់​សណ្ឋាគារ ភោជនីយដ្ឋាន និង​ម្ចាស់​អាជីវកម្ម ដែល​នៅ​តែ​បន្ត​លួច​បង្ហូរទឹក​ស្អុយ​ចូល​សមុទ្រ​ផ្គើន​ទៅ​នឹង​បម្រាម និង​ប៉ះពាល់​ដល់​បរិស្ថាន', 'http://kohsantepheapdaily.com.kh/article/117366.html', 'http://i.kspg.co/2015/3/8f3e54f5-dae8-47f4-ae04-03973b61ff07.jpg?w=285&h=170&mode=carve', 'ថ្ងៃសុក្រ ទី២៧ ខែមីនា ឆ្នាំ២០១៥ ម៉ោង ០៩:០៥', '3', '0', '0', 't', '2015-01-27', 'f');
INSERT INTO "tbnews" VALUES ('255', 'B020503', 'B020000', 'សាលា សឹ​ង្ហ​បុរី ថូ​ម៉ាស់ ជំរុញ​ឲ្យ​សិស្ស​របស់​ខ្លួន​ស្រឡាញ់​ចេះ​ស្រឡាញ់​បរិស្ថាន​និង​ព្រៃឈើ​ធម្មជាតិ', 'នេះ​ជា​លើ​ទី​១​ហើយ​ដែល សាលា​សឹ​ង្ហ​បុរី ថូ​ម៉ាស បាន​នាំ​សិស្ស​ជាង​២០០​នាក់​ចុះ​ទស្សនកិច្ច​សិក្សា​យល់​ដឹង​អំពី បរិស្ថាន​ព្រៃ​ភ្នំ​ធម្មជាតិ ជា​ពិសេស​ជីវិត​សត្វព្រៃ​ជា​ច្រើន​ប្រភេទ នៅ​រមណីយ​ដ្ឋាន', 'http://kohsantepheapdaily.com.kh/article/116762.html', 'http://i.kspg.co/2015/3/3cb65b9c-29cb-4a6f-a542-d64642a2c0d1.jpg?w=285&h=170&mode=carve', 'ថ្ងៃអង្គារ ទី១៧ ខែមីនា ឆ្នាំ២០១៥ ម៉ោង ១៦:៥៨', '0', '0', '0', 't', '2015-01-17', 'f');
INSERT INTO "tbnews" VALUES ('256', 'B020503', 'B020000', 'អាជ្ញាធរ​​ខណ្ឌ​ច្បារ​អំពៅ​ចុះ​សម្អាត​បរិស្ថាន​តាម​ដងផ្លូវ​​មួយ​ចំនួន​ក្នុងភូមិ​សាស្ត្រ ក្រោម​ប្រធាន​បទ «ទីក្រុង​ស្អាត»', 'រាជ​ធានី​ភ្នំពេញ ៖ អាជ្ញាធរ​ខណ្ឌ​​ច្បារ​អំពៅ និង​គ​ណៈ​​ក​ម្ម​ការ​ចម្រុះ​ ​ដែល​ដឹកនាំ​​​លោកអ៊ាង ស៊ី​ផា​ន អភិបាល​នៃ​​គ​ណៈ​​អភិបាល​ខណ្ឌ​​ច្បារ​​អំពៅ និង​មានការ​អញ្ជើញ​ចូលរួម​​ពី​សំណាក់​អភិបាល​រង​​ខណ្ឌ, ចៅសង្កាត់​, កម្លាំង​កង​រាជ​អាវុធ, កម្លាំង​នគរបាល, មន្ត្រី​', 'http://kohsantepheapdaily.com.kh/article/115665.html', 'http://i.kspg.co/2015/2/82aac5e5-2f5c-48bb-be38-193b82b886f6.jpg?w=285&h=170&mode=carve', 'ថ្ងៃសៅរ៏ ទី២៨ ខែកុម្ភៈ ឆ្នាំ២០១៥ ម៉ោង ០៩:៥៤', '0', '0', '0', 't', '2015-02-28', 'f');
INSERT INTO "tbnews" VALUES ('257', 'B020503', 'B020000', '​សំអាតក្រុង​កែប​លើ​ទឹកដី​ជាប់មាត់​សមុទ្រ​ចាប់ផ្តើម​នៅ​ថ្ងៃ​សៅរ៍​ចុង​ស​ប្តា​ហ៍​នេះ​ហើយ', 'រាជធានី​ភ្នំពេញ ៖ ព្រឹត្តិការណ៍ កែប សំអាត កែប (Kep Kep Clean) លើក​ទី​២ នៅ​ខេត្ត​កែប​នឹង​ចាប់ផ្តើម​ធ្វើ​សកម្មភាព​នៅ​ព្រឹក​ថ្ងៃ​ទី​១៣ ធ្នូ ក្រោម​គំនិត​ផ្តួចផ្តើម​ពី​អង្គការ​មូលនិធិ​ទឹកចិត្ត​ទាំង​បី ដើម្បី​បង្កើន​សោភ័ណ​ភាព និង​ភាព​ទាក់ទាញ ', 'http://kohsantepheapdaily.com.kh/article/110618.html', 'http://i.kspg.co/2014/12/c207d63e-b02a-47b0-9c80-17d1a0f5b291.jpg?w=285&h=170&mode=carve', 'ថ្ងៃពុធ ទី១០ ខែធ្នូ ឆ្នាំ២០១៤ ម៉ោង ១៦:៤៨', '0', '0', '0', 't', '2014-12-10', 'f');
INSERT INTO "tbnews" VALUES ('258', 'B020503', 'B020000', 'ប្រធាន​ផ្សារ​​ព្រំ​​រង​ការ​រិះគន់​​​ថា ទុកសំរាម​ចោល​រហូតដល់​ស្អុយ​រលួយ​ មិនព្រម​ដឹក​ចេញ​', 'ខេត្ត​ប៉ៃ​លិន ៖ ប្រធាន​​ផ្សារ​​ព្រំ​​លើ​រង​ការ​​រិះគន់​​ពី​សំណាក់​​ប្រជាពលរដ្ឋ និង​ក្រុម​អាជីវ​ករ​ ដែល​លក់ដូរ​នៅ​ក្នុង​​ផ្សារ​គ្រប់​ៗ​គ្នា​ថា មិន​គួរ​ទុក​សំរាម​​​​ចោល​​រយៈ​ពេល​យូរ​រហូតដល់​​ក្លាយទៅ​ជា​​រលួយ​ជះ​ក្លិន​ស្អុយ​​​​អាសោចន៍​​ និង​បង្ក​ទៅ​​ជា​ដង្កូវ​រ​វើក​​ពាសពេញ​​ក្នុង​គំនរ​សម្រាប់​​​នៅ​​​ជុំវិញ​ផ្សារ​​បែប​នេះឡើយ', 'http://kohsantepheapdaily.com.kh/article/104696.html', 'http://i.kspg.co/2014/9/7b15b015-def4-4ed7-8661-72fb320b9caf.jpg?w=285&h=170&mode=carve', 'ថ្ងៃអង្គារ ទី៩ ខែកញ្ញា ឆ្នាំ២០១៤ ម៉ោង ០៩:២៦', '0', '0', '0', 't', '2014-09-09', 'f');
INSERT INTO "tbnews" VALUES ('259', 'B020503', 'B020000', 'ក្រុម​​យុវជន​​កម្ពុជា​​ខេត្តពោធិ៍​សាត់​នាំ​​គ្នា​ដាំ​ដើម​ឈើ​លើក​កម្ពស់​​បរិស្ថាន​​ធម្មជាតិ', 'ខេត្តពោធិ៍សាត់ ​៖​ ក្រោម​យុទ្ធនាការ​យុវជន​ម្នាក់​ដើមឈើ​មួយ​ដើម​ កាលពី​ថ្ងៃ​ទី​២៤ ​សីហា ២០១៤ ​សហភាព​សហ​ព័ន្ធ​យុវជន​កម្ពុជា​ខេត្តពោធិ៍សាត់​ដឹកនាំ​ដោយ​លោក​ហេង សុខ​គង់ ​ជា​ប្រធាន​សហការ​ជាមួយ​ខណ្ឌ​រដ្ឋបាល​ព្រៃឈើ កងកម្លាំងប្រដាប់អាវុធ ព្រះសង្ឃ អាជ្ញាធរ​ភូមិ​-ឃុំ ​ស្រុក​ និង​ប្រជាពលរដ្ឋ​បាន​ចូលរួម​ដាំ​ដើមឈើ​នៅ​ឃុំ​បឹង​កន្ទួត ', 'http://kohsantepheapdaily.com.kh/article/103876.html', 'http://i.kspg.co/2014/8/788d2a93-9bc0-4468-821d-13c2fef1dcf6.jpg?w=285&h=170&mode=carve', 'ថ្ងៃព្រហស្បត្ត៏ ទី២៨ ខែសីហា ឆ្នាំ២០១៤ ម៉ោង ១៤:២១', '23', '0', '0', 't', '2014-08-28', 'f');
INSERT INTO "tbnews" VALUES ('260', 'B020503', 'B020000', 'ទិវា​បរិស្ថាន​ពិភព​លោក​ថ្ងៃទី​៥ មិថុនា កម្ពុជា​រៀបចំ​នៅ​ខេត្ត​កែប ក្រោម​ប្រធាន​បទ «រួម​គ្នា​ទប់​ស្កាត់​កំណើន​កម្ពស់​ទឹក​សមុទ្រ»', 'ទិវា​បរិស្ថាន​ពិភព​លោក​ថ្ងៃទី​៥ មិថុនា កម្ពុជា​រៀបចំ​នៅ​ខេត្ត​កែប ក្រោម​ប្រធាន​បទ «រួម​គ្នា​ទប់​ស្កាត់​កំណើន​កម្ពស់​ទឹក​សមុទ្រ»', 'http://kohsantepheapdaily.com.kh/article/97444.html', 'http://i.kspg.co/news_pic_kh.png?w=285&h=170&mode=carve', 'ថ្ងៃពុធ ទី៤ ខែមិថុនា ឆ្នាំ២០១៤ ម៉ោង ១៤:២៧', '23', '0', '0', 't', '2014-06-04', 'f');
INSERT INTO "tbnews" VALUES ('261', 'B020503', 'B020000', 'កសិ​ដ្ឋាន​ចិញ្ចឹម​ជ្រូក​ មាន់​៥​កន្លែង​ក្នុង​ស្រុក​កៀន​ស្វាយ​នៅ​តែ​បង្ក​ផល​ប៉ះ​ពាល់​បរិស្ថាន​ធ្ងន់​ធ្ងរ​ មិន​ឃើញ​មន្ត្រី​ជំនាញ​ចុះ​ពិនិត្យ', 'ខេត្ត​កណ្តាល​ ៖ ពាក់​ព័ន្ធ​នឹង​កសិ​ដ្ឋាន​ចិញ្ចឹម​ជ្រូក ​មាន់​៥​កន្លែង​ក្នុង​ស្រុក​កៀន​ស្វាយ​ដែលសារ​​ព័ត៌​មាន​បាន​ចុះ​ផ្សាយ​២​លេខ​រួច​ហើយ​នោះ ឥឡូវ​​នេះ​រឹត​តែ​បង្ក​ការ​ប៉ះ​ពាល់​ដល់​បរិស្ថាន​យ៉ាង​ធ្ងន់​ធ្ងរ​លើស​មុនទៅ​ទៀត​ ហើយ​មិន​ទាន់​ឃើញ​មាន​អាជ្ញា​ធរ​ សមត្ថ​កិច្ច​ មន្ត្រី​ជំនាញ ស្ថាប័ន​ពាក់​ព័ន្ធ​ណា​មួយ', 'http://kohsantepheapdaily.com.kh/article/97434.html', 'http://i.kspg.co/2014/6/01c04454-f8a0-414a-98f5-efffc615b386.jpg?w=285&h=170&mode=carve', 'ថ្ងៃពុធ ទី៤ ខែមិថុនា ឆ្នាំ២០១៤ ម៉ោង ១៣:១៤', '0', '0', '0', 't', '2014-06-04', 'f');
INSERT INTO "tbnews" VALUES ('262', 'B020503', 'B020000', 'អ្នករត់ម៉ូតូ​ឌុបម្នាក់​ ស៊ីឈ្នួល​ដឹកសម្រា​មយកទៅ​ចាក់ចោល​ខាងជើង​ភូមិប៉ា​ហ៊ីជើង​ យ៉ាង​អនាធិបតេយ្យ​នៅក្រុង​ប៉ៃលិន', 'ខេត្ត​ប៉ៃលិន​ ៖ ​គេសង្កេត​ឃើញ​សម្រាម​ជាច្រើន​ការ៉ុង ​ ត្រូវ​បាន​អ្នក​រត់ម៉ូតូ​ឌុបម្នាក់​ស៊ីឈ្នួ​ល​អ្នកភូមិ​ដឹកយក​​ទៅចោល​នៅក្បែ​រ​ផ្លូវ​លំ បង្ក​ឱ្យជះក្លិន​យ៉ាង​អាសោច៍​​ប៉ះ​ពាល់​ដល់ការរ​ស់នៅ​របស់​ប្រជាពល​រដ្ឋនៅ​ក្បែរភូមិ ​ និងអ្នក​រស់នៅ​ក្បែរគំនរ​សម្រាម​ ដែល​​ការដឹក​សម្រាម​យកទៅ​ចោល​យ៉ាង​អនាធិបតេយ្យ​នេះ ​ បានកើត​ឡើង​ជារៀង​រាល់ថ្ងៃ ​ស្ថិត​នៅក្បែរ​ផ្លូវលំ​មួយ​កន្លែង​​ក្នុង​', 'http://kohsantepheapdaily.com.kh/article/96821.html', 'http://i.kspg.co/2014/5/60097ab7-dd4f-4964-a279-74ff826d6748.jpg?w=285&h=170&mode=carve', 'ថ្ងៃចន្ទ ទី២៦ ខែឧសភា ឆ្នាំ២០១៤ ម៉ោង ២១:៣៩', '2', '0', '0', 't', '2014-05-26', 'f');
INSERT INTO "tbnews" VALUES ('263', 'B020503', 'B020000', 'កំណាត់​​ផ្លូវ​​ជាតិលេខ​​1ក្លាយ​ជា​ធុង​ដាក់​សំរាម​​របស់​អ្នក​ស្រុក​​នៅផ្សា​រ​​ក​អណ្តើក', 'ខេត្ត​ព្រៃ​វែង ៖ សំរាម​​ និង​​កាក​​សំណល់​ផ្សេង​ៗ ​​ត្រូវ​​បាន​​អ្នក​ស្រុក​​ដែល​រស់​នៅ​​ជុំ​វិញ​​​​​ផ្សារ​​​ក​អណ្តើក​នាំ​គ្នា​​ប្រមូល​ពី​តាម​ផ្ទះ​​របស់​ខ្លួន​​​និង​ពី​ក្នុង​ផ្សារ ​​យក​​មក​ចាក់​គរ​​លើ​គ្នា​​ជា​រៀង​រាល់​ថ្ងៃ នៅ​លើ​ចិញ្ចើម​​​​ផ្លូវ​ជាតិ​​លេខ​​១ ត្រង់​​ចំណុច​​មុខ​​វត្ត​​ក​អណ្តើក ​​ស្ថិត​​​ក្នុង​ឃុំ​​ប្រា សាទ ស្រុក​​កំពង់​ត្របែក ។', 'http://kohsantepheapdaily.com.kh/article/53276.html', 'http://i.kspg.co/2012/9/55d39b88-2ca2-49f7-afdb-6ac85779a9f5.jpg?w=285&h=170&mode=carve', 'ថ្ងៃអាទិត្យ ទី២៣ ខែកញ្ញា ឆ្នាំ២០១២ ម៉ោង ០៨:៤៥', '2', '0', '0', 't', '2012-09-23', 'f');
INSERT INTO "tbnews" VALUES ('264', 'B020503', 'B020000', 'អង្គការ​​​ចលនា​​ដើម្បី​សុខភាព​​ចែក​រង្វាន់ជូនដល់​អ្នក​​ប្រកួតឈប់​​ជក់បារី​', 'រាជធានីភ្នំពេញ ៖ ​​អង្គការ​ចលនា​​​ដើម្បី​​​សុខភាព ​កាលពី​រសៀល​ថ្ងៃទី​១៣ខែសីហា​កន្លង​មក​​ នៅមន្ទីរ​ពេទ្យ​​​​កុមារ​​ជាតិ​ បាន​​រៀបចំ​​ធ្វើ​​ពិធី​​​​ចែក​រង្វាន់​លើក​ទឹកចិត្ត​ដល់​ប្រជា​​ពលរដ្ឋ​​ចំនួន​៥​​គូ ដែល​បាន​ប្រកួត​ឈ្នះ​​​​នៅក្នុង​កម្ម​វិធី​ប្រឡង​​​ប្រជែង​ឈប់​ជក់​បារី​ ។​ ​លោក​វេជ្ជបណ្ឌិត​​មុំ គង់​ នាយ​ក​ប្រតិបត្តិ​​​​នៃ​អង្គការ​សុខភាព​​ពិភព​លោក​បាន​ឱ្យ​ដឹងថា កម្ម​វិធី​​នេះ​បាន​ធ្វើ​ឡើង​​​ចាប់​តាំងពី​ថ្ងៃទី​១ខែ​', 'http://kohsantepheapdaily.com.kh/article/50729.html', 'http://i.kspg.co/2012/8/2b38e35f-3849-4fb8-a5da-1d7bdc0024d0.jpg?w=285&h=170&mode=carve', 'ថ្ងៃសុក្រ ទី១៧ ខែសីហា ឆ្នាំ២០១២ ម៉ោង ០៩:៤៣', '23', '0', '0', 't', '2012-08-17', 'f');
INSERT INTO "tbnews" VALUES ('265', 'B030101', 'B030000', 'ក្រសួងសាធារណការ និង ប.ស.ស បើក​វគ្គ​បណ្តុះ​បណ្តាល​គ្រូ​បង្គោល អំពី​ច្បាប់ស្តី​ពី​ចរាចរណ៍​ផ្លូវ​គោក​', '<strong>ភ្នំពេញ៖</strong> បេឡាជាតិរបបសន្តិសុខសង្គម (ប.ស.ស) នៃក្រសួងការងារ បានសហការជាមួយ ក្រសួងសាធារណការ និងដឹកជញ្ជូន នៅថ្ងៃទី០៣ ដល់ថ្ងៃទី៥ ខែសីហា ឆ្នាំ២០១៥នេះ បានរៀបចំបើកវគ្គបណ្តុះបណ្តាលគ្រូបង្គោល អំពីច្បាប់ស្តីពីចរាចរណ៍ផ្លូវគោក និងសុវត្ថិភាព ធ្វើដំណើររបស់កម្មករនិយោជិត ជូនដល់បុគ្គលិកសហព័ន្ធសហជីពចំនួន ៤៥សហព័ន្ធសហជីព ស្មើនឹង ៩០នាក់ ។', 'http://www.dap-news.com/2011-06-14-02-39-55/96415-2015-08-03-10-08-44.html', 'http://s3-ap-southeast-1.amazonaws.com/images.freshnews/fn.photo/Aug2015/K-5327.jpg', 'Monday, 03 August 2015 17:07', '0', '0', '0', 't', '2015-08-03', 'f');
INSERT INTO "tbnews" VALUES ('266', 'B030101', 'B030000', 'ស្នងការខេត្តក្រចេះ ព្រមានចាត់វិធានការ លើមន្រ្តីនគរបាល ដែលមិនគោរពតួនាទី និងភារកិច្ច', '<strong>ក្រចេះ ៖</strong> ស្នងការនគរបាល ខេត្តក្រចេះ លោកឧត្តមសេនីយ៍ត្រី លន់ សុផាត បាន ព្រមានយ៉ាងធ្ងន់ៗ និងចាត់វិធានការ ដល់មន្រ្តីនគរបាល ទាំងឡាយណា ដែលបណ្តែតបណ្តោយ ឲ្យមានភាពចន្លោះប្រហោង លើសកម្មភាព កិច្ចការងារ ចុះយាមល្បាត តាមទីប្រជុំជន សាធារណៈដែលងាយរងគ្រោះ គឺ មិនអនុវត្តដូចទៅ តាមទិសដៅផែនការ សកម្មភាពដែលដាក់ជូន។', 'http://www.dap-news.com/2011-06-14-02-39-55/96413-2015-08-03-09-53-50.html', 'http://image.dap-news.com:81/dap-news/dap-images/2015/July/id-067/CH-02005.jpg', 'Monday, 03 August 2015 16:53', '323', '0', '0', 't', '2015-08-03', 'f');
INSERT INTO "tbnews" VALUES ('267', 'B030101', 'B030000', 'នគរបាល  ឃាត់ខ្លួនកម្មករ សំណង់ម្នាក់ ដកហូតថ្នាំញៀន ៥កញ្ចប់', '<strong>ព្រះសីហនុ ៖</strong> កម្លាំងនគរបាលការិយា ល័យប្រឆាំងគ្រឿងញៀននៃស្នងការដ្ឋាននគរ បាលខេត្ដព្រះសីហនុ បានធ្វើការស្រាវជ្រាវ ហើយឈានទៅដល់ការចាប់ខ្លួនកម្មករសំណង់ ម្នាក់ ពីបទចែកចាយនិងរក្សាទុកសារធាតុ ញៀន ព្រមទាំងដកហូតថ្នាំញៀនប្រភេទមេ តំហ្វេតាមីន ចំនួន៥កញ្ចប់ផងដែរ ។', 'http://www.dap-news.com/2011-06-14-02-39-55/96411-2015-08-03-09-45-46.html', 'http://image.dap-news.com:81/dap-news/dap-images/2015/July/id-067/CH-02003.jpg', 'Monday, 03 August 2015 16:45', '0', '0', '0', 't', '2015-08-03', 'f');
INSERT INTO "tbnews" VALUES ('268', 'B030101', 'B030000', 'រថយន្តស្ទូចមួយ គ្រឿង បានក្រឡាប់ ពេលស្ទូចម៉ាស៊ីន ភ្លើងឡើងលើអគារ ទប់ជំហរមិនជាប់', '<strong>ភ្នំពេញ៖</strong> រថយន្តស្ទួចមួយគ្រឿង បានក្រឡាប់ផ្ងារ កង់ឡើងលើ ខណៈស្ទួចម៉ាស៊ីនភ្លើងឡើងលើអគារ ហើយ ទប់ជំហមិនជាប់ ។ ហេតុការណ៍នេះ បានកើតឡើងកាលពីវេលាម៉ោង១និង៣០នាទីរសៀលថ្ងៃទី៣ខែសីហា ឆ្នាំ២០១៥ស្ថិតនៅតាមបណ្តោយផ្លូវ៤៦៦ ក្រុម២៥ ភូមិ៨ សង្កាត់បឹងត្របែក ខណ្ឌចំការមន ។', 'http://www.dap-news.com/2011-06-14-02-39-55/96406-2015-08-03-08-41-59.html', 'http://image.dap-news.com:81/dap-news/dap-images/2015/AUGUST/id-011/Hong-0020.jpg', 'Monday, 03 August 2015 15:40', '32', '0', '0', 't', '2015-08-03', 'f');
INSERT INTO "tbnews" VALUES ('269', 'B030101', 'B030000', 'លោក ច័ន្ទ ថេតថានរៈ នាយខណ្ឌរដ្ឋបាល ព្រៃឈើកណ្តាល ប្រគេនទៀនព្រះវស្សា និងដាំដើមឈើ នៅវត្តអង្គសណ្តាន់', '<b>កំពង់ស្ពឺ៖</b> លោក ច័ន្ទ ថេតថានរៈ នាយខណ្ឌរដ្ឋបាលព្រៃឈើកណ្តាល និងសហការី កាលពីថ្ងៃទី០២ ខែសីហា ឆ្នាំ ២០១៥ បានដង្ហែទៀនព្រះវស្សា បច្ច័យ និង ទេយ្យវត្ថុមួយចំនួនទៀត ទៅប្រគេនព្រះសង្ឃ ដែលកំពុងគង់ចាំព្រះ វង្សា ក្នុងវត្តពុទ្ធសីមាវត្តអង្គសណ្តាន់ ស្ថិតក្នុងភូមិអង្គសណ្តាន់ ឃុំព្រះស្រែ ស្រុកឧដុង្គ ខេត្តកំពង់ស្ពឺ។', 'http://www.dap-news.com/2011-06-14-02-39-55/96404-2015-08-03-08-07-18.html', 'http://image.dap-news.com:81/dap-news/dap-images/2015/July/id-009/ST-292.jpg', 'Monday, 03 August 2015 15:06', '0', '0', '0', 't', '2015-08-03', 'f');
INSERT INTO "tbnews" VALUES ('270', 'B030101', 'B030000', 'លោក ពៅ ពិសិដ្ឋ សមាជិកក្រុមប្រឹក្សា ខេត្តសៀមរាប ត្រូវបានប្រកាស ចូលកាន់ មុខតំណែង ជាអភិបាលរងខេត្ត', '<strong>សៀមរាប ៖</strong> ការប្រកាស ចូលកាន់មុខតំណែង ជាអភិបាលរង ខេត្តសៀមរាប របស់ លោក ពៅ ពិសិដ្ឋ អតីតសមាជិក ក្រុមប្រឹក្សាខេត្តសៀមរាប ជំនួសលោកអ ម៉ៅ វុឌ្ឍី ដែលត្រូវចូលនិវត្តន៍ នាព្រឹកថ្ងៃទី៣ ខែសីហា ២០១៥នេះ ក្រោមអធិបតីភាពរបស់ លោក លេង វី អនុរដ្ឋលេខាធិការ ក្រសួងមហាផ្ទៃ តំណាង របស់ សម្តេចក្រឡាហោម ស ខេង ឧបនាយរដ្ឋមន្ត្រី រដ្ឋមន្ត្រីក្រសួងមហាផ្ទៃ ។', 'http://www.dap-news.com/2011-06-14-02-39-55/96402-2015-08-03-07-54-12.html', 'http://image.dap-news.com:81/dap-news/dap-images/2015/AUGUST/id-011/Hong-0017.jpg', 'Monday, 03 August 2015 14:30', '0', '0', '0', 't', '2015-08-03', 'f');
INSERT INTO "tbnews" VALUES ('271', 'B030102', 'B030000', 'ទឹកជំនន់នៅភូមា បណ្តាលឲ្យមនុស្ស ៤៧នាក់ស្លាប់', '<strong>ណៃពិដោ៖</strong> ភ្លៀងធ្លាក់យ៉ាងសន្ធប់ បានបណ្តាលឲ្យមនុស្ស ៤៧នាក់ស្លាប់បាត់បង់ជីវិត នៅ ប្រទេសមីយ៉ាន់ម៉ា (ភូមា) និង មនុស្ស ២១០.០០០នាក់ផ្សេងទៀត រស់នៅក្នុងរដ្ឋចំនួន ១៤ នៅក្នុងសហភាពមួយនេះ រស់នៅក្នុងទឹកជំនន់ ចាប់តាំងពីខែមិថុនា មកម្ល៉េះ។ នេះបើតាម របាយការណ៍ពីក្រសួងកិច្ចការសង្គមរបស់ មីយ៉ាន់ម៉ា ដែលស៊ិនហួ ទទួលបាន នៅថ្ងៃចន្ទ ទី០៣ ខែសីហា ឆ្នាំ២០១៥។', 'http://www.dap-news.com/2011-06-14-02-40-26/96410-2015-08-03-09-09-07.html', 'http://mobile.abc.net.au/news/image/6665924-1x1-700x700.jpg', 'Monday, 03 August 2015 16:07', '3', '0', '0', 't', '2015-08-03', 'f');
INSERT INTO "tbnews" VALUES ('272', 'B030102', 'B030000', 'វេជ្ជបណ្ឌិតផ្នែក​វះ​កាត់​ប្តូរភេទ និង​បង្ក​កំ​ណើត​កូន​ក្នុង​កែវ​ដំ​បូង​គេ​បង្អស់​របស់​អាមេរិក ស្លាប់​ក្នុងវ័យ ១០៤ ឆ្នាំ', '<strong>ឡុងដ៍៖</strong> លោក Howard W. Jones Jr. ត្រូវបានគេស្គាល់ថា ជាគ្រូពេទ្យជំនាញផ្នែកវះកាត់រោគស្ត្រីម្នាក់ និងក៏ជាអ្នកបើកគ្លីនិកប្តូរភេទដំបូងគេ ដែលបានទទួលភាពជោគជ័យ សម្រាប់ការបង្កកំណើត កូនក្នុងកែងដំបូងគេបង្អស់ របស់សហរដ្ឋអាមេរិក បានស្លាប់កាលពីថ្ងៃសុក្រនៅក្នុង ស្រុក Norfolk ភាគខាងកើត របស់ចក្រភពអង់គ្លេស ក្នុងវ័យ ១០៤ ឆ្នាំ ។', 'http://www.dap-news.com/2011-06-14-02-40-26/96408-2015-08-03-08-46-48.html', 'http://image.lookingtoday.com:81/lookingtoday/looking-images/news/2015/August/ID-141/MrMSP-0055.jpg', 'Monday, 03 August 2015 15:44', '0', '0', '0', 't', '2015-08-03', 'f');
INSERT INTO "tbnews" VALUES ('273', 'B030102', 'B030000', 'ម្ចាស់ចំណាយ ២០០ដុល្លារ វះកាត់​ដុំ​សាច់ក្នុង​ខួរក្បាល​ឲ្យ​ត្រីកន្ទុយបី​របស់​ខ្លួន', '<strong>ស៊ីដនី៖</strong> ត្រីកន្ទុយបីមួយក្បាល ដែលមានឈ្មោះ George និងមានអាយុជាង១០ឆ្នាំ មកហើយនោះ ត្រូវបានវះកាត់យកដុំសាច់ ក្នុងខួរក្បាលចេញ ប្រកបដោយជោកជ័យ បន្ទាប់ពីធ្វើរោគវិនិច្ឆ័យ នេះបើយោងតាម ការចុះផ្សាយរបស់គេហទំព័រកម្សាន្តមួយរបស់អូស្រ្តាលី នាពេលថ្មីៗនេះ។', 'http://www.dap-news.com/2011-06-14-02-40-26/96405-2015-08-03-08-07-30.html', 'http://image.lookingtoday.com:81/lookingtoday/looking-images/news/2015/August/ID-141/MrMSP-0048.jpg', 'Monday, 03 August 2015 14:13', '23', '0', '0', 't', '2015-08-03', 'f');
INSERT INTO "tbnews" VALUES ('274', 'B030102', 'B030000', 'ស្នែងបុរាណដ៏ចម្លែក ត្រូវបានរកឃើញ​ នៅអ៊ីតាលី', '<strong>តូរ៉ុនតូ៖</strong> ក្រុមអ្នកបុរាណវិទ្យានិយាយថា វត្ថុបុរាណដែលត្រូវ បានគេរកឃើញកាលពី ៦០ឆ្នាំមុន នៅក្នុងប្រទេសអ៊ីតាលី ត្រូវបានគេកំណត់ ជាចុងក្រោយថា វាជាស្នែងសម្រាប់ផ្លុំរបស់ក្រុម កុសម្ព័ន្ធ មួយក្រុមដែលមានឈ្មោះថា Celtic ដែលស្ថិតនៅក្នុង យុគសម័យលោហៈ។', 'http://www.dap-news.com/2011-06-14-02-40-26/96373-2015-08-03-04-15-53.html', 'http://image.lookingtoday.com:81/lookingtoday/looking-images/news/2015/August/ID-140/hour-00421.jpg', 'Monday, 03 August 2015 11:10', '3', '0', '0', 't', '2015-08-03', 'f');
INSERT INTO "tbnews" VALUES ('275', 'B030102', 'B030000', 'ស្លាប់៨៦ និងរបួស៥៦នាក់ នៅក្នុងគ្រោះ​ទឺកជំនន់​នៅ​ភាគ​ឥសាន ប៉ាគីស្ថាន', '<strong>ប៉េសាវ៉ា៖</strong> ស្ថិតនៅភាគឥសាននៃប្រទេសប៉ាគីស្ថាន បានរងគ្រោះដោយសារទឹកជំនន់ធ្ងន់ធ្ងរ បណ្តាលឲ្យពលរដ្ឋចំនួន៨៦នាក់ បាត់បង់ជីវិត និង៥៦នាក់ ផ្សេងទៀតរងរបួសធ្ងន់ស្រាល ។', 'http://www.dap-news.com/2011-06-14-02-40-26/96336-2015-08-02-04-17-31.html', 'http://image.lookingtoday.com:81/lookingtoday/looking-images/news/2015/August/ID-141/MrMSP-0014.jpg', 'Sunday, 02 August 2015 11:14', '3', '0', '0', 't', '2015-08-02', 'f');
INSERT INTO "tbnews" VALUES ('276', 'B030102', 'B030000', 'ការអញ្ជើញ​ដល់របស់លោក ចន ឃឺរី លើទឹកដីអេហ្ស៊ីប ជាជំហរ​ក្នុងការចង​សម្ព័ន្ធភាព​រវាងអាម៉េរិក និងអេហ្ស៊ីប', '<strong>ក្រុងគែរ៖</strong> យោងតាមទីភ្នាក់ងារព័ត៌មានចិនស៊ិនហួ ចុះផ្សាយនៅថ្ងៃទី០២ ខែសីហា ឆ្នាំ២០១៥ថា លោក ចន ឃឺរី រដ្ឋមន្ត្រីក្រសួងការបរទេសសហរដ្ឋអាម៉េរិក បានអញ្ជើញទៅដល់ទឹកដីនៃប្រទេសអេហ្ស៊ីប កាលពីថ្ងៃសៅរ៍ម្សិលមិញ ដែលនេះជាការបំពេញទស្សនកិច្ចមួួយនៅក្នុងប្រទេសនៅតំបន់មជ្ឈិមបូព៌ា ។', 'http://www.dap-news.com/2011-06-14-02-40-26/96335-2015-08-02-04-07-41.html', 'img/dapnews.png', 'Sunday, 02 August 2015 11:06', '2', '0', '0', 't', '2015-08-02', 'f');
INSERT INTO "tbnews" VALUES ('277', 'B030401', 'B030000', 'ក្រុមហ៊ុន Kfour បង្កើត កម្មវិធីពិសេស អបអរខួប អនុស្សាវរីយ៍ លើកទី២១ឆ្នាំ', '<strong>ភ្នំពេញ៖</strong> Kfour Group ជាក្រុមហ៊ុន ផ្គត់ផ្គង់សម្ភារអេឡិចត្រូនិច ដ៏ធំមួយនៅកម្ពុជា នៅថ្ងៃទី៣១ ខែកក្កដា ឆ្នាំ២០១៥ បានប្រារព្ធពិធីអបអរសាទរ ខួបអនុស្សារីយ៍២១ឆ្នាំ របស់ខ្លួន។', 'http://www.dap-news.com/2011-06-14-02-43-39/96271--kfour-.html', 'http://image.dap-news.com:81/dap-news/dap-images/2015/July/id-014/that-0879.jpg', 'Friday, 31 July 2015 16:59', '0', '0', '0', 't', '2015-07-31', 'f');
INSERT INTO "tbnews" VALUES ('278', 'B030401', 'B030000', '&quot;Superphone&quot; អាចនឹងចេញលក់ នៅឆ្នាំ២០២០', 'ប្រធានបទក្តៅៗជាច្រើន ស្តីអំពីវិស័យឧស្សាហកម្ម ត្រូវបានលើកយកមកពិភាក្សា នៅឯកិច្ចប្រជុំកំពូល Global Device Summit នៅមុនថ្ងៃសមាជទូរស័ព្ទពិភពលោកមួយថ្ងៃ នៅទីក្រុងស៊ាងហៃ កាលពីថ្ងៃទី១៤ ខែកក្កដា ឆ្នាំ២០១៥ ។', 'http://www.dap-news.com/2011-06-14-02-43-39/96126-qsuperphoneq-.html', 'http://image.dap-news.com:81/dap-news/dap-images/2015/July/id-014/that-0789.jpg', 'Wednesday, 29 July 2015 15:10', '23', '0', '0', 't', '2015-07-29', 'f');
INSERT INTO "tbnews" VALUES ('279', 'B030401', 'B030000', 'ភ្ញៀវទេសចររុស្ស៊ី មកកម្សាន្ត ខេត្តព្រះសីហនុ នាំមុខសាសន៍ដទៃ', '<strong>ព្រះសីហនុ៖</strong> លោក នូ សុផល ប្រធានមន្ទីរទេសចរណ៍ ខេត្តព្រះសីហនុ បានថ្លែងប្រាប់ មជ្ឈមណ្ឌលព័ត៌មានដើមអម្ពិល ឲ្យដឹងថា រយៈពេល៦ខែ ដើមឆ្នាំ២០១៥នេះ ភ្ញៀវទេសចរ សរុបមានចំនួន ៨៨៥,៣៩៨នាក់ កើនឡើង ១៥.៨៧% ធៀបនឹងឆមាសទី១ឆ្នាំ២០១៤ ក្នុងនោះភ្ញៀវជាតិមាន ចំនួន ៦៦៩,០៦៦នាក់ កើនឡើង ១៨.៧៣% និងភ្ញៀវអន្តរជាតិ មានចំនួន២១៦,៣៣២នាក់ កើនឡើង ៧.៨៣% ។ &nbsp;', 'http://www.dap-news.com/2011-06-14-02-43-39/95735-2015-07-23-04-44-33.html', 'http://image.dap-news.com:81/dap-news/dap-images/2015/July/id-014/that-0576.jpg', 'Thursday, 23 July 2015 11:43', '2', '0', '0', 't', '2015-07-23', 'f');
INSERT INTO "tbnews" VALUES ('280', 'B030401', 'B030000', 'ផលិតផល បរិក្ខារបន្ទប់ទឹក លំដាប់ពិភពលោក ផ្ដល់ការ សន្សំសំចៃខ្ពស់ មកដល់កម្ពុជាហើយ', '<strong>ភ្នំពេញ៖</strong> ផលិតផលបរិក្ខារបន្ទប់ទឹក ម៉ាក E.C.A Serel ដែលមានគុណភាព លំដាប់ពិភពលោក និងផ្ដល់ការសន្សំសំចៃខ្ពស់ ត្រូវបាននាំមកកាន់ទីផ្សារកម្ពុជាហើយ និងចែកចាយ ផ្ដាច់មុខដោយក្រុមហ៊ុន Soma Trading។', 'http://www.dap-news.com/2011-06-14-02-43-39/95689-2015-07-22-08-06-21.html', 'http://image.dap-news.com:81/dap-news/dap-images/2015/July/id-014/that-0562.jpg', 'Wednesday, 22 July 2015 15:05', '0', '0', '0', 't', '2015-07-22', 'f');
INSERT INTO "tbnews" VALUES ('281', 'B030401', 'B030000', 'ស្រាបៀរអង្គរ ប្រារព្ធខួប ៥០ឆ្នាំ នៅកម្ពុជា ជាមួយនឹងការបង្ហាញ មេដាយថ្មីទៀត លើឆាកអន្តរជាតិ', '<strong>ភ្នំពេញ៖</strong> កាលពីថ្ងៃទី១៨ ខែកក្កដា ឆ្នាំ២០១៥ កន្លងទៅនេះ ក្រុមហ៊ុនស្រាបៀរកម្ពុជាខេមប្រ៊ូ បានប្រារព្ធខួបលើកទី៥០ឆ្នាំ របស់ខ្លួន នៅក្នុងប្រទេសកម្ពុជា ជាមួយនឹងការបង្ហាញមេដាយថ្មីចំនួន២ ទៀត ដែលក្រុមហ៊ុនបានឈ្នះ នៅលើឆាកអន្តរជាតិ លើស្រាបៀរកំពុង ពេញនិយម២ប្រភេទរបស់ខ្លួន ។', 'http://www.dap-news.com/2011-06-14-02-43-39/95445-2015-07-18-08-21-37.html', 'http://image.dap-news.com:81/dap-news/dap-images/2015/July/id-014/that-0475.jpg', 'Saturday, 18 July 2015 15:20', '23', '0', '0', 't', '2015-07-18', 'f');
INSERT INTO "tbnews" VALUES ('282', 'B030401', 'B030000', 'បើមានបុណ្យទាន ក្នុងមួយថ្ងៃ Aeon Mall ទទួលបានអតិថិជន ៥-៦ម៉ឺននាក់', '<strong>ភ្នំពេញ៖</strong> ជាផ្សារទំនើបជប៉ុន លំដាប់ថ្នាក់ អន្តរជាតិលេខ១ នៅកម្ពុជា Aeon Mall ពេលនេះ ទទួល បានអតិថិជន ជាមធ្យមរហូតដល់៥-៦ម៉ឺននាក់ ក្នុងមួយថ្ងៃ បើសិនជាមានបុណ្យទានម្តងៗ ប៉ុន្តែបើថ្ងៃធម្មតា ផ្សារមួយនេះ ទទួលបានអតិថិជន ប្រមាណ៣ម៉ឺននាក់។', 'http://www.dap-news.com/2011-06-14-02-43-39/95184--aeon-mall-.html', 'http://image.dap-news.com:81/dap-news/dap-images/2015/July/id-010/chanroth015-july-156.jpg', 'Saturday, 04 July 2015 16:16', '0', '0', '0', 't', '2015-07-04', 'f');
INSERT INTO "tbnews" VALUES ('283', 'B030302', 'B030000', 'លោក វ៉ាត់ ចំរើន៖ មុនឆ្នាំ១៩៧០ មាតាបិតាសិស្ស ស្ម័គ្របង់វិភាគទាន ១០រៀល ដើម្បីទិញឧបករណ៍ កីឡាឲ្យសិស្ស', '<strong>ភ្នំពេញ៖</strong> នៅមុនឆ្នាំ១៩៧០ មាតាបិតាសិស្សានុសិស្ស គ្រប់រូប បានស្ម័គ្រចូលរួម បង់វិភាគទាន ចំនួន១០រៀល ស្មើនឹង ២០០០រៀលពេលបច្ចុប្បន្ន សម្រាប់ទិញឧបករណ៍កីឡាឲ្យសិស្សលេងកម្សាន្ត នេះបើយោងតាមលោក វ៉ាត់ ចំរើន អគ្គលេខាធិការគណៈកម្មាធិការជាតិ អូទ្បំាពិកកម្ពុជា (NOCC) ថ្លែងប្រាប់មជ្ឈមណ្ឌលព័ត៌មានដើមអម្ពិល ព្រឹកមិញនេះ ។', 'http://www.dap-news.com/morokatmagazine/96368-2015-08-03-03-43-49.html', 'http://image.lookingtoday.com:81/lookingtoday/looking-images/news/2015/August/ID-140/hour-00417.jpg', 'Monday, 03 August 2015 10:40', '0', '0', '0', 't', '2015-08-03', 'f');
INSERT INTO "tbnews" VALUES ('284', 'B030302', 'B030000', 'អត្តពលិកដែល​ឈ្នះ​មេ​ដាយ​នៅ SEA Games 2015 ទទួល​ប្រាក់​រង្វាន់​ថ្ងៃ​នេះ', '<strong>ភ្នំពេញ៖</strong> ប្រតិភូ គ្រូបង្វឹក និងកីឡាករ-កីឡាការិនី ដែលបាន បញ្ជូនទៅចូលរួមប្រកួត ក្នុងព្រឹត្តិការណ៍កីឡាថ្នាក់ តំបន់អាស៊ាន SEA Games លើកទី២៨ ឆ្នាំ២០១៥ នៅប្រទេសសិង្ហបុរីដែលឈ្នះមេដាយ សរុបចំនួន១៥ ស្មើនឹង៣៧គ្រឿង គឺទទួលបានប្រាក់រង្វាន់លើកទឹកចិត្តថ្ងៃនេះ នៅទីស្នាក់ការគណៈកម្មាធិការជាតិអូឡាំពិកកម្ពុជា (NOCC) នេះបើយោងតាមរបាយការណ៍ ដែលមជ្ឈមណ្ឌលសារព័ត៌មានដើមអម្ពិល ទទួលបានល្ងាចនេះ ។', 'http://www.dap-news.com/morokatmagazine/96276--sea-games-2015-.html', 'http://image.lookingtoday.com:81//lookingtoday/looking-images/news/2015/July/ID-142/dp-0666.jpg', 'Friday, 31 July 2015 17:37', '0', '0', '0', 't', '2015-07-31', 'f');
INSERT INTO "tbnews" VALUES ('285', 'B030302', 'B030000', 'កាហ្វេ និងតែ ប្រហែលជាអាច ជួយការពារ ជំងឺខ្លាញ់ក្នុងថ្លើម', 'ក្រុមអ្នកស្រាវជ្រាវ ជនជាតិអាមេរិក និងសិង្ហបុរី បាននិយាយថា ការទទួលទានកាហ្វេ ក្នុងបរិមាណមួយសមរម្យជារៀងរាល់ថ្ងៃ អាចជួយការពារជំងឺខ្លាញ់ក្នុងថ្លើមបាន។', 'http://www.dap-news.com/morokatmagazine/96211-2015-07-30-09-50-30.html', 'http://image.dap-news.com:81/dap-news/dap-images/2015/July/id-014/that-0845.jpg', 'Thursday, 30 July 2015 16:49', '2', '0', '0', 't', '2015-07-30', 'f');
INSERT INTO "tbnews" VALUES ('286', 'B030302', 'B030000', 'ក្រុមឈុតតូច U-16 កម្ពុជា ឈ្នះ U-16 ហ្វីលីពីន ១-០ នៅមុខអ្នកគាំទ្រ ជាង១ម៉ឺននាក់', 'null', 'http://www.dap-news.com/morokatmagazine/96083--u-16-u-16-.html', 'http://image.dap-news.com:81/dap-news/dap-images/2015/July/id-067/CH-01501.jpg', 'Tuesday, 28 July 2015 21:57', '0', '0', '0', 't', '2015-07-28', 'f');
INSERT INTO "tbnews" VALUES ('287', 'B030302', 'B030000', 'ប្រើប្រាស់​ស្វាយ​ទុំ ដើម្បី​បំបាត់​​មុនមុខ', '<strong>សុខភាព៖</strong> ប្រសិនបើអ្នកកំពុងតែមានការព្រួយបារម្ភទៅ នឹងរោគមុននៅលើផ្ទៃមុខនោះ ស្វាយទុំ អាចជួយដោះស្រាយបានដោយគ្រាន់តែ យកស្វាយទុំមួយផ្លែ លាយជាមួយនឹងទឹកក្រូចឆ្មា ពាក់កណ្តាលស្លាបព្រាបាយ ម្សៅគ្រាប់ ១ ភាគ ៤ស្លាបព្រាបាយ និងទឹកឃ្មុំ ១ស្លាបព្រាកាហ្វេ ហើយយកស្វាយទុំ ទៅឈ្លីឲ្យបែកស្អិតកូរ ជាមួយនឹងចំណែក ផ្សំទាំងអស់ដែលមាន រហូតក្លាយទៅជាល្បាយខាប់មួយ។', 'http://www.dap-news.com/morokatmagazine/95967-2015-07-27-06-53-14.html', 'http://image.lookingtoday.com:81/lookingtoday/looking-images/news/2015/July/ID-141/MrMSP-0755.jpg', 'Monday, 27 July 2015 13:48', '2', '0', '0', 't', '2015-07-27', 'f');
INSERT INTO "tbnews" VALUES ('288', 'B030302', 'B030000', 'ចាំបាច់​ត្រូវ​តែ ពាក់​វ៉ែន​តា​ខ្មៅ​ពេល​ដើរ​ហាល​ថ្ងៃ', 'ក្រុមអ្នកស្រាវជ្រាវជនជាតិអូស្រា្តលីនិយាយថា ការពាក់វ៉ែនតាសម្រាប់ការពារកម្តៅថ្ងៃ អាចកាត់បន្ថយភាពហត់នឿយ ព្រមទាំងមានផលប្រយោជន៍ សម្រាប់អ្នកគេងមិនលក់ និងអ្នកធ្វើការច្រើន។', 'http://www.dap-news.com/morokatmagazine/95814-2015-07-24-07-31-35.html', 'http://image.lookingtoday.com:81//lookingtoday/looking-images/news/2015/July/ID-142/dp-0491.jpg', 'Friday, 24 July 2015 14:24', '2', '0', '0', 't', '2015-07-24', 'f');
INSERT INTO "tbnews" VALUES ('289', 'B030302', 'B030000', 'ឱក សុគន្ធកញ្ញា កាន់​តែ​លេច​ធ្លោ​ជា​មួយ​ជំនួយ​ការ​ថត​រូប​ថ្មី', '<strong>ភ្នំពេញ៖</strong> ជាមួយនឹងការតុបតែងមុខ យ៉ាងស្រស់ស្អាត កញ្ញា ឱក សុគន្ធកញ្ញា ក្នុងសម្លៀកបំពាក់ដ៏ស៊ីវិល័យ និងថ្លៃថ្នូរ ពិតជាសាកសមឥតខ្ចោះ ជាមួយរូបរាងរបស់ទូរស័ព្ទដៃដ៏ទំនើបរបស់នាង។ រូបភាពដ៏ទាក់ទាញ ដែលត្រូវបានបង្ហោះលើទំព័រហ្វេសប៊ុក របស់តារាចម្រៀងដ៏ល្បីនៅកម្ពុជា ឱក សុគន្ធកញ្ញា ត្រូវបានក្រុម Fan ដែលស្រឡាញ់ និងគាំទ្រនាង ចុច Like និង Share យ៉ាងច្រើនសន្ធឹក។', 'http://www.dap-news.com/morokatmagazine/95794-samsung-galaxy-s6-edge-.html', 'http://image.lookingtoday.com:81/lookingtoday/looking-images/news/2015/July/ID-140/hour-01092.jpg', 'Friday, 24 July 2015 11:46', '2', '0', '0', 't', '2015-07-24', 'f');
INSERT INTO "tbnews" VALUES ('290', 'B030302', 'B030000', '៣ចំនុច ការពារមិនឲ្យ មានស្នាម លើស្បែក', 'ដើម្បីចៀសវាងការកើតមុន ឬការរលាកផ្សេងៗ ដែលអាចនាំទៅរកការកើតស្នាមលើ ស្បែកនោះពិបាកនឹងធ្វើបាន ប៉ុន្តែទោះជាយ៉ាងណាក្តី អ្នកអាចការពារកុំឲ្យកើតមានស្នាមខ្មៅ ឬស្នាមផ្លែសាច់លើស្បែករបស់អ្នកបាន ដោយវិធីងាយៗដូចខាងក្រោមៈ', 'http://www.dap-news.com/morokatmagazine/95691-2015-07-22-08-30-39.html', 'http://image.lookingtoday.com:81/lookingtoday/looking-images/news/2015/July/ID-140/hour-00971.jpg', 'Wednesday, 22 July 2015 15:27', '0', '0', '0', 't', '2015-07-22', 'f');
INSERT INTO "tbnews" VALUES ('291', 'B010105', 'B010000', 'ប្លែក​ភ្នែក​ទៀត​ហើយ​ជា​មួយ​នឹង​ម៉ូតូ​ដែល​អាច​ជិះ​លើ​ទឹក​បាន​​​', ' វា​មាន​កែប​កម្ពស់ ០,៩៩​ម៉ែត្រ​ពី​ផ្ទៃ​ដី​ និង​ពី​កង់​មុខ​ទៅ​កង់​ក្រោយ​មាន​ប្រវែង​ ១,៤៩​ម៉ែត្រ ព្រម​ទាំង​មាន​ទម្ងន់​ ៩៧,៥​គីឡូក្រាម...', 'http://news.sabay.com.kh/article/440617', 'http://cdn.sabay.com/cdn/news.sabay.com.kh/wp-content/uploads/2015/08/featured6-285x170.jpg?ebb82d', '3 August, 2015', '0', '0', '0', 't', '2015-08-03', 'f');
