INSERT INTO "tbnews" VALUES ('293', 'B010105', 'B010000', 'អនុវត្ត​​៣​វិធី​​ងាយ​ៗ ​ជួយ​​បង្កើន​​​ល្បឿន​​​កុំព្យូទ័រ​ ​(​វីដេអូ​)​', '​បញ្ហា​​កុំព្យូទ័រ​ដំណើរការ​យឺត​​​អាច​បណ្ដាល​មក​ពី​​ប្រិយមិត្ត​ពុំ​ដែល​បាន​​អនុវត្ត​មុខងារ​សំខាន់​បី​​ដូចជា​​សម្អាត​ File  រៀប​ចំ​ File ​​System ​ឲ្យ​បាន​ត្រឹម​ត្រូវ​ និង...', 'http://news.sabay.com.kh/article/440276', 'http://cdn.sabay.com/cdn/news.sabay.com.kh/wp-content/uploads/2015/08/speed-up-pc-285x170.jpg?ebb82d', '3 August, 2015', '21', '1', '0', 't', '2015-08-03', 'f');
INSERT INTO "tbnews" VALUES ('294', 'B010105', 'B010000', 'Huawei ​បាន​វ៉ា​ Microsoft ​ ខណៈ​តាម​ពីក្រោយ​ Apple និង Samsung', '​ក្រុមហ៊ុន​ផលិត​ទូរសព្ទ​ Smartphone  ​ដ៏​ធំ​របស់​ចិន​​ Huawei​ ​បាន​ក្លាយ​ជា​​ក្រុមហ៊ុន​ផលិត​ Smartphone ដ៏​ធំ​បំផុត​លំដាប់​ទី​បី​ក្នុង​ពិភព​លោក...', 'http://news.sabay.com.kh/article/440188', 'http://cdn.sabay.com/cdn/news.sabay.com.kh/wp-content/uploads/2015/08/Untitled-15-285x170.jpg?ebb82d', '4 August, 2015', '1', '0', '0', 'f', '2015-08-03', 'f');
INSERT INTO "tbnews" VALUES ('295', 'B010105', 'B010000', 'លេច​ចេញ​ច្បាស់​ក្រឡែត​នូវ​​រូប​រាង​របស់ ​​Galaxy Note 5 និង​ S6 Edge Plus', 'ដូច​នេះ​មាន​តែ​ចាំ​តាម​ដាន​ទាំង​អស់​គ្នា​ពី​ការ​ប្រកាស​របស់​ Samsung ដែល​នឹង​ធ្វើ​ឡើង​ក្នុង​ពេល​ឆាប់ៗ​ខាង​មុខ​នេះ...', 'http://news.sabay.com.kh/article/440242', 'http://cdn.sabay.com/cdn/news.sabay.com.kh/wp-content/uploads/2015/08/samsung-galaxy-note-5-evleaks-285x170.jpg?ebb82d', '3 August, 2015', '3', '0', '2', 'f', '2015-08-03', 'f');
INSERT INTO "tbnews" VALUES ('296', 'B010105', 'B010000', 'រូប​រាង​ប្លែក​ៗ​របស់​ USB Flash​ សម័យ​បច្ចុប្បន្ន​​', 'ក្នុង​រយៈពេល​ប្រហែល​ជា​ប្រាំ​ឆ្នាំ​ចុង​ក្រោយ​នេះ​ គេ​សង្កេត​ឃើញ​ថា​ ក្រុមហ៊ុន​មួយ​ចំនួន​ក្នុង​ពិភពលោក​បាន​រចនា​ USB Flash​ ជា​រូបរាង​ផលិត​ផល​របស់​ក្រុមហ៊ុន​គេ​ ឬ​ជា​រូប​រាង​ផ្សេង​ៗ​ ដើម្បី​ចែក​ជូន​បុគ្គលិក ​ក៏​ដូច​ជា​អតិថិជន។ តាម​រយៈ​ការ​រចនា​នេះ​ USB Flash​ ខ្លះ​មើល​ទៅ​គួរ​ឲ្យ​អស់​សំណើច​តែ​ម្ដង។ តោះ! មើល​រូប​រាង​ USB Flash​ ខាង​ក្រោម​នេះ​មើល៍​ ប្លែក​ប៉ុណ្ណា៖ &nbsp; &nbsp; USB Flash​ មាន​រូប​រាង​ជា​គ្រាប់​កាំ​ភ្លើង​ &nbsp; USB Flash​ មាន​រូប​រាង​ដូច​​ជាដុំ​​ស្ករ​ &nbsp; USB Flash​ មាន​រូប​រាង​ជាសត្វ​មាន់​ &nbsp; USB Flash​ មាន​រូប​រាង​ជាដប​ស្រា​ &nbsp; &nbsp; USB Flash​ មាន​រូប​រាង​ជា​ថ្ម​ពិល​ &nbsp; USB Flash​ មាន​រូប​រាង​ជាខ្សៀ​ &nbsp; USB Flash​ មាន​រូប​រាង​ជា​កាមេរ៉ា​ &nbsp; &nbsp; USB Flash​ មាន​រូប​រាង​ជា​រូប​រាង​សត្វ​ឆ្កែ​ USB [&hellip;]', 'http://news.sabay.com.kh/article/440030', 'http://cdn.sabay.com/cdn/news.sabay.com.kh/wp-content/uploads/2015/08/il_fullxfull.545518361_se50-285x170.jpg?ebb82d', '2 August, 2015', '3', '0', '0', 'f', '2015-08-02', 'f');
INSERT INTO "tbnews" VALUES ('297', 'B010105', 'B010000', 'ស្ថាបនិក​ហ្វេសប៊ុក​ Mark Zuckerberg​ ក្លាយ​ជា​ឪពុក​គេ​ហើយ!', 'ប្រធាន​ក្រុមហ៊ុន​ហ្វេសប៊ុក​ Mark Zuckerberg​ និង​ភរិយា​ Priscilla Chan​ បាន​ក្លាយ​ជា​ឪពុក​ម្ដាយ​របស់​គេ​ហើយ​ ហើយ​ពូក​គេ​រំពឹង​ថា ​កូន​នោះ​ជា​កូន​ស្រី​​ទៀត​ផង។ &nbsp; &nbsp; គូ​ស្វាមីភរិយា​ទាំង​ពីរ​បាន​បង្ហោះ​សារ​នេះ​នៅ​លើ​ទំព័រ​ហ្វេសប៊ុក​របស់​ Mark Zuckerberg​ ដែល​មាន​អ្នក​តាម​ដាន​៣៣​លាន​នាក់​ថា៖ &#8221; Priscilla Chan​ និង​ខ្ញុំ​មាន​ដំណឹង​ដ៏​រំភើប​មួយ​ យើងនឹង​​បាន​កូន​ស្រី​មួយ​រូប&#8221;។ &nbsp; Mark Zuckerberg​ ថា​ ការ​បង្ហោះ​ព័ត៌មាន​អំពី​ភរិយា​មាន​ផ្ទៃ​ពោះ​បែប​នេះ​ អាច​នឹង​ធ្វើ​ឲ្យ​អ្នក​លេង​ហ្វេសប៊ុក​ផ្សេង​ទៀត​មាន​អារម្មណ៍​ថា​ចង់​ចែក​រំលែក​ព័ត៌មានបែប​ហ្នឹង​​ដែរ​ ពេល​ភរិយា​របស់​​​គេ​មាន​ផ្ទៃពោះ។ &nbsp; ការ​ប្រកាស​ដំណឹង​ប្រពន្ធ​មានផ្ទៃ​ពោះ​​លើ​បណ្ដាញ​សង្គម​របស់​ Mark Zuckerberg​​ មិន​បាន​បញ្ជាក់​ថា​ កូន​របស់​ពួក​គេ​នឹង​កើត​នៅ​ពេល​ណា​នោះ​ទេ៕ &nbsp; &nbsp; -ចុច​អាន៖ ឧបករណ៍​៧​យ៉ាង ​អាច​ជួយ​​ដក​ស៊ីម​ពី​ iPhone ​មិន​បាច់​ទៅ​​រក​​តូប​លក់​ទូរសព្ទ​ &nbsp; &nbsp; ប្រភព៖ BBC', 'http://news.sabay.com.kh/article/440018', 'http://cdn.sabay.com/cdn/news.sabay.com.kh/wp-content/uploads/2015/08/featured4-285x170.jpg?ebb82d', '2 August, 2015', '0', '0', '0', 'f', '2015-08-02', 'f');
