INSERT INTO "tbnews" VALUES ('313', 'B010505', 'B010000', 'និស្សិត​សិក្សា​នៅ​រុស្ស៊ី​ ម៉ុង​ គីមស្រុង​​​​ពោរពេញ​ដោយ​ការ​តស៊ូ ​​ពេល​នេះ​​​យ៉ាង​ណា​​ហើយ?', 'គីមស្រុង​និយាយ​ថា «មួយ​ថ្ងៃ​ខ្ញុំ​ត្រូវ​ចំណាយ​ប្រហែល​ ៤ ​ទៅ​ ៥​ ដុល្លារ។ ​​ខ្ញុំ​ទិញ​ម្ហូប​យក​មក​ធ្វើ​ខ្លួន​ឯង ​ធ្វើ​ម្ដង​ញ៉ាំ​​បី​ដង​អីចឹង​ទៅ...', 'http://news.sabay.com.kh/article/439266', 'http://cdn.sabay.com/cdn/news.sabay.com.kh/wp-content/uploads/2015/07/Feature-Russia-scholarship-student-285x170.jpg?ebb82d', '3 August, 2015', '3', '21', '23', 'f', '2015-08-03', 'f');
