INSERT INTO "tbnews" VALUES ('324', 'B020202', 'B020000', 'ការ​វិភាគ​បាល់ទាត់​ចម្រុះ​នៅ​តំបន់​អឺរ៉ុប​ដើម​ស​ប្តា​ហ៍​មាន​15​ប្រកួត', 'ការ​ប្រកួត​បាល់ទាត់​ចម្រុះ​តំបន់​អឺ​រ៉ុ​ប​សម្រាប់​រាត្រី​ថ្ងៃ​ចន្ទ​នេះ​មាន 15 ប្រកួត ។ គូ​ល្អ Leipzig ប៉ះ Greuther និង Nancy ប៉ះ Tours ។  ដា​ណឺ​ម៉ាក​ស៊ុប​ពើ​រលីក ជុំ​ទី 4', 'http://kohsantepheapdaily.com.kh/article/125775.html', 'http://i.kspg.co/2015/8/8b9b9121-c6f2-4cd4-827a-22b9563e5610.jpg?w=285&h=170&mode=carve', 'ថ្ងៃចន្ទ ទី៣ ខែសីហា ឆ្នាំ២០១៥ ម៉ោង ០៩:២៣', '2', '0', '0', 't', '2015-08-03', 'f');
