INSERT INTO "tbnews" VALUES ('366', 'B020101', 'B020000', 'នគ​បាល​​ប៉ុ​ស្តិ៍​ច្រក​របៀង​វរ​​ការពារ​ព្រំដែន​លេខ​៦១១​ ផ្គើន​នឹងប្រសាសន៍​សម្តេច​ក្រឡាហោម​ ស ​ខេ​ង', 'ខេត្តស្វាយរៀង ៖ មន្ត្រី​នគរបាល​តាម​ប៉ុស្តិ៍​ច្រករបៀង​ព្រំដែន​នៃ​វរ​ការពារ​ព្រំដែន​លេខ​៦១១ នៃ​ស្នងការ​នគរបាល​ខេត្តស្វាយរៀង​កំពុង​ផ្គើន​នឹង​ប្រសាសន៍​ដ៏​ខ្ពង់ខ្ពស់​របស់​សម្តេច​ក្រឡាហោម ស ខេ​ង ឧបនាយករដ្ឋមន្ត្រី រដ្ឋមន្ត្រី​ក្រសួងមហាផ្ទៃ ស្តី​ពី​ការ​លុប​បំបាត់​​ច្រករបៀង ដែល​កំពុង​កើត​មាន​ឡើង​នៅ​តាម​បណ្តោយ​ព្រំដែន ក្នុង​ខេត្តស្វាយរៀង បែរជា​មន្ត្រី​នគ​បាល​ឈរជើង​នៅ​តាម​', 'http://kohsantepheapdaily.com.kh/article/125818.html', 'http://i.kspg.co/2015/8/2345ee50-fdd5-49a2-ab5f-e8a0b44121af.jpg?w=285&h=170&mode=carve', 'ថ្ងៃចន្ទ ទី៣ ខែសីហា ឆ្នាំ២០១៥ ម៉ោង ១៤:៣២', '0', '0', '1', 't', '2015-08-03', 'f');
INSERT INTO "tbnews" VALUES ('367', 'B020101', 'B020000', 'អភិបាល​ខណ្ឌមានជ័យ​ចុះ​ត្រួត​ពិ​និ​ត្រ​ស្ថានភាព​សាលា​បឋម​២​ក្រោយ​រង​ការ​ជន់​លិច​ដោយ​ជំនន់​ទឹកភ្លៀង', 'រាជធានី​ភ្នំពេញ ៖ នៅ​ព្រឹក​ថ្ងៃ​ទី​០៣ ខែសីហា ឆ្នាំ​២០១៥​នេះ លោក​ពេជ្រ កែវ​មុនី អភិបាល នៃ​គណៈ​អភិបាល​ខណ្ឌមានជ័យ អមដំណើរ​ដោយ​លោក ឌី រ័​ត្ន​ខេ​ម​រុណ អភិបាលរង​ខណ្ឌ និង​មន្ត្រី​មូលដ្ឋាន​បាន​អ​ញ្ចើ​ញ​ចុះ​ត្រួត​ពិ​និ​ត្រ​មើល​ទីតាំង​សាលា​បឋម​សិក្សា​២ ក្នុង​មូលដ្ឋាន គឺ​សាលា​បឋម​សិក្សា​សឡា និង​សាលា​បឋម​សិក្សា​ភូមិ​ឫស្សី', 'http://kohsantepheapdaily.com.kh/article/125811.html', 'http://i.kspg.co/2015/8/b148c4bc-9953-408a-9e3a-49836aa03bcc.jpg?w=285&h=170&mode=carve', 'ថ្ងៃចន្ទ ទី៣ ខែសីហា ឆ្នាំ២០១៥ ម៉ោង ១៤:១៥', '0', '0', '0', 't', '2015-08-03', 'f');
INSERT INTO "tbnews" VALUES ('368', 'B020101', 'B020000', 'សម្ពោធ​ដាក់ឱ្យប្រើប្រាស់​ថាមពល​ពន្លឺព្រះអាទិត្យ ​មាន​តម្លៃ​ជាង​​៥​ម៉ឺន​ដុល្លារ​​ក្នុង​​វត្ត​ព្រែក​​ប្រាំង', 'ខេត្តកណ្តាល ៖ ថាមពល​ដើរ​ដោយ​ពន្លឺព្រះអាទិត្យ ដែល​មាន​កម្លាំង​៦០​គីឡូវ៉ាត់ និង​កម្លាំង​ប្រើប្រាស់​ប្រចាំ​ថ្ងៃ​១១០​គីឡូវ៉ាត់ (​ពេល​ថ្ងៃ​ប្រើ​បាន​៥៥​គីឡូវ៉ាត់ និង​ពេល​យប់​ប្រើ​បាន​៥៥​គីឡូវ៉ាត់​) និង​មាន​ផ្ទាំង​SOLAR ចំនួន​៨០​បន្ទះ ព្រមទាំង​បាន​រៀបចំ​ប្រព័ន្ធ​ខ្សែ​បណ្តាញ ដែល​ធានា​សុវត្ថិភាព​ក្នុង​ទីធ្លា​វត្ត​ដោយ​ចំណាយ​ថវិកា​ចំនួន​៥​ម៉ឺន​២​ពាន់​ដុល្លារ​សហរដ្ឋអាមេរិក ត្រូវ​ប្រារព្ធ​ពិធី​វេរ​ឆ្លង និង', 'http://kohsantepheapdaily.com.kh/article/125808.html', 'http://i.kspg.co/2015/8/3c2264b2-549c-4e41-bf84-2e52f06dbec2.jpg?w=285&h=170&mode=carve', 'ថ្ងៃចន្ទ ទី៣ ខែសីហា ឆ្នាំ២០១៥ ម៉ោង ១៤:០៧', '0', '0', '6', 't', '2015-08-03', 'f');
INSERT INTO "tbnews" VALUES ('369', 'B020101', 'B020000', '​ឆក់​កាបូប និង​ទូរស័ព្ទ​ ក្នុង​ពេល​តែ​មួយ ​​នៅ​​ច្រមុះជ្រូក​ក្បាលថ្នល់', '​រាជ​ធានី​ភ្នំពេញ ៖ ​ស្ត្រី​ម្នាក់​​​ឈប់​ម៉ូតូ​និយាយ​ទូរស័ព្ទ​ ត្រូវ​​​ជនសង្ស័យ​ម្នាក់​ឆក់​យក​ទៅ​ហើយ​រត់គេច​ខ្លួន​បាត់​ ស្រាប់តែ​មាន​ជនសង្ស័យ​៣​នាក់​ទៀត​ឆក់​កាបូប​ស្ត្រី​ម្នាក់​ទៀត​ បង្ក​ឲ្យ​មានការ​ភ្ញាក់ផ្អើល​', 'http://kohsantepheapdaily.com.kh/article/125805.html', 'http://i.kspg.co/2015/8/1c4515ad-7bda-4ebb-9442-8eb8dd11f92b.jpg?w=285&h=170&mode=carve', 'ថ្ងៃចន្ទ ទី៣ ខែសីហា ឆ្នាំ២០១៥ ម៉ោង ១៣:៤៦', '21', '0', '0', 't', '2015-08-03', 'f');
INSERT INTO "tbnews" VALUES ('370', 'B020401', 'B020000', 'កសិករ​ស្រុក​ពេ​ជ្រា​ដា​រីករាយ​នឹង​ការ​ប្រមូល​ផលសណ្តែក ខណៈ​តម្លៃ​លើ​ទីផ្សារ​ថ្លៃ​ជាង​មុន', 'ខេត្តមណ្ឌលគិរី ៖ ប្រជា​កសិករ​នៅ​ឃុំ​ពូ​ជ្រៃ ស្រុក​ពេ​ជ្រា​ដា ដែល​ប្រកប​របរ​ដាំ​ដំណាំ​សណ្ដែកបាយ​កំពុង​សប្បាយ​រីករាយ​នឹង​ការ​ប្រមូល​ផល​សណ្ដែក​របស់​ខ្លួន ខណៈ​តម្លៃនៅលើទីផ្សារ​ឆ្នាំ​នេះ​ហាក់ដូចជា​បាន​ថ្លៃ​ជាង​ឆ្នាំ​មុន​។ ', 'http://kohsantepheapdaily.com.kh/article/124641.html', 'http://i.kspg.co/2015/7/536af65d-ffee-42d0-8c5c-3f77c8ff2a8f.jpg?w=285&h=170&mode=carve', 'ថ្ងៃអាទិត្យ ទី១៩ ខែកក្កដា ឆ្នាំ២០១៥ ម៉ោង ១៣:៤០', '0', '0', '0', 't', '2015-07-19', 'f');
INSERT INTO "tbnews" VALUES ('371', 'B020103', 'B020000', 'ខណៈនេះ​ លោក កឹម សុខា​ កំពុងថ្លែងក្នុង​វេទិកាតំណាងរាស្រ្ត​និងម្ចាស់ឆ្នោតនៅស្រុកថ្ពង', 'ខេត្តកំពង់ស្ពឺ ៖​ ប្រធានស្តីទីគណបក្សសង្គ្រោះជាតិ ​លោក កឹម សុខា​ ​បានចូលរួមក្នុងវេទិកា​តំណាងរាស្ត្រ និងម្ចាស់ឆ្នោត ​នៅក្នុងភូមិព្រៃមិច ​ឃុំវាលពន់ ស្រុកថ្ពង​ នៅព្រឹកថ្ងៃទី០២ ​សីហា​ ២០១៥ ។', 'http://kohsantepheapdaily.com.kh/article/125692.html', 'http://i.kspg.co/news_pic_kh.png?w=285&h=170&mode=carve', 'ថ្ងៃអាទិត្យ ទី២ ខែសីហា ឆ្នាំ២០១៥ ម៉ោង ១០:០២', '1', '0', '3', 't', '2015-08-01', 'f');
INSERT INTO "tbnews" VALUES ('372', 'B020103', 'B020000', 'សកម្មជន​បក្ស​សង្គ្រោះ​ជាតិ 4​នាក់​ចូល​មក​រួម​ជាមួយ បក្ស​ប្រជាជន​ខេត្តកំពត', 'ខេត្តកំពត ៖ នៅ​ស្នាក់​ការ​គណបក្ស​ប្រជាជន​កម្ពុជា​ខេត្ត កាលពី​វេលា​ម៉ោង​២​រសៀល ថ្ងៃ​ទី​៩ កក្កដា លោក ខូ​យ ឃុន​ហ៊ួ​រ អភិបាលខេត្ត និង​ជា​ប្រធាន​គណៈកម្មាធិការ​គណបក្ស​ប្រជាជន​កម្ពុជា​ខេត្ត បាន​ទទួលសមាជិក​គណបក្ស​សង្គ្រោះ​ជាតិ​ដែល​ចូល​មក', 'http://kohsantepheapdaily.com.kh/article/124034.html', 'http://i.kspg.co/2015/7/a3e1e397-3a74-4a46-81f7-8a105115c96d.jpg?w=285&h=170&mode=carve', 'ថ្ងៃសៅរ៏ ទី១១ ខែកក្កដា ឆ្នាំ២០១៥ ម៉ោង ១៣:៣៦', '0', '0', '3', 't', '2015-07-11', 'f');
INSERT INTO "tbnews" VALUES ('373', 'B020103', 'B020000', 'រឿងវិវាទព្រំដែន​ ៖ លោក សម រង្ស៊ី ​អំពាវនាវឲ្យខ្មែរ​រួបរួមគ្នាដាច់ខាត​', 'រាជធានីភ្នំពេញ ៖​ ប្រធានគណបក្សសង្គ្រោះជាតិ​ លោក សម រង្ស៊ី ​បានអំពាវនាវឲ្យខ្មែរ​ទំាងអស់រួបរួមគ្នា​គ្រប់និន្នាការនយោបាយ​ដើម្បី​ចាប់ដៃ​គ្នាការពារ​ទឹកដី​កុំឲ្យ​បាត់បង់ ។ ', 'http://kohsantepheapdaily.com.kh/article/123553.html', 'http://i.kspg.co/2015/7/1650c99f-32f3-4a51-bbf0-ed75bc17a338.jpg?w=285&h=170&mode=carve', 'ថ្ងៃអាទិត្យ ទី៥ ខែកក្កដា ឆ្នាំ២០១៥ ម៉ោង ១៤:១៥', '21', '0', '1', 't', '2015-07-05', 'f');
INSERT INTO "tbnews" VALUES ('374', 'B020103', 'B020000', 'CNRP​ នឹងផ្សព្វផ្សាយ​ពីការចាប់ផ្តើម​ទទួលវិភាគទាន​ដើម្បីទ្រទ្រង់​ទូរទស្សន៍ព្រះអាទិត្យ​នៅថ្ងៃ២៧ ​មិថុនា', 'រាជធានីភ្នំពេញ​ ៖ គណបក្សសង្គ្រោះជាតិ​ (CNRP) ​នឹងរៀបចំពិធី​ផ្សព្វផ្សាយពីការ​ចាប់ផ្តើមទទួលវិភាគទាន​ដើម្បីទ្រទ្រង់​ទូរទស្សន៍​​ព្រះអាទិត្យ ដែលជា​ទូរទស្សន៍​របស់​បក្សប្រឆាំងនេះ ។ ', 'http://kohsantepheapdaily.com.kh/article/122872.html', 'http://i.kspg.co/news_pic_kh.png?w=285&h=170&mode=carve', 'ថ្ងៃព្រហស្បត្ត៏ ទី២៥ ខែមិថុនា ឆ្នាំ២០១៥ ម៉ោង ១០:៣២', '1', '0', '0', 't', '2015-06-25', 'f');
INSERT INTO "tbnews" VALUES ('375', 'B020104', 'B020000', 'គ្រោះ​រាំង​ស្ងួត​រយៈពេល​២​ខែ ​ធ្វើ​ឲ្យ​ស្រែ​ស្រូវ​កសិករ​ នៅ​ខេត្តពោធិ៍សាត់​ក្រៀម​ស្វិត', 'ខេត្តពោធិ៍សាត់ ៖ សំណាប​ពង្រោះ សន្ទូង​របស់​ប្រជា​កសិករ ក្នុង​ក្រុង​ពោធិ៍សាត់ និង​ស្រុក​ចំនួន​៥​ទៀត ទូ​ទាំង​ខេត្តពោធិ៍សាត់ បាន​ក្រៀម​ស្វិត​ជា​បណ្ដើរ​ៗ​ហើយ ដោយសារ​គ្រោះ​រាំង​ស្ងួត​អូស​បន្លាយ​អស់​រយៈពេល​ប្រហែល​ពីរ​ខែ​កន្លង​មកនេះ', 'http://kohsantepheapdaily.com.kh/article/124832.html', 'http://i.kspg.co/2015/7/0006bd66-060f-4063-a41f-8e9a7bc207b8.jpg?w=285&h=170&mode=carve', 'ថ្ងៃអង្គារ ទី២១ ខែកក្កដា ឆ្នាំ២០១៥ ម៉ោង ១៤:៣១', '0', '0', '1', 't', '2015-07-21', 'f');
INSERT INTO "tbnews" VALUES ('376', 'B020104', 'B020000', 'ផ្លែ​ព្រីង​មាន​ហាង​ឆេង​នៅ​រដូវ​វស្សា​', 'រាជ​ធានី​ភ្នំពេញ ៖ នៅ​រដូវ​វស្សា​នេះ កសិករ​មក​ពី​ស្រុក​២ គីស្រុក​តានី​ទូក​មាស និង​ស្រុក​អង្គ​រ​ជ័យ​បាន​ដាំ​ដើម​ព្រីង​ ហើយ​ឱ្យ​តែ​ចូល​ដល់​ខែ​ឧសភានេះ​​ពួក​គាត់​នាំគ្នា​បេះយក​មក​លក់​នៅ​ផ្សារ​ដើម​គរ​ ', 'http://kohsantepheapdaily.com.kh/article/120090.html', 'http://i.kspg.co/2015/5/a4cadec2-a4b2-40d5-ba28-018fd6b85ca2.jpg?w=285&h=170&mode=carve', 'ថ្ងៃអាទិត្យ ទី១៧ ខែឧសភា ឆ្នាំ២០១៥ ម៉ោង ១៦:១៦', '1', '0', '1', 't', '2015-05-17', 'f');
INSERT INTO "tbnews" VALUES ('377', 'B020104', 'B020000', 'កែ​ច្នៃ​លាមក​ជ្រូក​បង្កើត​ជា​ហ្គា​ស​សម្រាប់​ផ្គត់ផ្គង់​ការ​ប្រើប្រាស់ និង​ត្រៀម​ចែកចាយ', 'ខេត្ត​ប៉ៃលិន ៖ កសិដ្ឋាន​ចិញ្ចឹម​ជ្រូក​ដ៏​ធំ​មួយ ដែល​សាងសង់​ស្រប​តាម​ស្តង់ដា​បច្ចេកទេស មិន​បង្ក​ក្លិនស្អុយ​ធ្វើ​ឱ្យ​ប៉ះពាល់​ដល់​បរិស្ថាន​រស់នៅ​របស់​ពលរដ្ឋ​នៅ​ជុំវិញ គឺ​ស្ថិត​នៅ​ជាប់​ផ្លូវជាតិ​លេខ​៥៧ ', 'http://kohsantepheapdaily.com.kh/article/115294.html', 'http://i.kspg.co/2015/2/60f30523-9bba-4ffc-a020-f492c2a85bf9.jpg?w=285&h=170&mode=carve', 'ថ្ងៃអាទិត្យ ទី២២ ខែកុម្ភៈ ឆ្នាំ២០១៥ ម៉ោង ១០:១៦', '0', '0', '31', 't', '2015-02-22', 'f');
INSERT INTO "tbnews" VALUES ('378', 'B020104', 'B020000', 'ប្រជាកសិករ​ កាក់កប​នឹងរបរ​ដាំដំណាំ​សាវម៉ាវ ​នៅក្នុង​ខេត្តប៉ៃលិន', 'ខេត្តប៉ៃលិន ៖ សាវ​​ម៉ាវជា​ឈ្មោះរុក្ខជា​​តិហូបផ្លែមួយប្រ​ភេទ ​ដែល​មាន​ស្លឹក​ស្រដៀ​ងទៅនឹ​ងបំពេញ​រាជ​ តែ​មាន​ស្លឹកតូច​ៗ ជាង​បំពេញ​រាជ ហើយ​ផ្លែ​វាមាន​លក្ខណៈ​ជាចង្កោម​ សំបក​ផ្លែមាន​ព្រុយ​ស្មាញ គេ​បរិភោគ​វាជា​ចំណី ដោយ​សាច់ខ្លះ​មាន រស​ជាតិជូរ​អែម ខ្លះទៀត​រស​ជាតិ​ផ្អែមឆ្ងាញ់, ​ជារុក្ខជាតិ​មានប្រយោ​ជន៍ គួរ​ដាំ​ឲ្យ​ក្រាស់​ក្រែល​ក្នុងស្រុក ​។ ជាក់​ស្តែង​នៅ​ក្នុង', 'http://kohsantepheapdaily.com.kh/article/98773.html', 'http://i.kspg.co/2014/6/75af73fb-c10e-49af-a2f9-7a9e28a16dcd.jpg?w=285&h=170&mode=carve', 'ថ្ងៃសៅរ៏ ទី២១ ខែមិថុនា ឆ្នាំ២០១៤ ម៉ោង ២៣:២២', '121', '0', '0', 't', '2014-06-21', 'f');
INSERT INTO "tbnews" VALUES ('379', 'B020104', 'B020000', 'ប្រជាកសិករ ​១គ្រួសារខិត​ខំប្រឹងប្រែង​​ដាំបន្លែបន្ទាប់​បន្សំបន្ថែម​ដើម្បីទ្រទ្រង់ជីវភាព​រស់នៅប្រចាំ​ថ្ងៃ', 'ខេត្ត​ប៉ៃលិន​ ៖ គេសង្កេត​ឃើញមាន​ប្រជាកសិករ​១គ្រួសារ​ រស់នៅ​ក្នុងភូមិ​ដីក្រហម​ ស​ងា្កត់អូរតា​វ៉ៅ ក្រុង​ប៉ៃលិន​ បើទោះ​បីជា ពួក​គេមាន​ភាពរវល់​ មមាញឹក​យ៉ាង​ណាក៏​ដោយ​នៅតែ​ខិតខំប្រឹង​ប្រែងដាំបន្លែ​បន្ទាប់បន្សំ​បន្ថែមទៀត​ជាច្រើន​ដើម្បីបាន​ផល យក​ទៅលក់បាន​ប្រាក់កម្រៃ​សម្រាប់​ទ្រទ្រង់ជីវភា​ពរស់នៅ​ប្រចាំថ្ងៃជា​ទីពេញចិត្ត​ដោយបន្ទាប់​ពីពួក​គេបាន​ដាំដំណាំ​ដំឡូង​មី ', 'http://kohsantepheapdaily.com.kh/article/96021.html', 'http://i.kspg.co/2014/5/0099c2c6-6cde-4402-a0be-7126dfb439bc.jpg?w=285&h=170&mode=carve', 'ថ្ងៃព្រហស្បត្ត៏ ទី១៥ ខែឧសភា ឆ្នាំ២០១៤ ម៉ោង ២៣:៥៧', '3233', '33230', '0', 't', '2014-05-15', 'f');
INSERT INTO "tbnews" VALUES ('380', 'B020104', 'B020000', 'មុខ​របរ​ចិញ្ចឹម​ចង្រិត​លក់​របស់​អ្នក​ខេត្ត​ព្រៃវែង​', 'ខេត្ត​ព្រៃវែង ៖ ​ចង្រិត​កំពុង​ត្រូវ​បាន​ប្រជា​ពល​រដ្ឋ​​នៅតាម​បណ្តា​ខេត្ត​មួយ​ចំនួន​ផ្អើល​នាំ​គ្នា​ចិញ្ចឹម​​ដោយ​សារ​តែ វា​ជា​មុខ​របរ​ដែល​ប្រើ​ប្រាស់​ដើម​ទុន​តិច ពេល​វេលា​ខ្លី តែ​ទទួល​បាន​ទិន្ន​ផល​ច្រើន។ ជាក់​ស្តែង​​នៅ​ក្នុង​តំបន់​អ្នក​លឿង ដែល​ស្ថិត​​ក្នុង​ស្រុក​ពាមរក៍ ​ខេត្តព្រៃវែង ​គ្រាន់តែ​រយៈ​ពេល​ជាង​១​​ខែ​ប៉ុណ្ណោះ​ ប្រជា​ពល​រដ្ឋ​ដែល​បាន​ចាប់​យក​មុខ​របរ​នេះ​បាន​កើន​ឡើង​ជាង​១០​គ្រួសារ​ទៅ​ហើយ ។ ', 'http://kohsantepheapdaily.com.kh/article/47493.html', 'http://i.kspg.co/2012/7/8e80c740-6a18-4507-9b98-9644638d3bb3.jpg?w=285&h=170&mode=carve', 'ថ្ងៃចន្ទ ទី៩ ខែកក្កដា ឆ្នាំ២០១២ ម៉ោង ១៥:០៦', '1', '34545', '0', 't', '2012-07-09', 'f');
INSERT INTO "tbnews" VALUES ('381', 'B020503', 'B020000', 'លោក​ជាវ តាយ ដឹក​នាំ​មន្ត្រី​រាជ​ការ​ជុំ​វិញ​ខេត្ត​ប្រារព្ធ​ទិវា​បរិស្ថាន​​នៅ​ស្រុក​លើក​ដែក​', 'ខេត្ត​កណ្តាល​ ៖ លោក​ជាវ តាយ អភិ​បាល​រង​ខេត្ត​កាល​ពី​ព្រឹក​ថ្ងៃ​ទី​៤ មិថុនា ២០១៤ បាន​ដឹក​នាំ​​មន្ត្រី​រាជ​ការ​ជុំ​វិញ​សាលា​ខេត្ត​ប្រារព្ធ​ទិវា​បរិស្ថាន​ពិភព​លោក​នៅ​សាលា​ស្រុក​លើក​ដែក​ ដោយនាំ​យក​​កូន​ឈើ​ប្រភេទ​ឈើ​ទាល​ និង​គគីចំនួន​១.៥០០​ដើម​ទៅ​ដាំ និង​ចែក​ជូន​ប្រជា​ពលរដ្ឋ​​។  ពិធី​នេះ​មានការ​ចូលរួម​ពី​​ប្រជា​ពលរដ្ឋ​ លោក​គ្រូ អ្នក​គ្រូ សិស្សា​នុសិស្ស​ និង​កង​កម្លាំង​ប្រដាប់​អាវុធ​​យ៉ាង​ច្រើន​កុះ​ករ​។', 'http://kohsantepheapdaily.com.kh/article/97462.html', 'http://i.kspg.co/2014/6/441dd1e1-f93d-4732-8937-414f8e0d058e.jpg?w=285&h=170&mode=carve', 'ថ្ងៃពុធ ទី៤ ខែមិថុនា ឆ្នាំ២០១៤ ម៉ោង ១៦:០០', '34', '3', '0', 't', '2014-06-04', 'f');
INSERT INTO "tbnews" VALUES ('382', 'B020503', 'B020000', 'អូរព្រំដែនជាប់​ច្រកទ្វារអន្តរជាតិប៉ោយប៉ែ​តកំពុងក្លាយជាកន្លែងចាក់​សំរាម', 'ខេត្ត​បន្ទាយ​មាន​ជ័យ ៖ ​អូរ​ព្រំដែន​ជាប់​និង​ខាង​ជើង​ច្រក​ទ្វារ​អន្តរ​ជាតិ​ប៉ោយ​ប៉ែត​ ដែល​ភ្ញៀវ​អន្តរ​ជាតិ​ឆ្លង​កាត់​ កំពុង​តែ​ក្លាយ​ទៅ​ជា​កន្លែង​ចាក់​សំរាម​ដែល​បង្ក​ឱ្យ​មាន​ការ​ប៉ះ​ពាល់​ ដល់​បរិស្ថាន​និង​ធ្វើ​ឱ្យ​បាត់​បង់​សោ​ភ័ណ​ភាព​ទី​ក្រុង ។  ក្រុង​ប៉ោយ​ប៉ែត​គឺ​ជា​ក្រុង​មួយ​​ដែល​មាន​ព្រំ​ដែន​​ជាប់​ទៅ​និង​ប្រ​ទេស​ថៃ​ ហើយ​ជា​ក្រុង​មួយ​ក្នុង​ចំណម​ស្រុក​ក្រុង​ទាំង​៩ ដែល​', 'http://kohsantepheapdaily.com.kh/article/97332.html', 'http://i.kspg.co/2014/6/bcf044a6-802e-461f-93ed-0875950221e4.jpg?w=285&h=170&mode=carve', 'ថ្ងៃចន្ទ ទី២ ខែមិថុនា ឆ្នាំ២០១៤ ម៉ោង ២៣:៥៧', '132', '76', '0', 't', '2014-06-01', 'f');
INSERT INTO "tbnews" VALUES ('383', 'B020503', 'B020000', 'ម៉ូតូ​​លក់​​នំប៉ាវ​​ជិះ​ប​ុក​ម៉ូតូ​គុប​ជ្រុង​ចំ​ពាក់​កណ្តាល​', 'រាជធានី​ភ្នំពេញ ៖ ម៉ូតូ​សង់​កូរ៉េ ពណ៌​ក្រហម ដឹក​នំ​ប៉ាវ​លក់​ជិះ​ទៅ​បុក​ម៉ូតូ​គុប​ជ្រុង​​៥០ ពណ៌ស្លែ ​ជិះគ្នា​​២​នាក់​ជា​ស្ត្រី បណ្តាល​ឱ្យ​ដួល​រង​របួស​ធ្ងន់​​២​នាក់ និងស្រាល​ម្នាក់ ។ គ្រោះថ្នាក់​នេះ​កើតឡើង​នៅចំណុច​ខាង​​ត្បូង​មន្ទីរពេទ្យ​មិត្តភាព​ខ្មែរ-សូវៀត ​តាម​ផ្លូវ​លេខ​២៧១ មុខ​ផ្ទះ​លេខ​៤០២ ​សង្កាត់​ទំនប់​ទឹក ខណ្ឌចំការ​មន ​កាលពីវេលា​ម៉ោង​១​និង​១០​នាទីរំលង​អធ្រាត្រ​ឈានចូល​ថ្ងៃទី​២ មករា ២០១៣។', 'http://kohsantepheapdaily.com.kh/article/60807.html', 'http://i.kspg.co/2013/1/4447719f-2e04-4fbc-9308-10fdeee3aec0.jpg?w=285&h=170&mode=carve', 'ថ្ងៃពុធ ទី២ ខែមករា ឆ្នាំ២០១៣ ម៉ោង ១៣:៤៧', '77', '212', '0', 't', '2013-01-01', 'f');
INSERT INTO "tbnews" VALUES ('384', 'B020503', 'B020000', 'ក្រុម​អាជីវករ​ផ្សារ​ស្វាយ​​ជ្រំ​​រអ៊ូរទាំ​ថា​​ អ្នក​​ម៉ៅ​ការ​​ផ្សារ​ទុក​សំរាម​​មិន​​​កើប​ចោល', 'ខេត្ត​ស្វាយ​រៀង​ ៖​​ ​ក្រុម​អាជីវ​ករ​ជា​ច្រើន​​នាក់​ដែល​លក់​​ដូរ​នៅ​ផ្សារ​ស្វាយ​ជ្រំ​​ ​​ស្រុក​ស្វាយ​ជ្រំ និយាយ​ថា ក្នុង​១​ថ្ងៃ​ៗ​ផ្សារ​នេះ​​មាន​សំរាម​​មិន​​ជា​ច្រើន​ប៉ុន្មាន​​​​ទេ ដោយ​សារ​តែ​​​អាជីវ​ករ​​​លក់​ដូរ​នៅ​ផ្សារ​នេះ​​មាន​ចំនួន​តិច​​ ហើយ​បើ​គេ​​កើប​​សំរាម​​ទាំង​នោះ​យក​ទៅ​ចោល​ជា​រៀង​រាល់​ថ្ងៃ ក៏​ប្រាកដ​​ជា​គ្មាន​បញ្ហា​​ចោទ​​​ដែរ ។ ប៉ុន្តែ​​ដោយ​​សារ​តែ​ក្រុម​​អនា​ម័យ​របស់​​​អ្នក​ម៉ៅ​ការ​​ផ្សារ​មិន​​ព្រម​កើប​រាល់​ថ្ងៃ គឺ​ទុក​​', 'http://kohsantepheapdaily.com.kh/article/51149.html', 'http://i.kspg.co/2012/8/8514252d-dc68-40ae-a238-712860bc2009.jpg?w=285&h=170&mode=carve', 'ថ្ងៃព្រហស្បត្ត៏ ទី២៣ ខែសីហា ឆ្នាំ២០១២ ម៉ោង ០៩:២៩', '234', '544', '0', 't', '2012-08-23', 'f');
INSERT INTO "tbnews" VALUES ('385', 'B020503', 'B020000', 'រោងចក្រ​​​ចំហុយ​​ថ្នាំ​ជក់​​​នៅ​តា​ខ្មៅ​​បង្កផល​ប៉ះ​ពាល់​​​ដល់​សុខភាព​​​ប្រជាពលរដ្ឋ​​', 'ខេត្តកណ្តាល៖ រោង​ចក្រ​​ចំហុយ​ថ្នាំ​ជក់​​ដែល​​មាន​ទីតាំង​​​​នៅភូមិ​​តា​ខ្មៅ សង្កាត់​តា​ខ្មៅ ​ក្រុង​តា​​ខ្មៅ បច្ចុប្បន្ន​​​​កំពុង​ដំណើរ​ការ​​​សង្វាក់​​ផលិត​កម្ម​​របស់​ខ្លួន​​យ៉ាង​សកម្ម តែ​បញ្ហា​​​​ដែលកំពុង​កើត​​ឡើង​នោះគឺ​​​​​ធ្វើ​ឱ្យ​​មាន​ផល​ប៉ះ​ពាល់​​យ៉ាង​ខ្លាំង​​​ដល់​ផ្លូវ​ដង្ហើម​​របស់​ប្រជា ពលរដ្ឋ​​​រស់នៅ​ក្បែរ​​​ខាង  នេះ​បើតាម​​​ការ​អះអាង​របស់​ប្រជាពល​រដ្ឋ​​​ ។', 'http://kohsantepheapdaily.com.kh/article/50554.html', 'http://i.kspg.co/2012/8/a000db15-8185-49e4-85f1-8622bda1472d.jpg?w=285&h=170&mode=carve', 'ថ្ងៃពុធ ទី១៥ ខែសីហា ឆ្នាំ២០១២ ម៉ោង ០៨:៥៤', '1', '323', '0', 't', '2012-08-15', 'f');
INSERT INTO "tbnews" VALUES ('386', 'B030101', 'B030000', 'កម្លាំងនគរបាល ក្រុងប៉ោយប៉ែត ចុះបង្ក្រាបទីតាំង ភ្នាល់បាល់ ជ្រកក្រោម យីហោ អ៊ីនធឺណែត ឃាត់ខ្លួនបុគ្គលិក ៣នាក់', '<strong>បន្ទាយមានជ័យៈ</strong> កម្លាំងនគរបាល ក្រុងប៉ោយប៉ែត បានបើកប្រតិបត្តិការ ចុះបង្ក្រាបទីតាំងភ្នាល់បាល់មួយកន្លែង ជ្រកក្រោម ហាងអ៊ីធឺណែត ដែលមានយីហោ 77 Net &nbsp;ស្ថិតនៅតាមបណ្តាលផ្លូវដើមពន្ទ្រា ភូមិក្បាលស្ពាន២ សង្កាត់ប៉ោយប៉ែត ក្រុងប៉ោយប៉ែត ខេត្តបន្ទាយមានជ័យ ដោយឃាត់ខ្លួនបុគ្គលិកចំនួន៣នាក់ ព្រមទាំងវត្ថុតាងជាច្រើនផ្សេងទៀត ។', 'http://www.dap-news.com/2011-06-14-02-39-55/96423-2015-08-03-17-06-03.html', 'http://image.dap-news.com:81/dap-news/dap-images/2015/August/id-067/CH-00009.jpg', 'Tuesday, 04 August 2015 00:04', '1', '32', '0', 't', '2015-08-04', 'f');
INSERT INTO "tbnews" VALUES ('387', 'B030101', 'B030000', 'ក្រុមឆបោក យកលុយតាម ទូរស័ព្ទដូរស្ទាយថ្មី ទំនើបជាមុន តែប៉ះចំអ្នកដឹង បោកអត់បាន', '<strong>បាត់ដំបង៖ </strong>កាលពីរសៀល ថ្ងៃទី០៣ ខែសីហា ឆ្នាំ២០១៥ មានក្រុមឆបោកយក លុយតាមទូរស័ព្ទ ដោយអះអាងថា អ្នកត្រូវគេបោកនោះ បានឈ្នះរង្វាន់ជាសាច់ប្រាក់ ពីក្រុមហ៊ុនស្មាត ដោយដូរស្ទាយថ្មីទំនើបជាមុន តែប៉ះចំអ្នកធ្លាប់ដឹង ជើងចាស់បោកអត់បាន ព្រមទាំងបានថតសម្លេងក្នុងការសន្ទនា ជាច្រើនលើកទៀតផង។', 'http://www.dap-news.com/2011-06-14-02-39-55/96421-2015-08-03-14-59-29.html', 'img/dapnews.png', 'Monday, 03 August 2015 21:49', '4545', '78', '24112', 't', '2015-08-03', 'f');
INSERT INTO "tbnews" VALUES ('388', 'B030101', 'B030000', 'ឃុំខ្លួនយុវជន៦នាក់ ពាក់ព័ន្ធករណី ចាប់រំលភសេពសន្ថវៈ បូកស្ត្រីម្នាក់ រួចសម្លាប់យក សពរុំក្នុងអង្រឹង ទៅបោះចោលនៅក្នុងដីឡូត៍មួយកន្លែង ក្បែរវត្តអង្គតាមិញ', '<strong>ភ្នំពេញ ៖</strong>&nbsp; ចៅក្រមស៊ើបសួរ នៃសាលាដំបូងរាជធានីភ្នំពេញ កាលពីថ្ងៃច័ន្ទ ទី ៣ ខែ សីហា ឆ្នាំ ២០១៥ បានចេញដីកាឃុំខ្លួន យុវជនជាប់ចោទជន ៦នាក់ ពីបទ « ឃាតកម្មដោយមានទារុណកម្ម អំពើឃោរឃៅ ឬអំពើរំលោភសេពសន្ថវៈ»&nbsp; តាម មាត្រា២០៥ នៃក្រមព្រហ្មទណ្ឌ ជាប់ពាក់ព័ន្ធនឹង ករណីចាប់រំលភសេពសន្ថវៈបូកស្ត្រីម្នាក់ បានសម្រេចម្នាក់ម្តងៗ រួចសម្លាប់នាងចោល យកសពរុំក្នុងអង្រឹង ទៅបោះចោលនៅក្នុងដីឡូត៍មួយកន្លែង ក្បែរវត្តអង្គតាមិញ ស្ថិតនៅក្នុង សង្កាត់កាកាប ខណ្ឌពោធិ៍សែនជ័យ កាលសប្តាហ៍មុន។', 'http://www.dap-news.com/2011-06-14-02-39-55/96420-2015-08-03-14-36-12.html', 'http://image.dap-news.com:81/dap-news/dap-images/2015/August/id-067/CH-00002.jpg', 'Monday, 03 August 2015 21:31', '4', '56', '0', 't', '2015-08-03', 'f');
INSERT INTO "tbnews" VALUES ('389', 'B030101', 'B030000', 'ទេសរដ្ឋមន្រ្តី យឹម ណុលឡា ប្រាប់សមាជិកបក្ស ឲ្យសកម្មការងារ ដើម្បីនាំប្រយោជន៍ ជូនប្រជាពលរដ្ឋ', '<b>តាកែវ៖</b> ទេសរដ្ឋមន្រ្តី យឹម ណុលឡា អនុប្រធានក្រុមការងារ ថ្នាក់កណ្តាលចុះជួយខេត្តតាកែវ និងជាសហប្រធាន ក្រុមការងារថ្នាក់កណ្តាលចុះជួយស្រុកព្រៃកប្បាស នៃគណបក្សប្រជាជនកម្ពុជា បានដាក់ចេញនូវផែនការការងារ ចំនួន ៤ចំណុច ឲ្យសមាជិក សមាជិការបស់ខ្លួន អនុវត្តឲ្យបានខ្ជាប់ខ្ជួន ដើម្បីនាំនូវប្រយោជន៍ជូនប្រជាពលរដ្ឋនៅតាមមូលដ្ឋាន ហើយក៏ជាការជួយនាំមកនូវប្រជាប្រិយភាពសម្រាប់បក្សប្រជាជនផងដែរ។', 'http://www.dap-news.com/2011-06-14-02-39-55/96419-2015-08-03-13-41-51.html', 'http://image.dap-news.com:81/dap-news/dap-images/2015/July/id-009/ST-301.jpg', 'Monday, 03 August 2015 20:41', '5', '545', '0', 't', '2015-08-03', 'f');
INSERT INTO "tbnews" VALUES ('390', 'B030101', 'B030000', 'លោក ឯម ច័ន្ទមករា ស្នើឲ្យភាគីពាក់ព័ន្ធ គ្រប់មជ្ឈដ្ឋាន យកចិត្តទុកដាក់ លើការងារពិការភាព', '<b>កំពត៖</b> លោក ឯម ច័ន្តមករា អគ្គលេខាធិការវ័យក្មេង នៃអគ្គលេខាធិការដ្ឋានក្រុមប្រឹក្សាសកម្មភាពជនពិការកម្ពុជា &nbsp;បានទទូចឲ្យ ភាគីពាក់ព័ន្ធនៅគ្រប់មជ្ឈដ្ឋានទាំងអស់ យកចិត្តទុកដាក់ទៅការងារពិការភាព ដើម្បីនាំមកនូវឧត្តម ប្រយោជន៍សម្រាប់ជនពិការនៅកម្ពុជា ដែលជាង ៣០ម៉ឺននាក់។', 'http://www.dap-news.com/2011-06-14-02-39-55/96417-2015-08-03-12-18-31.html', 'img/dapnews.png', 'Monday, 03 August 2015 19:17', '2', '32', '3', 't', '2015-08-03', 'f');
INSERT INTO "tbnews" VALUES ('391', 'B030102', 'B030000', 'ហេតុអ្វីចាំបាច់ ត្រូវគេងថ្ងៃ?', 'ហេតុអ្វីត្របកភ្នែកប្រែជាធ្ងន់ ក្រោយពេលបរិភោគអាហារ ថ្ងៃត្រង់រួច? តើអ្នកតែជំនះដោយ មិនព្រមធ្មេចមួយ ស្រឡេតតែមែនទេ?', 'http://www.dap-news.com/2011-06-14-02-40-26/96379-2015-08-03-04-38-22.html', 'http://api.theweek.com/sites/default/files/styles/tw_image_9_4/public/61202_article_full.jpg?itok=8DjXdAXD&resize=1260x560', 'Monday, 03 August 2015 11:35', '2', '0', '3', 't', '2015-08-03', 'f');
INSERT INTO "tbnews" VALUES ('392', 'B030102', 'B030000', 'ប្រធាន​គណបក្ស​ កាន់​អំណាច​កាណាដា នឹង​លើក​យក​បញ្ហា​សេដ្ឋកិច្ច​ មកធ្វើ​ការឃោសនា​ បោះឆ្នោត​អាណត្តិ​ថ្មី​', '<strong>អូតាវ៉ា៖</strong> យុទ្ធនាការឃោសនាបោះឆ្នោត នៅប្រទេសកាណាដា មានរយៈពេល ១១សប្តាហ៍ មុនពេលដែល ការបោះឆ្នោតសភាជាតិ លើកទី ៤២ បានមកដល់ គឺនៅថ្ងៃទី ១៩ ខែតុលា ខាងមុខ ហើយនាយករដ្ឋមន្រ្តីកាណាដា លោក Stephen Harper នឹងយកវិស័យសេដ្ឋកិច្ច ដើម្បីមកឃោសនា ខណៈដែលពេលនេះ ប្រទេស កំពុងជួបនឹងបញ្ហាសេដ្ឋកិច្ចស្រាប់ផង។', 'http://www.dap-news.com/2011-06-14-02-40-26/96350-2015-08-02-07-32-12.html', 'http://image.lookingtoday.com:81/lookingtoday/looking-images/news/2015/May/ID-099/v_0929.jpg', 'Sunday, 02 August 2015 14:28', '0', '0', '0', 't', '2015-08-02', 'f');
