INSERT INTO "tbnews" VALUES ('394', 'B010505', 'B010000', 'ស្លាកយីហោ​ល្បីៗ​អាមេរិក​​​​រំលេច​​ខ្លួន​​នៅ​ទឹកដី​រុស្ស៊ី​', 'ភាព​តានតឹង​រវាង​រុស្ស៊ី ​និង​អាមេរិក ​​យ​នាំ​ទៅ​ដល់​ការ​មន្ទិល ​សង្ស័យ​ពី​សម្ពាធ​បរិយាកាស​ពាណិជ្ជកម្ម​​ ​​និង​ ​ការ​វិនិយោគ​​រវាង​មហា​អំណាច​ទាំង​ទ្វេ...', 'http://news.sabay.com.kh/article/440128', 'http://cdn.sabay.com/cdn/news.sabay.com.kh/wp-content/uploads/2015/08/Feature-617x324-1-285x170.jpg?ebb82d', '4 August, 2015', '0', '0', '2', 't', '2015-08-04', 'f');
