INSERT INTO "tbnews" VALUES ('409', 'B020201', 'B020000', 'មាស សុខ​គ្រី ឱ្យ​អូត ភូ​ថង ឡើង​ធ្វើ​អាជ្ញាកណ្ដាល', 'រាជធានី​ភ្នំពេញ ៖ បន្ទាប់​ពី​បង្ហាញខ្លួន​ក្នុង​នាម​ជា​គណៈកម្មការ​ផ្ដល់​ពិន្ទុ​បាន​រយៈពេល​២​ស​ប្ដា​ហ៍​នៅ​ក្នុង​ព្រឹត្តិការណ៍​ប្រដាល់​របស់​ស្ថានីយទូរទស្សន៍​ប៉ុស្ដិ៍​លេខ​៥ លោក​មាស សុខ​គ្រី អ្នក​រៀបចំ​មន្ត្រី​សហព័ន្ធ​ប្រដាល់​នៅ​ក្នុង​ព្រឹត្តិការណ៍​នេះ', 'http://kohsantepheapdaily.com.kh/article/126004.html', 'http://i.kspg.co/2015/8/550994ec-b907-48d8-a224-37db8c65743f.jpg?w=285&h=170&mode=carve', 'ថ្ងៃពុធ ទី៥ ខែសីហា ឆ្នាំ២០១៥ ម៉ោង ១៤:៣៦', '2', '0', '0', 't', '2015-08-05', 'f');
INSERT INTO "tbnews" VALUES ('410', 'B020201', 'B020000', 'អេ ភូ​ថង ឡើង​កាលី​ប​ធ្វើ​ពិធីករ​ប្រដាល់​លើ​កញ្ចក់​ទូរទស្សន៍', 'រាជធានី​ភ្នំពេញ ៖ បើ​ទោះជា​លោក​អេ ភូ​ថង ត្រូវ​គេ​ដឹង​ជា​ទូទៅ​ថា មិនសូវ​និយាយ​ភាសា​ខ្មែរ​បាន​ច្បាស់​ព្រោះតែ​រស់នៅ​ខេត្តកោះកុង​តាំងពី​តូច និង​ប្រឡូក​ការងារ​នៅ​ជាយ​ដែន​ថៃ​ឡើង​ចេះ​ស្ទាត់​ភាសា​ថៃ​ជាង​ភាសា​កំណើត', 'http://kohsantepheapdaily.com.kh/article/125906.html', 'http://i.kspg.co/2015/8/6cff5c58-ec99-4e95-8ce5-681835248acf.jpg?w=285&h=170&mode=carve', 'ថ្ងៃអង្គារ ទី៤ ខែសីហា ឆ្នាំ២០១៥ ម៉ោង ១៤:៥២', '0', '0', '0', 't', '2015-08-04', 'f');
INSERT INTO "tbnews" VALUES ('411', 'B020302', 'B020000', 'ខាត់ សុ​ឃី​ម ប្រកាស​លក់​ផ្ទះ​បើក​សំអាងការ​តម្លៃ​80​ម៉ឺន $​សង្ស័យ​រកស៊ី​មិន​ជោគជ័យ', 'រាជធានី​ភ្នំពេញ ៖ អ្នក​នាង​ខាត់ សុ​ឃី​ម បាន​ប្រកាស​លក់​ផ្ទះ​បើក​សំអាងការ​របស់​នាង  ទំហំ​៤​គុណ​នឹង​១៣​ម៉ែត្រ​ចំនួន​២​ល្វែង នៅ​ផ្សារ​ដេប៉ូ រាជធានី​ភ្នំពេញ ក្នុង​តម្លៃ​៨០​ម៉ឺន​ដុល្លារ​អា​មេ​រិ​ក ។', 'http://kohsantepheapdaily.com.kh/article/125964.html', 'http://i.kspg.co/2015/8/90e10707-836e-419f-9ab2-1af16f270b81.jpg?w=285&h=170&mode=carve', 'ថ្ងៃពុធ ទី៥ ខែសីហា ឆ្នាំ២០១៥ ម៉ោង ១០:០០', '32', '0', '0', 't', '2015-08-05', 'f');
INSERT INTO "tbnews" VALUES ('412', 'B020302', 'B020000', 'តារា​សម្តែង​ហេង ស្រី​ពេជ្រ​ ជួប​ទុក្ខធំ ​ព្រោះ​ឪពុក​មាន​គ្រោះថ្នាក់​ចរាចរណ៍', 'រាជ​ធានី​ភ្នំពេញ​ ៖ តារា​សម្តែង​សាច់​ស​ដែល​លេចធ្លោ​ក្នុង​តួអង្គ​បែប​បងស្រី​ និង​ជា​តួ​ជំទាវ​កញ្ញា​ហេង ស្រី​ពេជ្រ​ កំពុង​តែ​មាន​អារម្មណ៍​លំបាក​ក្នុង​ចិត្តជា​ខ្លាំង​នៅ​ពេល​ដែល​នាង​ស្រាប់តែ​ភ្លាម​ៗ​ឪពុក​ម្តាយបង្កើត', 'http://kohsantepheapdaily.com.kh/article/124840.html', 'http://i.kspg.co/2015/7/c21e957e-0c4a-48d8-860e-c1fe4bd5c4ab.jpg?w=285&h=170&mode=carve', 'ថ្ងៃអង្គារ ទី២១ ខែកក្កដា ឆ្នាំ២០១៥ ម៉ោង ១៥:០២', '2', '0', '0', 't', '2015-07-21', 'f');
INSERT INTO "tbnews" VALUES ('413', 'B020302', 'B020000', 'ទុក​បេះដូង​ក្រៅ​រង្វង់​ស្នេហា ទឹម រ​ដ្ឋា ស្រវា​ការងារ​សិល្បៈ​និង​ជំនួញ', 'រាជធានី​ភ្នំពេញ ៖ តារា​សម្តែង និង​បង្ហាញ​ម៉ូ​ដ​រាង​ខ្ពស់​ស្តើង កញ្ញា​ទឹម រ​ដ្ឋា មួយ​រយៈ​ចុង​ក្រោយ​នេះ​អាជីព​សិល្បៈ​កាន់តែ​ប្រសើរ​ឡើង​បន្ទាប់​ពី​បាន​បញ្ចប់​ចំណងស្នេហា​ជាមួយ​បុរស​បា​៉​គី​ស្ថាន ។ ទន្ទឹម​អាជីព​សិល្បៈ​រីក​ចម្រើន ទឹម រ​ដ្ឋា', 'http://kohsantepheapdaily.com.kh/article/124693.html', 'http://i.kspg.co/2015/7/c30156f2-05b3-455a-9ea6-d15c10c4ae50.jpg?w=285&h=170&mode=carve', 'ថ្ងៃចន្ទ ទី២០ ខែកក្កដា ឆ្នាំ២០១៥ ម៉ោង ០៩:៤២', '0', '0', '0', 't', '2015-07-20', 'f');
INSERT INTO "tbnews" VALUES ('414', 'B020107', 'B020000', 'ល្បែង​កីឡា​ប្រជាប្រិយ​ខ្មែរ «​ប្រណាំង​ដំរី​»', 'យោង​តាម​សៀវភៅ «​ប្រពៃណី និង​ទំនៀមទម្លាប់​ខ្មែរ​» របស់​ព្រឹទ្ធាចារ្យ​មៀ​ច ប៉ុណ្ណ អធិប្បាយ​ថា ល្បែង​«​ប្រណាំង​ដំរី​»​មាន​គេ​លេង​នៅ​តំបន់​ដែល​សម្បូរ​ដំរី ពិសេស​តំបន់​ជាប់​នឹង​ព្រៃធំ និង​ភ្នំ​ច្រើន ក្នុង​នោះ​យើង​អាច​', 'http://kohsantepheapdaily.com.kh/article/125968.html', 'http://i.kspg.co/2015/8/38df2edd-4ff4-49a1-82af-922d792d44bf.jpg?w=285&h=170&mode=carve', 'ថ្ងៃពុធ ទី៥ ខែសីហា ឆ្នាំ២០១៥ ម៉ោង ១០:០៤', '2', '0', '0', 't', '2015-08-05', 'f');
