INSERT INTO "tbnews" VALUES ('417', 'B020301', 'B020000', 'ឡាវ​គ្រោង​អភិវឌ្ឍ «​ភ្នំ​ភូ​បៀ​» អតីត​សមរភូមិ​ក្តៅ​ជា​តំបន់​ទេសចរណ៍', 'សារព័ត៌មាន​បរទេស​កាលពី​ពេល ថ្មី​ៗ​កន្លង​មក​ផ្សាយ​ថា ប្រទេស​ឡាវ​បាន​សាងសង់​ផ្លូវ​ចូល​ទៅ​«​ភ្នំ​ភូ​បៀ​» និង​សង់​បន្ត​រហូត​កំពូលភ្នំ គឺជា​គម្រោងការ​អភិវឌ្ឍ​ភ្នំ​ខ្ពស់​បំផុត​នៅ​ក្នុង​ប្រទេស​នោះ​', 'http://kohsantepheapdaily.com.kh/article/121388.html', 'http://i.kspg.co/2015/6/6c36756f-76fb-466a-b2de-b57a65d138ee.jpg?w=285&h=170&mode=carve', 'ថ្ងៃសុក្រ ទី៥ ខែមិថុនា ឆ្នាំ២០១៥ ម៉ោង ១៥:១២', '123', '0', '0', 't', '2015-06-05', 'f');
INSERT INTO "tbnews" VALUES ('418', 'B020504', 'B020000', 'អ្នក​ស្រុក​ជាង​៣០០​នាក់​​នៅ​ឃុំ​បាធាយ តវ៉ា​ឱ្យ​បញ្ចុះ​​តម្លៃ​អគ្គិ​សនី​', 'ខេត្ត​កំពង់​ចាម ៖ ​ប្រជា​ពលរដ្ឋ​ប្រមាណ​ជាង​៣០០​នាក់ ​មកពី​ភូមិ​ស្វាយ​​ពក ​ភូមិ​ស្រះ​ព្រីង និង​ភូមិ​បាធាយ ឃុំ​បាធាយ បាន​​នាំ​គ្នា​តវ៉ា​សុំ​ឱ្យ​​បញ្ចុះ​តម្លៃ​​អគ្គិ​សនី​ពី​តម្លៃ​៣.១០០រៀល​ក្នុង​១​គីឡូ​​វ៉ាត់ ​មក​ត្រឹម​​១.៨០០​រៀល ។ ​ការ​ប្រមូល​ផ្តុំ​​​គ្នា​តវ៉ា​នេះ​ធ្វើ​ឡើង​នៅ​មុខ​សាលា​ស្រុក​បាធាយ កាលពី​វេលា​ម៉ោង​៩​និង​៣០​ព្រឹក​ថ្ងៃទី​១៨ មិថុនា ២០១២ ។', 'http://kohsantepheapdaily.com.kh/article/45582.html', 'http://i.kspg.co/news_pic_kh.png?w=285&h=170&mode=carve', 'ថ្ងៃចន្ទ ទី១៨ ខែមិថុនា ឆ្នាំ២០១២ ម៉ោង ១០:២០', '0', '0', '0', 't', '2012-06-18', 'f');
