INSERT INTO "tbnews" VALUES ('427', 'B020102', 'B020000', 'ព្រហ្មចារី​តម្លៃ​1000$ ម្តាយមីង​2​នាក់​ចាប់បង្ខំ​ក្មួយ​ស្រី​លក់​ព្រហ្មចារី​នៅ​សណ្ឋាគារ​ណៃ​អីុន', 'រាជធានី​ភ្នំពេញ ៖ ស្ត្រី​ជា​ម្តាយមីង​២​នាក់​និង​មេការ​សំណង់​ម្នាក់ ត្រូវ​បាន​នគរបាល​ការិយាល័យ​ប្រឆាំង​ការ​ជួញដូរ​មនុស្ស និង ការពារ​អនីតិជន​នៃ​ស្នងការដ្ឋាន​នគរបាល​រាជធានី​ឃាត់ខ្លួន​ជា​បន្តបន្ទាប់​កាលពី​រសៀល ថ្ងៃ​ទី​៣ និង​ព្រឹក​ថ្ងៃ​ទី​៤ សីហា ពី​បទ', 'http://kohsantepheapdaily.com.kh/article/125962.html', 'http://i.kspg.co/2015/8/d4ea647c-de39-4782-b70c-952301076fd5.jpg?w=285&h=170&mode=carve', 'ថ្ងៃពុធ ទី៥ ខែសីហា ឆ្នាំ២០១៥ ម៉ោង ០៩:៥២', '0', '0', '0', 't', '2015-08-05', 'f');
INSERT INTO "tbnews" VALUES ('428', 'B020102', 'B020000', 'កម្មករ​សំណង់​នៅ​ថៃ​ម្នាក់​ស្លាប់​ដោយសារ​ធ្លាក់​ដែក​ពី​ជាន់​ទី​៩​ចំ​ក្បាល​នៅ​ជាន់​ទី​៣', 'ខេត្តបន្ទាយមានជ័យ ៖ ពលករ​ខ្មែរ​ម្នាក់​បាន​ស្លាប់​នៅ​មន្ទីរពេទ្យ​បន្ទាប់​ពី​រង​របួសធ្ងន់​ដោយសារ​ការ​ធ្លាក់​ដែក​ពី​ជាន់​ទី​៩​ចំ​ក្បាល​នៅ​ជាន់​ទី​៣ ខណៈ​ជន​រង​គ្រោះ​កំពុងធ្វើការ​ជា​កម្មករ​សំណង់​នៅ​ក្រុង​បាងកក ។', 'http://kohsantepheapdaily.com.kh/article/125949.html', 'http://i.kspg.co/2015/8/e427a0a9-f58f-4739-96ba-4548132abe05.jpg?w=285&h=170&mode=carve', 'ថ្ងៃអង្គារ ទី៤ ខែសីហា ឆ្នាំ២០១៥ ម៉ោង ២១:៤០', '32', '0', '0', 't', '2015-08-04', 'f');
INSERT INTO "tbnews" VALUES ('429', 'B020102', 'B020000', 'ម្ចាស់​រថយន្ត​ចាប់ខ្លួន​ជនសង្ស័យ​លួច​កាច់​កញ្ចក់​រថយន្ត​ម្នាក់​ប្រគល់​ឲ្យ​សមត្ថកិច្ច', 'រាជធានី​ភ្នំពេញ ៖ ជនសង្ស័យ​ម្នាក់​បាន​ធ្វើ​សកម្មភាព​លួច​កាច់​កញ្ចក់​រថយន្ត​ម៉ាក​ហា​យ​លែ​ន​ឌ័​រ ដែល​ម្ចាស់​បាន​ចត​នៅ​ខាង​មុខ​ផ្ទះ​បាន​សម្រេច តែ​ត្រូវ​បាន​ម្ចាស់​រថយន្ត​ឃើញ​ទាន់ ចាប់​ឃាត់ខ្លួន​ប្រគល់​ឲ្យ​សមត្ថកិច្ច​នគរបាល​ប៉ុស្តិ៍​បឹង​កេងកង​៣ ។', 'http://kohsantepheapdaily.com.kh/article/125945.html', 'http://i.kspg.co/2015/8/de12fa41-af5f-4f56-973d-d31e3f637a42.jpg?w=285&h=170&mode=carve', 'ថ្ងៃអង្គារ ទី៤ ខែសីហា ឆ្នាំ២០១៥ ម៉ោង ២០:១១', '23', '0', '0', 't', '2015-08-04', 'f');
INSERT INTO "tbnews" VALUES ('430', 'B020102', 'B020000', 'អាវុធហត្ថ​ខេត្ត​បញ្ជូន​ជនសង្ស័យ​ជួញដូរ​គ្រឿងញៀន​វ័យក្មេង​ម្នាក់ និង​វត្ថុ​តាង​ទៅ​សាលាដំបូង​ខេត្តបន្ទាយ​មាន​ជ័យ', 'ខេត្តបន្ទាយមានជ័យ ៖ ជនសង្ស័យ​ជួញដូរ​សារធាតុ​ញៀន​ខុសច្បាប់​វ័យក្មេង​ម្នាក់​ត្រូវ​បាន​កម្លាំង​កងរាជអាវុធហត្ថ​ខេត្ត​បង្ហាញ​មុខជា​សាធារណៈ និង​រៀបចំ​សំណុំរឿង​បញ្ជូន​ទៅ​សាល​ដំបូង​ខេត្តបន្ទាយមានជ័យ ដើម្បី​ចាត់ការ​តាម​និ​តិ​វិធី​ច្បាប់​នៅ​ថ្ងៃ​ទី​០៤ ខែសីហា ឆ្នាំ​២០១៥ នេះ ។', 'http://kohsantepheapdaily.com.kh/article/125944.html', 'http://i.kspg.co/2015/8/7d7048bb-5af7-4afc-baf5-57807acd0c52.jpg?w=285&h=170&mode=carve', 'ថ្ងៃអង្គារ ទី៤ ខែសីហា ឆ្នាំ២០១៥ ម៉ោង ១៩:៤៥', '2', '0', '0', 't', '2015-08-04', 'f');
INSERT INTO "tbnews" VALUES ('431', 'B020102', 'B020000', 'លោក កែវ មុនី ចេញ​ដីកាចាប់ខ្លួន​សកម្មជន​គណបក្ស​សង្គ្រោះ​៥​នាក់​បន្ថែម​ទៀត', 'រាជធានី​ភ្នំពេញ ៖ កាលពី​ល្ងាច​ថ្ងៃ​ទី​០៤ សីហា ២០១៥ លោក កែវ មុនី ចៅក្រម​ស៊ើបសួរ​សាលាដំបូង​រាជធានី​ភ្នំពេញ​បាន​ចេញ​ដីកា​បង្គាប់​ឱ្យ​ចាប់ខ្លួន​សកម្មជន​គណបក្ស​សង្គ្រោះ​ជាតិ​៥​នាក់​បន្ថែម​ទៀត​ដែល​បាន​ចូលរួម​ក្នុង​សកម្មភាព​បង្ក​ហិង្សា​នៅ​ទីលាន​ប្រជាធិបតេយ្យ​កាលពី​ថ្ងៃ​ទី​១៥ កក្កដា ២០១៤ ក​ន្ល​ទៅ​នោះ ។', 'http://kohsantepheapdaily.com.kh/article/125942.html', 'http://i.kspg.co/2015/8/5853381f-0494-43ff-a60d-33ec15b0511a.jpg?w=285&h=170&mode=carve', 'ថ្ងៃអង្គារ ទី៤ ខែសីហា ឆ្នាំ២០១៥ ម៉ោង ១៩:០១', '2', '0', '0', 't', '2015-08-04', 'f');
INSERT INTO "tbnews" VALUES ('432', 'B020102', 'B020000', '​​ប៉ូលិស​ខណ្ឌដង្កោ​ចាប់ខ្លួន​មុខសញ្ញា​ចោរឆក់​កាបូប​ជនបរទេស​បាន​ម្នាក់', 'រាជ​ធានី​ភ្នំពេញ ​៖​ កម្លាំង​ប៉ូលិសនៃ​អធិការដ្ឋាន​ខណ្ឌដង្កោបាន​​ចាប់ខ្លួន​ជនសង្ស័យចោរឆក់​កាបូបបាន​ម្នាក់ បន្ទាប់​ពី​​​ជនសង្ស័យ​ធ្វើ​សកម្មភាព​ឆក់​កាបូប​ជនបរទេសបាន​សម្រេច​ហើយ​ព្យាយាម​​​រត់គេច​ខ្លួន', 'http://kohsantepheapdaily.com.kh/article/125926.html', 'http://i.kspg.co/2015/8/3a4da796-a8f6-4cd9-912d-9b657e9b690d.jpg?w=285&h=170&mode=carve', 'ថ្ងៃអង្គារ ទី៤ ខែសីហា ឆ្នាំ២០១៥ ម៉ោង ១៥:៤៧', '0', '0', '0', 't', '2015-08-04', 'f');
INSERT INTO "tbnews" VALUES ('433', 'B020102', 'B020000', '​ឃាត់ខ្លួន​ជនសង្ស័យ​ម្នាក់​ពី​បទ​​ប៉ុន​ប៉ង​​អំពើឃាតកម្ម​ និង​​ប្រើ​​ប្រាស់​​អាវុធ​ខុសច្បាប់', 'ខេត្តបន្ទាយមានជ័យ​ ៖ កម្លាំង​កង​រាជ​អាវុធ​ហត្ថ​​​​ ឃាត់ខ្លួន​ជនសង្ស័យ​ម្នាក់​ពី​បទ​ប៉ុនប៉ង​អំពើ​​ឃាតកម្ម​ និង​រក្សា​ទុក​​អាវុធ​ខុសច្បាប់ ​បញ្ជូន​ទៅ​សាលាដំបូង​ខេត្ត​ ចាត់ការ​តាម​ច្បាប់​នៅ​ថ្ងៃ​ទី​៤ ​សីហា​ ២០១៥​ ។', 'http://kohsantepheapdaily.com.kh/article/125905.html', 'http://i.kspg.co/2015/8/bec08613-4643-4640-a888-d1d513b9862e.jpg?w=285&h=170&mode=carve', 'ថ្ងៃអង្គារ ទី៤ ខែសីហា ឆ្នាំ២០១៥ ម៉ោង ១៤:៤៧', '21', '0', '0', 't', '2015-08-04', 'f');
INSERT INTO "tbnews" VALUES ('434', 'B020102', 'B020000', 'បុរស​វ័យ​ចំណាស់​​ម្នាក់​ត្រូវ​ចោរ​បាញ់​មួយ​គ្រាប់ចំ​ក្បាល​ប្លន់​យក​ម៉ូតូ​ទៅ​បាត់​', 'ខេត្ត​តាកែវ​ ៖ បុរស​វ័យ​ចំណាស់​ជា​អ្នក​រត់​ម៉ូតូ​ឌុប​ម្នាក់​ត្រូវ​បាន​ជន​ដៃ​ដល់​បាញ់​​​១​គ្រាប់ចំ​ក្បាល​ស្លាប់​ភ្លាមៗ​នៅ​នឹង​កន្លែង​ រួច​ប្លន់​យក​ម៉ូតូ​មួយ​គ្រឿង​ជិះ​គេច​ខ្លួន​បាត់​។', 'http://kohsantepheapdaily.com.kh/article/125904.html', 'http://i.kspg.co/2015/8/9a56c696-b0d3-4827-9fe0-c18be46fa6e3.jpg?w=285&h=170&mode=carve', 'ថ្ងៃអង្គារ ទី៤ ខែសីហា ឆ្នាំ២០១៥ ម៉ោង ១៤:៤០', '0', '0', '0', 't', '2015-08-04', 'f');
INSERT INTO "tbnews" VALUES ('435', 'B020102', 'B020000', '​ខណៈនេះ​​មាន​បុរស​សង្ស័យ​បែក​ថ្នាំម្នាក់​​ប្រុង​លោត​​ពី​លើ​អគារ​ភោជនីយ​ដ្ឋាន​​ថ្ម​ដា ​​ជាន់​ទី៣ ​', '​រាជ​ធានី​ភ្នំពេញ ៖ តាមសេចក្តី​រាយការណ៍​ពី​អ្នក​ឆ្លើយ​ឆ្លង​ព័ត៌មាន​ ​បាន​ឱ្យ​ដឹង​ថា ​ខណៈ​ពេល​នេះ មាន​បុរស​សង្ស័យ​បែក​ថ្នាំ​ម្នាក់​បម្រុង​លោត​​ពី​អគារ​ភោជនីយ​ដ្ឋាន​​ថ្ម​ដា​ជាន់​ទី៣ ​​​តាម​​បណ្តោយ​មហា​វិថី​កម្ពុជា​ក្រោម', 'http://kohsantepheapdaily.com.kh/article/125895.html', 'http://i.kspg.co/2015/8/63d6d5bb-dc62-4e00-89cb-deb592706090.jpg?w=285&h=170&mode=carve', 'ថ្ងៃអង្គារ ទី៤ ខែសីហា ឆ្នាំ២០១៥ ម៉ោង ១២:៤៤', '1', '0', '0', 't', '2015-08-04', 'f');
INSERT INTO "tbnews" VALUES ('436', 'B020102', 'B020000', 'មនុស្ស​7​នាក់​ដែល​ឃាត់ខ្លួន​រឿង​ដឹក​សារធាតុ​សង្ស័យ បាន​នៅ​ក្រៅ​ឃុំ', 'ខេត្តមណ្ឌលគិរី ៖ មនុស្ស​ចំនួន​៧​នាក់​ដែល​ជាប់​ពាក់ព័ន្ធ​នឹង​ករណី​ដឹក​ជញ្ជូន​សារធាតុ​ប​ស្សុទ្ធ​រ៉ែ​មាស​គ្មាន​ច្បាប់​ជិត​៧​តោន ដែល​ត្រូវ​បាន​កម្លាំង​នគរបាល​ស្រុក​កោះ​ញែក​ឃាត់​ទុក រួម​ជាមួយ​រថយន្ត​សាំ​យ៉ុ​ង​២​គ្រឿង បញ្ជូន​មក​ស្នងការដ្ឋាន​នគរបាល​ខេត្ត', 'http://kohsantepheapdaily.com.kh/article/125874.html', 'http://i.kspg.co/2015/8/40d5404f-5b5d-4176-aac2-d2e82436ab39.jpg?w=285&h=170&mode=carve', 'ថ្ងៃអង្គារ ទី៤ ខែសីហា ឆ្នាំ២០១៥ ម៉ោង ០៩:២៨', '0', '0', '0', 't', '2015-08-04', 'f');
INSERT INTO "tbnews" VALUES ('437', 'B020102', 'B020000', 'មក​ពី​ដើរ​លេង​នៅ​កោះ​ពេជ្រ​ចោរ​ភ្ជង់​ប្លន់​យក​ម៉ូតូ MSX ទៅបាត់', 'រាជ​ធានីភ្នំពេញ ៖ យុវ​ជន​ម្នាក់​ជិះ​ម៉ូតូ​មក​ពី​ដើរ​លេង​នៅ​កោះ​ពេជ្រ​ត្រូវ​ជនសង្ស័យ​ភ្ជង់​ប្លន់​យក​ម៉ូតូ MSX មួយ​គ្រឿង​​ទៅ​បាត់​​ កាលពី​វេលា​ម៉ោង​៣​ និង​៥០​នាទី​ទៀប​ភ្លឺ​ ថ្ងៃ​ទី​៤ ​សីហា តាម​ផ្លូវលេខ​១៦៣ ', 'http://kohsantepheapdaily.com.kh/article/125861.html', 'http://i.kspg.co/2015/8/8ceca903-0c60-43bf-be5e-e04abe4322fc.jpg?w=285&h=170&mode=carve', 'ថ្ងៃអង្គារ ទី៤ ខែសីហា ឆ្នាំ២០១៥ ម៉ោង ០៨:៤៩', '123', '0', '0', 't', '2015-08-04', 'f');
INSERT INTO "tbnews" VALUES ('438', 'B020101', 'B020000', '​ក្រុម​អាជីវ​ករ​​ក្រុង​ព្រះ​សីហនុ​ត្អូញត្អែរថា ​ការ​តាំង​ពិ​ព័រណ៍ពាណិជ្ជកម្ម​មិត្តភាព​កម្ពុជា-ថៃ ធ្វើ​ឱ្យ​ពួក​គាត់​​ដាច់​បាយ​', '​​ខេត្ត​​ព្រះ​សីហនុ ៖ ពលរដ្ឋ​ដែល​មាន​លំនៅ​​ឋាន​​​ នៅ​ចំណុច​អតីត​មហោស្រព​ចាស់ ​និង​​គ្លី​និ​ក​ឯកជន​​ចំនួន​២​កន្លែង​​ផង​ដែរ ​ស្ថិត​នៅ​ក្នុង​ភូមិ២ សង្កាត់២ ក្រុង​ព្រះ​សីហ​នុ ក៏​ដូច​ជា​មន្ត្រីរាជការ ​អាជីវករ​លក់ដូរ ​​បាន​ត្អូញត្អែរ', 'http://kohsantepheapdaily.com.kh/article/126029.html', 'http://i.kspg.co/2015/8/10813ce6-95aa-415a-943e-16b80f617f9c.jpg?w=285&h=170&mode=carve', 'ថ្ងៃពុធ ទី៥ ខែសីហា ឆ្នាំ២០១៥ ម៉ោង ១៦:០០', '0', '0', '0', 't', '2015-08-05', 'f');
INSERT INTO "tbnews" VALUES ('439', 'B020101', 'B020000', 'លោក​ហ៊ុន ម៉ា​ណែ​ត ​៖​ លើកទឹកចិត្ត​យុវជន​ចូលរួម​ជាមួយ​រាជរដ្ឋាភិបាល ​ដើម្បី​អភិវឌ្ឍ​ន៍​ប្រទេស', 'រាជធានី​ភ្នំពេញ ៖ ថ្លែង​ក្នុង​ពិធី​បំពាក់​អាវ​សកម្មជន​យុវជន​គណបក្ស​ប្រជាជន​កម្ពុជា សាខា​បក្ស​អគ្គនាយកដ្ឋាន​គយ និង​រដ្ឋាករ​កម្ពុជា កាលពី​ថ្ងៃ​ទី​៥ សីហា ឆ្នាំ​២០១៥ នេះ លោក​ហ៊ុន ម៉ា​ណែ​ត បាន​អំពាវនាវ និង​លើកទឹកចិត្ត​ឱ្យ​យុវជន​ចូលរួម​ក្នុង​ការ​អភិ វឌ្ឍន៍​ប្រទេស ជាមួយ​រាជរដ្ឋាភិបាល​កម្ពុជា ។', 'http://kohsantepheapdaily.com.kh/article/126022.html', 'http://i.kspg.co/2015/8/b20749bf-fcfc-4630-aed8-7a06d7ed1bfa.jpg?w=285&h=170&mode=carve', 'ថ្ងៃពុធ ទី៥ ខែសីហា ឆ្នាំ២០១៥ ម៉ោង ១៥:៣១', '1', '0', '0', 't', '2015-08-05', 'f');
INSERT INTO "tbnews" VALUES ('440', 'B020101', 'B020000', 'ខណៈនេះ​នៅសាលាខេត្តស្វាយរៀង​មានរៀបចំពិធី​ប្រកាសតែងតំាង​ផ្ទេរតំណែង​ព្រះរាជអាជ្ញា-​ព្រះរាជអាជ្ញារង', 'ខេត្តស្វាយរៀង ៖ ​ព្រះរាជអាជ្ញា​និងព្រះរាជអាជ្ញារងនៃ​អយ្យការអមសាលាដំបូង​ខេត្តស្វាយរៀង ​បានត្រូវធ្វើពិធីប្រកាស​តែងតំាងនិងផ្ទេរតំណែង​ជាផ្លូវការក្រោម​អធិបតីលោក ', 'http://kohsantepheapdaily.com.kh/article/126018.html', 'http://i.kspg.co/2015/8/451aafa1-8f3b-4ddf-ae87-dfe9bd0a1972.jpg?w=285&h=170&mode=carve', 'ថ្ងៃពុធ ទី៥ ខែសីហា ឆ្នាំ២០១៥ ម៉ោង ១៥:២៥', '1', '0', '0', 't', '2015-08-05', 'f');
INSERT INTO "tbnews" VALUES ('441', 'B020101', 'B020000', '​ភ្នំ​ស្រុក​ញ៉ែម​ ត្រូវ​បាន​គេ​ដាក់​គ្រឿង​​បំផ្ទុះ​​យក​​​កន្លែង​​សាង​សង់​​​​រោង​ចក្រ​ស៊ីម៉ងត៍​ត្បូង​ពេជ្រ', 'ខេត្ត​កំពត ៖ ​ភ្នំស្រុកញ៉ែម ជា​ភ្នំ​ធំ​ល្មម​ ​​​មាន​ឈ្មោះ​បោះ​សំឡេង​​តាំង​ពី​ដូន​តា​មកម៉្លេះ ​​​អ្នក​ភូមិ​ព្រៃ​តា​ព្រឹត​ក៏​បាន​ស្គាល់​ច្បាស់​​​ដែរ​​ថា ​​​កាល​ពី​ជាង​១០​ឆ្នាំ​មុន ​ភ្នំ​នេះ​សម្បូរ​ទៅ​ដោយ​សត្វ​ព្រៃ​គ្រប់ប្រភេទ', 'http://kohsantepheapdaily.com.kh/article/126010.html', 'http://i.kspg.co/2015/8/f719e63f-9c64-439c-9cf3-d1fd1146e8b2.jpg?w=285&h=170&mode=carve', 'ថ្ងៃពុធ ទី៥ ខែសីហា ឆ្នាំ២០១៥ ម៉ោង ១៤:៥៥', '0', '0', '0', 't', '2015-08-05', 'f');
INSERT INTO "tbnews" VALUES ('442', 'B020101', 'B020000', 'កម្លាំង​ប៉េ​អឹ​ម​ខេត្ត សហការ​ជាមួយ​ប៉េ​អឹ​ម​បេ​.៨​ចុង​ឃ្នៀស ចាប់​រថយន្ត​ដឹក​កូន​ត្រី​រ៉ស់​១០​ម៉ឺន​ក្បាល​ប្រគល់​ឱ្យ​រដ្ឋបាល​ជលផល', 'ខេត្តសៀមរាប ៖ កម្លាំង​ការិយាល័យ​បទល្មើស​ព្រហ្មទណ្ឌ​កងរាជអាវុធហត្ថ​ខេត្ត សហការ​ជាមួយ​ស្នាក់​ការ​សន្តិសុខ​បេ​៨​ចុង​ឃ្នៀស ដឹកនាំ​បញ្ជា​ផ្ទាល់​ដោយ​លោក​ឧត្តមសេនីយត្រី ប៉ោ វណ្ណ​និ​ត មេបញ្ជាការ​កងរាជអាវុធហត្ថ​ខេត្ត បានឱ្យ​កម្លាំង​ក្រោម​ឱវាទ ចុះ​ល្បាត​តាម​ផ្លូវ​ក្នុង​សង្កាត់​ចុង​ឃ្នៀស ក៏​មានការ​សង្ស័យ​ឃើញ​រថយន្ត​ម៉ាក​ហា​យ​ឡិន​ឌ័​រ ពណ៌​ទឹកប្រាក់​មួយ​គ្រឿង ក៏​ហៅ​ឱ្យ​ឈប់', 'http://kohsantepheapdaily.com.kh/article/126007.html', 'http://i.kspg.co/2015/8/6434c343-76d7-421a-885b-5114bafab2cd.jpg?w=285&h=170&mode=carve', 'ថ្ងៃពុធ ទី៥ ខែសីហា ឆ្នាំ២០១៥ ម៉ោង ១៤:៥៤', '1', '0', '0', 't', '2015-08-05', 'f');
INSERT INTO "tbnews" VALUES ('443', 'B020101', 'B020000', 'ភ្លៀង​ធ្លាក់​ខ្លាំង កសិករ​ដែល​ប្រកប​របរ​កសិកម្ម​មាន​ភាព​សប្បាយ​រីករាយ', 'ខេត្តកំពង់ស្ពឺ ៖ ចាប់តាំងពី​ដើមខែ​ឧសភា ២០១៥ ដែល​ជាដើម​រដូវវស្សា​រហូត​មក​ដល់​ដើមខែ​សីហា​នេះ ប្រជាពលរដ្ឋ​ទូ​ទាំង​ខេត្ត​ធ្វើ​ស្រូវ​ទាំង​ស្ទូង និង​ព្រោះ​បាន​១៩.២៥៧​ហិ​កតា លើ​ចំនួន​១១៣.៦០០​ហិ​កតា ។ ដោយពេល​នេះ​បងប្អូន​ប្រជា​កសិករ​យើង​កំពុង​បន្ត​សាប និង​ស្ទូង ហើយ​អ្នកខ្លះ​ទៀត​ក៏​ព្រោះ', 'http://kohsantepheapdaily.com.kh/article/126005.html', 'http://i.kspg.co/2015/8/ad33958b-7285-4e8b-8459-d2ecbe26a7dd.jpg?w=285&h=170&mode=carve', 'ថ្ងៃពុធ ទី៥ ខែសីហា ឆ្នាំ២០១៥ ម៉ោង ១៤:៤៤', '31', '0', '0', 't', '2015-08-05', 'f');
INSERT INTO "tbnews" VALUES ('444', 'B020101', 'B020000', '​សំណុំរឿង​គ្រូពេទ្យ​រង​ការ​ចោទ​រំលោភ​ប្រ​ពន្ធអ្នកជម្ងឺ​​បានធូរស្រាល​ បន្ទាប់​ពី​នាង​ព្រម​ទ​ទួល​សំណង', 'ខេត្តក្រចេះ​ ៖ សំណុំរឿង​គ្រូពេទ្យ​រង​ការ​ចោទ​ថា បាន​រំលោភ​សេពសន្ថវៈ​លើ​ប្រ​ព​ន្ធអ្នកជម្ងឺដែល​ជា​រឿងអាស្រូវ​ដ៏​ក្តៅ​គ​គុ​គ​ក្នុង​មន្ទីរពេទ្យ​បង្អែក​ខេត្ត ​​ហើយ​ម​ជ្ឈ​ដ្ឋា​នានា​ក្នុង​ខេត្ត​​​កំពុង​តាមដាន​លើ​បញ្ហា​នេះផង​ដែរ​', 'http://kohsantepheapdaily.com.kh/article/126003.html', 'http://i.kspg.co/2015/8/987502fe-5bd7-498e-b645-a39589ad0565.jpg?w=285&h=170&mode=carve', 'ថ្ងៃពុធ ទី៥ ខែសីហា ឆ្នាំ២០១៥ ម៉ោង ១៤:៣៥', '0', '0', '0', 't', '2015-08-05', 'f');
