INSERT INTO "tbnews" VALUES ('446', 'B020101', 'B020000', '​ប្រជាពលរដ្ឋ​ក្រីក្រ​៣០​គ្រួសារក្នុង​សង្កាត់​ព្រែក​តា​សេកទទួលបាន​អំណោយ​សម្តេច​កិត្តិ​ព្រឹទ្ធ​បណ្ឌិត​', 'រាជធានី​ភ្នំពេញ​ ៖ ​អំណោយ​​របស់​សម្តេច​កិ​តិ្ត​ព្រឹទ្ធ​បណ្ឌិត ប៊ុ​ន រ៉ា​នី ហ៊ុន សែន​ ប្រធាន​កាកបាទក្រហមកម្ពុជា​ត្រូវ​បាន​គណ​:​ក​ម្មា​ធិ​ការអនុ​សាខា​កាកបាទក្រហមកម្ពុជា​ខណ្ឌ​ជ្រោយចង្វារ​', 'http://kohsantepheapdaily.com.kh/article/126001.html', 'http://i.kspg.co/2015/8/6fb61a57-5b71-49fc-b5f0-d080195b507b.jpg?w=285&h=170&mode=carve', 'ថ្ងៃពុធ ទី៥ ខែសីហា ឆ្នាំ២០១៥ ម៉ោង ១៤:១៩', '32', '0', '0', 't', '2015-08-05', 'f');
