INSERT INTO "tbnews" VALUES ('454', 'B020101', 'B020000', '​ដើរ​​រក​ប្រ​ព​ន្ឋ​មិនឃើញ​បោក​បំផ្លាញ​ទូរ​ទស្ស​ន៍ម្តាយក្មេក និង​ដុត​ផ្ទះ​ខ្លួនឯង​​', 'ខេត្តកំពង់ឆ្នាំង ៖​ បុរស​ម្នាក់​ត្រូវ​បាន​សមត្ថកិច្ច​​ចាប់​ឃាត់ខ្លួន​ភា្ល​ម​ៗ បន្ទាប់​ពី​ប្រើ​អំពើ​ហិ​ង្សា​ដោយ​វាយ​កម្ទេច​ទូរទស្ស​ន៍​នៅផ្ទះ​ម្តាយក្មេក និង​ដុត​ផ្ទះ​ខ្លួនឯង ​កាល​ពី​​វេលា​ម៉ោង ៨​និង​១០​នាទីយប់​', 'http://kohsantepheapdaily.com.kh/article/125988.html', 'http://i.kspg.co/2015/8/991e772f-6de9-4083-8e88-98d331d8c66e.jpg?w=285&h=170&mode=carve', 'ថ្ងៃពុធ ទី៥ ខែសីហា ឆ្នាំ២០១៥ ម៉ោង ១៣:២៣', '231', '0', '0', 't', '2015-08-05', 'f');
INSERT INTO "tbnews" VALUES ('455', 'B020101', 'B020000', 'នគរបាល​ប៉ុស្តិ៍​ស្រះ​ចក​ឃាត់ខ្លួន​ជនសង្ស័យ​ជួញដូរ​ថ្នាំ​ញៀន​ម្នាក់', 'រាជ​ធានីភ្នំពេញ ៖ មន្ត្រី​នគរបាល​ប៉ុ​ស្តិ៍​រដ្ឋបាល​ស្រះ​ចក​បាន​និយាយ​ថា ជនសង្ស័យ​ជួញដូរ​ថ្នាំ​ញៀន​ម្នាក់​ត្រូវ​កម្លាំង​នគរបាល​ប៉ុស្តិ៍​រដ្ឋបាល​ស្រះ​ចក​ជិះ​ម៉ូ​តូ​ល្បាត ចាប់​​ឃាត់ខ្លួន​កាលពី​វេលា​ម៉ោង​០៩​យប់', 'http://kohsantepheapdaily.com.kh/article/125987.html', 'http://i.kspg.co/2015/8/20a64fea-02d1-403e-8440-aec79aaac9be.jpg?w=285&h=170&mode=carve', 'ថ្ងៃពុធ ទី៥ ខែសីហា ឆ្នាំ២០១៥ ម៉ោង ១៣:២២', '0', '0', '0', 't', '2015-08-05', 'f');
INSERT INTO "tbnews" VALUES ('456', 'B020101', 'B020000', 'រួម​គ្នា​ជម្រុញ​​កំណើន​ភ្ញៀវ​ទេសចរសម្រាប់​ប្រទេស​កម្ពុជា-ហ្វី​លី​ពី​ន​', 'រាជធានី​ភ្នំពេញ ​៖ នៅ​ក្នុង​ជំនួប​ពិភាក្សា​រវាង​​លោក​ថោ​ង ខុន ​រដ្ឋមន្ត្រី​ក្រសួងទេសចរណ៍​ និង​លោក​គ្រី​ស្តូ​ហ្វើ ប៊ី​ម៉ុ​ន​ធើ​រ៉ូ (H.E Mr Christopher B.Montero)​ ឯកអគ្គរដ្ឋ​ទូត​ហ្វី​លី​ពី​ន​ថ្មី​​ប្រចាំ​ប្រទេស​កម្ពុ​ជា កាល​ពី​​រសៀល​ថ្ងៃ​ទី​៤​ សីហា ​២០១៥ នៅ​ទីស្តី​ការ​ក្រសួង​ទេសចរណ៍​​​ ភាគី​ទាំង​២​សុទ្ធតែ​លើក​ឡើង​​អំពី​វិធានការ ​និង​បញ្ហា​សំខាន់​ៗ​ ដើម្បី​ជម្រុញ​កំណើន​ភ្ញៀវ​ទេសចរ​ សម្រាប់​', 'http://kohsantepheapdaily.com.kh/article/125986.html', 'http://i.kspg.co/2015/8/db2508c5-1770-43d1-a914-d88d4894a03a.jpg?w=285&h=170&mode=carve', 'ថ្ងៃពុធ ទី៥ ខែសីហា ឆ្នាំ២០១៥ ម៉ោង ១៣:១៧', '0', '0', '0', 't', '2015-08-05', 'f');
INSERT INTO "tbnews" VALUES ('457', 'B020401', 'B020000', 'តម្លៃ​ទំនិញប្រើប្រាស់​ប្រចាំ​ថ្ងៃស​ប្តា​ហ៍​ទី​1​នៃ​ខែសីហា 2015', 'រាជធានី​ភ្នំពេញ ៖  តម្លៃ​ទំនិញប្រើប្រាស់​ប្រចាំ​ថ្ងៃ​នៅ​ផ្សារ​ធំ​ថ្មី​ក្នុង​ស​ប្តា​ហ៍​ទី​១​នៃ​ខែសីហា ២០១៥ នេះ   មុខទំនិញ គ្រឿង​សមុទ្រ សាច់​ជ្រូក​-​គោ និង​បន្លែ មានការ​ប្រែប្រួល​តម្លៃ​តិចតួច ។ សូម​មើល​តារាង​ខាងក្រោម ៖', 'http://kohsantepheapdaily.com.kh/article/125714.html', 'http://i.kspg.co/2015/8/cd8514f2-661e-4aa0-9aba-4cbbaf12af0f.jpg?w=285&h=170&mode=carve', 'ថ្ងៃអាទិត្យ ទី២ ខែសីហា ឆ្នាំ២០១៥ ម៉ោង ១៤:០៣', '123', '0', '0', 't', '2015-08-01', 'f');
INSERT INTO "tbnews" VALUES ('458', 'B020401', 'B020000', 'អាជីវករ​លក់​ត្បូង​ក្នុងស្រុក​លើក​ឡើង​ខុស​គ្នា​ពី​កា​ធ្វើ​អា​ជី​កម្ម​រយៈពេល​7​ខែ​ឆ្នាំ​នេះ', 'រាជធានី​ភ្នំពេញ ៖ អ្នកស្រី យឹ​ម សោភ័ណ ម្ចាស់​ហាង​ជឹ​ម លឹ​ម​ហឿ​ន ទិញ​លក់​ត្បូង​ប៉ៃលិន និង​គ្រឿងអលង្ការ ដែល​មាន​ទីតាំង​ស្ថិត​នៅ​សង្កាត់​វត្ត​ភ្នំ បាន​ឲ្យ​ដឹង​ថា ការ​ធ្វើ​អាជីវកម្ម​ទិញ លក់​ត្បូង​មាន​តម្លៃ​កម្ពុជា ', 'http://kohsantepheapdaily.com.kh/article/125710.html', 'http://i.kspg.co/2015/8/1d3f3dbd-fdb7-486f-8d7c-d93020232443.jpg?w=285&h=170&mode=carve', 'ថ្ងៃអាទិត្យ ទី២ ខែសីហា ឆ្នាំ២០១៥ ម៉ោង ១០:៥៩', '23', '0', '0', 't', '2015-08-01', 'f');
INSERT INTO "tbnews" VALUES ('459', 'B020103', 'B020000', 'សម្តេច​តេ​ជោ ៖ ការ​បោះ​ឆ្នោត​ថ្នាក់​ជាតិ ធ្វើ​នៅ​អាទិត្យ ទី​4 នៃ​ខែកក្កដា ដដែល គណបក្ស​ប្រជាជន​កម្ពុជា​ប្រកាស ដាច់ខាត​មិន​កែ​រដ្ឋធម្មនុញ្ញ​ឡើយ', 'ខេត្តកណ្តាល ៖ សម្តេច​អគ្គមហាសេនាបតី​តេ​ជោ ហ៊ុន សែន នាយក​រដ្ឋមន្ត្រី​នៃ​ព្រះរាជាណាចក្រ​កម្ពុជា កាលពី​ព្រឹក​ថ្ងៃ​ទី​៣ ខែសីហា បាន​ប្រកាស​ក្នុង​ពិធី​សម្ពោធ​បើក​ឱ្យ​ប្រើប្រាស់​ជា​ផ្លូវការ​ស្ពាន​មិត្តភាព​កម្ពុជា​-​ចិន តា​ខ្មៅ ប្រវែង​៨៥៥​ម៉ែត្រ', 'http://kohsantepheapdaily.com.kh/article/125868.html', 'http://i.kspg.co/2015/8/c95038c5-f554-4009-bf83-c30f9e2c03c8.jpg?w=285&h=170&mode=carve', 'ថ្ងៃអង្គារ ទី៤ ខែសីហា ឆ្នាំ២០១៥ ម៉ោង ០៩:១២', '0', '0', '0', 't', '2015-08-04', 'f');
INSERT INTO "tbnews" VALUES ('460', 'B020104', 'B020000', 'វិធីសាស្ត្រ​ក្នុង​ការ​ដាំ​ត្រឡាច​បាន​ផ្លែ​ច្រើន', 'ខេត្តតាកែវ ៖ ការ​ដាំ​ដំណាំ​ទៅ​តាម​ទម្លាប់​ដែល​គ្មាន​ចំណេះ​ជំនាញ​ខាង​បច្ចេកទេស មិនមែន​ថា​មិន​ទទួលផល​ទេ គ្រាន់តែ​មិន​បាន​ដូច​បំណង​របស់​យើង ។ ប៉ុន្តែ​បើ​យើង​មានបទពិសោធន៍ គួប​ផ្សំ​នឹង​បច្ចេកទេស ', 'http://kohsantepheapdaily.com.kh/article/113984.html', 'http://i.kspg.co/2015/2/0c4487de-e4ff-4c9d-af93-7da5f773cad7.jpg?w=285&h=170&mode=carve', 'ថ្ងៃអាទិត្យ ទី១ ខែកុម្ភៈ ឆ្នាំ២០១៥ ម៉ោង ០៩:៥៦', '231', '0', '0', 't', '2015-02-01', 'f');
INSERT INTO "tbnews" VALUES ('461', 'B020104', 'B020000', 'នៅតាមចម្កា​រប្រជាកសិក​រប្តូរមកដាំ​ដំណាំដំឡូងមី​ច្រើនជាង​ដំណាំពោតក្រហម​នៅខេត្តប៉ៃ​លិន', 'ខេត្ត​ប៉ៃលិន​ ៖ តាំងពីដើ​មរៀងមក​ប្រជាកសិក​រភាគច្រើន​រស់នៅ​ក្នុងខេត្ត​ប៉ៃលិន​និយម​ ដាំដំណាំ​ពោត​ក្រហមច្រើន​​ជាង ដំណាំ​ណាៗទាំង​អស់ ។ ​ប៉ុន្តែនៅចុងក្រោយ​នេះ គេសង្កេតឃើញ​ថា ចាប់តាំងពី​ចុងឆ្នាំ២០១៣​ រហូតមកដល់​ ដើម​ឆ្នាំ ២០១៤​ ប្រជាពលរដ្ឋ​ស្ទើរតែទាំងអ​ស់បានងាក​បែរ ពីការដាំ​ដំណាំពោត​ក្រហម មកសម្រុក​ដាំដំណាំដំឡូង​មីទៅវិញ ​។', 'http://kohsantepheapdaily.com.kh/article/96134.html', 'http://i.kspg.co/2014/5/9eea7e7f-5eb9-412b-81f2-2fcb54cfab05.jpg?w=285&h=170&mode=carve', 'ថ្ងៃអាទិត្យ ទី១៨ ខែឧសភា ឆ្នាំ២០១៤ ម៉ោង ០៥:២៧', '0', '0', '0', 't', '2014-05-18', 'f');
INSERT INTO "tbnews" VALUES ('462', 'B020503', 'B020000', 'នារី​​ម្នាក់​​ស្រវឹងស្រា​បើក​រថ​យន្ត​បុក​បង្គោល​ស្លាក​ប៉ាណូ​ក្រុម​ហ៊ុន​ទូរស័ព្ទមេត​ហ្វូន​ រួច​ព្យាយាម​បើក​រត់​ត្រូវ​សមត្ថ​កិច្ច​ឃាត់​បាន​', 'រាជ​ធានី​ភ្នំពេញ​ ៖ នារី​ម្នាក់​អាយុ​ប្រមាណជាង​​ ២​០​ឆ្នាំ ទំនង​​​ស្រវឹង​​ស្រា​ បាន​បើក​រថ​យន្ត​កាមរី​បាឡែន​ ពណ៌​ស្ករ​ ១​គ្រឿង​ ពាក់​ស្លាក​លេខ​ ភ្នំពេញ​ 2W-1106 ធ្វើ​ដំណើរ​តាម​បណ្តោយ​ផ្លូវ​ជាតិ​លេខ​៦A ខណៈ​មក​ដល់​ចំណុច​ខាង​កើត​ស្ពាន​ជ្រោយ​ចង្វា​ ក៏​បាន​រេច​ង្កូត​ជ្រុ​លទៅ​បុក​​បង្គោល​ស្លាក​ប៉ាណូ​របស់​ក្រុម​ហ៊ុន​ទូរស័ព្ទ​មេត​ហ្វូន​ បណ្តាល​ឲ្យ​រង​ការ​ខូច​ខាត​ ហើយ​រថយន្តបាន​ព្យាយាម​បើក​រត់​មក​ដល់​សង្កាត់​បឹ​ងព្រលិត​ ខណ្ឌ​ ៧មករា​ ទើប​ត្រូវ​សមត្ថ​កិច្ច​ឃាត់​ចាប់​បាន​។', 'http://kohsantepheapdaily.com.kh/article/90807.html', 'http://i.kspg.co/2014/2/bc8892e8-73e6-4106-b902-fe65638648c9.jpg?w=285&h=170&mode=carve', 'ថ្ងៃសៅរ៏ ទី២២ ខែកុម្ភៈ ឆ្នាំ២០១៤ ម៉ោង ០១:៣៤', '0', '0', '0', 't', '2014-02-22', 'f');
INSERT INTO "tbnews" VALUES ('463', 'B020503', 'B020000', 'គំនរសំរាម​និងក្លិនស្អុយ​កំពុង​តាំងពិព័រណ៍​យ៉ាងសង្ហា​នៅ​ផ្សារ​អ្នក​លឿង​​', '​ខេត្ត​ព្រៃវែង ៖ គំនរសំរាម​ដែល​គេ​យក​​មក​គរ​ទុក​នៅ​មុខ​ផ្សារ​អ្នកលឿង​ត្រើយ​ខាង​កើត​ចំនួន​២កន្លែង​ជាប់​ផ្លូវជាតិលេខ​១ ស្ថិត​ក្នុង​ភូមិ​៤ ឃុំ​ព្រែកខ្សាយ​«ខ» ស្រុក​ពាមរក៍​​​ ​មាន​រយៈ​ពេល​​៣​ថ្ងៃ​មក​ហើយ​ ធ្វើ​ឱ្យ​គំនរ​សំរាម​កាន់​តែ​ខ្ពស់​ទៅ​ៗ​ដូច​កូន​ភ្នំ​​​។ ម្យ៉ាង​ទៀត​ពេលត្រូវ​ថ្ងៃ​ ត្រូវ​ភ្លៀង​ ត្រូវ​ខ្យល់​ ​ធ្វើឱ្យ​កាកសំណល់​សំរាម​ទាំងនេះ​​ស្អុយ​​​រលួយ ​​ជះ​​​ក្លិន​ទៅ​លើ​ប្រជាពលរដ្ឋ​​ដែល​រស់​នៅ​ជិត​នោះ ', 'http://kohsantepheapdaily.com.kh/article/51637.html', 'http://i.kspg.co/2012/8/dbdc80a3-e80a-448c-abfa-84e283d1852d.jpg?w=285&h=170&mode=carve', 'ថ្ងៃសុក្រ ទី៣១ ខែសីហា ឆ្នាំ២០១២ ម៉ោង ០៨:៥៧', '0', '0', '0', 't', '2012-08-31', 'f');
INSERT INTO "tbnews" VALUES ('464', 'B020503', 'B020000', 'រោងចក្រ​​​ចំហុយ​​ថ្នាំ​ជក់​​​នៅ​តា​ខ្មៅ​​បង្កផល​ប៉ះ​ពាល់​​​ដល់​សុខភាព​​​ប្រជាពលរដ្ឋ​​', 'ខេត្តកណ្តាល៖ រោង​ចក្រ​​ចំហុយ​ថ្នាំ​ជក់​​ដែល​​មាន​ទីតាំង​​​​នៅភូមិ​​តា​ខ្មៅ សង្កាត់​តា​ខ្មៅ ​ក្រុង​តា​​ខ្មៅ បច្ចុប្បន្ន​​​​កំពុង​ដំណើរ​ការ​​​សង្វាក់​​ផលិត​កម្ម​​របស់​ខ្លួន​​យ៉ាង​សកម្ម តែ​បញ្ហា​​​​ដែលកំពុង​កើត​​ឡើង​នោះគឺ​​​​​ធ្វើ​ឱ្យ​​មាន​ផល​ប៉ះ​ពាល់​​យ៉ាង​ខ្លាំង​​​ដល់​ផ្លូវ​ដង្ហើម​​របស់​ប្រជា ពលរដ្ឋ​​​រស់នៅ​ក្បែរ​​​ខាង  នេះ​បើតាម​​​ការ​អះអាង​របស់​ប្រជាពល​រដ្ឋ​​​ ។', 'http://kohsantepheapdaily.com.kh/article/50555.html', 'http://i.kspg.co/2012/8/7edc07cb-0b5e-4627-b68b-89b48393490c.jpg?w=285&h=170&mode=carve', 'ថ្ងៃពុធ ទី១៥ ខែសីហា ឆ្នាំ២០១២ ម៉ោង ០៨:៤៩', '1', '0', '0', 't', '2012-08-15', 'f');
INSERT INTO "tbnews" VALUES ('465', 'B030101', 'B030000', 'ចូលរួមពិធីសម្ពោធ Galaxy Tab S2 ទាំងអស់គ្នា! ជាវ Tab S2 ថែមកាស Bluetooth ឆ្នាំងសាកចល័ត រួមទាំងឱកាស ឈ្នះ Galaxy S6!', '<strong style="line-height: 2;">ភ្នំពេញ៖</strong><span style="line-height: 2;"> ក្រោយពីមានការទន្ទឹងរង់ចាំយ៉ាងអន្ទះសារបស់អ្នកគាំទ្រអស់ជាច្រើនខែមក ទីបំផុត ក្រុមហ៊ុន សាមសុង បានប្រកាសថ្ងៃសម្ពោធលក់ Galaxy Tab S2 ជាផ្លូវការនៅកម្ពុជា គឺនៅថ្ងៃទី០៩ ខែសីហា ឆ្នាំ២០១៥ ដែលនឹងមានការចូលរួមពីតារាចម្រៀងដ៏ល្បីប្រចាំព្រះរាជាណាចក្រកម្ពុជា គឺ កញ្ញា ឱក សុគន្ធកញ្ញា ផងដែរ នាហាងទំនើប សាមសុង អប្សរា ដែលមានទីតាំងនៅ ផ្ទះលេខ 48-50 ផ្លូវនេរុ ចម្ងាយប្រមាណ ១០០ម៉ែត្រ ខាងជើងផ្សារដេប៉ូ។</span>', 'http://www.dap-news.com/2011-06-14-02-39-55/96537--galaxy-tab-s2---tab-s2--bluetooth----galaxy-s6.html', 'http://image.lookingtoday.com:81/lookingtoday/looking-images/news/2015/August/ID-140/hour-00623.jpg', 'Wednesday, 05 August 2015 15:55', '3', '0', '0', 't', '2015-08-05', 'f');
INSERT INTO "tbnews" VALUES ('466', 'B030101', 'B030000', 'CNRP ទាមទារឲ្យដោះលែង សកម្មជនរបស់ខ្លួន ដោយគ្មានលក្ខខណ្ឌ', 'null', 'http://www.dap-news.com/2011-06-14-02-39-55/96535-cnrp-.html', 'http://image.dap-news.com:81/dap-news/dap-images/2015/July/id-009/ST-321.jpg', 'Wednesday, 05 August 2015 15:29', '32', '0', '0', 't', '2015-08-05', 'f');
INSERT INTO "tbnews" VALUES ('467', 'B030101', 'B030000', 'បុរសម្នាក់ទៀត ត្រូវបានចាប់ខ្លួន ពាក់ព័ន្ធ នឹងអំពើហិង្សា នៅលើស្ពាននាគ ខណៈ២នាក់ ត្រូវបានបញ្ជូន ទៅពន្ធនាគារ', '<strong>ភ្នំពេញ៖</strong> ប៉ូលិសរាជធានីភ្នំពេញ បាននិយាយថា បុរសម្នាក់ទៀត ពាក់ព័ន្ធនឹងអំពើហិង្សា នៅលើស្ពាននាគ ក្បែរទីលានប្រជាធិបតេយ្យ ត្រូវបានចាប់ខ្លួន ដោយកម្លាំងសមត្ថកិច្ចរាជធានីភ្នំពេញ នៅរសៀល ថ្ងៃទី០៥ ខែសីហា ឆ្នាំ២០១៥នេះ ខណៈដែល បុរស២នាក់ ទំនងជាសកម្មជនគណបក្សសង្រ្គោះជាតិ ត្រូវបានតុលាការបញ្ជូន ទៅកាន់ពន្ធនាគារព្រៃស ដើម្បីឃុំខ្លួនបណ្តោះអាសន្ន ។', 'http://www.dap-news.com/2011-06-14-02-39-55/96534-2015-08-05-08-23-57.html', 'img/dapnews.png', 'Wednesday, 05 August 2015 15:23', '0', '0', '0', 't', '2015-08-05', 'f');
INSERT INTO "tbnews" VALUES ('468', 'B030101', 'B030000', 'ក្មេងប្រុស អាយុ៧ឆ្នាំម្នាក់ ត្រូវរថយន្តបុក និងកិនស្លាប់ យ៉ាងអាណោចអាធម នៅស្រុកកៀនស្វាយ ខណៈចេញពី សាលារៀនទៅផ្ទះ', '<strong>កណ្តាល ៖</strong> ក្មេងប្រុសម្នាក់ ត្រូវរថយន្តធុនធំ បើកបុក និងកិនពីលើ បណ្តាលឲ្យបែកក្បាល និងធ្លាយពោះវៀន ស្លាប់ភ្លាមៗនៅកន្លែងកើតហេតុ កាលវេលាម៉ោង១០ និង៤៥នាទី ថ្ងៃទី០៥ ខែសីហាឆ្នាំ២០១៥ នៅចន្លោះគីឡូម៉ែត្រលេខ៣២-៣៣ លើកំណាត់ផ្លូវជាតិលេខ១ ក្នុងភូមិព្រែកប៉ុល ឃុំបន្ទាយដែក ស្រុកកៀនស្វាយ ខេត្តកណ្តាល។', 'http://www.dap-news.com/2011-06-14-02-39-55/96531-2015-08-05-07-31-21.html', 'http://image.dap-news.com:81/dap-news/dap-images/2015/August/id-014/that-0124.jpg', 'Wednesday, 05 August 2015 14:30', '321', '0', '0', 't', '2015-08-05', 'f');
INSERT INTO "tbnews" VALUES ('469', 'B030101', 'B030000', 'តុលាការសម្រេចឃុំខ្លួន សកម្មជន CNRP ២នាក់ទៀត ក្នុងសំណុំរឿងហិង្សា ក្បែរទីលានប្រជាធិបតេយ្យ', '<b>ភ្នំពេញ៖ </b>បន្ទាប់ពីសួរដេញដោលពេញមួយព្រឹក ចៅក្រមសាលាដំបូងរាជធានីភ្នំពេញ លោក កែវ មុនី បានសម្រេច &nbsp;ឃុំខ្លួនសកម្មជនគណបក្សសង្រ្គោះជាតិ២នាក់ទៀតហើយ ដោយចោទប្រកាន់ពីបទចូលរួបកុបកម្ម ដែលបានប្រ &nbsp;ព្រឹត្តឡើងកាលពីថ្ងៃទី១៥ ខែកក្កដា ឆ្នាំ២០១៤។', 'http://www.dap-news.com/2011-06-14-02-39-55/96529-2015-08-05-06-44-46.html', 'https://scontent-hkg3-1.xx.fbcdn.net/hphotos-xtp1/v/t1.0-9/s720x720/11828607_1479981282316238_431614320208487623_n.jpg?oh=182fa0d9b88aaa57f2a6fe6b14f70b83&oe=56833F26', 'Wednesday, 05 August 2015 13:44', '2', '0', '0', 't', '2015-08-05', 'f');
INSERT INTO "tbnews" VALUES ('470', 'B030101', 'B030000', 'អាជ្ញាធរភ្នំពេញ ឲ្យCNRP ទៅរ៉ៃអង្គាសប្រាក់ វិភាគទាន នៅទីស្នាក់ការ របស់ខ្លួនវិញ', '<strong>ភ្នំពេញ ៖</strong> អាជ្ញាធរភ្នំពេញ បានស្នើឲ្យគណបក្ស សង្រ្គោះជាតិ (CNRP) រៀបចំការ ប្រមូលវិភាគទាន ដើម្បី ទ្រងទ្រង់ទូរទស្សន៍ ព្រះអាទិត្យ នៅទីស្នាក់ការកណ្តាល របស់គណបក្សវិញ ដើម្បីកាត់បន្ថយ ភាពអសកម្មភាព នានា ដែលកើតមានឡើង។', 'http://www.dap-news.com/2011-06-14-02-39-55/96527--cnrp-.html', 'http://image.dap-news.com:81/dap-news/dap-images/2015/AUGUST/id-011/Hong-0146.jpg', 'Wednesday, 05 August 2015 13:23', '2', '0', '0', 't', '2015-08-05', 'f');
INSERT INTO "tbnews" VALUES ('471', 'B030101', 'B030000', 'តើលោក អ្នកចង់ប្រើប្រាស់ កម្មវិធីគណនេយ្យ មួយណា ?', 'បើលោកអ្នក មានការពិបាក ក្នុងការជ្រើសរើស កម្មវិធីគណនេយ្យ ឲ្យត្រូវជាមួយមុខរបរ ឬស្ថានភាព អាជីវកម្មរបស់ខ្លួន Leopard Business Consultancy Co., Ltd.(LBC) ជាក្រុមហ៊ុនដែលមានឯកទេសច្បាស់លាស់ លើវិជ្ជាជីវនេះ អាចជួយលោកអ្នកបានគ្រប់បែបយ៉ាង។ LBC ផ្តល់ជូនលោកអ្នករួមមាន៖', 'http://www.dap-news.com/2011-06-14-02-39-55/96525-2015-08-05-05-37-25.html', 'http://image.dap-news.com:81/dap-news/dap-images/2015/June/id-010/chanroth015-ju-119.jpg', 'Wednesday, 05 August 2015 12:37', '23', '0', '0', 't', '2015-08-05', 'f');
INSERT INTO "tbnews" VALUES ('472', 'B030101', 'B030000', 'និស្សិតកម្ពុជា ៩៨រូប ទទួលបាន អាហារូបករណ៍ ទៅសិក្សានៅបារាំង', '<b>ភ្នំពេញ៖</b> និស្សិកម្ពុជា ៩៨រូបទៀតហើយ ទទួលបានអាហារូបករណ៍ ចេញទៅសិក្សានៅតាមគ្រឹះស្ថានសិក្សានានា &nbsp;នៅប្រទេសបារាំង សម្រាប់ឆ្នាំសិក្សា២០១៥-២០១៦។ មុនចេញដំណើរទៅសិក្សានៅប្រទេសបារាំង នៅរសៀល ថ្ងៃទី០៤ ខែសីហា ឆ្នាំ២០១៥ ស្ថានទូតបារាំង បានរៀបចំពិធីជប់លៀងមួយ ដើម្បីអបអរសាទរ ក៏ដូចជាការជូនដំ &nbsp;ណើរដល់ពួកគេផងដែរ។', 'http://www.dap-news.com/2011-06-14-02-39-55/96524-2015-08-05-05-32-21.html', 'http://image.dap-news.com:81/dap-news/dap-images/2015/July/id-009/ST-318.jpg', 'Wednesday, 05 August 2015 12:31', '0', '0', '0', 't', '2015-08-05', 'f');
INSERT INTO "tbnews" VALUES ('473', 'B030101', 'B030000', 'អាដហុកសរសើរ រាជរដ្ឋាភិបាល ក្នុងការរំដោះស្រ្តីខ្មែរ ដែលគេជួញ ដូរទៅចិន', '<strong>ភ្នំពេញ ៖</strong> ប្រធានសមាគម សិទ្ធិមនុស្ស អាដហុក លោក ធន សារ៉ាយ បានលើកសរសើរ ដល់ប្រមុខ រាជរដ្ឋាភិបាល កម្ពុជា បានបញ្ចេញ សកម្មភាពជួយរំដោះក្រុមស្ត្រីខ្មែរ ដែលគេយកទៅជួញដូរនៅប្រទេសចិន បានច្រើនករណី និងបានរកកន្លែងសុវត្ថិភាព ដល់ពួកគេបានល្អ ប្រសើរជាងមុនផងដែរ។', 'http://www.dap-news.com/2011-06-14-02-39-55/96523-2015-08-05-05-23-15.html', 'http://image.dap-news.com:81/dap-news/dap-images/2015/AUGUST/id-011/Hong-0143.jpg', 'Wednesday, 05 August 2015 12:21', '123', '0', '0', 't', '2015-08-05', 'f');
INSERT INTO "tbnews" VALUES ('474', 'B030102', 'B030000', 'អាថ៌កំបាំងអំពីភាពរឹងមាំ របស់សំបុកពីងពាង', '<strong>ជប៉ុន៖</strong> ក្រុមអ្នកស្រាវជ្រាវ ជនជាតិអាមេរិក បាននិយាយថា ការសិក្សាមួយ ចំនួនអំពីសំបុក ពីងពាងបានបង្ហាញ អំពីមូលហេតុ ដែលសំបុកពីងពាង មានលក្ខណៈវិញចូលគ្នាជារាងមូល ហើយមានគុណភាពរឹងមាំ ជាងខ្សែខ្ទង់ ព្យាណូដល់ទៅ៥ដង។', 'http://www.dap-news.com/2011-06-14-02-40-26/96533-2015-08-05-08-23-53.html', 'http://image.dap-news.com:81/dap-news/dap-images/2015/AUGUST/id-011/Hong-0148.jpg', 'Wednesday, 05 August 2015 15:20', '0', '0', '0', 't', '2015-08-05', 'f');
INSERT INTO "tbnews" VALUES ('475', 'B030102', 'B030000', 'សាឡង់ ដឹកអ្នកដំណើរ លិចនៅកេនយ៉ា នាំឲ្យមនុស្សស្លាប់ និងបាត់ខ្លួនជិត ២០០នាក់', '<strong>គីស៊ូមូ ៖</strong> យ៉ាងហោច ណាស់មនុស្ស ២នាក់បានស្លាប់ និង ២០នាក់ផ្សេងទៀតត្រូវបានជួយ សង្គ្រោះ បន្ទាប់ ពីសាឡង់ដឹកអ្នកដំណើរ ដែលមានមនុស្សជិះ ជាង ២០០នាក់ បានបុក ជាមួយនឹងទូកនេសាទ បណ្តាលឲ្យលិច ទៅក្នុងបឹង វិចតូរៀ ស្ថិតនៅភាគខាងលិចប្រទេស កេនយ៉ា នៅថ្ងៃពុធ ទី០៤ ខែសីហា ឆ្នាំ២០១៥ នេះ។', 'http://www.dap-news.com/2011-06-14-02-40-26/96532-2015-08-05-07-38-26.html', 'http://media.worldbulletin.net/news/2014/12/28/turkish-ship.jpg', 'Wednesday, 05 August 2015 14:37', '1', '0', '0', 't', '2015-08-05', 'f');
INSERT INTO "tbnews" VALUES ('476', 'B030102', 'B030000', 'លេងសើចម៉េចបាន! អណ្តើកក៏ចេះ ស្លៀកខោអាវដែរ!', 'ធ្លាប់តែលឺសត្វឆ្កែ ឆ្មានិងជ្រូក ពាក់ខោអាវ តែឥឡូវនេះ អណ្តើកក៍មាន សំលៀល បំពាក់ស្លៀកនឹងគេដែរ។ សត្វអណ្តើកចិញ្ចឹមមួយក្បាលឈ្មោះ Wasabi ត្រូវបានម្ចាស់របស់ វាច្នៃម៉ូដសំលៀកបំពាក់ឲ្យ ដោយមើលទៅស្អាតគួរឲ្យស្រលាញ់។', 'http://www.dap-news.com/2011-06-14-02-40-26/96530-2015-08-05-07-10-56.html', 'http://image.dap-news.com:81/dap-news/dap-images/2015/AUGUST/id-011/Hong-0147.jpg', 'Wednesday, 05 August 2015 14:09', '231', '0', '0', 't', '2015-08-05', 'f');
INSERT INTO "tbnews" VALUES ('477', 'B030102', 'B030000', 'តើភពអង្គារ ធ្លាប់មានទ្វីបដែរឬទេ?', '<strong>&nbsp;អវកាស៖</strong> ក្រុមអ្នកស្រាវជ្រាវ បាននិយាយថា ជាមួយនឹងការប្រើប្រាស់ កាំរស្មីឡាស៊ែរ របស់យានរុករកភពអង្គារ Rover Curiosity ភ្នាក់ងារអវកាសណាសាបានរកឃើញ ដុំថ្មមួយចំនួននៅលើភពក្រហម ដែលមានលក្ខណៈ ស្រដៀងនឹងស្រទាប់ទ្វីបចាស់ ជាងគេបំផុតរបស់ភពផែនដីដែរ។', 'http://www.dap-news.com/2011-06-14-02-40-26/96528-2015-08-05-06-39-08.html', 'http://image.dap-news.com:81/dap-news/dap-images/2015/AUGUST/id-011/Hong-0145.jpg', 'Wednesday, 05 August 2015 13:38', '23', '0', '0', 't', '2015-08-05', 'f');
INSERT INTO "tbnews" VALUES ('478', 'B030102', 'B030000', 'ហេតុអ្វី បេសកកម្ម New Horizons ទៅកាន់ភពភ្លុយតូ ក្លាយជារឿង ដ៏គួរឲ្យចងចាំ?', '<strong>&nbsp;(អវកាស)</strong> ៖ ក្រុមអ្នកស្រាវជ្រាវ បាននិយាយថា បេសកកម្មយានរុករក New Horizons ទៅកាន់ ភព ភ្លុយតូ របស់ភ្នាក់ងារអវកាសណា សាបាន បន្សល់ទុកនៅកេរ្តិ៍មរតកវិទ្យាសាស្រ្តដ៏មហិមា ហើយនេះ ក៏ជាភាពជោគជ័យដ៏អស្ចារ្យដែលបានឆ្ពោះទៅដល់ទីកន្លែងដាច់ស្រយ៉ាល បំផុត នៃប្រព័ន្ធព្រះ អាទិត្យរបស់យើងផងដែរ។', 'http://www.dap-news.com/2011-06-14-02-40-26/96517--new-horizons-.html', 'https://cdn0.vox-cdn.com/thumbor/Fwpr5rosOHZnDnWNKtEdrJHXrsw=/cdn0.vox-cdn.com/uploads/chorus_asset/file/2520282/Pluto.0.jpg', 'Wednesday, 05 August 2015 11:22', '2', '0', '0', 't', '2015-08-05', 'f');
INSERT INTO "tbnews" VALUES ('479', 'B030102', 'B030000', 'ចិន សន្យានឹង កសាងផ្លូវនាវា ចរណ៍ដ៏រលូន ជាមួយ សមាគមអាស៊ាន', 'ប៉េកាំង៖ រដ្ឋមន្រ្តីការបរទេសចិន លោក វ៉ាង យី បានឆ្លើយនឹងសំណួរ របស់ក្រុមអ្នក សារព័ត៌មាននៅក្នុង អំឡុងពេលកិច្ចប្រជុំ រដ្ឋមន្រ្តីការបរទេស អាស៊ាន ដែលធ្វើឡើងនៅ ទីក្រុងគូឡាឡាំពួរ ប្រទេសសិង្ហបុរីថា ប្រទេសចិន នឹងសាងសង់ផ្លូវនាវាចរណ៍ដ៏រលូនក្នុង សម័យ សតវត្សរ៍ទី២១ របស់ខ្លួនជាមួយនឹង សមាគមប្រជាជាតិអាស៊ីអាគ្នេយ៍ (អាស៊ាន)។', 'http://www.dap-news.com/2011-06-14-02-40-26/96513-2015-08-05-03-44-45.html', 'http://image.dap-news.com:81/dap-news/dap-images/2015/AUGUST/id-011/Hong-0124.jpg', 'Wednesday, 05 August 2015 10:43', '1', '0', '0', 't', '2015-08-05', 'f');
INSERT INTO "tbnews" VALUES ('480', 'B030102', 'B030000', 'ទីក្រុង កាប៊ុល កំពុងស្តារហេដ្ឋា រចនាសម្ព័ន្ធ ឡើងវិញ យ៉ាងរហ័ស', '<strong>កាប៊ុល៖</strong> ប្រទេសអាហ្វហ្គានីស្ថាន បានទទួលរងនូវព្យុះសង្គ្រាម ដោយសារតែការលុកលុយ របស់អាមេរិក កាលពីឆ្នាំ ២០០១ ដើម្បីតាមកម្ចាត់ មេភេរវករ អន្តរជាតិ ដែលបានវាយប្រហារ ទីក្រុង ញូយ៉ក កាលពីខែកញ្ញា ឆ្នាំ ២០០១ នោះ។', 'http://www.dap-news.com/2011-06-14-02-40-26/96512-2015-08-05-03-39-42.html', 'http://image.dap-news.com:81/dap-news/dap-images/2015/August/id-010/chanroth015-aug-05.jpg', 'Wednesday, 05 August 2015 10:35', '36', '0', '0', 't', '2015-08-05', 'f');
INSERT INTO "tbnews" VALUES ('481', 'B030102', 'B030000', 'ទឹកជំនន់ និង ការបាក់ដី នៅឥណ្ឌា ឆក់យក ជីវិតមនុស្ស ២១៥នាក់', '<strong>កុលកាតា ៖</strong> រដ្ឋវែសត៍ ប៊ែងហ្គាល់ ភាគខាងលិចប្រទេសឥណ្ឌា នៅតែកំពុងទទួលរងគ្រោះ ដោយសារទឹកជំនន់ និង ការបាក់ដី ដែលបង្កឡើង ដោយព្យុះភ្លៀង ដដែល ស្របពេលដែល មនុស្សស្លាប់ត្រូវបានគេរាយការណ៍ថា កើនឡើងដល់ ២១៥នាក់ហើយ។', 'http://www.dap-news.com/2011-06-14-02-40-26/96505-2015-08-05-02-46-44.html', 'http://image.dap-news.com:81/dap-news/dap-images/2015/AUGUST/id-011/Hong-0111.jpg', 'Wednesday, 05 August 2015 09:43', '1', '0', '0', 't', '2015-08-05', 'f');
INSERT INTO "tbnews" VALUES ('482', 'B030401', 'B030000', 'រថយន្ត Porsche Cayman ប្រកាសលក់ ជាផ្លូវការ នៅកម្ពុជា ជាលើកដំបូង', '<strong>ភ្នំពេញ៖</strong> ក្រុមហ៊ុន Precision Cars (Cambodia ) Ltd. ជាក្រុមហ៊ុននាំចូលរថយន្ត Porsche មកកម្ពុជា នៅថ្ងៃទី ១៧ ខែ កក្កដា ឆ្នាំ ២០១៥ បានសម្ភោធដាក់លក់រថយន្តស្ព័រម៉ាក Porsche Cayman ដ៏ស្រស់ស្អាត និងទំនើប ដំបូងគេបង្អស់នៅកម្ពុជា។', 'http://www.dap-news.com/2011-06-14-02-43-39/95389--porsche-cayman-.html', 'http://image.dap-news.com:81/dap-news/dap-images/2015/July/id-014/that-0455.jpg', 'Friday, 17 July 2015 14:41', '1', '0', '0', 't', '2015-07-17', 'f');
INSERT INTO "tbnews" VALUES ('483', 'B030302', 'B030000', 'ព័ត៌មានលំអិត ជុំវិញដំណើរវិលត្រឡប់ របស់ម្ចាស់ជយលាភី មេដាយមាស-សំរឹទ្ធ និងគ្រូបង្វឹក មុនទទួលបាន ប្រាក់រង្វាន់ជិត ៤០០លានរៀល ពីរាជរដ្ឋាភិបាល', '<strong>ភ្នំពេញ៖</strong> ក្រោយបញ្ចប់បេសកកម្ម ចូលរួមប្រកួតក្នុងព្រឹត្តិការណ៍ 4th Vovinam World Championships នៅប្រទេស អាល់ហ្សេរី (Algeria) កាលពីចុងខែកក្កដាកន្លងទៅ នៅល្ងាចថ្ងៃទី០៤ ខែសីហាម្សិលមិញ ប្រតិភូ និងកីឡាករជម្រើស ជាតិកម្ពុជា បានវិលត្រឡប់មកប្រទេសកំណើតខ្លួនវិញហើយ ដោយបាននាំមកជាមួយ នូវមេដាយ ២គ្រឿង (មាស១ និងសំរឹទ្ធ១) ផងដែរ ។', 'http://www.dap-news.com/morokatmagazine/96499-2015-08-05-00-53-12.html', 'http://image.lookingtoday.com:81/lookingtoday/looking-images/news/2015/August/ID-140/hour-00533.jpg', 'Wednesday, 05 August 2015 07:47', '0', '0', '0', 't', '2015-08-05', 'f');
INSERT INTO "tbnews" VALUES ('484', 'B030302', 'B030000', 'សាសា ត្រូវ​មហាជន​រិះ​គន់​ថា ខ្លួន​ឯង​ចេះ​ទាម​ទារ​រក​យុត្តិធម៌ តែ​ពេល​ជិះ​រថយន្ត​បង្ក​គ្រោះ​ថ្នាក់ ដល់​អ្នក​ដទៃ បែរ​ជា​គេច​វេស', '<strong>ភ្នំពេញ៖</strong> នៅក្នុងបណ្ដាញសារព័ត៌មាន ក្នុងរយៈពេលថ្មីៗ ចុងក្រោយនេះ ត្រូវបានមហាជន ចាប់អារម្មណ៍ខ្លាំងទៅលើ សំណុំរឿង របស់អតីតពិធីការិនី ឯក សុជាតា ហៅសាសា ក្នុងអំពើហិង្សា ជាមួយនឹងលោកឧកញ៉ា សុខ ប៊ុន រហូតលោកឧកញ៉ាសម្បូរលុយរូបនេះ បាត់បង់នូវតំណែងជាឧកញ៉ា បាត់បង់កិត្តិយស ចូលខ្លួនមកទទួលទោសតាមច្បាប់ជាធរមាន ខណៈដែលខ្លួនបានប្រព្រឹត្តទង្វើ មិនគប្បីជាមួយនឹង សាសា ។', 'http://www.dap-news.com/morokatmagazine/96487-2015-08-04-10-20-20.html', 'http://image.lookingtoday.com:81/lookingtoday/looking-images/news/2015/August/ID-141/MrMSP-0063.jpg', 'Tuesday, 04 August 2015 17:19', '5', '0', '0', 't', '2015-08-04', 'f');
INSERT INTO "tbnews" VALUES ('485', 'B030302', 'B030000', 'បង្កប់ឧបករណ៍​ ក្រោមអណ្តាត ដើម្បីបង្កាអាការៈ គេងស្រម៉ុក', 'អ្នកវិទ្យាសាស្រ្ត បានបង្កើតឧបករណ៍វេជ្ជសាស្រ្តថ្មី ស្រដៀងនឹងឧបករណ៍ជំរុញចង្វាក់ បេះដូងដើម្បីបង្កប់នៅក្រោមអណ្តាត ការពារការគេងស្រម៉ុក។', 'http://www.dap-news.com/morokatmagazine/96436-2015-08-04-03-28-32.html', 'http://curefor-snoring.com/wp-content/uploads/2013/05/Anti-snoring-devices.jpg', 'Tuesday, 04 August 2015 10:25', '0', '0', '0', 't', '2015-08-04', 'f');
INSERT INTO "tbnews" VALUES ('486', 'B030302', 'B030000', 'ការទទួលទាន ខ្លាញ់ត្រី កាត់បន្ថយ គ្រោះថ្នាក់ នៃការកើតជំងឺ ដាច់សរសៃឈាម ខួរក្បាល', '<strong>អង់គ្លេស៖</strong> ក្រុមអ្នកស្រាវជ្រាវ ជនជាតិអង់គ្លេស និងអាល្លឺម៉ង់ បាននិយាយថា ការទទួលទាន ខ្លាញ់ត្រី ២ដង ក្នុងមួយសប្តាហ៍ មានអត្ថប្រយោជន៍ ក្នុងការកាត់បន្ថយគ្រោះថ្នាក់ នៃការកើតជំងឺដាច់សរសៃឈាមខួរក្បាល។', 'http://www.dap-news.com/morokatmagazine/96401-2015-08-03-07-38-09.html', 'http://image.dap-news.com:81/dap-news/dap-images/2015/August/id-014/that-0058.jpg', 'Monday, 03 August 2015 14:37', '51', '0', '0', 't', '2015-08-03', 'f');
