INSERT INTO "tbnews" VALUES ('489', 'B010105', 'B010000', 'Facebook ពូកែ​​​ផ្ញើ​សារ​ចូល​ពេក​ បិទ​កុំ​ឲ្យ​ផ្ញើ​ចូល​​មាន​​អី​ពិបាក​ (វីដេអូ)', 'ប្រសិន​បើ​​ប្រិយមិត្ត​កំពុង​​ប្រើ​ប្រាស​អ៊ីមែល​ដែល​បាន​យក​ទៅ​​បង្កើត​ Facebook ​​នោះ​​ប្រិយមិត្ត​ប្រាកដ​ជា​បាន​ឃើញ​សារ​យ៉ាង​ច្រើន​បាន​ផ្ញើ​​ចូល​ទៅ...', 'http://news.sabay.com.kh/article/442257', 'http://cdn.sabay.com/cdn/news.sabay.com.kh/wp-content/uploads/2015/08/Disable-FB-Message-send-to-Email1-285x170.jpg?ebb82d', '5 August, 2015', '1', '0', '0', 'f', '2015-08-05', 'f');
INSERT INTO "tbnews" VALUES ('490', 'B010105', 'B010000', 'និស្សិត​កម្ពុជា​នឹង​ទទួល​ចំណេះ​ដឹង​ឌីហ្សាញ​លំដាប់​ពិភពលោក​ឆាប់​ៗ​នេះ', 'នាយក​គ្រប់​គ្រង​រូប​នេះ​បញ្ជាក់​ថា ឥឡូវ​មាន​តែ​សាលា​តិចណូ​ដែល​ជា​ដៃគូ​សហការ​ដំបូង​គេ​ក្នុង​ការ​បណ្តុះ​បណ្តាលលើ​ជំនាញ​នេះ​...', 'http://news.sabay.com.kh/article/442206', 'http://cdn.sabay.com/cdn/news.sabay.com.kh/wp-content/uploads/2015/08/ddddddd-285x170.jpg?ebb82d', '5 August, 2015', '1', '0', '0', 'f', '2015-08-05', 'f');
INSERT INTO "tbnews" VALUES ('491', 'B010105', 'B010000', 'ត្រូវ​ឆ្លាត​អីចឹង​ ដឹង​របៀប​បង្ការ​មុន​ កុំ​ឲ្យ​គេ​បិទ​អ៊ីមែល​ចោល!​', 'ក្រោយ​ពី​មាន​បណ្ដាញ​សង្គម​ហ្វេសប៊ុក​ មនុស្ស​ជា​ច្រើន​លែង​សកម្ម​ជាមួយ​អ៊ីមែល​ ដូចជា​ Gmail, Yahoo ឬ​ Hotspot​ របស់​ខ្លួន​ទៀត។ ដល់​យូរ​ៗ​ម្ដង​ទៅ​បើក​ប្រើ​ មិន​អាច​ចូល​មើល​គណនី​របស់​ខ្លួន​បាន​ទេ។ អ្នក​ខ្លះ​យល់​ថា​ គណនី​របស់​ខ្លួន​ត្រូវ​បាន​គេ​លួច​ ឬ​ Hack ​តែ​តាម​ពិត​គឺ​ត្រូវ​បាន​បិទ​ដោយ​​ក្រុមហ៊ុន​ទៅ​វិញ​ទេ​ ព្រោះ​ខាន​ប្រើ​យូរ​ពេក។ &nbsp; អីចឹង​ដើម្បី​បង្ការ​កុំ​ឲ្យ​គេ​បិទ​អ៊ីមែល​របស់​អ្នក​ចោល​ អ្នក​អាច​មើល​រយៈពេល​នៃ​ការ​កំណត់​របស់​ក្រុមហ៊ុន​ផ្ដល់​សេវា​កម្ម​អ៊ីមែល​ដោយ​ឥត​គិត​ថ្លៃ​នេះ​ដល់​អ្នក៖ &nbsp; &nbsp; អ៊ីមែល​ Gmail៖ Gmail​ នឹង​បញ្ចប់​គណនី​របស់​អ្នក​ ប្រសិន​បើ​អ្នក​មិន​ Log In​ ចូល​គណនី​របស់​អ្នក​ក្នុង​រយៈ​ពេល​៩​ខែ។ &nbsp; &nbsp; &nbsp; អ៊ីមែល​ Yahoo៖ Yahoo នឹង​លុប​គណនី​របស់​អ្នក​ចោល​ ប្រសិន​បើ​អ្នក​មិន​បាន​ធ្វើ​ការ​ Log In​ ចូល​គណនី​ក្នុង​រយៈ​ពេល​១២​ខែ​ទេ​នោះ។ ដូច្នេះ​ដើម្បី​កុំ​ឲ្យ​គេ​លុប​ម៉ែល​ Yahoo​ របស់​អ្នក​ អ្នក​គួរ​តែ​ចូល​បើក​វា​ឲ្យ​បាន​ម្ដង​ក្នុង​រយៈពេល​១១​ខែ។ &nbsp; &nbsp; &nbsp; អ៊ីមែល​ Hotmail៖ សម្រាប់​ Hotmail វិញ​ នឹង​ធ្វើ​ការ​លុប​គណនី​របស់​អ្នក​ ប្រសិន​បើ​មិន​​អ្នក​ Log [&hellip;]', 'http://news.sabay.com.kh/article/442164', 'http://cdn.sabay.com/cdn/news.sabay.com.kh/wp-content/uploads/2015/08/Gmail-285x170.jpg?ebb82d', '5 August, 2015', '0', '0', '3', 't', '2015-08-05', 'f');
