INSERT INTO "tbnews" VALUES ('499', 'B010302', 'B010000', 'ខ្មែរ​១​ជីវិត ចេញ​បទ​ចម្រៀង​ឲ្យ​យុវវ័យ​រួម​កម្លាំង​គ្នា', 'ឡេង សុវណ្ណ​តារា ហៅ​ខ្មែរ​១​ជីវិត បាន​បញ្ចេញ​បទ​ចម្រៀង​ប្រភេទ Original Song មួយ​ទៀត ក្រោយ​សម្រេច​បាន​ជា​ច្រើន​បទ​ហើយ​នោះ។ បទ​ថ្មី​នេះ មាន​អត្ថន័យ​វែង​ឆ្ងាយ ដោយ​ពន្យុះ​ទឹក​ចិត្ត​យុវវ័យ​ឲ្យ​រួម​កម្លាំង​គ្នា។ ដក​ស្រង់​ពី​បទ​ចម្រៀង សុវណ្ណ​តារា ឬ​ខ្មែរ​១​ជីវិត លើក​ឡើង​ថា &#8220;សំអាង​លើ​ពាក្យ​ពីរ​ម៉ាត់ ដែល​អាច​ជម្នះ​រឿង​រាល់​ភាព​អវិជ្ជមាន គឺ​សាមគ្គី និង​ឆន្ទៈ​ដ៏​មោះមុត គ្មាន​អ្វី​ដែល​អាច​បំបាក់​បាន&#8221;។ &nbsp; &nbsp; លោក បន្ត​ថា ផ្ដើម​ពី​សាមគ្គី និង​ឆន្ទៈ​មោះមុត​នេះ សូម្បី​តែ​មេឃ​ដ៏​ធំ​ល្វឹងល្វើយ ក៏​យើង​អាច​ទាញ​ទម្លាក់​បាន​ដែរ។ ហើយ​នេះ គឺ​ជា​ចំណង​ជើង​បទ​ចម្រៀង​ថ្មី​នេះ &#8220;ទម្លាក់​មេឃ&#8221;។ Original Song មួយ​បទ​នេះ មិន​ទាន់​​ចេញ​ជា​វីដេអូ​ចម្រៀង​ពេញ​លេញ​នូវ​ឡើយ​ទេ គឺ​មាន​ត្រឹម Teaser ប៉ុណ្ណោះ។ &nbsp; តោះ&#8230;ឥឡូវ​ស្ដាប់​ទាំង​អស់​គ្នា​សិន!!! &nbsp; &nbsp; អត្ថបទ៖ ទារិកា', 'http://news.sabay.com.kh/article/442215', 'http://cdn.sabay.com/cdn/news.sabay.com.kh/wp-content/uploads/2015/08/11844201_828220260618631_1450286434_n1-285x170.jpg?ebb82d', '5 August, 2015', '0', '0', '0', 'f', '2015-08-05', 'f');
