INSERT INTO "tbnews" VALUES ('541', 'B020202', 'B020000', 'បាល់ទាត់​អីុ​តា​លី​ស៊ុប​ពើរ​ខាប់​វគ្គ​ផ្តាច់ព្រ័ត្រ និង​អីុតា​លី​ខាប់​ជុំ​ទី​2 សម្រាប់​រាត្រី​ថ្ងៃ​សៅរ៍​-​អាទិត្យ​នេះ​មាន 18 ប្រកួត', 'ការ​ប្រកួត​បាល់ទាត់​អីុតា​លី​ស៊ុប​ពើរ​ខាប់​វគ្គ​ផ្តាច់ព្រ័ត្រ និង​អីិុតា​លី​ខាប់​ជុំ​ទី​2 សម្រាប់​រាត្រី​ថ្ងៃ​សៅរ៍​-​អាទិត្យ​នេះ​មាន 18 ប្រកួត ។ សេះបង្កង់ Juventus ល្អ​ជាង Lazio, ខណៈ Spezia, Vicenza និង Perugia សង្ឃឹម​ឡើង​ទៅ​ជុំ​បន្ត ។', 'http://kohsantepheapdaily.com.kh/article/126239.html', 'http://i.kspg.co/2015/8/9e2d06e6-83d5-4774-a291-9b139433bfb4.jpg?w=285&h=170&mode=carve', 'ថ្ងៃអាទិត្យ ទី៩ ខែសីហា ឆ្នាំ២០១៥ ម៉ោង ១០:០៣', '2', '0', '0', 't', '2015-08-09', 'f');
