INSERT INTO "tbnews" VALUES ('552', 'B020201', 'B020000', 'ពិធី​សំណេះសំណាល​ជាមួយ​កីឡាករ​សែន រ៉ា​ឌី និង​ម៉ាត់ ឆ្វេង', 'រាជធានី​ភ្នំពេញ ៖ សហព័ន្ធ​កីឡាប្រដាល់​កម្ពុជា​បាន​រៀបចំ​ធ្វើ​ពិធី​សំណេះសំណាល​មួយ​ជាមួយ​កីឡាករ​សែន រ៉ា​ឌី និង​កីឡាករ​ម៉ាត់ ឆ្វេង ព្រមទាំង​កីឡាករ​មួយ​ចំនួន​ទៀត​ដែល​មាន​ស្នាដៃ​ក្នុង​ការ​ប្រកួត​ថ្នាក់​ជាតិ ', 'http://kohsantepheapdaily.com.kh/article/126166.html', 'http://i.kspg.co/2015/8/5ff21c2b-7c73-43f8-8683-f33157eb828f.jpg?w=285&h=170&mode=carve', 'ថ្ងៃសុក្រ ទី៧ ខែសីហា ឆ្នាំ២០១៥ ម៉ោង ១៤:១២', '0', '0', '0', 't', '2015-08-07', 'f');
