INSERT INTO "tbnews" VALUES ('563', 'B020302', 'B020000', 'ឆាយ វី​រៈ​យុទ្ធ  បាន​តំណែង​តួឯក​លើក​ដំបូង​ជាមួយ​កញ្ញា​សុខ ពិសី', 'រាជធានី​ភ្នំពេញ ៖ ខ្សែភាពយន្ត​ខ្នាត​ធំ​រឿង «​អា​ឡេវ​ចូល​ក្រុង​» របស់​ផលិតកម្ម  សាន់​ដេ ចាប់ផ្តើម​ផលិត​ហើយ ។ រឿង​ថ្មី​នេះ តំណែង​តួឯក​បាន​ទៅ​កញ្ញា​សុខ ពិសី និង​លោក​ឆាយ វី​រៈ​យុទ្ធ ជា​តារាចម្រៀង​កំពុង', 'http://kohsantepheapdaily.com.kh/article/126422.html', 'http://i.kspg.co/2015/8/d07cf4f2-baaf-4e04-b2ff-aa58c571fc63.jpg?w=285&h=170&mode=carve', 'ថ្ងៃអង្គារ ទី១១ ខែសីហា ឆ្នាំ២០១៥ ម៉ោង ០៩:៥៣', '0', '0', '0', 't', '2015-08-11', 'f');
