INSERT INTO "tbnews" VALUES ('566', 'B020302', 'B020000', 'នាង​អីុត​បាន​ល្បី​ចោល​ប្តី​នៅ​ស្រុក', 'រាជធានី​ភ្នំពេញ ៖ ព្រោះតែ​ល្បីឈ្មោះ​ក្នុង​នាម​ជាតា​រា​កំប្លែង នាង​អីុត​មិន​បាន​នៅ​រួមរស់​ជាមួយ​ប្តី​ឡើយ គឺ​ប្តី​នៅ​ឯ​ស្រុក​កំណើត ចំណែក​នាង​រស់នៅ​ភ្នំពេញ ។ ប្តី​នៅ​ដោយ​ប្តី ប្រពន្ធ​នៅ​ដោយ​ប្រពន្ធ ។ រយៈពេល​យូរ​ខែ​មក​ហើយ', 'http://kohsantepheapdaily.com.kh/article/126235.html', 'http://i.kspg.co/2015/8/23e131f0-210b-46e0-9fe4-e637d473ae05.jpg?w=285&h=170&mode=carve', 'ថ្ងៃអាទិត្យ ទី៩ ខែសីហា ឆ្នាំ២០១៥ ម៉ោង ០៩:៥០', '0', '0', '0', 't', '2015-08-09', 'f');
INSERT INTO "tbnews" VALUES ('567', 'B020302', 'B020000', 'តារា​បង្ហាញ​ម៉ូ​ដ​កូ​ឡុំ​ប៊ី​ប្រឈម​នឹង​ទោស​ប្រហារជីវិតទាក់ទិន​នឹង​បទល្មើស​គ្រឿងញៀន​នៅ​ប្រទេស​ចិន', 'តារា​ម៉ូ​ដែល​វ័យ​២២​ឆ្នាំ នាង​ជូ​លេ​ណា ឡូ​ប៉េ​ស ប្រឈម​មុខ​នឹង​ការ​កាត់ទោស​ប្រហារជីវិត​នៅ​ប្រទេស​ចិន ដោយ​កាលពី​ពេល ថ្មី​ៗ​កន្លង​មក ជាប់ចោទ​ពី​បទ​ជួញដូរ​គ្រឿងញៀន ក្រោយ​ពី​មន្ត្រី​ប៉ូលិស​រក​ឃើញ​', 'http://kohsantepheapdaily.com.kh/article/126139.html', 'http://i.kspg.co/2015/8/c09dbee7-7485-4a2b-8dfc-05b28640c997.jpg?w=285&h=170&mode=carve', 'ថ្ងៃសុក្រ ទី៧ ខែសីហា ឆ្នាំ២០១៥ ម៉ោង ១០:០០', '0', '0', '0', 't', '2015-08-07', 'f');
INSERT INTO "tbnews" VALUES ('568', 'B020302', 'B020000', 'ផ្តល់​សំណើច​ជូន​ទស្សនិកជន​តែ​ក្រៅ​ឆាក​នាយ​ចេក​គ្មាន​សំណើច​ទេ​ដ្បិត​ជំពាក់​បំណុល​គេ​ច្រើន', 'រាជធានី​ភ្នំពេញ ៖ តារា​កំប្លែង​វ័យ​ចំណាស់​នាយ​ចេក​ឈ្មោះ​ពិត​ឈុំ ប៊ុ​ន​ឈឿ​ន បច្ចុប្បន្ន​នេះ​មិន​ស្រណុកចិត្ត​ទេ​ព្រោះតែ​រឿង​ជំពាក់​បំណុល​គេ​រាប់​ពាន់​ដុល្លារ ។ ការ​ជំពាក់​បំណុល​គេ​នោះ​ដោយសារ​ខាតចុងខាតដើម​លើ​មុខរបរ​ចិញ្ចឹម​ជ្រូក ។ ', 'http://kohsantepheapdaily.com.kh/article/126134.html', 'http://i.kspg.co/2015/8/56ee901f-8a10-410c-907a-a6c212c864a3.jpg?w=285&h=170&mode=carve', 'ថ្ងៃសុក្រ ទី៧ ខែសីហា ឆ្នាំ២០១៥ ម៉ោង ០៩:៤៥', '0', '0', '0', 't', '2015-08-07', 'f');
INSERT INTO "tbnews" VALUES ('569', 'B020302', 'B020000', 'ថៅកែ​ល្ខោន « ភី សុ​គ​ន្ធី » បើក​ផលិតកម្ម​ភាពយន្ត​ផលិត​ស្នាដៃ​ដំបូង​រឿង​« បេះដូង​កូ​ពី »', 'រាជធានី​ភ្នំពេញ ៖ លោក​អ្នក​ធ្លាប់តែ​ស្គាល់ឈ្មោះ​ក្រុម​ល្ខោនបាសាក់ «​ភី សុ​គ​ន្ធី​» នៅ​ស្រុក​បាទី ខេត្តតាកែវ ប៉ុន្តែ​ពេល​នេះ​ថៅកែ​ក្រុម​ល្ខោន​នោះ គឺ​អ្នកស្រី​សួង ធី ហៅ​សុ​គ​ន្ធី បាន​បើក​ផលិតកម្ម​ភាពយន្ត​ថ្មី​មួយ​មានឈ្មោះ​ថា ', 'http://kohsantepheapdaily.com.kh/article/126063.html', 'http://i.kspg.co/2015/8/9c3658e2-1987-458c-80d2-814d2b1d9cef.jpg?w=285&h=170&mode=carve', 'ថ្ងៃព្រហស្បត្ត៏ ទី៦ ខែសីហា ឆ្នាំ២០១៥ ម៉ោង ១០:៣០', '0', '0', '0', 't', '2015-08-06', 'f');
INSERT INTO "tbnews" VALUES ('570', 'B020302', 'B020000', 'ខ្សែភាពយន្ត​ស្តី​ពី​ជីវិត​ក្មេង​ស្រី​ម្នាក់​នៅ​ក្នុង​របប​ខ្មែរក្រហមរបស់​អេ​ន​ចេ​លី​ណា ជោ​លី ថត​នៅ​ចុងឆ្នាំ តួ​សម្តែង​ខ្មែរ​ទាំងអស់', 'រាជធានី​ភ្នំពេញ ៖ មានការ​ចាប់អារម្មណ៍​ខ្លាំង​ដែល​តា​រាហូ​លី​វូ​ដ​ដ៏​លេចធ្លោ​អេ​ន​ចេ​លី​ណា ជោ​លី ត្រៀម​ផលិត​ខ្សែភាពយន្ត​ដែល​បង្ហាញ​ពី​ជីវិត​ពិត​នា​សម័យ​ខ្មែរក្រហម ។ ខ្សែភាពយន្ត នោះ​គ្រោង​ផ្តល់​ឱកាស​ដល់​សិល្បករ​ខ្មែរ​', 'http://kohsantepheapdaily.com.kh/article/125866.html', 'http://i.kspg.co/2015/8/c7a8febe-aadf-4b78-b2db-054b1d1188ff.jpg?w=285&h=170&mode=carve', 'ថ្ងៃអង្គារ ទី៤ ខែសីហា ឆ្នាំ២០១៥ ម៉ោង ០៩:០៥', '0', '0', '0', 't', '2015-08-04', 'f');
INSERT INTO "tbnews" VALUES ('571', 'B020301', 'B020000', 'បេតិកភណ្ឌ​ពិភពលោក​ថ្មី​ចំនួន​27​ប្រចាំឆ្នាំ​2015', 'សារព័ត៌មាន​បរទេស​ផ្សាយ​ថា គណៈកម្មការ​បេតិកភណ្ឌ​ពិភពលោក​របស់​អង្គការ​សិក្សា​ស្រាវជ្រាវ​វិទ្យាសាស្ត្រ និង​វប្បធម៌​នៃ​អង្គការសហប្រជាជាតិ (UNESCO) បាន​បញ្ចប់​កិច្ចប្រជុំ​លើក​ទី​៣៩ កាលពី​ថ្ងៃ​ទី​៨ កក្កដា ២០១៥ ', 'http://kohsantepheapdaily.com.kh/article/126326.html', 'http://i.kspg.co/2015/8/a866eea1-c75f-4fdf-a304-c33ac36f8a99.jpg?w=285&h=170&mode=carve', 'ថ្ងៃចន្ទ ទី១០ ខែសីហា ឆ្នាំ២០១៥ ម៉ោង ១០:២២', '0', '0', '0', 't', '2015-08-10', 'f');
INSERT INTO "tbnews" VALUES ('572', 'B020301', 'B020000', 'ទឹកជ្រោះ​កា​ទៀង​នៅ​កណ្តាល​ព្រៃ​ភ្នំ​ត្រជាក់​កាយ ត្រជាក់​ភ្នែក', 'ខេត្តរតនគិរី ៖ ទឹកជ្រោះ​កា​ទៀង​ស្ថិត​នៅ​ក្នុងភូមិ​កា​ទៀង ឃុំ​ឡា​បាំង​ពីរ ស្រុក​លំ​ផាត់ មាន​ចម្ងាយ​ប្រមាណ​៩​គីឡូម៉ែត្រ ពី​ក្រុង​បាន​លុង នៃ​ខេត្តរតនគិរី ។ មុន​ធ្វើ​ដំណើរ​ទៅ​ដល់​រមណីយដ្ឋាន​ទឹកជ្រោះ​', 'http://kohsantepheapdaily.com.kh/article/119949.html', 'http://i.kspg.co/2015/5/57f7ae42-6725-4f7f-8055-f29fb4cf5489.jpg?w=285&h=170&mode=carve', 'ថ្ងៃសុក្រ ទី១៥ ខែឧសភា ឆ្នាំ២០១៥ ម៉ោង ០៩:៣៤', '0', '0', '0', 't', '2015-05-15', 'f');
INSERT INTO "tbnews" VALUES ('573', 'B020301', 'B020000', 'មន្ត្រី​អាជ្ញាធរ​អប្សរា​​​អះអាង​ថា​ គូ​ស្នេ​ហ៍សត្វ​ទោច​ «​សា​រ៉ា​និ​ច និង​បារាយ​ណ៍»​ បាន​បង្កើតកូន​មួយ​​ហើយ', 'ខេត្តសៀមរាប​ ៖​ នៅ​ទីបំផុត​ឆ្លងកាត់​ការ​រស់នៅ​ដោយ​មាន​សេរីភាព​ក្នុង​ព្រៃ​ឧទ្យាន​អង្គរ គូ​ស្នេ​ហ៍​ទោច​ម្កុដ ដែល​ក្រុម​អ្នក​អភិរក្ស​សត្វព្រៃ​បាន​ដាក់​ឈ្មោះ​ «​សា​រ៉ា​និ​ច និង ​បារាយ​ណ៍» បាន​បង្កើតកូន​មួយ​ក្បាល​ហើយ ។', 'http://kohsantepheapdaily.com.kh/article/118838.html', 'http://i.kspg.co/2015/4/aa36b70e-5c1a-4a37-9eef-83b7d1faaea9.jpg?w=285&h=170&mode=carve', 'ថ្ងៃអាទិត្យ ទី២៦ ខែមេសា ឆ្នាំ២០១៥ ម៉ោង ១៧:១២', '0', '0', '0', 't', '2015-04-26', 'f');
INSERT INTO "tbnews" VALUES ('574', 'B020501', 'B020000', 'តាម​ការ​សិក្សា​បង្ហាញ​ថា ក្រុម​យុវវ័យ​ចុច​ទូរស័ព្ទ​ពេល​បើកបរ​ច្រើន​ជាងគេ', 'បើ​ទោះជា​មានការ​ហាមប្រាម​តាម​ផ្លូវច្បាប់​ក្នុង​ប្រទេស (​សហរដ្ឋអាមេរិក​) អំពី​ការ​ចុច​ទូរស័ព្ទ​ខណៈ​ពេល​កំពុង​បើករថយន្ត​ក៏​ដូច​ជា​ការ​បើក​យុទ្ធនាការ​ផ្សព្វផ្សាយ​ពី​សុវត្ថិ​ភាព​​សាធារណៈ​យ៉ាងណា​ក្តី​ក៏​មាន', 'http://kohsantepheapdaily.com.kh/article/126519.html', 'http://i.kspg.co/2015/8/2228ac14-f4a1-450c-9d32-bb51199497b9.jpg?w=285&h=170&mode=carve', 'ថ្ងៃពុធ ទី១២ ខែសីហា ឆ្នាំ២០១៥ ម៉ោង ១០:២៩', '0', '0', '0', 't', '2015-08-12', 'f');
INSERT INTO "tbnews" VALUES ('575', 'B020505', 'B020000', 'ជំងឺ​​ច្រាល​ទឹក​អាស៊ី​តក្នុង​ក្រពះ​អាច​នឹង​វិវត្តន៍កើត​ទៅ​ជា​ជំងឺ​មហា​រី​បំពង់​​អាហារ', 'កាលពី​ពេល​ថ្មី​ៗ​នេះ លោក​វេជ្ជបណ្ឌិត អ៊ុ​ង ចក្រាវុធ ប្រធាន​ផ្នែក​ជំងឺថ្លើម ក្រពះ ពោះវៀន និង​ជំងឺ​ប្ញ​ស​ដូង​បាត​នៃ​មន្ទីរពេទ្យ​កាល់​ម៉ែ​ត​បាន​មាន​ប្រសាសន៍​ប្រាប់​កោះសន្តិភាព​ឱ្យ​ដឹង​ថា ជំងឺ​ច្រាល​ទឹក​អាស៊ីត​ក្នុង​ក្រពះ គឺជា​ជំងឺ​មួយ​ដែល​កើត​មាន​ច្រើន​ខ្លាំង​ណាស់ ', 'http://kohsantepheapdaily.com.kh/article/126501.html', 'http://i.kspg.co/2015/8/830c7687-ba7c-4cd1-b200-9d998d891449.jpg?w=285&h=170&mode=carve', 'ថ្ងៃពុធ ទី១២ ខែសីហា ឆ្នាំ២០១៥ ម៉ោង ០៨:៤០', '0', '0', '0', 't', '2015-08-12', 'f');
INSERT INTO "tbnews" VALUES ('576', 'B020505', 'B020000', 'ស្វែងយល់​ពី​ជំងឺ​គ្រុន​ពោះវៀន', 'ជំងឺ​គ្រុនពោះវៀន​ជា​ទូទៅ​រោគ​សញ្ញា​មាន​ដូច​ជា ៖ -​គ្រុនផ្តាសាយ -​ឈឺក្បាល និង​ឈឺបំពង់ក ហើយ​ភាគច្រើន​មាន​ក្អក​ស្ងួត -​គ្រុនក្តៅ​ឡើង​ខ្ពស់​ហើយ​ចុះ​វិញ ប៉ុន្តែ​ចេះ​តែ​កើនឡើង​មួយ​ថ្ងៃ​បន្តិច​ៗ រហូត​ដល់​៤០​អង្សា ឬ​លើស​ពី​នេះ', 'http://kohsantepheapdaily.com.kh/article/126406.html', 'http://i.kspg.co/2015/8/e0f84678-f3b3-40b8-9755-3921f6c42b83.jpg?w=285&h=170&mode=carve', 'ថ្ងៃអង្គារ ទី១១ ខែសីហា ឆ្នាំ២០១៥ ម៉ោង ០៨:៣៦', '0', '0', '0', 't', '2015-08-11', 'f');
INSERT INTO "tbnews" VALUES ('577', 'B020505', 'B020000', 'ប្រេងឆា​ខ្លះ​ទទួលទាន​ទៅ​អាច​បង្កើន​ហានិភ័យ​ពី​ជំងឺ​បេះដូង', 'តាម​ការ​សិក្សា​មួយ​បាន​បង្ហាញ​ថា ប្រេងឆា​មួយ​ចំនួន​ដែល​គេ​អះអាង​ពី​មុន​មក​ថា ទទួលទាន​ធ្វើ​ឲ្យ​មាន​សុខភាព​ល្អ​តែ​មិន​ទូទៅ​ទេ​គឺ​ពិតជា​មាន​ប្រេងឆា​ខ្លះ​អាច​បង្កើន​ហានិភ័យ​ពី​ជំងឺ​បេះដូង ។ ', 'http://kohsantepheapdaily.com.kh/article/126367.html', 'http://i.kspg.co/2015/8/44520b26-2aec-449b-9483-bef4f4b85ddf.jpg?w=285&h=170&mode=carve', 'ថ្ងៃចន្ទ ទី១០ ខែសីហា ឆ្នាំ២០១៥ ម៉ោង ១៥:០៣', '0', '0', '0', 't', '2015-08-10', 'f');
INSERT INTO "tbnews" VALUES ('578', 'B020505', 'B020000', 'ពុល​អាហារ​ធ្វើ​ពី​សាច់​មាន់​មាន​អាការ​ដូច​ម្ដេច?', ' បាក់តេរី​ដែល​ស្ថិត​នៅ​ក្នុង​អាហារ​ឆៅ ឬ​ចម្អិន​មិន​បាន​ឆ្អិន​ល្អ ឬ​ក៏​របប​អាហារ​ទាំងឡាយ​ណា​ដែល​រក្សា​ទុក​មិន​បាន​ត្រឹមត្រូវ​អាច​បណ្តាល​ឱ្យ​ក្លាយទៅជា​អាហារ​ពុល​បាន ។ ដោយឡែក​សាច់​មាន់​', 'http://kohsantepheapdaily.com.kh/article/126366.html', 'http://i.kspg.co/2015/8/4552b56c-186a-4c8f-98ac-ad8f2a5eb805.jpg?w=285&h=170&mode=carve', 'ថ្ងៃចន្ទ ទី១០ ខែសីហា ឆ្នាំ២០១៥ ម៉ោង ១៤:៥៩', '0', '0', '0', 't', '2015-08-10', 'f');
