INSERT INTO "tbnews" VALUES ('581', 'B020505', 'B020000', 'ដង្កូវ​ស៊ី​ធ្មេញ ឬ​ធ្មេញ​ពុក​អាច​ផ្តាច់​អាយុជីវិត​អ្នកជំងឺ​បាន​បើ​មិន​បាន​ព្យាបាល', 'ដង្កូវ​ស៊ី​ធ្មេញ ឬ​ជំងឺ​ធ្មេញ​ពុក អាច​បណ្តាល​ឲ្យ​អ្នកជំងឺ​ស្លាប់​បាន បើសិនជា​គាត់​មិន​បាន​ព្យាបាល​ឲ្យ​បាន​ទាន់​ពេលវេលា នេះ​បើ​តាម​ការ​ឲ្យ​ដឹង​របស់​លោក​សាស្ត្រាចារ្យ​ទន្ត​បណ្ឌិត គា​ម ប៊​ន', 'http://kohsantepheapdaily.com.kh/article/126336.html', 'http://i.kspg.co/2015/8/e2d5d2d0-d474-4149-a2b1-8d6c924ab96e.jpg?w=285&h=170&mode=carve', 'ថ្ងៃចន្ទ ទី១០ ខែសីហា ឆ្នាំ២០១៥ ម៉ោង ១១:០១', '0', '0', '0', 't', '2015-08-10', 'f');
INSERT INTO "tbnews" VALUES ('582', 'B020505', 'B020000', 'លោក​អ្នកមាន​អាការ​ស្គមស្គាំង​ស្លេកស្លាំង​គួរ​គិតដល់​មេរោគ​តេន​ញ៉ា​ក្នុង​ក្រពះ​ពោះវៀន', 'បើ​លោក​អ្នក​ឬ​កូនចៅ​មាន​អាការ​ស្លេកស្លាំង ស្គមរីងរៃ​បើ​ទោះជា​ទទួលទាន​ច្រើន​យ៉ាងណា​ក៏​ដោយ សូម​គិត​ទៅ​ដល់​តេន​ញ៉ា​ឬ​ព្រូន​ដែល​កើត​មាននៅ​ក្នុង​ក្រពះ​ពោះវៀន​ផង  ព្រោះ​មនុស្ស​ដែល​មាន​អាការ​បែប​នេះ​', 'http://kohsantepheapdaily.com.kh/article/126335.html', 'http://i.kspg.co/2015/8/c78c4e5f-cf0b-4ca3-925c-0899d2e398ef.jpg?w=285&h=170&mode=carve', 'ថ្ងៃចន្ទ ទី១០ ខែសីហា ឆ្នាំ២០១៥ ម៉ោង ១០:៥៨', '0', '0', '0', 't', '2015-08-10', 'f');
INSERT INTO "tbnews" VALUES ('583', 'B020505', 'B020000', 'ហា​ត់​ប្រាណ​ដោយ​រត់​ផ្តល់​អត្ថប្រយោជន៍​អ្វីខ្លះ​ដល់​ស​ខ​ភាព?', 'ពិតប្រាកដ​ណាស់ មាន​ហេតុផល​សុខភាព​១០​យ៉ាង​ដែល​យើង​គួរ​ហាត់ប្រាណ​ដោយ​ការ​រត់ ដែល​ហេតុផល​ទាំងនោះ​មាន​ដូច​ជា ៖  ១-​ដុត​រំលាយ​សារធាតុ​ខ្លាញ់​បាន​ឆាប់រ​ហ័​ស ៖', 'http://kohsantepheapdaily.com.kh/article/126334.html', 'http://i.kspg.co/2015/8/a4e0c4fb-0a65-415f-8b64-dd1ce6be9eb3.jpg?w=285&h=170&mode=carve', 'ថ្ងៃចន្ទ ទី១០ ខែសីហា ឆ្នាំ២០១៥ ម៉ោង ១០:៥៣', '0', '0', '0', 't', '2015-08-10', 'f');
