INSERT INTO "tbnews" VALUES ('585', 'B020505', 'B020000', 'ជំងឺ​រលាក​ទងសួត​ អាច​បណ្ដាល​ឱ្យ​កុមារ​ស្លាប់​​បាន', 'បើ​តាម​ការ​ឱ្យ​ដឹង​របស់​លោក​វេជ្ជបណ្ឌិត ហុង សុខ​ឃាង ឯកទេស​ជំងឺ​កុមារ​នៃ​មន្ទីរពេទ្យ​កុមារ​ជាតិ​បាន​ប្រាប់​កោះសន្តិភាព​ឱ្យ​ដឹង​ថា​ជំងឺ​ជំងឺ​រលាក​ទងសួត អាច​បណ្ដាល​ឱ្យ​កុមារ​ស្លាប់​បាន បើសិនជា​មានការ​ធ្វេ​រ​ប្រហែស​នោះ ហើយ​ក្រុម​កុមារ​ដែល​មាន​អត្រា​គ្រោះ​', 'http://kohsantepheapdaily.com.kh/article/126325.html', 'http://i.kspg.co/2015/8/640bfa12-385e-40c7-b45a-711ba7abb3f4.jpg?w=285&h=170&mode=carve', 'ថ្ងៃចន្ទ ទី១០ ខែសីហា ឆ្នាំ២០១៥ ម៉ោង ១០:១៨', '0', '0', '0', 't', '2015-08-10', 'f');
INSERT INTO "tbnews" VALUES ('586', 'B020505', 'B020000', 'ដង្កូវ​​ស៊ី​​ធ្មេញ ឬជំងឺ​ធ្មេញ​​ពុក អាច​បណ្តាល​ឱ្យ​អ្នកជំងឺ​ស្លាប់​​ បើសិនជា​មិន​​ព្យា​បាល', 'ជំងឺ​ដង្កូវ​ស៊ី​ធ្មេញ ឬ​ជំងឺ​ធ្មេញ​ពុក អាច​បណ្តាល​ឱ្យ​អ្នកជំងឺ​ស្លាប់​បាន បើសិនជា​មិន​បាន​ធ្វើការ​ព្យាបាល​ឱ្យ​បាន​ទាន់​ពេលវេលា នេះ​បើ​តាម​ការ​ឱ្យ​ដឹង​របស់​លោក​សាស្ត្រាចារ្យ​ទន្ត​បណ្ឌិត គា​ម ប៊​ន ឯកទេស​ផ្នែក​ទន្តសាស្ត្រ​នៃ​មន្ទីរពេទ្យ​មិត្តភាព​ខ្មែរ​-​សូ​វៀត ដែល​បាន​ប្រាប់​កោះសន្តិភាព​ឱ្យ​ដឹង កាលពី​ពី​ពេល​ថ្មី​ៗ​នេះ​។', 'http://kohsantepheapdaily.com.kh/article/126142.html', 'http://i.kspg.co/2015/8/0e876827-d145-4229-b1fe-bcf02b97235f.jpg?w=285&h=170&mode=carve', 'ថ្ងៃសុក្រ ទី៧ ខែសីហា ឆ្នាំ២០១៥ ម៉ោង ១០:១០', '0', '0', '0', 't', '2015-08-07', 'f');
INSERT INTO "tbnews" VALUES ('587', 'B020505', 'B020000', 'កង្វះ​វី​តា​មី C អាច​បណ្តាល​ឱ្យ​កើត​មាន​ជំងឺរាក់​ស៊ី​ធ្មេញ', 'បើ​តាម​ការ​ឱ្យ​ដឹង​របស់​លោក​សាស្ត្រាចារ្យ​ទន្ត​បណ្ឌិត គា​ម ប៊​ន ឯកទេស​ផ្នែក​ទន្តសាស្ត្រ​នៃ​មន្ទីរពេទ្យ​មិត្តភាព​ខ្មែរ​-​សូ​វៀតបាន​ប្រាប់​កោះសន្តិភាព​ដឹង​កាលពី​វេលា​ព្រឹក​ថ្ងៃ​ទី​៤ សីហា នេះ​ថា ក្រៅពី​ការ​មិន​មាន​អនាម័យ​ចំពោះ​សុខភាព​ធ្មេញ​ហើយ​នោះ កង្វះ​វីតាមីន C​ក៏​ជា​កត្តា​ចម្បង ដែល​បណ្តាល​ឱ្យ​កើត​មាន​ជំងឺរាក់​ស៊ី​ធ្មេញ​ផង​ដែរ ។', 'http://kohsantepheapdaily.com.kh/article/126111.html', 'http://i.kspg.co/2015/8/6ec5bf52-4f2c-445b-9b07-65bdff8e014b.jpg?w=285&h=170&mode=carve', 'ថ្ងៃព្រហស្បត្ត៏ ទី៦ ខែសីហា ឆ្នាំ២០១៥ ម៉ោង ១៦:៤៨', '0', '0', '0', 't', '2015-08-06', 'f');
