INSERT INTO "tbnews" VALUES ('589', 'B020102', 'B020000', 'ខ្ចី​ម៉ូតូ​ពី​កំពង់ឆ្នាំង​មក​លក់​នៅ​ពោធិ៍សាត់​ត្រូវ​អស់លក្ខណ៍', 'ខេត្តពោធិ៍សាត់​ ៖ ​បុរស​ម្នាក់​បាន​លួច​ទូរស័ព្ទ​ចំនួន​០២​គ្រឿង​ពី​ខេត្តពោធិ៍សាត់​ រួច​ហើយ​គេចខ្លួន​ទៅ​ខេត្តកំពង់ឆ្នាំង​បាត់បាន​ប៉ុន្មាន​ថ្ងៃ​ក្រោយមក​បាន​ទៅ​ខ្ចី​ម៉ូតូ​របស់​ម្ចាស់​សិប្បកម្ម​ផលិត​ទឹកកក​នៅ​ឯ​ខេត្តកំពង់ឆ្នាំង​', 'http://kohsantepheapdaily.com.kh/article/126548.html', 'http://i.kspg.co/2015/8/abc1c9c3-946f-46d6-8ade-22e9f7bd6c98.jpg?w=285&h=170&mode=carve', 'ថ្ងៃពុធ ទី១២ ខែសីហា ឆ្នាំ២០១៥ ម៉ោង ១៤:៣៥', '0', '0', '0', 't', '2015-08-12', 'f');
INSERT INTO "tbnews" VALUES ('590', 'B020102', 'B020000', 'ការិយាល័យ​ប្រឆាំង​គ្រឿងញៀន​ខេត្ត​ចាប់ខ្លួន​ជនសង្ស័យ​ពាក់ព័ន្ធ​គ្រឿងញៀន​បាន​២​នាក់', 'ខេត្តកំពង់ចាម ៖ កម្លាំង​នគរបាល​ការិយាល័យ​ប្រឆាំង​គ្រឿងញៀន​ខេត្តកំពង់ចាម​បាន​ចាប់ខ្លួន​សង្ស័យ​ពាក់ព័ន្ធ​គ្រឿងញៀន​បាន​ចំនួន​២​នាក់​​បន្ត​បន្ទាប់​គ្នា​នៅ​កន្លែង​ផ្សេង​គ្នា ។', 'http://kohsantepheapdaily.com.kh/article/126523.html', 'http://i.kspg.co/2015/8/43b42214-3a6e-462b-8a5c-525706c0ab23.jpg?w=285&h=170&mode=carve', 'ថ្ងៃពុធ ទី១២ ខែសីហា ឆ្នាំ២០១៥ ម៉ោង ១០:៤៦', '0', '0', '0', 't', '2015-08-12', 'f');
INSERT INTO "tbnews" VALUES ('591', 'B020102', 'B020000', 'គ្រប់គ្រង​ជនបរទេស ចំនួន​ជនអន្តោប្រវេសន៍​បាន​កើនឡើង​បន្តបន្ទាប់​ពី​1​ឆ្នាំទៅ​1​ឆ្នាំ', 'រាជធានី​ភ្នំពេញ ៖ លោកនាយ​ឧត្តមសេនីយ៍​សុខ ផល អគ្គនាយក​នៃ​អគ្គនាយកដ្ឋាន​អន្តោប្រវេសន៍​ក្រសួងមហាផ្ទៃ​ឲ្យ​ដឹង​ថា ការ​ធ្វើ​ជំរឿន​ជនបរទេស​នៅ​ក្នុង​ប្រទេស​កម្ពុជា​បាន​ធ្វើ​ឡើង​ចំនួន​៣​ដង​មក​ហើយ និង​បង្ហាញ​ថា', 'http://kohsantepheapdaily.com.kh/article/126510.html', 'http://i.kspg.co/2015/8/f097c14e-be8f-4211-ad81-c519b8bd438a.jpg?w=285&h=170&mode=carve', 'ថ្ងៃពុធ ទី១២ ខែសីហា ឆ្នាំ២០១៥ ម៉ោង ០៩:៥៧', '0', '0', '0', 't', '2015-08-12', 'f');
INSERT INTO "tbnews" VALUES ('592', 'B020102', 'B020000', 'ដេញ​ចេញពី​មុខ​តូប ត្រូវ​ជន​វិកលចរិត​គប់​នឹង​ដុំ​ថ្ម​ដេរ​៥​ថ្នេរ', 'ខេត្តបន្ទាយមានជ័យ ៖ ស្ត្រី​ម្នាក់​ត្រូវ​មនុស្ស​វិកលចរិត​ទាញ​ដុំ​ថ្ម​គប់​បណ្តាល​ឲ្យ​បែកក្បាល​ដេរ​ដល់​ទៅ​៥​ថ្មេ​រ ព្រោះតែ​ដេញ​ជន​វិកល​ច​រិ​ក​នោះ​ឲ្យ​ដើរ​ចេញពី​តូប​លក់ដូរ​របស់​ខ្លួន បង្ក​ឲ្យ​មាន​ភ្ញាក់ផ្អើល​​​កាលពី​វេលា​ម៉ោង​៧ និង​៣០​នាទី​ព្រឹក ថ្ងៃ​ទី​១១ ខែសីហា ឆ្នាំ​២០១៥ នៅ​ចំណុច​ផ្លូវជាតិ​លេខ​៦', 'http://kohsantepheapdaily.com.kh/article/126499.html', 'http://i.kspg.co/2015/8/b2b12f74-f939-4282-b5f7-a0cfcf46b944.jpg?w=285&h=170&mode=carve', 'ថ្ងៃអង្គារ ទី១១ ខែសីហា ឆ្នាំ២០១៥ ម៉ោង ២០:៤៤', '0', '0', '0', 't', '2015-08-11', 'f');
INSERT INTO "tbnews" VALUES ('593', 'B020102', 'B020000', 'ប៉េ​អឹ​ម​ខេត្ត​ឃាត់ខ្លួន​ជនសង្ស័យ​២​នាក់​បញ្ជូន​ទៅ​តុលាការ​ពី​បទ​ជួញដូរ​គ្រឿងញៀន', 'ខេត្តសៀមរាប ៖ កម្លាំង​កងរាជអាវុធហត្ថ​ប្រឆាំង​គ្រឿងញៀន​ខេត្ត​បាន​ធ្វើការ​ចាប់ខ្លួន​ជន​ស​ង្ស័ យ​២​នាក់​នៅ​ចំណុច​ក្នុង​ផ្សារ​ប្រិមប្រិយ កន្លែង​អ៊ិ​ន​ធើ​រ​ណេ​ត ពី​បទ​ជួញដូរ​ដោយ​ខុសច្បាប់​នូវ​សារ​ធាតុ​ញៀន ដែល​ដឹកនាំ​ផ្ទាល់​ដោយ​លោក​វរសេនីយ៍ឯក ស្រី យូ​រ៉ា', 'http://kohsantepheapdaily.com.kh/article/126471.html', 'http://i.kspg.co/2015/8/4fda84a3-815b-4ff8-adef-72370a7adccc.jpg?w=285&h=170&mode=carve', 'ថ្ងៃអង្គារ ទី១១ ខែសីហា ឆ្នាំ២០១៥ ម៉ោង ១៥:២៥', '0', '0', '0', 't', '2015-08-11', 'f');
INSERT INTO "tbnews" VALUES ('594', 'B020102', 'B020000', 'មុន​នេះ​បន្តិច ចោរ​ប្រុស​-​ស្រី​២​នាក់​ឆក់​កាបូប​មិន​បាន​សម្រេច​តាម​បណ្តោយ​មហាវិថី​សហព័ន្ធ​រុ​ស្ស៊ី', 'រាជធានី​ភ្នំពេញ ៖ មុន​នេះ​បន្តិច ម៉ោង​ប្រមាណ​ជា​១​រសៀល ថ្ងៃ​ទី​១១ សីហា ២០១៥ មាន​សកម្មភាព​ចោរឆក់​កាបូប​មិន​បាន​សម្រេច​នៅ​ម្តុំ​តា​កា​ប៉ោង តាម​បណ្តោយ​មហាវិថី​សហព័ន្ធ​រុ​ស្ស៊ី ។', 'http://kohsantepheapdaily.com.kh/article/126450.html', 'http://i.kspg.co/2015/8/b2ec89a0-a666-457f-9c7c-08990f25c57c.jpg?w=285&h=170&mode=carve', 'ថ្ងៃអង្គារ ទី១១ ខែសីហា ឆ្នាំ២០១៥ ម៉ោង ១៣:៥៤', '0', '0', '0', 't', '2015-08-11', 'f');
INSERT INTO "tbnews" VALUES ('595', 'B020102', 'B020000', 'មុននេះបន្តិច​ អ្នកភូមិប្រទះឃើញ​សពបុរសម្នាក់​នៅក្នុងប្រឡាយនៅ​សង្កាត់ព្រៃស', 'រាជធានីភ្នំពេញ ​៖ ​សាកសពបុរសម្នាក់​បានត្រូវអ្នកភូមិប្រទះឃើញ​នៅក្នុងប្រឡាយដែល​មានទឹកមួយកន្លែង ​ស្ថិតក្នុងភូមិព្រៃទីទុយ ​សង្កាត់ព្រៃស ​ខណ្ឌដង្កោ ។ ', 'http://kohsantepheapdaily.com.kh/article/126442.html', 'http://i.kspg.co/2015/8/4e9157aa-d13b-465c-9d9e-bf20461a0120.jpg?w=285&h=170&mode=carve', 'ថ្ងៃអង្គារ ទី១១ ខែសីហា ឆ្នាំ២០១៥ ម៉ោង ១១:៣៦', '0', '0', '0', 't', '2015-08-11', 'f');
INSERT INTO "tbnews" VALUES ('596', 'B020102', 'B020000', 'កម្មករ​រោងចក្រ​ស្បែកជើង ជូ យូ អ៊ី​ន​ដាស​ស្ទ្រី ប្រមូលផ្តុំ​គ្នា​ដុត​កង់​ឡាន​តវ៉ា​រឿង​បើក​ប្រាក់ខែ និង​បង្ខំ​ឱ្យ​ថែម​ម៉ោង', 'រាជធានី​ភ្នំពេញ ៖ មុន​នេះ​បន្តិច​កម្មករ​រោងចក្រ​ស្បែកជើង ជូ យូ អ៊ី​ន​ដាស​ស្ទ្រី ដែល​មា​នទី​តាំងនៅ​តាម​បណ្តោយ​ផ្លូវ​លេខ​១៩៨៦ ស្ថិត​ក្នុងភូមិ​ភ្នំពេញ​ថ្មី សង្កាត់​ភ្នំពេញ​ថ្មី ខណ្ឌ​សែន​សុខ បាន​នាំ​គ្នា​ដុត​កង់​ឡាន​ធ្វើការ​តវ៉ា​ប្រឆាំង​ថៅកែ​រោងចក្រ​ពាក់ព័ន្ធ​រឿង​បើក​ប្រាក់ខែ​មិន​គ្រប់ និង​បង្ខំ​ឱ្យ​កម្មករ​ថែម​ម៉ោង ។', 'http://kohsantepheapdaily.com.kh/article/126424.html', 'http://i.kspg.co/2015/8/eca6700d-af8e-4f98-8877-70448349de5a.jpg?w=285&h=170&mode=carve', 'ថ្ងៃអង្គារ ទី១១ ខែសីហា ឆ្នាំ២០១៥ ម៉ោង ១០:០១', '0', '0', '0', 't', '2015-08-11', 'f');
INSERT INTO "tbnews" VALUES ('597', 'B020102', 'B020000', '​បុរស​ម្នាក់​សង្ស័យបែក​ថ្នាំ​យក​ដុំ​ថ្ម​គប់​អ្នក​យក​ព័ត៌មាន​​កំពុង​ជិះ​ម៉ូតូ​', '​រាជ​ធានីភ្នំពេញ ៖ អ្នក​យក​ព័ត៌មាន​ឱ្យ​ទូរទស្សន៍​​ម្នាក់​កំពុង​​ជិះ​ម៉ូតូ​​​ទៅ​ស្ថានីយ​ ស្រាប់តែ​មាន​ជន​សង្ស័យ​​បែក​ថ្នាំ​ម្នាក់​​​ដើរ​តាម​ផ្លូវ​​​យក​ដុំ​ថ្ម​គប់​​ បណ្តាល​ឲ្យ​បែក​ក្បាំង​ម៉ូតូ តែ​មិន​មាន​រង​របួស​ទេ', 'http://kohsantepheapdaily.com.kh/article/126420.html', 'http://i.kspg.co/2015/8/ef75d67a-2cb2-410e-b321-e18a00cc46db.jpg?w=285&h=170&mode=carve', 'ថ្ងៃអង្គារ ទី១១ ខែសីហា ឆ្នាំ២០១៥ ម៉ោង ០៩:៤៦', '0', '0', '0', 't', '2015-08-11', 'f');
INSERT INTO "tbnews" VALUES ('598', 'B020102', 'B020000', 'ស្រែក​ឆោ​ឡា​ថា​ចោរប្លន់​ម៉ូតូ តែ​សមត្ថកិច្ច​សង្ស័យ​ថា​ជា​រឿងប្រឌិត', 'រាជធានី​ភ្នំពេញ ៖ ស្ត្រី​ម្នាក់​ជិះ​ម៉ូតូ​ទៅ​បង់លុយ​ថ្លៃ​ភ្លើង​ត្រូវ​បាន​ជនសង្ស័យ​ជា​ចោរប្លន់​ម្នាក់​ប្រើ​កូនកាំបិត​ចុង​ស្រួច​ស្ទាក់​ភ្ជង់​ប្លន់​យក​ម៉ូតូ​បាន​សម្រេច បន្ទាប់​មក​បាន​ជិះ​គេច​ខ្លួន​បាត់​ដោយ​សុ​វត្តិភាព ។ ហេតុការណ៍​នេះ​បាន​បង្ក​ឲ្យ​មានការ​ភ្ញាក់ផ្អើល​កាលពី​វេលា​ម៉ោង​២ និង​៣០​នាទី​រសៀល', 'http://kohsantepheapdaily.com.kh/article/126403.html', 'http://i.kspg.co/2015/8/46b9c032-1629-417c-af91-eb9e7480460c.jpg?w=285&h=170&mode=carve', 'ថ្ងៃចន្ទ ទី១០ ខែសីហា ឆ្នាំ២០១៥ ម៉ោង ២០:០៧', '0', '0', '0', 't', '2015-08-10', 'f');
INSERT INTO "tbnews" VALUES ('599', 'B020102', 'B020000', 'ចាប់ខ្លួន​យុវជន​ម្នាក់​ពាក់ព័ន្ធ​បរិហា​កេរ្តិ៍ និង​គំរាម​តាម​ហ្វេ​ស​ប៊ុ​ក', 'ខេត្តបន្ទាយមានជ័យ ៖ យុវជន​ម្នាក់​ព្រោះតែ​ស្រឡាញ់​កូនក្រមុំ​របស់​គេ​មិន​បាន បាន​យក​រូប​អាសអាភាស​កាត់ត និង​គំរាម ព្រមទាំង​បរិហារកេរ្តិ៍​តាម​ប្រព័ន្ធ​ហ្វេ​ស​ប៊ុ​ក ដែល​ធ្វើ​ឲ្យ​ប៉ះពាល់​យ៉ាងធ្ងន់ធ្ងរ​ដល់​កិត្តិយស​គ្រួសារ​ខាង​ស្រី ត្រូវ​បាន​សមត្ថកិច្ច​នគរបាល​ការិយាល័យ​ព្រហ្មទណ្ឌ​កម្រិត​ស្រាល', 'http://kohsantepheapdaily.com.kh/article/126402.html', 'http://i.kspg.co/2015/8/a34d31a4-7fac-4517-b79f-49a91c7ab767.jpg?w=285&h=170&mode=carve', 'ថ្ងៃចន្ទ ទី១០ ខែសីហា ឆ្នាំ២០១៥ ម៉ោង ១៩:៤៧', '0', '0', '0', 't', '2015-08-10', 'f');
INSERT INTO "tbnews" VALUES ('600', 'B020102', 'B020000', 'ឃាត់ខ្លួន​​​ជន​សង្ស័យចែកចាយ​គ្រឿងញៀន​​​​៣​នាក់ ​​ដកហូត​បាន​ថ្នាំ​ញៀន​ចំនួន​២១​កញ្ចប់', '​រាជ​ធានី​ភ្នំពេញ ៖ ​​​​​ជន​សង្ស័យ​​​ជា​​មុខសញ្ញា​​ប្រើ​ប្រាស់ និង​ចែកចាយ​គ្រឿងញៀន​យ៉ាង​សកម្ម​​​ម្នាក់​ ត្រូវ​បាន​​នគរបាល​ប៉ុស្តិ៍​ផ្សារ​ដើម​ថ្កូវ​​ចាប់​ឃាត់ខ្លួន និង​ដកហូត​បាន​ថ្នាំ​ញៀន​​ប្រភេទ​ម៉ាទឹក​កកចំនួន​៨​កញ្ចប់​តូច​​', 'http://kohsantepheapdaily.com.kh/article/126369.html', 'http://i.kspg.co/2015/8/4ef2e32d-68d2-46e3-9b8d-3875da7378ed.jpg?w=285&h=170&mode=carve', 'ថ្ងៃចន្ទ ទី១០ ខែសីហា ឆ្នាំ២០១៥ ម៉ោង ១៥:១៨', '0', '0', '0', 't', '2015-08-10', 'f');
INSERT INTO "tbnews" VALUES ('601', 'B020102', 'B020000', 'អ្នក​លួច​កាប់​គ្រញូង​ត្រូវ​ទាហាន​សៀម​បាញ់​ស្លាប់​4​នាក់', 'ខេត្តឧត្តរមានជ័យ ៖ ប្រជាជន​កម្ពុ​ជាមួយ​ក្រុម​ដែល​ឆ្លង​ដែន​ទៅ​លួច​កាប់​គ្រញូង​ក្នុង​ទឹកដី​ថៃ បាន​ទទួល​រង​នូវ​ការ​បាញ់ប្រហារ​ដូច​បាញ់​សត្វ​ជា​ថ្មី​ទៀត កាលពី​វេលា​ម៉ោង​៤​រសៀល​ថ្ងៃ​ទី​៥ សីហា កន្លង​មកនេះ បង្កើត​បាន​នូវ​មហា​សោកនាដកម្ម​ដល់​អាយុជីវិត​ដ៏​ធ្ងន់ធ្ងរ', 'http://kohsantepheapdaily.com.kh/article/126359.html', 'http://i.kspg.co/2015/8/5a9c5a17-503c-4664-aba2-58d155a1b631.jpg?w=285&h=170&mode=carve', 'ថ្ងៃចន្ទ ទី១០ ខែសីហា ឆ្នាំ២០១៥ ម៉ោង ១៤:៤១', '0', '0', '0', 't', '2015-08-10', 'f');
INSERT INTO "tbnews" VALUES ('602', 'B020102', 'B020000', 'បញ្ជូន​ជនសង្ស័យ​២​នាក់​ទៅ​តុលាការ​ក្រោយ​ឃាត់ខ្លួន​ជាមួយ​ថ្នាំ​ញៀន', 'ខេត្តស្ទឹងត្រែង ៖ កាលពី​ព្រឹក​ថ្ងៃ​ទី​១០​ សីហា​ ២០១៥​ នេះ កម្លាំង​កងរាជអាវុធហត្ថ​បាន​បញ្ជូន​ជនសង្ស័យ​ចំនួន​២​នាក់​ទៅ​តុលាការ ​បន្ទាប់​ពី​ឃាត់ខ្លួន​ពាក់ព័ន្ធ​ការ​ចែកចាយ ​និង​ប្រើប្រាស់​ថ្នាំ​ញៀន', 'http://kohsantepheapdaily.com.kh/article/126353.html', 'http://i.kspg.co/2015/8/b2ba4dfe-dbff-44d6-8364-1c86eb17f391.jpg?w=285&h=170&mode=carve', 'ថ្ងៃចន្ទ ទី១០ ខែសីហា ឆ្នាំ២០១៥ ម៉ោង ១៤:២៥', '0', '0', '0', 't', '2015-08-10', 'f');
INSERT INTO "tbnews" VALUES ('603', 'B020102', 'B020000', 'ស្ត្រី​ខ្មែរ​នៅ​ម៉ា​ទ្បេ​ស៊ី​4​នាក់​និង​នៅ​ចិន​ម្នាក់​ត្រូវ​បាន​ជួយ​អន្តរាគមន៍​បាន​មក​ស្រុក​វិញ', 'រាជធានី​ភ្នំពេញ ៖ យោង​តាម​ប្រភព​ព័ត៌មាន​ពី​លោក ជុំ សុន្ទរី អ្នកនាំពាក្យ​ក្រសួងការបរទេស​និង​សហប្រតិបត្តិការ​អន្តរជាតិ កាលពី​ថ្ងៃ​ទី​៨ សីហា បានឱ្យ​ដឹង​ថា ស្ថាន​ឯកអគ្គរាជទូត​កម្ពុជា​ប្រចាំ​ប្រទេស​ម៉ា​ទ្បេ​ស៊ី បាន​អន្តរាគមន៍​ជួយ​ពល​ការិនី​ខ្មែរ​រង​គ្រោះ​ចំនួន​៤​រូប', 'http://kohsantepheapdaily.com.kh/article/126350.html', 'http://i.kspg.co/news_pic_kh.png?w=285&h=170&mode=carve', 'ថ្ងៃចន្ទ ទី១០ ខែសីហា ឆ្នាំ២០១៥ ម៉ោង ១៤:១៨', '0', '0', '0', 't', '2015-08-10', 'f');
INSERT INTO "tbnews" VALUES ('604', 'B020102', 'B020000', '​នគរបាល​ប៉ុ​ស្តិ៍​វត្ត​ភ្នំ​ឃាត់ខ្លួន​ចោរ​ស្រី​លួច​កាបូបបាន​ម្នាក់', 'រាជ​ធានីភ្នំពេញ ៖ ​​ស្ត្រី​ម្នាក់​ក្នុង​ចំណោម​បក្ខពួក​ពីរ​នាក់​ត្រូវ​កម្លាំង​នគរបាល​ប៉ុស្តិ៍​រដ្ឋបាល​វត្ត​ភ្នំ​ឃាត់ខ្លួន​ ក្រោយ​ពី​ពួក​គេ​​ធ្វើ​សកម្មភាព​បន្លំ​លួច​កាបូប​ និង​ទូរស័ព្ទ​ពី​បុរស​ម្នាក់​', 'http://kohsantepheapdaily.com.kh/article/126349.html', 'http://i.kspg.co/2015/8/445fcef3-b56a-4630-82c0-808733e420c6.jpg?w=285&h=170&mode=carve', 'ថ្ងៃចន្ទ ទី១០ ខែសីហា ឆ្នាំ២០១៥ ម៉ោង ១៤:១៦', '0', '0', '0', 't', '2015-08-10', 'f');
INSERT INTO "tbnews" VALUES ('605', 'B020102', 'B020000', 'បង្ហាញខ្លួន​ទទួលទោស លោក ឈូក បណ្ឌិត និង​លោក លឹ​ម ស្នេហ៍​គំនិត ចេញ​មុខ​សារភាព', 'រាជធានី​ភ្នំពេញ ៖ ទីបំផុត លោក ឈូក បណ្ឌិត អតីត​អភិបាលក្រុង​បា​វិត ដែល​ចេះ​គេចខ្លួន​ជាង​៣​ឆ្នាំ បាន​ចូល​សារភាព​ទទួលទោស គឺ​៥​ថ្ងៃក្រោយ​ពេល​សម្តេច​តេ​ជោ ហ៊ុន សែន បាន​ថ្លែង​ក្នុង​ពិធី​សម្ពោធ​ស្ពាន​មិត្តភាព​កម្ពុជា​-​ចិន នៅ​ក្រុង​តា​ខ្មៅ', 'http://kohsantepheapdaily.com.kh/article/126348.html', 'http://i.kspg.co/2015/8/9707298d-dd57-4d9a-b3e6-aa19c7790daa.jpg?w=285&h=170&mode=carve', 'ថ្ងៃចន្ទ ទី១០ ខែសីហា ឆ្នាំ២០១៥ ម៉ោង ១៤:១៥', '0', '0', '0', 't', '2015-08-10', 'f');
INSERT INTO "tbnews" VALUES ('606', 'B020102', 'B020000', '​ឃាត់ខ្លួន​ស្ត្រី​២​នាក់​ដកហូត​បាន​ថ្នាំ​ញៀន​ចំនួន​៧​កញ្ចប់នៅ​ព្រៃវែង', 'ខេត្ត​ព្រៃវែង ៖ ស្ត្រី​២​នាក់ត្រូវកំលាំង​កងរាជអាវុធហត្ថ​ខេត្តព្រៃវែង​​ចាប់​ឃាត់ខ្លួនជា​បន្ត​បន្ទាប់ បន្ទាប់​ពី​ឆែកឆេរ​រក​ឃើញ​ថ្នាំ​ញៀន​ចំនួន​៧​កញ្ចប់ ស្មើនឹង​១៧១.៣៧​ក្រាមលាក់​តាម​ខ្លួន​របស់​ពួក​គេ​។', 'http://kohsantepheapdaily.com.kh/article/126344.html', 'http://i.kspg.co/2015/8/9a5f9e06-c4b4-4827-b989-8eb063b17a3a.jpg?w=285&h=170&mode=carve', 'ថ្ងៃចន្ទ ទី១០ ខែសីហា ឆ្នាំ២០១៥ ម៉ោង ១៣:៤៧', '0', '0', '0', 't', '2015-08-10', 'f');
INSERT INTO "tbnews" VALUES ('607', 'B020102', 'B020000', 'ចេញពី​ធ្វើការ​មក​ដល់​មុខ​ផ្ទះ​ត្រូវ​ចោរ​ភ្ជង់​ប្លន់​យក​ម៉ូតូ​', '​រាជ​ធានីភ្នំពេញ ​៖ នារី​ម្នាក់​ត្រូវ​​ក្រុម​ចោរ​ភ្ជង់​ប្លន់​យក​ម៉ូតូ​បាន​សម្រេច ​ខ​ណៈ​​ដែល​​នារី​​រង​គ្រោះ​​​ជិះ​ម៉ូតូត្រឡប់​មក​ពី​ធ្វើការ​​​ ​កាលពី​វេលា​ម៉ោង​១១​យប់​ថ្ងៃ​ទី៩​​ សីហា​​ ២០១៥ នៅ​មុខ​ផ្ទះ​​នារី​រង​គ្រោះ', 'http://kohsantepheapdaily.com.kh/article/126312.html', 'http://i.kspg.co/2015/8/a45d1004-0e34-4a82-824a-e92ecf35cca0.jpg?w=285&h=170&mode=carve', 'ថ្ងៃចន្ទ ទី១០ ខែសីហា ឆ្នាំ២០១៥ ម៉ោង ០៩:២៦', '0', '0', '0', 't', '2015-08-10', 'f');
INSERT INTO "tbnews" VALUES ('608', 'B020102', 'B020000', 'ភ្លើង​ប្រច័ណ្ឌប្តី​កំណាច​កាប់​ប្រហារ​ប្រពន្ធ​គ្មាន​ត្រា​ប្រណី និង​កាប់​នគ​បាល​របួសធ្ងន់​២​នាក់​នៅ​ស្វាយ​ជ្រុំ', 'ខេត្តស្វាយរៀង ៖ កាលពី​វេលា​ម៉ោង​៤ និង​៥​នាទី​ល្ងាច ថ្ងៃ​ទី​៩ សីហា ២០១៥ នៅ​ភូមិ​តា​រក្ស ឃុំ​ឈើទាល ស្រុក​ស្វាយ​ជ្រំ មាន​ករណី​ប្តី​កាប់​ប្រហារ​ប្រពន្ធ​យ៉ាង​សាហាវ​បណ្តាល​ឲ្យ​រង​របួស​យ៉ាងធ្ងន់ធ្ងរ ហើយ​ពេល​សមត្ថកិច្ច​នគ​បាល​ចូល​ទៅ​ជួយ', 'http://kohsantepheapdaily.com.kh/article/126301.html', 'http://i.kspg.co/2015/8/5b209394-0f5e-48c9-a941-673fd03ad330.jpg?w=285&h=170&mode=carve', 'ថ្ងៃអាទិត្យ ទី៩ ខែសីហា ឆ្នាំ២០១៥ ម៉ោង ២៣:១៨', '0', '0', '0', 't', '2015-08-09', 'f');
INSERT INTO "tbnews" VALUES ('609', 'B020102', 'B020000', 'បង្ក្រាប​ទីតាំង​ល្បែង​បាញ់​ត្រី​នៅ​ប៉ោយ​ប៉ែ​ត ដកហូត​ទូ​ហ្គេ​ម​៧​ទូ និង​ក្មេង​ៗ២២​នាក់​យក​ទៅ​អប់រំ', 'ខេត្តបន្ទាយមានជ័យ ៖ នគរបាល​ប៉ុស្តិ៍​រដ្ឋបាល​សង្កាត់​ប៉ោយ​ប៉ែ​ត និង​នគរបាល​យុត្តិធម៌ នៃ​អធិការ​នគរបាល​ក្រុង​ប៉ោយ​ប៉ែ​ត​បាន​ធ្វើការ​បង្ក្រាប​ទីតាំង​ល្បែង​បាញ់​ត្រី​មួយ​កន្លែង​កណ្ដាល​ផ្សារ​ហ៊ុយ​ឡេ​ង ស្ថិត​នៅ​ភូមិ​មិត្តភាព សង្កាត់​-​ក្រុង​ប៉ោយ​ប៉ែ​ត', 'http://kohsantepheapdaily.com.kh/article/126300.html', 'http://i.kspg.co/2015/8/b78cdfe7-adef-4615-a9cf-340de1a42afd.jpg?w=285&h=170&mode=carve', 'ថ្ងៃអាទិត្យ ទី៩ ខែសីហា ឆ្នាំ២០១៥ ម៉ោង ២២:៥១', '0', '0', '0', 't', '2015-08-09', 'f');
INSERT INTO "tbnews" VALUES ('610', 'B020101', 'B020000', 'អគ្គ​​រដ្ឋ​ទូត​​បេឡា​រូស​ប្រចាំ​កម្ពុជា​​​សុំ​ជួប​​សម្ភាសន៍​ជាមួយ​កាសែត​៣​ ដែល​ល្បីនៅកម្ពុជា​', 'លិខិត​ជា​ផ្លូវការ​មួយ​របស់​ក្រសួងការបរទេស និង​សហប្រតិបត្តិការ​អន្តរជាតិ ចុះ​ថ្ងៃ​ទី​១២ សីហា ២០១៥ ជម្រាប​មក​ប្រតិភូ​រាជ​រដ្ឋា​ភិ​បាល​ទទួល​បន្ទុក​អគ្គនាយក​ទូរទស្សន៍​ជាតិ​កម្ពុជា លោក​និពន្ធ​នាយក​សារព័ត៌មាន​រស្មី​កម្ពុជា, លោក​និពន្ធ​នាយក​សារ​ព័ត៌​មាន​កោះសន្តិភាព និងលោក​និពន្ធ​នាយក​សារព័ត៌មាន​កម្ពុជា​ថ្មី ដែល​នៅ​ក្នុង​កម្មវត្ថុ​ចែង​ថា ៖', 'http://kohsantepheapdaily.com.kh/article/126555.html', 'http://i.kspg.co/news_pic_kh.png?w=285&h=170&mode=carve', 'ថ្ងៃពុធ ទី១២ ខែសីហា ឆ្នាំ២០១៥ ម៉ោង ១៥:៤៤', '0', '0', '0', 't', '2015-08-12', 'f');
INSERT INTO "tbnews" VALUES ('611', 'B020101', 'B020000', 'សភាពាណិជ្ជកម្ម​អា​មេ​រិ​ក​នៅ​ក្រុង​ហុងកុង​មក​ស្វែង​យល់​ពី​បរិយាកាស​វិនិយោគ​នៅ​កម្ពុជា', 'រាជធានី​ភ្នំពេញ ​៖ ប្រតិភូ​សភាពាណិជ្ជកម្ម​សហរដ្ឋអាមេរិក​​ប្រចាំ​​​ក្រុង​ហុងកុង​ចំនួន​១៦​រូប​បាន​មក​កាន់​កម្ពុជា ដើម្បី​ស្វែង​យល់​​ពី​បរិយា​កាស​វិនិ​យោគ រួម​ទាំង​ប្រ​ព័ន្ធ​​ច្បាប់​ការងារ​ទាំងឡាយ​​នៅ​ប្រទេស​កម្ពុជា​ ។ ', 'http://kohsantepheapdaily.com.kh/article/126550.html', 'http://i.kspg.co/2015/8/807383b3-e9e9-4f3b-9b91-db32abd1ead9.jpg?w=285&h=170&mode=carve', 'ថ្ងៃពុធ ទី១២ ខែសីហា ឆ្នាំ២០១៥ ម៉ោង ១៥:០២', '0', '0', '0', 't', '2015-08-12', 'f');
INSERT INTO "tbnews" VALUES ('612', 'B020101', 'B020000', '​លើក​ពេល​ប្រកាសសាល​ក្រម​​សំណុំ​រឿង​រួម​គំនិត​ក្បត់​ផ្តួល​រំលំ​រដ្ឋា​ភិ​បាលទៅ​ថ្ងៃ​ទី​២៦​ សីហា', 'រាជ​ធានី​ភ្នំពេញ ​៖ ​ចៅក្រម​តុលាការ​កំពូល​កាល​ពី​ព្រឹក​ថ្ងៃ​ទី​១២​ សីហា​ ​២០១៥​ នេះបាន​បញ្ជាក់​សវ​នាកា​រលើ​បណ្តឹងសុំ​នៅ​ក្រៅ​ឃុំ​របស់​បុរស​ជាប់ចោទ​ចំនួន​១១​នាក់​ដែល​តុលាការ​បាន​ចោទ​ពី​បទ​រួមគំនិត​ក្បត់', 'http://kohsantepheapdaily.com.kh/article/126549.html', 'http://i.kspg.co/2015/8/e6a3c950-091c-4994-94ae-39b3118d5d9d.jpg?w=285&h=170&mode=carve', 'ថ្ងៃពុធ ទី១២ ខែសីហា ឆ្នាំ២០១៥ ម៉ោង ១៤:៥០', '0', '0', '0', 't', '2015-08-12', 'f');
INSERT INTO "tbnews" VALUES ('613', 'B020101', 'B020000', 'អាជ្ញាធរ​ខណ្ឌ​ពោធិ៍​សែន​ជ័យ​​​រៀបចំ​​វេទិកាសាធារណៈ​នៅ​ក្នុង​វត្ត​អង្គ​បឹង​ចក', 'រាជ​ធានីភ្នំពេញ ​៖ អាជ្ញាធរ​ខណ្ឌ​ពោធិ៍​សែន​ជ័យ បាន​រៀបចំ​ធ្វើ​វេទិកាសាធារណៈ​មួយដើម្បី​ឲ្យ​ប្រជាពលរដ្ឋ​បានបញ្ចេញ​មតិយោបល់ពី​គោលបំណង និង​សំណូមពរ​ផ្សេង​ៗ​ដែល​ពួក​គាត់ត្រូវការ​បញ្ចេញ​', 'http://kohsantepheapdaily.com.kh/article/126547.html', 'http://i.kspg.co/2015/8/4ce6c871-ed9e-49ca-b795-6af81f5a1c6f.jpg?w=285&h=170&mode=carve', 'ថ្ងៃពុធ ទី១២ ខែសីហា ឆ្នាំ២០១៥ ម៉ោង ១៤:២៩', '0', '0', '0', 't', '2015-08-12', 'f');
INSERT INTO "tbnews" VALUES ('614', 'B020101', 'B020000', 'អាជ្ញាធរ​ខេត្ត​កំពុង​មាន​ចំណាត់ការ​លើ​រថយន្ត​ដឹកអុស ខណៈ​បាន​បង្ក​ឱ្យ​ទ្រព្យសម្បត្តិ​រដ្ឋ​ត្រូវ​ខូចខាត', 'ខេត្តក្រចេះ ៖ អាជ្ញាធរ​ខេត្តក្រចេះ​បាន​អះអាង​ថា មានទ្រព្យ​សម្បត្តិ​រដ្ឋ​មួយ​ចំនួន ដូច​ជា​បង្កាន់ដៃ​ស្ពានបេតុង​, ផ្លូវ​កៅស៊ូ និង​ស្លាក​ប៉ាណូ​ចរាចរណ៍​ត្រូវ​បាន​ទទួល​រង​នូវ​ការ​ខូចខាត ដែល​បង្ក​ឡើង​ដោយ​រថយន្ត​យី​ឌុ​ប​ដឹកអុស​បើក​បំ​បុក បណ្តាល​ឱ្យ​ឆាបឆេះ​ខូច​ខាត គិត​អស់​ជា​ច្រើន​ម៉ឺន​ដុល្លារ ។', 'http://kohsantepheapdaily.com.kh/article/126546.html', 'http://i.kspg.co/2015/8/249f902f-69ca-4834-a792-a922cf2b68c9.jpg?w=285&h=170&mode=carve', 'ថ្ងៃពុធ ទី១២ ខែសីហា ឆ្នាំ២០១៥ ម៉ោង ១៤:២២', '0', '0', '0', 't', '2015-08-12', 'f');
INSERT INTO "tbnews" VALUES ('615', 'B020101', 'B020000', '​​ឃាត់ខ្លួន​មនុស្ស​៦​នាក់​​​ពាក់​ព័ន្ធ​ពី​បទ​លួច​ប្រេង​គ្រឿងចក្រ​ក្រុមហ៊ុន', 'ខេត្ត​កំពង់ស្ពឺ ៖​ យ៉ាង​ហោច​ណាស់​មនុស្ស​៦​នាក់​ និង​ប្រេង​ពីរ​ប៊ីដុង​ត្រូវ​បាន​សមត្ថកិច្ច​ឃាត់​ បន្ទាប់​ពី​ក្រុម​មនុស្ស​ទាំងនេះ​កំពុង​ធ្វើ​សកម្មភាព​បូម​ប្រេង​ចេញពី​គ្រឿងចក្រ​របស់​ក្រុមហ៊ុន​។', 'http://kohsantepheapdaily.com.kh/article/126545.html', 'http://i.kspg.co/2015/8/b5c173d8-ad5e-40bd-8059-813c136d18b7.jpg?w=285&h=170&mode=carve', 'ថ្ងៃពុធ ទី១២ ខែសីហា ឆ្នាំ២០១៥ ម៉ោង ១៤:២០', '0', '0', '0', 't', '2015-08-12', 'f');
INSERT INTO "tbnews" VALUES ('616', 'B020101', 'B020000', 'បើសិន​ជាអាជ្ញាធរនៅ​តែ​​ធ្វើ​មិន​ដឹង​មិន​ឮ​ បឹង​​​​​កំសាន្ត​ធម្ម​ជាតិ​​​ នឹង​ត្រូវ​បាត់​បង់​មិន​ខាន', 'ខេត្តរតនគិរី ៖ បឹង​ធម្មជាតិ​មួយ​​​ឈ្មោះ​ បឹងកំសាន្ត​ ​​​ស្ថិតនៅ​​ក្នុង​ភូមិ​អូរកន្សែង សង្កាត់​បឹង​កន្សែង ​ក្រុង​បានលុង ប​ច្ចុប្ប​ន្ន​​កំពុង​ទទួល​រង​នូវ​​ការ​ចាក់​ដី​លុប និង​សាងសង់​ផ្ទះសំណាក់ ភោ​ជនី​យដ្ឋា​ន លយ​ចូល​ក្នុង​បឹង​ជា​ច្រើន​ម៉ែត្រ', 'http://kohsantepheapdaily.com.kh/article/126544.html', 'http://i.kspg.co/2015/8/0c78e986-60f6-4f7f-9b5f-039938b94f26.jpg?w=285&h=170&mode=carve', 'ថ្ងៃពុធ ទី១២ ខែសីហា ឆ្នាំ២០១៥ ម៉ោង ១៤:១៤', '0', '0', '0', 't', '2015-08-12', 'f');
INSERT INTO "tbnews" VALUES ('617', 'B020101', 'B020000', '​អ្នកទោស​ខ្មែរ​ម្នាក់​ត្រូវ​អាជ្ញាធរ​ថៃ​ដោះ​លែង​បញ្ជូន​ឲ្យ​អាជ្ញាធរ​កម្ពុជា​វិញ​', 'ខេត្ត​ឧត្តរមានជ័យ ៖ អាជ្ញាធរ​ថៃ​បាន​ធ្វើការ​ដោះ​លែង​អ្នកទោស​ខ្មែរ​ម្នាក់​ប្រគល់​ឲ្យ​អាជ្ញាធរ​កម្ពុជាតាម​មាត់ច្រក​ទ្វារ​អន្តរជាតិ​ជាំសា​ងាំ ​កាលពី​រសៀល​ ថ្ងៃ​ទី​១១ ​សីហា​ ២០១៥​​ ​ក្នុងភូមិ​ជើងភ្នំ ឃុំ​ត្រពាំង​ប្រី​យ៍ ', 'http://kohsantepheapdaily.com.kh/article/126542.html', 'http://i.kspg.co/2015/8/ab2fa3e1-9f54-4da7-b5bc-dcda23f01104.jpg?w=285&h=170&mode=carve', 'ថ្ងៃពុធ ទី១២ ខែសីហា ឆ្នាំ២០១៥ ម៉ោង ១៤:០៧', '0', '0', '0', 't', '2015-08-12', 'f');
INSERT INTO "tbnews" VALUES ('618', 'B020101', 'B020000', 'នារីលក់ចាប់ហួយ​ម្នាក់​ឆ្លៀត​​លួច​ជួញដូរគ្រឿងញៀន​ ត្រូវ​ប៉េអឹម​ក្របួចបាន​', 'ខេត្តកំពត ៖ បញ្ហា​គ្រឿងញៀន គឺជា​ការ​ព្រួយបារម្ភ​យ៉ាង​ខ្លាំង ចំពោះ​ប្រជាពលរដ្ឋ​គ្រប់​មជ្ឈដ្ឋាន​ទូទៅ​នៅ​ក្នុង​ក្រុង​កំពត ក៏​ដូច​ជា​នៅ​ទូ​ទាំង​ខេត្ត ដែល​បច្ចុប្បន្ន​កំពុង​វាយលុក​យ៉ាង​ខ្លាំង ។', 'http://kohsantepheapdaily.com.kh/article/126539.html', 'http://i.kspg.co/2015/8/1b99647f-6996-4fdd-b7a1-211c51edee93.jpg?w=285&h=170&mode=carve', 'ថ្ងៃពុធ ទី១២ ខែសីហា ឆ្នាំ២០១៥ ម៉ោង ១៤:០៣', '0', '0', '0', 't', '2015-08-12', 'f');
INSERT INTO "tbnews" VALUES ('619', 'B020101', 'B020000', 'បង្ក្រាប​ទីតាំង​ហ្គេ​ម​បាញ់​ត្រី​ខុសច្បាប់​នៅ​សង្កាត់​ចាក់​អង្រែ​លើ ឃាត់ខ្លួន​មនុស្ស​ប្រុស​ស្រី​៥​នាក់', 'រាជធានី​ភ្នំពេញ ៖ កាលពី​​​ល្ងាច​ថ្ងៃ​ទី​១១ ​សីហា ​​២០១៥ កម្លាំង​អាជ្ញាធរ​ចម្រុះ​ខណ្ឌមាន​ជ័យ ដឹកនាំ​ដោយ​លោក​ឌី រ័​ត្ន​ខេ​ម​រុណ អភិបាលរង​ខណ្ឌ ដោយ​មានការ​សម្របសម្រួល​ពី​ព្រះរាជអាជ្ញារ​រង​អម​សាលាដំបូង​', 'http://kohsantepheapdaily.com.kh/article/126538.html', 'http://i.kspg.co/2015/8/65547eb8-72b6-4c61-85d7-9963c0ca3fcc.jpg?w=285&h=170&mode=carve', 'ថ្ងៃពុធ ទី១២ ខែសីហា ឆ្នាំ២០១៥ ម៉ោង ១៤:០០', '0', '0', '0', 't', '2015-08-12', 'f');
INSERT INTO "tbnews" VALUES ('620', 'B020101', 'B020000', 'តំណាង​ពលរដ្ឋ​៣៨​គ្រួសារ​ផ្តិត​មេដៃ ប្តឹង​អាជ្ញាធរ​មូលដ្ឋាន​ឃុបឃិត​ឈូស​ឆាយដី​លក់​ឱ្យ​ឈ្មួញ', 'ខេត្តកំពង់ធំ ៖ តំណាង​ប្រជាពលរដ្ឋ​ចំនួន​៣៨​គ្រួសារ នាំ​គ្នា​ផ្តឹ​ត​មេដៃ​ដាក់​ពាក្យបណ្តឹង​ថា អាជ្ញាធរ​ឃុំ​-​ស្រុក​-​ខេត្ត និង​មន្ទីរ​ជំនាញ​ឃុបឃិត​គ្នា​ឱ្យ​គ្រឿងចក្រ​ឈូស​ឆាយដី​លើ​ដំណាំ​របស់​ពួក​គាត់​លក់​ឱ្យ​ឈ្មួញ ។', 'http://kohsantepheapdaily.com.kh/article/126532.html', 'http://i.kspg.co/2015/8/034a2914-3471-4cdd-be09-ec7bbcc1c66b.jpg?w=285&h=170&mode=carve', 'ថ្ងៃពុធ ទី១២ ខែសីហា ឆ្នាំ២០១៥ ម៉ោង ១៣:៤៧', '0', '0', '0', 't', '2015-08-12', 'f');
INSERT INTO "tbnews" VALUES ('621', 'B020101', 'B020000', 'សមត្ថ​កិច្ច​ចម្រុះកែវ​សីមា​ស្ទាក់​ចាប់​បាន​រថយន្ត​កាមរីដឹក​សាច់​សត្វ​ព្រៃជាង​១០០​គីឡូ​ក្រាម', 'ខេត្ត​មណ្ឌល​គិរី ៖ បុរស​បី​នាក់​រួម​និង​សាច់សត្វ​ព្រៃ​ចំនួន​១៤៩​គីឡូ​ក្រាម​ត្រូវ​បាន​កម្លាំង​ជំនាញចាប់​បង្ក្រាប​នៅ​តាម​បណ្តោយ​ផ្លូវជាតិ​លេខ​៧៦ មុន​នេះ​បន្តិច នៅ​មុខ​ស្នាក់​ការ​អភិរក្ស​ជីវចម្រុះ​សីមា ', 'http://kohsantepheapdaily.com.kh/article/126528.html', 'http://i.kspg.co/2015/8/e1c31c57-e031-4266-90c8-85584abd5ab1.jpg?w=285&h=170&mode=carve', 'ថ្ងៃពុធ ទី១២ ខែសីហា ឆ្នាំ២០១៥ ម៉ោង ១៣:១០', '0', '0', '0', 't', '2015-08-12', 'f');
INSERT INTO "tbnews" VALUES ('622', 'B020101', 'B020000', '​​​អ្នក​​បេះ​ត្រកួន​ប្រទះ​ឃើញ​​សព​​​អណ្តែក​​ក្នុង​ប្រឡាយ​ទឹក', '​ខេត្តបន្ទាយមានជ័យ ៖ ​សព​បុរស​ម្នាក់​ ត្រូវ​បាន​អ្នក​បេះ​ត្រកួន​ប្រទះ​ឃើញ​អណ្តែត​ក្នុង​ប្រឡាយ​​ទឹក​ កាល​ពី​​វេលា​ម៉ោង​១រសៀល​​ថ្ងៃ​ទី​១១ ​សីហា​ ២០១៥ ​​ស្ថិត​​នៅ​ក្នុងភូមិ​គីឡូ​ម៉ែ​ត្រ​​​លេខ​៤​ សង្កាត់​ផ្សារ​កណ្តាល', 'http://kohsantepheapdaily.com.kh/article/126527.html', 'http://i.kspg.co/2015/8/a8606c47-7f5e-4dca-b747-b0debbf3a328.jpg?w=285&h=170&mode=carve', 'ថ្ងៃពុធ ទី១២ ខែសីហា ឆ្នាំ២០១៥ ម៉ោង ១២:២៨', '0', '0', '0', 't', '2015-08-12', 'f');
INSERT INTO "tbnews" VALUES ('623', 'B020101', 'B020000', 'អ្នកភូមិ៤៣គ្រួសារ​ លើកគ្នាតវ៉ាដីធ្លី​នៅមុខសាលា​ក្រុងប៉ោយប៉ែត', 'ខេត្តបន្ទាយមានជ័យ ៖​ ក្រុមអ្នកភូមិបាលិលេយ្យ​ចំនួន៤៣គ្រួសារ​បានលើកគ្នាមកតវ៉ានៅ​មុខសាលាក្រុងប៉ោយប៉ែត ​ដើម្បីឲ្យអាជ្ញាធរជួយ​ដោះស្រាយ​គោលនយោបាយនិង​ស្នើឲ្យមានបើកផ្លូវ ។ ', 'http://kohsantepheapdaily.com.kh/article/126526.html', 'http://i.kspg.co/2015/8/e05d83d2-2e98-47b2-8a05-898b72caa573.jpg?w=285&h=170&mode=carve', 'ថ្ងៃពុធ ទី១២ ខែសីហា ឆ្នាំ២០១៥ ម៉ោង ១១:១៦', '0', '0', '0', 't', '2015-08-12', 'f');
