INSERT INTO "tbnews" VALUES ('625', 'B020101', 'B020000', 'អាជ្ញា​ធរ​ខណ្ឌ​ជ្រោយ​ចង្វារ​​កោះអញ្ជើញ​​​ម្ចាស់​ភោជនីយ​ដ្ឋាន​ក្នុង​មូល​ដ្ឋានមក​ណែ​នាំ​', 'រាជ​ធានី​ភ្នំពេញ​ ៖ តាម​សេចក្តី​រាយការណ៍​បាន​ឱ្យ​ដឹង​ថា នៅ​ព្រឹក​ថ្ងៃ​ទី​១២ សីហា នេះ អាជ្ញា​ធរ​ខណ្ឌ​ជ្រោយ​ចង្វារ​បាន​អញ្ជើញ​ប្រជា​ពលរដ្ឋ​ជា​ម្ចាស់​អាជីវ​កម្ម​ក្នុង​មូល​ដ្ឋាន​តាម​ផ្លូវ​ជាតិ​លេខ៦', 'http://kohsantepheapdaily.com.kh/article/126521.html', 'http://i.kspg.co/2015/8/9832f980-4b03-4ed0-8408-54a4518a71ac.jpg?w=285&h=170&mode=carve', 'ថ្ងៃពុធ ទី១២ ខែសីហា ឆ្នាំ២០១៥ ម៉ោង ១០:៣៨', '0', '0', '0', 't', '2015-08-12', 'f');
INSERT INTO "tbnews" VALUES ('626', 'B020101', 'B020000', 'ថ្ងៃ​កើតហេតុ​កញ្ញា​សា​សា​ផឹក​ស៊ីនៅ​ហាង​តោ​2​បាត់ដំបង', 'រាជធានី​ភ្នំពេញ ៖ ជន​២​នាក់​ដំបូង​ដែល​ខកខាន មិន​បាន​ចូល​ទៅ​ឆ្លើយ​បំភ្លឺ​នៅ​ចំពោះ​មុខ​តំណាង​អយ្យការ តាម​កាលកំណត់​កាលពីរសៀល​ថ្ងៃ​ទី​១០ សីហា ជុំវិញ​សំណុំរឿង​គ្រោះថ្នាក់​ចរាចរណ៍​មួយ​ដែល​គេ​សង្ស័យ​ថា បង្ក​ឡើង​ដោយ​កញ្ញា​សា​សា', 'http://kohsantepheapdaily.com.kh/article/126520.html', 'http://i.kspg.co/2015/8/1f5bbaa6-9ab5-432b-8cb1-4fd50b4a0c7c.jpg?w=285&h=170&mode=carve', 'ថ្ងៃពុធ ទី១២ ខែសីហា ឆ្នាំ២០១៥ ម៉ោង ១០:៣០', '0', '0', '0', 't', '2015-08-12', 'f');
INSERT INTO "tbnews" VALUES ('627', 'B020101', 'B020000', '​កើត​រោគសរសៃប្រសាទចេះ​តែ​ស្រមៃឃើញ​គេ​ស្តី​ឱ្យ តូច​ចិត្ត​យក​កាំ​បិត​​ចាក់ខ្លួន​ឯងធ្លាយ​​ពោះវៀន​​​', 'ខេត្តស្វាយរៀង ​៖ ​​​យុវជន​ម្នាក់​កើតរោគ​សរសៃប្រសាទចេះតែ​​ស្រ​មើស្រមៃ​ឃើញ​ឪពុក​ស្តីបន្ទោស​ពេល​ញ៉ាំ​បាយ​ ​​ឃើញ​ជីតា​ស្តីបន្ទោស​ថា​ ដើរ​គេច​សាលា​មិន​ចូល​រៀន ​និង​ឃើញ​ព្រះសង្ឃ​ស្តីបន្ទោស​ថា​ មិន​ចេះ​', 'http://kohsantepheapdaily.com.kh/article/126518.html', 'http://i.kspg.co/2015/8/ce0e67fb-4539-413a-b81d-697661b5e6dd.jpg?w=285&h=170&mode=carve', 'ថ្ងៃពុធ ទី១២ ខែសីហា ឆ្នាំ២០១៥ ម៉ោង ១០:១៨', '0', '0', '0', 't', '2015-08-12', 'f');
INSERT INTO "tbnews" VALUES ('628', 'B020101', 'B020000', 'អាជ្ញាធរ​ខណ្ឌ​ចុះ​ឃាត់​ការ​វាយ​កម្ទេច​អតីត​ភូមិ​គ្រឹះ​ឧកញ៉ា វេ​ង តិច​ឡា', 'រាជធានី​ភ្នំពេញ ៖ អតីត​ភូមិ​គ្រឹះ​របស់​ឧកញ៉ា វេ​ង តិច​ឡា និង​ភរិយា​ឈ្មោះ លី ពៅ ជា​ម្ចាស់​វិមាន​សុភមង្គល​ដែល​មានរឿង​អាស្រូវ​វាយ​ធ្វើ​ទារុណកម្ម​លើ​កុមារ​កាលពី​៣​ឆ្នាំ​កន្លង​ទៅ ហើយ​មាន​វាសនា​ខ្ពស់​មិន​ត្រូវ​បាន​សមត្ថកិច្ច​ចាប់ខ្លួន​ដូច​អ្នក​ផ្សេង​ទៀត ', 'http://kohsantepheapdaily.com.kh/article/126515.html', 'http://i.kspg.co/2015/8/3ce56cb8-7156-4560-b52f-5e76401c154a.jpg?w=285&h=170&mode=carve', 'ថ្ងៃពុធ ទី១២ ខែសីហា ឆ្នាំ២០១៥ ម៉ោង ១០:១២', '0', '0', '0', 't', '2015-08-12', 'f');
INSERT INTO "tbnews" VALUES ('629', 'B020101', 'B020000', 'សិស្ស​វិទ្យាល័យ ហ៊ុន សែន អង្គ​សុរភី ជិត​10​នាក់​ទាមទារ​ឱ្យ​បាន​ចូល​ប្រ​ទ្ប​ង​បាក់​ឌុ​ប', 'ខេត្តកំពត ៖ សិស្ស​នៅ​វិទ្យាល័យ​ហ៊ុន សែន អង្គ​សុរភី ស្រុក​កំពង់ត្រាច​ជិត​១០​នាក់ បាន​ខកចិត្ត​ដោយសារ​តែ​ពួក​គេ​មិន​មានឈ្មោះ​ប្រ​ទ្ប​ង​សញ្ញាបត្រ​មធ្យមសិក្សា​ទុតិយភូមិ​(​បាក់​ឌុ​ប​)​ឆ្នាំ​នេះ​។ ', 'http://kohsantepheapdaily.com.kh/article/126506.html', 'http://i.kspg.co/2015/8/e4c79188-49f9-4353-aab5-a18113d58684.jpg?w=285&h=170&mode=carve', 'ថ្ងៃពុធ ទី១២ ខែសីហា ឆ្នាំ២០១៥ ម៉ោង ០៩:៤៧', '0', '0', '0', 't', '2015-08-12', 'f');
INSERT INTO "tbnews" VALUES ('630', 'B020101', 'B020000', 'ក្រសួង​​មហាផ្ទៃ​បណ្តេញ​ជន​អន្តោប្រវេស​ន៍​ខុស​ច្បាប់​ចំនួន​៥២នាក់​​ចេញពី​កម្ពុជា​នៅ​ព្រឹ​កនេះ​', '​រាជ​ធានី​ភ្នំពេញ ៖ តាម​សេចក្តី​រាយការណ៍ពី​អ្នក​ឆ្លើយ​ឆ្លង​ព័ត៌មាន បាន​ឱ្យ​ដឹង​ថា ​​នៅ​ព្រឹក​ថ្ងៃទី១២​ សីហា នេះ អគ្គ​នាយក​ដ្ឋាននគរ​បាល​​ជន​អន្តោ​ប្រវេសន៍​នៃ​ក្រសួង​មហាផ្ទៃ បាន​ធ្វើការ​បណ្តេញ​ជន​អន្តោប្រវេស​ន៍', 'http://kohsantepheapdaily.com.kh/article/126505.html', 'http://i.kspg.co/2015/8/c43b626d-6ba9-41f0-855a-1f0ed035ceaf.jpg?w=285&h=170&mode=carve', 'ថ្ងៃពុធ ទី១២ ខែសីហា ឆ្នាំ២០១៥ ម៉ោង ០៩:៤៥', '0', '0', '0', 't', '2015-08-12', 'f');
INSERT INTO "tbnews" VALUES ('631', 'B020101', 'B020000', 'រដ្ឋ​មន្ត្រី​ក្រសួង​អប់រំ​ចេញ​សេចក្តី​ណែ​នាំ​ឱ្យ​ទប់​ស្កាត់​សកម្ម​ភាព​នយោ​បាយ​នៅ​តាម​គ្រឹះ​ស្ថាន​សិក្សា', 'រដ្ឋ​មន្ត្រី​ក្រសួង​អប់រំ​ចេញ​សេចក្តី​ណែ​នាំ​ឱ្យ​ទប់​ស្កាត់​សកម្ម​ភាព​នយោ​បាយ​នៅ​តាម​គ្រឹះ​ស្ថាន​សិក្សា', 'http://kohsantepheapdaily.com.kh/article/126504.html', 'http://i.kspg.co/2015/8/b8746c5f-7f44-437f-b627-1c8307193beb.jpg?w=285&h=170&mode=carve', 'ថ្ងៃពុធ ទី១២ ខែសីហា ឆ្នាំ២០១៥ ម៉ោង ០៩:៤២', '0', '0', '0', 't', '2015-08-12', 'f');
