INSERT INTO "tbnews" VALUES ('633', 'B020101', 'B020000', 'គណ​បក្ស​ប្រជា​ធិប​តេយ្យ​មូល​ដ្ឋាន​បើក​សិក្ខា​សាលា​ផ្សព្វ​ផ្សាយ​អំពី​គណ​បក្ស​ខ្លួន​នៅ​ថ្ងៃទី​១៥ សីហា', 'គណ​បក្ស​ប្រជា​ធិប​តេយ្យ​មូល​ដ្ឋាន​បើក​សិក្ខា​សាលា​ផ្សព្វ​ផ្សាយ​អំពី​គណ​បក្ស​ខ្លួន​នៅ​ថ្ងៃទី​១៥ សីហា', 'http://kohsantepheapdaily.com.kh/article/126502.html', 'http://i.kspg.co/2015/8/879fb413-d298-405e-bda6-e11e2d6f597d.jpg?w=285&h=170&mode=carve', 'ថ្ងៃពុធ ទី១២ ខែសីហា ឆ្នាំ២០១៥ ម៉ោង ០៩:១១', '0', '0', '0', 't', '2015-08-12', 'f');
INSERT INTO "tbnews" VALUES ('634', 'B020101', 'B020000', 'សិក្ខាសាលា​ផ្សព្វផ្សាយ​របកគំហើញ នៃ​ការ​សិក្សា​ស្រាវជ្រាវ​ថ្នាក់​ជាតិ​ស្តី​ពី​អំពើ​ហិង្សា​លើ​កុមារ', 'ខេត្តសៀមរាប ៖ តាម​ប្រសាសន៍​របស់​លោក​ជំទាវ ទេស ច័ន្ទ​សា​រឿ​ន អនុរដ្ឋលេខាធិការ​ក្រសួង​កិច្ចការ​នារី នៅ​ក្នុង​ពិធី​ផ្សព្វផ្សាយ​របកគំហើញ នៃ​ការ​សិក្សា​ស្រាវជ្រាវ​ថ្នាក់​ជាតិ​ស្តី​ពី​អំពើ​ហិង្សា​លើ​កុមារ​នៅ​កម្ពុជា​ឆ្នាំ​២០១៣ ដែល​ធ្វើ​ឡើង​នៅ​រដ្ឋបាល​សាលាខេត្ត​សៀមរាប', 'http://kohsantepheapdaily.com.kh/article/126500.html', 'http://i.kspg.co/2015/8/f21ed7e5-172e-4575-aab4-c0d1914c15df.jpg?w=285&h=170&mode=carve', 'ថ្ងៃអង្គារ ទី១១ ខែសីហា ឆ្នាំ២០១៥ ម៉ោង ២០:៥៩', '0', '0', '0', 't', '2015-08-11', 'f');
INSERT INTO "tbnews" VALUES ('635', 'B020401', 'B020000', 'តម្លៃ​ទំនិញប្រើប្រាស់​ប្រចាំ​ថ្ងៃស​ប្តា​ហ៍​ទី​2​នៃ​ខែសីហា 2015', 'រាជធានី​ភ្នំពេញ ៖ តម្លៃ​ទំនិញប្រើប្រាស់​ប្រចាំ​ថ្ងៃ​នៅ​ផ្សារ​ធំ​ថ្មី​ក្នុង​ស​ប្តា​ហ៍​ទី​2​នៃ​ខែសីហា ២០១៥ នេះ មុខទំនិញ​ប្រភេទ​គ្រឿង​សមុទ្រ មិន​មាន​អ្វី​ប្រែប្រួល​ទេ ។ ចំណែក​សាច់​ជ្រូក​-​គោ បន្លែ', 'http://kohsantepheapdaily.com.kh/article/126249.html', 'http://i.kspg.co/2015/8/3c9d3a44-66fe-42df-95b3-1d3e403642a3.jpg?w=285&h=170&mode=carve', 'ថ្ងៃអាទិត្យ ទី៩ ខែសីហា ឆ្នាំ២០១៥ ម៉ោង ១០:៥០', '0', '0', '0', 't', '2015-08-09', 'f');
INSERT INTO "tbnews" VALUES ('636', 'B020401', 'B020000', 'គិត​ត្រឹម​ខែកក្កដា 2015 ខេត្ត​ចំនួន​12 រង​ផល​ប៉ះពាល់​ដោយ​គ្រោះ​ធម្មជាតិ', 'រាជធានី​ភ្នំពេញ ៖ ផ្ទៃដី​សរុប​ចំនួន​១៥៤.៤៥៣​ហិ​កតា ក្នុង​នោះ​មាន​សំណាប​ជាង​២​ពាន់​ហិ​កតា សន្ទូង និង​ស្រូវពង្រោះ​ចំនួន​១៥២.៥០៩​ហិ​កតា ត្រូវ​រង​ផល​ប៉ះពាល់​ដោយសារ​ខ្វះខាត​ទឹក ។', 'http://kohsantepheapdaily.com.kh/article/126248.html', 'http://i.kspg.co/2015/8/db8dbd78-ccc4-48c9-a4cf-18ea44936438.jpg?w=285&h=170&mode=carve', 'ថ្ងៃអាទិត្យ ទី៩ ខែសីហា ឆ្នាំ២០១៥ ម៉ោង ១០:៤៣', '0', '0', '0', 't', '2015-08-09', 'f');
INSERT INTO "tbnews" VALUES ('637', 'B020401', 'B020000', 'ម្រេច​កំពត​នឹង​រក្សា​តម្លៃ​ថេរ​ចាប់ពី​បច្ចុប្បន្ន​រហូត​ដល់​2 ទៅ​3​ឆ្នាំ​ខាង​មុខ', 'រាជធានី​ភ្នំពេញ ៖ រយៈពេល​ប៉ុន្មាន​ឆ្នាំ​មកនេះ ទីផ្សារ​ម្រេច​ខេត្តកំពត​ដូច​ជា​មាន​ហាងឆេង​ខ្លាំង ដែល​កាលពីដើម​ឡើយ​ប្រជា​កសិករ​ដាំ​ម្រេច​តិចតួច​បំផុត និង​ទុក​សម្រាប់​ធ្វើ​ជា​គ្រឿងទេស​ប៉ុណ្ណោះ និង​មិន​ទាន់​មាន​ទីផ្សារ​នៅឡើយ​។ ', 'http://kohsantepheapdaily.com.kh/article/126247.html', 'http://i.kspg.co/2015/8/13a94b61-ae06-42e4-93cb-c3e4edd95969.jpg?w=285&h=170&mode=carve', 'ថ្ងៃអាទិត្យ ទី៩ ខែសីហា ឆ្នាំ២០១៥ ម៉ោង ១០:៣៦', '0', '0', '0', 't', '2015-08-09', 'f');
INSERT INTO "tbnews" VALUES ('638', 'B020401', 'B020000', 'តម្លៃ​ទំនិញប្រើប្រាស់​ប្រចាំ​ថ្ងៃ​ស​ប្តា​ហ៍​ទី​2​នៃ​ខែកក្កដា 2015', 'រាជធានី​ភ្នំពេញ ៖ តម្លៃ​ទំនិញប្រើប្រាស់​ប្រចាំ​ថ្ងៃ​នៅ​ផ្សារ​ធំ​ថ្មី​ក្នុង​ស​ប្តា​ហ៍​ទី​២ ខែកក្កដា ២០១៥ នេះ មុខទំនិញ គ្រឿង​សមុទ្រ សាច់​ជ្រូក សាច់​គោ បន្លែ និង​គ្រឿងទេស ឃើញ​ថា ប្រែប្រួល​តិចតួច ។ សូម​មើល​តារាង​ខាងក្រោម ៖', 'http://kohsantepheapdaily.com.kh/article/124082.html', 'http://i.kspg.co/2015/7/0c67fb14-6407-48b2-80ac-c71a704c35ef.jpg?w=285&h=170&mode=carve', 'ថ្ងៃអាទិត្យ ទី១២ ខែកក្កដា ឆ្នាំ២០១៥ ម៉ោង ១០:៥០', '0', '0', '0', 't', '2015-07-12', 'f');
INSERT INTO "tbnews" VALUES ('639', 'B020103', 'B020000', 'ជំនួប​ជិត​2​ម៉ោង ការ​ចាប់ខ្លួន​សកម្មជន​បក្ស​សង្គ្រោះ​ជាតិ​ព្រមព្រៀង​ដោះស្រាយ​តាម​នីតិវិធីតុលាការ', 'រាជធានី​ភ្នំពេញ ៖ ជំនួប​បិទទ្វារ​អស់​រយៈពេល​ជិត​២​ម៉ោង​រវាង​ថា្ន​ក់​ដឹកនាំ​តំណាងរាស្ត្រ​ភាគច្រើន និង​ក្រុម​តំណាងរាស្ត្រ​ភាគតិច បាន​ត្រឹម​លើក​បញ្ហា​និង​ព្រមព្រៀង​គ្នា​លើក​គោលការណ៍​ចំនួន​៤​ចំណុច​មក​ពិភាក្សា​គ្នា', 'http://kohsantepheapdaily.com.kh/article/126417.html', 'http://i.kspg.co/2015/8/d5b1c88a-9655-4635-b5b6-cad3a0edda91.jpg?w=285&h=170&mode=carve', 'ថ្ងៃអង្គារ ទី១១ ខែសីហា ឆ្នាំ២០១៥ ម៉ោង ០៩:៣២', '0', '0', '0', 't', '2015-08-11', 'f');
INSERT INTO "tbnews" VALUES ('640', 'B020103', 'B020000', 'មន្ត្រីជាន់ខ្ពស់​ដែល​បក្ស​សង្គ្រោះ​ជាតិ​បណ្តេញចេញ បាន​ក្លាយជា​ស្ថាបនិក​គណបក្ស​ថ្មី​ឈ្មោះ ខ្មែរ​សាមគ្គី', 'រាជធានី​ភ្នំពេញ ៖ អតីត​សមាជិកា​គណបក្ស​សង្គ្រោះ​ជាតិ​លោកស្រី ឡាក់ សុភាព ដែល​ត្រូវ​បាន​បណ្តេញ​ចេញពី​គណបក្ស​របស់​ខ្លួន​កាលពី​ចុងឆ្នាំ​២០១៤​កន្លង​មក បាន​បង្កើត​គណបក្ស​នយោបាយ​មួយ​ថ្មី​ដើម្បី​ដណ្តើម​សន្លឹក​ឆ្នោត​មានឈ្មោះ​ថា', 'http://kohsantepheapdaily.com.kh/article/126354.html', 'http://i.kspg.co/news_pic_kh.png?w=285&h=170&mode=carve', 'ថ្ងៃចន្ទ ទី១០ ខែសីហា ឆ្នាំ២០១៥ ម៉ោង ១៤:២៨', '0', '0', '0', 't', '2015-08-10', 'f');
INSERT INTO "tbnews" VALUES ('641', 'B020103', 'B020000', 'ព្រឹកស្អែក ​CNRP ​នឹងប្រគល់ផែនទី​ព្រំដែនជូនរាជបណ្ឌិតសភា​កម្ពុជា', 'រាជធានីភ្នំពេញ៖ ​គណបក្សសង្គ្រោះជាតិ(CNRP) ​នឹងប្រគល់ផែនទីព្រំដែនខ្នាត​ 1/ 100 000​ ជូនរាជបណ្ឌិតសភាកម្ពុជា​ដើម្បីយកទៅស្រាវជ្រាវ​និងផ្ទៀងផ្ទាត់ នៅម៉ោង ១០ និង៣០នាទីព្រឹកថ្ងៃទី​ ១០ សីហា ​២០១៥ ។ ', 'http://kohsantepheapdaily.com.kh/article/126261.html', 'http://i.kspg.co/news_pic_kh.png?w=285&h=170&mode=carve', 'ថ្ងៃអាទិត្យ ទី៩ ខែសីហា ឆ្នាំ២០១៥ ម៉ោង ១៤:៥៨', '0', '0', '0', 't', '2015-08-09', 'f');
INSERT INTO "tbnews" VALUES ('642', 'B020103', 'B020000', 'សារ​ទៅ​បក្ស​ប្រឆាំង អ្នក​មិន​ហ៊ាន​បញ្ចេញ​ផែនទី​ដែល​ចាត់​ទុក​ថា​ត្រឹមត្រូវ គឺ​បោកប្រាស់​ប្រជាជន', 'ខេត្តកណ្តាល ៖ សម្តេច​អគ្គមហាសេនាបតី​តេ​ជោ ហ៊ុន សែន នាយក​រដ្ឋមន្ត្រី​នៃ​ព្រះរាជាណាចក្រ​កម្ពុជា បាន​អំពាវនាវ​ដល់​គ្រប់់​បក្សនយោបាយ​ទាំងអស់ ដើម្បី​ឯកភាព​ជាតិ​គឺ​ត្រូវ​ឯកភាព​គ្នា​លើ​ផែនទី​ពិត សូម​យក​មក​ដាក់ សូម​បញ្ចេញ​មក', 'http://kohsantepheapdaily.com.kh/article/125867.html', 'http://i.kspg.co/2015/8/74e06f59-ac52-460e-a279-8f0a979ebca6.jpg?w=285&h=170&mode=carve', 'ថ្ងៃអង្គារ ទី៤ ខែសីហា ឆ្នាំ២០១៥ ម៉ោង ០៩:០៥', '0', '0', '0', 't', '2015-08-04', 'f');
INSERT INTO "tbnews" VALUES ('643', 'B030101', 'B030000', 'តុលាការស្វាយរៀង កាត់ទោសឲ្យ មន្រ្តីសហជីព ចលនាកម្មករ ៤នាក់ ជាប់ពន្ធនាគារ១៣ខែ', '<strong>ភ្នំពេញ ៖</strong> មន្រ្តីសហជីព ចលនាកម្មករ ចំនួន៤នាក់ នៅព្រឹកថ្ងៃទី១២ ខែសីហា ឆ្នាំ២០១៥នេះ ត្រូវបានតុលាការ ខេត្តស្វាយរៀង កាត់ទោស ឲ្យជាប់ពន្ធនាគារ រយៈពេល១៣ខែ ពីបទញុះញុង ឲ្យប្រព្រឹត្តបទឧក្រិដ្ឋកម្ម។', 'http://www.dap-news.com/2011-06-14-02-39-55/96965-2015-08-12-08-26-54.html', 'img/dapnews.png', 'Wednesday, 12 August 2015 15:26', '0', '0', '0', 't', '2015-08-12', 'f');
INSERT INTO "tbnews" VALUES ('644', 'B030101', 'B030000', 'រថយន្តដឹកឈើប្រណីត សង្ស័យរបស់លោក ឌុច សាវឿន លួចដឹកឈើប្រណីត ទាំងកណ្តាលថ្ងៃ', '<b>មណ្ឌលគិរី៖</b> រថយន្តឡង់គ្រីស័រមួយគ្រឿង ដែលគេសង្ស័យថា ជារបស់ ឌុច សាវឿន មន្រ្តីនគរបាល នៃវរៈកងអនុ សេនាតូចការពារព្រំដែនជើងគោកលេខ៦២១ បានលួចដឹកឈើធ្នង់ពេញរថយន្តបម្រុងនាំទៅប្រទេសវៀតណាម &nbsp;តាមច្រកដាក់ដាំទាំងកណ្តាលថ្ងៃ តែគេចមិនផុតពីខ្សែភ្នែកកម្លាំងកងរាជអាវុធហត្ថខេត្តមណ្ឌលគិរី ប្រដេញតាម ស្ទាក់ចាប់ និងប្រគល់ទាំងរថយន្ត និងឈើទៅឲ្យមន្រ្តីរដ្ឋាបាលព្រៃឈើខេត្ត តែអ្នកបើកបររត់គេចខ្លួនបាត់ហើយ។', 'http://www.dap-news.com/2011-06-14-02-39-55/96963-2015-08-12-07-48-06.html', 'http://image.dap-news.com:81/dap-news/dap-images/2015/July/id-009/ST-395.jpg', 'Wednesday, 12 August 2015 14:47', '0', '0', '0', 't', '2015-08-12', 'f');
