INSERT INTO "tbnews" VALUES ('646', 'B030101', 'B030000', 'ជនជាតិចិន ស្រវឹងស្រា បើករថយន្ដ បុកពុំនូកដី ក្រឡាប់ផ្ងារជើង', '<strong>ភ្នំពេញ ៖</strong> គ្រោះថ្នាក់ ចរាចរណ៍មួយ បានកើតឡើង បង្កដោយបុរស ជនជាតិចិន ស្រវឹងស្រា បើករថយន្តបុកពំនូកដីក្រឡាប់ផ្ងារជើង បណ្តាលឲ្យ រងបួសធ្ងន់ កាលពីវេលាម៉ោង១០ និង៤៥នាទីយប់ ថ្ងៃទី១១ ខែសីហា ឆ្នាំ២០១៥ តាមបណ្ដោយផ្លូវណតប្រ៊ីដ ស្ថិតក្នុងភូមិដំណាក់ធំ៣ សង្កាត់ ស្ទឹងមានជ័យ ខណ្ឌមានជ័យ។', 'http://www.dap-news.com/2011-06-14-02-39-55/96959-2015-08-12-06-28-37.html', 'http://image.dap-news.com:81/dap-news/dap-images/2015/AUGUST/id-011/Hong-0417.jpg', 'Wednesday, 12 August 2015 13:25', '0', '0', '0', 't', '2015-08-12', 'f');
INSERT INTO "tbnews" VALUES ('647', 'B030101', 'B030000', 'អាជ្ញាធរខណ្ឌពោធិ៍សែនជ័យ ចុះធ្វើវេទិកាសាធារណៈ នៅបរិវេណវត្តអង្គបឹងចក ប្រជាពលរដ្ឋជាង៤០០នាក់ ចូលរួម', '<strong style="line-height: 2;">ភ្នំពេញ៖</strong><span style="line-height: 2;"> អជ្ញាធរខណ្ឌពោធិ៍សែនជ័យ នៅព្រឹកថ្ងៃទី១២ ខែសីហា ឆ្នាំ២០១៥នេះ បានរៀបចំវេទិការសាធារណៈថ្នាក់ខណ្ឌ នៅក្នុងបរិវេណវត្តអង្គបឹងចក សង្កាត់កំបូល ក្រោមអធិបតី លោក ក្រូច ផាន ប្រធានក្រុមប្រឹក្សាខណ្ឌ លោក ហែម ដារិទ្ធិ អភិបាល នៃគណៈអភិបាល ខណ្ឌពោធិសែនជយ័ និងមានសមាសភាពប្រជាពលរដ្ឋ មកពីសង្កាត់ចំនួន៤ មានសង្កាត់កំបូល សង្កាត់បឹងធំ សង្កាត់ឪឡោក និងសង្កាត់ស្នោរ ប្រមាណ៤០០ នាក់។</span>', 'http://www.dap-news.com/2011-06-14-02-39-55/96958-2015-08-12-06-04-36.html', 'http://image.lookingtoday.com:81/lookingtoday/looking-images/news/2015/August/ID-140/hour-01020.jpg', 'Wednesday, 12 August 2015 13:03', '0', '0', '0', 't', '2015-08-12', 'f');
INSERT INTO "tbnews" VALUES ('648', 'B030101', 'B030000', 'អាស៊ាននឹងជ្រើសរើស ពលរដ្ឋកម្ពុជា មួយរូបដើម្បីទទួល រង្វាន់ទឹកប្រាក់ ១ម៉ឺនដុល្លារ', '<strong>ភ្នំពេញ ៖</strong> អាស៊ាននឹងជ្រើសរើស ពលរដ្ឋកម្ពុជា មួយរូបដើម្បី ទទួលរង្វាន់ទឹក ប្រាក់១០០០០ដុល្លារ អាមេរិក ក្នុងកម្មវិធី “រង្វាន់ប្រជាជនអាស៊ាន” នាឱកាសកិច្ចប្រជុំកំពូលអាស៊ាន លើកទី២៧ នៅទីក្រុងកូឡាឡាំពួរ ប្រទេសម៉ាឡេស៊ី នៅខែវិច្ឆិកា ឆ្នាំ២០១៥ ខាងមុខ ។', 'http://www.dap-news.com/2011-06-14-02-39-55/96957-2015-08-12-05-43-17.html', 'img/dapnews.png', 'Wednesday, 12 August 2015 12:42', '0', '0', '0', 't', '2015-08-12', 'f');
INSERT INTO "tbnews" VALUES ('649', 'B030101', 'B030000', 'វគ្គបណ្តុះបណ្តាល ស្តីពីៈ​ “ពង្រឹងសមត្ថភាព ដើម្បីទទួល បានការងារ”“Capacity Strengthening for Being Employed”', 'សេចក្តីផ្តើមៈ ដោយឃើញថាប្អូនៗ ជាសិស្សនិងនិស្សិត កំពុងស្ថិតនៅក្នុងភាព ប្រឈម នៃបរិបទ ការងារ ដែលមានលក្ខណៈប្រកួតប្រជែង, តម្រូវការឱ្យមានបទពិសោធន៍ និងសមត្ថភាពក្នុងការងារ។', 'http://www.dap-news.com/2011-06-14-02-39-55/96955--capacity-strengthening-for-being-employed.html', 'http://image.dap-news.com:81/dap-news/dap-images/2015/August/id-010/chanroth015-aug-52.jpg', 'Wednesday, 12 August 2015 12:39', '0', '0', '0', 't', '2015-08-12', 'f');
INSERT INTO "tbnews" VALUES ('650', 'B030101', 'B030000', 'ការងារពិការភាព ពង្រីកវិសាលភាព ដល់ខេត្តឧត្តរមានជ័យ', '<b>ឧត្តរមានជ័យ៖</b> ផែនការយុទ្ធសាស្ត្រជាតិស្តីពីពិការភាព ២០១៤-២០១៨ រួមនិងកិច្ចការជាច្រើនផ្សេងទៀត ដែល សុទ្ធសឹងនាំមកនូវឧត្តមប្រយោជន៍សម្រាប់ជនពិការ ត្រូវបានលើកយកមកជជែកពិភាក្សា និងផ្សព្វផ្សាយដល់មន្រ្តី សាលាខេត្តឧត្តរមានជ័យ និង មន្រ្តីមកពីគ្រប់មន្ទីរពាក់ព័ន្ធក្នុងខេត្ត បន្ទាប់ពីក្រសួង និងបណ្តាខេត្តមួយចំនួនទៀត &nbsp;ត្រូវបានធ្វើការផ្សព្វផ្សាយរួចចាល់ហើយ។', 'http://www.dap-news.com/2011-06-14-02-39-55/96956-2015-08-12-05-40-30.html', 'http://image.dap-news.com:81/dap-news/dap-images/2015/July/id-009/ST-390.jpg', 'Wednesday, 12 August 2015 12:39', '0', '0', '0', 't', '2015-08-12', 'f');
INSERT INTO "tbnews" VALUES ('651', 'B030101', 'B030000', 'CNRPស្នើឲ្យ អភិបាល ខេត្តស្វាយរៀង ពន្លឿនដោះស្រាយ ទំនាស់ដីធ្លី ជូនប្រជាពលរដ្ឋ', '<strong>ភ្នំពេញ ៖</strong> លោក អេង ឆៃអ៊ាង ប្រធានគណៈកម្មការទី១នៃរដ្ឋសភា និងជាតំណាងរាស្រ្ត គណបក្ស សង្រ្គោះជាតិ (CNRP) បានស្នើឲ្យអភិបាល ខេត្តស្វាយរៀង ពន្លឿនដោះស្រាយ ករណីទំនាស់ដីធ្លី រវាងប្រជាពលរដ្ឋ និង ក្រុមហ៊ុនចំការកៅស៊ូពាមជាំង។', 'http://www.dap-news.com/2011-06-14-02-39-55/96954-cnrp-----.html', 'img/dapnews.png', 'Wednesday, 12 August 2015 12:39', '0', '0', '0', 't', '2015-08-12', 'f');
INSERT INTO "tbnews" VALUES ('652', 'B030101', 'B030000', 'ថ្នាក់ដឹកនាំខេត្តនាំ យកអំណោយ ចែកជូនគ្រួសារ ដែលរស់នៅលើ ខ្នងប្រឡាយ', '<strong>បន្ទាយមានជ័យ ៖</strong> កាលពីរសៀលថ្ងៃទី១១ ខែសីហា ឆ្នាំ២០១៥ ថ្នាក់ដឹកនាំខេត្ត បាននាំយក អំណោយទៅចែកជូន និងបានសន្យា បន្តជួយផ្ដល់ នូវសំពត់តង់ប្រក់ដំបូលផ្ទះ និងរកដីមួយកន្លែង ដែលសមរម្យជូនទៅដល់គ្រួសារទីទាល់ក្រ ដែលរស់នៅខ្នងប្រឡាយ ដែលស្ថិតនៅភូមិ/សង្កាត់សូភី ក្រុងសិរីសោភ័ណ ខេត្តបន្ទាយមានជ័យ ។', 'http://www.dap-news.com/2011-06-14-02-39-55/96953-2015-08-12-05-33-31.html', 'http://image.dap-news.com:81/dap-news/dap-images/2015/AUGUST/id-011/Hong-0412.jpg', 'Wednesday, 12 August 2015 12:33', '0', '0', '0', 't', '2015-08-12', 'f');
INSERT INTO "tbnews" VALUES ('653', 'B030102', 'B030000', 'សារជាតិអូមេហ្គា -៣អាចកាត់ បន្ថយគ្រោះថ្នាក់ នៃជំងឺផ្លូវចិត្ត', '<strong>ឡុងដ៏៖</strong> ទីភ្នាក់ងារព័ត៌មាន ចិនស៊ិនហួ ចេញផ្សាយនៅថ្ងៃពុធ ទី ១២ ខែសីហា ឆ្នាំ ២០១៥ បានឲ្យដឹងថា សម្រាប់ក្មេងៗ ដែលអាចមានគ្រោះ ថ្នាក់ខ្ពស់នៃការវិវត្តន៍ជំងឺផ្លូវចិត្ត គួរតែ ទទួលទានសារជាតិអូមេហ្គា-៣ ឲ្យបានច្រើន ដោយសារតែសារជាតិនេះ អាចកាត់បន្ថយ គ្រោះថ្នាក់នៃជំងឺ ផ្លូវចិត្តនេះយ៉ាងប្រសើរ ដែលការ រកឃើញថ្មីនេះត្រូវបានចេញផ្សាយ ជាសាធារណៈនៅក្នុងទស្សនាវដ្តីទំនាក់ទំនងធម្មជាតិរបស់ ចក្រភព អង់គ្លេស។', 'http://www.dap-news.com/2011-06-14-02-40-26/96964-2015-08-12-08-03-08.html', 'http://timesofindia.indiatimes.com/thumb/msid-48449744,width-300,resizemode-4/Omega-3-may-reduce-psychosis-risk.jpg', 'Wednesday, 12 August 2015 15:02', '0', '0', '0', 't', '2015-08-12', 'f');
INSERT INTO "tbnews" VALUES ('654', 'B030102', 'B030000', 'ការសិក្សាថ្មីបង្ហាញថា ចក្រវាឡកំពុង តែសាបសូន្យ បន្តិចម្តងៗ', '<strong>ឡូសអេនជឺឡែស ៖</strong> យោងតាមការ សិក្សាស្រាវជ្រាវថ្មី ក្រុមតារាវិទូអន្តរជាតិ បានបង្ហាញថា បច្ចុប្បន្នចក្រវាឡ របស់យើងមានថាមពល កាន់តែខ្សោយទៅៗ ដោយថាមពលរបស់វាមានតែ ប្រហែលពាក់កណ្តាល នៃថាមពល ដែលចក្រវាឡធ្លាប់មានកាលពី ២រយពាន់លានឆ្នាំមុន ប៉ុណ្ណោះ ដែលនេះបង្ហាញឲ្យឃើញថា ចក្រវាឡរបស់ យើង និងអាចស្លាប់បន្តិចម្តងៗ។', 'http://www.dap-news.com/2011-06-14-02-40-26/96962-2015-08-12-07-27-37.html', 'http://theghic.com/wp-content/uploads/2015/08/galazies.jpg', 'Wednesday, 12 August 2015 14:26', '0', '0', '0', 't', '2015-08-12', 'f');
INSERT INTO "tbnews" VALUES ('655', 'B030102', 'B030000', 'បំណែកសង្ស័យជា MH370 ដែលរកឃើញនៅម៉ាល់ឌីវ នឹងត្រូវបញ្ជូនទៅម៉ាឡេស៊ី', '<strong>គូឡាឡាំពួរ៖</strong> បំណែកមួយចំនួនដែលត្រូវបានរកឃើញ នៅកោះ ម៉ាល់ឌីវ ដែលគេសង្ស័យ ថាជាផ្នែករបស់យន្ដហោះ នៃជើងហោះហើរក្រុមហ៊ុនអាកាសចរណ៍ម៉ាឡេស៊ី MH370 នោះ នឹងត្រូវបានគេបញ្ជូនទៅកាន់ម៉ាឡេស៊ីវិញ សម្រាប់វិភាគបន្ដថា វាពិតជាបំណែករបស់ យន្ដហោះ នោះពិតប្រាកដឫយ៉ាងណា។', 'http://www.dap-news.com/2011-06-14-02-40-26/96961--mh370--.html', 'http://image.lookingtoday.com:81/lookingtoday/looking-images/news/2015/August/ID-141/MrMSP-0429.jpg', 'Wednesday, 12 August 2015 14:08', '0', '0', '0', 't', '2015-08-12', 'f');
INSERT INTO "tbnews" VALUES ('656', 'B030102', 'B030000', 'ព័រទុយហ្គាល់៖ បុរសអាយុ ២០ឆ្នាំ ដុតព្រៃលេង ឆេះអស់ជាង ១០០ហិកតា ត្រូវប៉ូលិសចាប់ខ្លួន', '<strong>លីសបោន</strong> ៖ បុរសព័រទុយហ្គាល់ អាយុ២០ឆ្នាំម្នាក់ ត្រូវបានប៉ូលិសចាប់ខ្លួន បន្ទាប់ពីរូបគេ បានដុតព្រៃមួយកន្លែង ជាការកំសាន្ត តែបែរជានាំឲ្យជ្រុល កើតចេញជាមហន្តរាយ ខ្លាំងទៅវិញ កាលពីថ្ងៃទី១៨ និង ១៩ ខែឧសភា កន្លងទៅ។ នេះបើតាមការអះអាង របស់ប៉ូលិសព័រទុយហ្គាល់ នៅថ្ងៃពុធ ទី១២ ខែសីហា ឆ្នាំ ២០១៥។', 'http://www.dap-news.com/2011-06-14-02-40-26/96944-2015-08-12-04-11-47.html', 'http://i.ytimg.com/vi/gOf4OmIEgk0/0.jpg', 'Wednesday, 12 August 2015 11:11', '0', '0', '0', 't', '2015-08-12', 'f');
INSERT INTO "tbnews" VALUES ('657', 'B030102', 'B030000', 'មនុស្ស ៤០នាក់បាត់ខ្លួន បន្ទាប់ពីដីបាក់ នៅភាគពាយ័ព្យចិន', '<strong>ហ្ស៊ីអាន ៖</strong> ប្រជាជនចិន យ៉ាងហោចណាស់ ៤០នាក់ កំពុងបាត់ខ្លួន បន្ទាប់ពីគ្រោះមហន្តរាយ បាក់ដីបានគ្របសង្កត់ ទៅលើលំនៅដ្ឋានរបស់ពួកគេ ស្ថិតនៅក្នុងខេត្តសានស៊ី ជាតំបន់ភាគ ពាយ័ព្យរបស់ ប្រទេស នៅថ្ងៃពុធ ទី១២ ខែសីហា ឆ្នាំ២០១៥ នេះ។', 'http://www.dap-news.com/2011-06-14-02-40-26/96939-2015-08-12-03-23-39.html', 'http://i.ytimg.com/vi/CIOefXyMmus/0.jpg', 'Wednesday, 12 August 2015 10:23', '0', '0', '0', 't', '2015-08-12', 'f');
INSERT INTO "tbnews" VALUES ('658', 'B030102', 'B030000', 'នាយករដ្ឋមន្ត្រី លីប៊ី ព្រមលាលែង ចេញពីតំណែង ដើម្បីសន្តិភាពជាតិ', '<strong>ទ្រីប៉ូលី ៖</strong> លោកនាយករដ្ឋមន្ត្រី លីប៊ី អាប់ឌុលឡាហ៍ អាល់ ស៊ីននី បានថ្លែងនៅថ្ងៃពុធ ទី១២ ខែសីហា ឆ្នាំ២០១៥ នេះតាមកញ្ចក់ទូរទស្សន៍រដ្ឋរបស់ខ្លួនថា លោកនឹងលាលែងចេញពី តំណែង ដើម្បីសន្តិភាព ជាតិ។', 'http://www.dap-news.com/2011-06-14-02-40-26/96935-2015-08-12-02-53-14.html', 'http://cdn.charismanews.com/images/stories/2015/featured-news/Libyan-Prime-Minister.JPG', 'Wednesday, 12 August 2015 09:52', '0', '0', '0', 't', '2015-08-12', 'f');
INSERT INTO "tbnews" VALUES ('659', 'B030102', 'B030000', 'មនុស្សប្រមាណ ៥០នាក់ស្លាប់ ក្នុងហេតុ ការណ៍បំផ្ទុះគ្រាប់បែក នៅនីហ្សេរីយ៉ា', '<strong>(Reuters)</strong> ៖ ហេតុការណ៍ផ្ទុះ គ្រាប់បែក នៅទីផ្សារ មួយកន្លែង ភាគឦសានរដ្ឋ Borno ប្រទេសនីហ្សេរីយ៉ា ដែលបានបណ្តាល ឲ្យមនុស្សយ៉ាង ហោចណាស់ ៤៧នាក់ស្លាប់ និង ៥២នាក់ផ្សេងទៀតរងរបួស នេះបើយោង តាមប្រភពពីយោធាម្នាក់ និងជនស៊ីវិល ដកស្រង់ដោយសារព័ត៌មាន Reuters ចេញផ្សាយនៅថ្ងៃទី១១ ខែសីហា ឆ្នាំ២០១៥ ។', 'http://www.dap-news.com/2011-06-14-02-40-26/96932-2015-08-12-02-29-32.html', 'http://image.dap-news.com:81/dap-news/dap-images/2015/AUGUST/id-011/Hong-0366.jpg', 'Wednesday, 12 August 2015 09:27', '0', '0', '0', 't', '2015-08-12', 'f');
INSERT INTO "tbnews" VALUES ('660', 'B030102', 'B030000', 'សេចក្តីប្រកាស ៖ នេប៉ាល់ នឹងអាច ទទួលរង គ្រោះរញ្ជួយ ផែនដីបន្ថែមទៀត', '<strong>កាត្មាន់ឌូ៖</strong> ក្រុមអ្នកស្រាវជ្រាវ បាននិយាយថា គ្រោះមហន្តរាយ រញ្ជួយដីដែលបាន បំផ្លាញប្រទេស នេប៉ាល់ កាលពីខែមេសា បានបង្ហាញឲ្យឃើញពី ថាមពលរបស់វា ដែលត្រូវបានគេរំពឹងទុកថា នឹងអាចកើតឡើង ម្តងទៀត នាថ្ងៃអនាគត នៅក្នុងប្រទេស អាស៊ីខាងត្បូងមួយនេះ ដោយវាអាចមាន សភាពធ្ងន់ធ្ងរ ជាងមុនទៅទៀត។', 'http://www.dap-news.com/2011-06-14-02-40-26/96897-2015-08-11-08-27-17.html', 'http://i.telegraph.co.uk/multimedia/archive/01207/earthquake-wreckag_1207931c.jpg', 'Tuesday, 11 August 2015 15:26', '0', '0', '0', 't', '2015-08-11', 'f');
INSERT INTO "tbnews" VALUES ('661', 'B030401', 'B030000', 'RMA ផ្តល់សេវា ត្រួតពិនិត្យ បច្ចេកទេស រថយន្តម៉ាក Jaguar Land Rover និង Range Rover ដោយឥតគិតថ្លៃ ១ខែ', '<strong>ភ្នំពេញ៖</strong> អគ្គនាយក ក្រុមហ៊ុន អ អិម អេ ខេមបូឌា (RMA Cambodia) បានប្រកាសផ្តល់សេវាត្រួតពិនិត្យ បច្ចេកទេសរថយន្តម៉ាក Jaguar Land Rover និង Range Rover គ្រប់ស៊េរីទាំងអស់ ដោយឥតគិតថ្លៃ រយៈពេល ១។', 'http://www.dap-news.com/2011-06-14-02-43-39/96657-rma-jaguar-land-rover-range-rover-.html', 'http://image.dap-news.com:81/dap-news/dap-images/2015/August/id-014/that-0163.jpg', 'Friday, 07 August 2015 13:13', '0', '0', '0', 't', '2015-08-07', 'f');
INSERT INTO "tbnews" VALUES ('662', 'B030401', 'B030000', 'ណេសកាហ្វេ រៀបចំកម្មវិធី ប្រគំតន្ត្រី កក្រើក ផ្សារទំនើបអ៊ីអន', '<strong>ភ្នំពេញ៖</strong> ក្រុមហ៊ុន ចែកចាយផលិតផល Nestlé ដែលជាក្រុមហ៊ុនផ្គត់ផ្គង់ផលិតផល ឧបភោគបរិភោគធំមួយ នៅកម្ពុជា កាលពីថ្ងៃទី ០១ ខែសីហា ឆ្នាំ២០១៥ កន្លងទៅនេះ បានរៀបចំកម្មវិធីប្រគំតន្ត្រីណេសកាហ្វេ យ៉ាងគគ្រឹកគគ្រេងបំផុត នៅLively Plaza ជាន់ផ្ទាល់ដី នៃផ្សារទំនើបអ៊ីអនម៉ល។', 'http://www.dap-news.com/2011-06-14-02-43-39/96409-2015-08-03-09-00-29.html', 'http://image.dap-news.com:81/dap-news/dap-images/2015/August/id-014/that-0059.jpg', 'Monday, 03 August 2015 15:59', '0', '0', '0', 't', '2015-08-03', 'f');
INSERT INTO "tbnews" VALUES ('663', 'B030302', 'B030000', 'ទិវារត់​ម៉ារ៉ា​តុង​ចក្រ​ភព​អង្គរ ត្រូវ​បាន​បញ្ចប់​ជា​មួយ​នឹង​អត្តពលិក​ជាង ២៧០០នាក់​ចូល​រួម', '<strong>សៀមរាប៖</strong> ទិវារត់ប្រណាំងម៉ារ៉ាតុងអន្ដរជាតិ ចក្រភពអង្គរ លើកទី២ ឆ្នាំ២០១៥ នៅលើចម្ងាយផ្លូវ ៤២,១៩៥ គីឡូ ម៉ែត្រនៅមុខប្រាសាទអង្គរវត្ត នៃខេត្តសៀមរាប ត្រូវបានបញ្ចប់ជាស្ថាពរហើយនាព្រឹកនេះ ជាមួយនឹងអត្តពលិកជាង ២៧០០នាក់ចូលរួម មកពី៤៨ប្រទេស ក្នុងនោះជនបរទេសមានចំនួន ១.៤៥៨ នាក់ ដោយចិនជាប់លេខ១ ជប៉ុន លេខ២ និងម៉ាឡេស៊ី ជាប់លេខ៣ ដែលចុះឈ្មោះចូលរួមរត់ច្រើនជាងគេ ។', 'http://www.dap-news.com/morokatmagazine/96731-2015-08-09-03-09-58.html', 'http://image.lookingtoday.com:81/lookingtoday/looking-images/news/2015/August/ID-141/MrMSP-0266.jpg', 'Sunday, 09 August 2015 10:07', '0', '0', '0', 't', '2015-08-09', 'f');
INSERT INTO "tbnews" VALUES ('664', 'B030302', 'B030000', 'រឿង សង្គ្រាមបងប្អូន នឹងនាំ ទៅកាន់បរទេស បន្ទាប់ពី ចាក់បញ្ចាំង នៅកម្ពុជា', '<strong>ភ្នំពេញ៖</strong> ខ្សែភាពយន្តខ្នាតធំ រឿង ”សង្គ្រាមបងប្អូន” របស់ស្ថានីយ៍ទូរទស្សន៍ CTN ដែលបានធ្វើការសហការ ជាមួយបណ្តាញផ្សព្វផ្សាយ GMA7 របស់ប្រទេសហ្វីលីពីន បាន ប្រកាសសម្ពោធដាក់ បញ្ចាំងកាលពីថ្ងៃទី៧ សីហា ឆ្នាំ២០១៥ នៅរោងភាពយន្ត Legend ផ្សារទំនើបTK Avenue ជាមួយនឹងវត្តមានតារាសម្តែងជាតិ និងអន្តរជាតិជាច្រើន។ ខ្សែភាពយន្តនេះ ចាប់បញ្ចាំងនៅកម្ពុជាគ្រប់រោងភាពយន្ត ពីថ្ងៃទី៩ ខែសីហា មុន ពេល ចាក់បញ្ចាំងនៅរោងភាពយន្ត នៅប្រទេសហ្វីលីពីន និងប្រទេសជាច្រើនទៀត។', 'http://www.dap-news.com/morokatmagazine/96716-2015-08-08-09-03-18.html', 'http://image.dap-news.com:81/dap-news/dap-images/2015/August/id-014/that-0211.jpg', 'Saturday, 08 August 2015 16:02', '0', '0', '0', 't', '2015-08-08', 'f');
INSERT INTO "tbnews" VALUES ('665', 'B030302', 'B030000', 'ក្រុមបាល់ទាត់​ក្រសួង​ការ​បរ​ទេស ឈ្នះ​ក្រុម​បរទេស ៣-០', 'null', 'http://www.dap-news.com/morokatmagazine/96710-2015-08-08-07-01-11.html', 'http://image.lookingtoday.com:81/lookingtoday/looking-images/news/2015/August/ID-141/MrMSP-0247.jpg', 'Saturday, 08 August 2015 13:53', '0', '0', '0', 't', '2015-08-08', 'f');
INSERT INTO "tbnews" VALUES ('666', 'B030302', 'B030000', 'អាជ្ញាធរជាតិអប្សរា ចេញប្លង់រត់ម៉ារ៉ាតុង និង សុំការយោគយល់ពីភ្ញៀវទេសចរ', '<strong>ភ្នំពេញ៖</strong> ស្របពេលក្រសួងទេសចរណ៍ និងគណៈកម្មា ធិការជាតិអូឡាំពិកម្ពុជា ទទួលបានការអនុញ្ញាតពីរាជ រដ្ឋាភិបាល រៀបចំទិវារត់ប្រណាំងម៉ារ៉ាតុងអន្ដរជាតិចក្រ ភពអង្គរ លើកទី២ នៅតំបន់អង្គរ នាថ្ងៃទី០៩ ខែសីហា ស្អែកនេះអាជ្ញាធរ ជាតិអប្សរា បានចេញសេចក្ដីប្រកាសព័ត៌មានមួយ ដើម្បីសុំការយោគយល់អធ្យាស្រ័យពីសំណាក់សាធារណជន ជា ពិសេសគឺភ្ញៀវទេសចរ ដើម្បីសម្រួលដល់ការរៀបចំព្រឹត្តិការណ៍ អន្ដរជាតិដ៏អស្ចារ្យនេះទទួលបានជោគជ័យ ។', 'http://www.dap-news.com/morokatmagazine/96695-2015-08-08-04-20-01.html', 'http://image.lookingtoday.com:81/lookingtoday/looking-images/news/2015/August/ID-140/hour-00777.jpg', 'Saturday, 08 August 2015 11:19', '0', '0', '0', 't', '2015-08-08', 'f');
INSERT INTO "tbnews" VALUES ('667', 'B030302', 'B030000', 'កីឡាករមកពី ៤៨ប្រទេស ចូលរួមរត់ប្រណាំងម៉ារ៉ាតុង ខេត្តសៀមរាប ចុងសប្ដាហ៍នេះ', '<strong>ភ្នំពេញ៖</strong> គណៈកម្មការរៀបចំ ទិវារត់ប្រណាំងម៉ារ៉ាតុងអន្ដរជាតិ ចក្រភពអង្គរ (The ២nd Angkor Empire Full and Half Marathon) លើកទី២ បានឲ្យដឹងថា សម្រាប់ឆ្នាំទី២នេះ គឺមានអត្ដពលិក ចម្រុះជាតិសាសន៍មកពី៤៨ប្រទេស ដែលបានចុះឈ្មោះ ចូលរួមរត់លើចម្ងាយផ្លូវ ៤២.១៩៥ គីឡូម៉ែត្រ ជុំវិញតំបន់អង្គរ ខេត្តសៀមរាប ដែលនឹងរៀបចំនៅ ថ្ងៃទី០៩ ខែសីហា ឆ្នាំ២០១៥ ចុងសប្តាហ៍នេះ ។', 'http://www.dap-news.com/morokatmagazine/96571-2015-08-06-04-23-32.html', 'http://image.lookingtoday.com:81/lookingtoday/looking-images/news/2015/August/ID-140/hour-00640.jpg', 'Thursday, 06 August 2015 11:18', '0', '0', '0', 't', '2015-08-06', 'f');
INSERT INTO "tbnews" VALUES ('668', 'B030302', 'B030000', 'ម្ចាស់មេដាយ​ប្រាក់​ កីឡា SEA Games ស៊ន ដាវីន លាឈប់ពី ក្រុមជម្រើសជាតិ', '<strong>ភ្នំពេញ៖</strong> កីឡាការិនី ស៊ន ដាវីន ដែលត្រូវជាបងស្រីបង្កើត របស់ម្ចាស់មេដាយមាស កីឡាអាស៊ី ស៊ន សៀវម៉ី បានផ្ញើលិខិតលាឈប់ពី ក្រុមជម្រើសជាតិកម្ពុជា ហើយពេលនេះ ដោយមូលហេតុនឿហត់ និងគ្មានពេលគ្រប់គ្រាន់ ដើម្បីធ្វើការហ្វឹកហាត់ ។', 'http://www.dap-news.com/morokatmagazine/96500--sea-games-.html', 'http://image.lookingtoday.com:81/lookingtoday/looking-images/news/2015/August/ID-140/hour-00539.jpg', 'Wednesday, 05 August 2015 08:35', '0', '0', '0', 't', '2015-08-05', 'f');
