INSERT INTO "tbnews" VALUES ('704', 'B010201', 'B010000', 'កម្មវិធី​ប្រកួត​របស់ U-18 ​ភ្នំពេញក្រោន ​ក្នុង​ពាន​អន្តរជាតិដែល​មាន​ប្រាក់​​រង្វាន់ ៥ ម៉ឺន​ដុល្លារ', 'ក្រុម​បាល់ទាត់​យុវជន​ក្រោម​អាយុ​១៨ ឆ្នាំ​របស់​ភ្នំពេញក្រោន (U-18) ជា​បេក្ខភាព​មួយ​ក្នុង​ចំណោម ៨ ក្រុម​ផ្សេង​ទៀត​មក​ពី​ ៦ ប្រទេស​ផ្សេង​គ្នា​ចូលរួម...', 'http://news.sabay.com.kh/article/448674', 'http://cdn.sabay.com/cdn/news.sabay.com.kh/wp-content/uploads/2015/08/featured52-285x170.jpg?ebb82d', '12 August, 2015', '2', '312', '32', 'f', '2015-08-12', 'f');
