INSERT INTO "tbnews" VALUES ('744', 'B020302', 'B020000', 'បេក្ខជន​កម្ពុជា​បង្កើត​ប្រវត្ដិសាស្ដ្រ​ជូន​ជាតិ​ដោយ​ឈ្នះ​មេដាយ​ប្រាក់​ក្នុង​កម្មវិធី MOSWC 2015', 'ទីបំផុត​បេក្ខជន​ឆ្នើម​កម្ពុជា​ម្នាក់ ក្នុង​ចំណោម​៣​នាក់​ដែល​ចូលរួម​ប្រកួត​ការ​ប្រកួត​លើ​វិញ្ញាសា ដើម្បី​ដណ្ដើម​ជើងឯក​កុំព្យូទ័រ​ការិយាល័យ​ប្រចាំឆ្នាំ​២០១៥ MOSWC 2015 វគ្គ​ផ្ដាច់ព្រ័ត្រ​លើ​មុខ​ជំនាញ​ខុស​ៗ​គ្នា ', 'http://kohsantepheapdaily.com.kh/article/126652.html', 'http://i.kspg.co/2015/8/0e52f9c4-d2cf-4b29-8912-a89e4a652bb7.jpg?w=285&h=170&mode=carve', 'ថ្ងៃព្រហស្បត្ត៏ ទី១៣ ខែសីហា ឆ្នាំ២០១៥ ម៉ោង ១៦:០៣', '0', '0', '0', 't', '2015-08-13', 'f');
