INSERT INTO "tbnews" VALUES ('747', 'B020302', 'B020000', 'ជី​ជី មិន​សប្បាយ​ចិត្តការ​ផ្សាយ​ពី​រឿង​ស្នេហា​ចាត់ទុកជា​រនាំង​បិទផ្លូវអាជីព​សិល្បៈ​របស់​នាង', 'រាជធានី​ភ្នំពេញ ៖ កញ្ញា​ជី​ជី ដែល​បាត់​ឱកាស​ក្តោប​តំណែង​តួឯក​ក្នុង​ខ្សែភាពយន្ត​មួយ​រយៈពេល​កន្លង​មក ពេល​នេះ​មានឱកាស​ជា​ថ្មី​ដោយ​បាន​ចុះ​កិច្ចសន្យា​ជាមួយ​ផលិតកម្ម​មួយ​ចំនួន ។ ', 'http://kohsantepheapdaily.com.kh/article/126591.html', 'http://i.kspg.co/2015/8/af95d953-bfef-4779-9cdf-aad1c9b6cc18.jpg?w=285&h=170&mode=carve', 'ថ្ងៃព្រហស្បត្ត៏ ទី១៣ ខែសីហា ឆ្នាំ២០១៥ ម៉ោង ១០:១០', '0', '0', '0', 't', '2015-08-13', 'f');
