INSERT INTO "tbnews" VALUES ('749', 'B020301', 'B020000', '«​ភ្នំ​ក្រោល​» គ្របដណ្តប់​ដោយ​ព្រៃឈើ​គួរ​គាប់​កម្សាន្តឈ្មោះ​មាន​ប្រវត្តិ​រឿងនិទាន​តាំងពី​យូរលង់​មក​ហើយ', 'ខេត្តមណ្ឌលគិរី ៖ តំបន់​ទេសចរណ៍វប្បធម៌​«​ភ្នំ​ក្រោល​» ស្ថិត​នៅ​ភូមិ​អូរ​រ​យ៉េះ ឃុំ​ស្រែ​សង្គម ស្រុក​កោះ​ញែក ខេត្តមណ្ឌលគិរី មាន​ចម្ងាយ​ផ្លូវ​៣.០០០​ម៉ែត្រ ពី​ទីរួមស្រុក​កោះ​ញែក និង​មាន​ចម្ងាយ​១១១​គីឡូម៉ែត្រ ពី​ទីរួមខេត្ត​មណ្ឌលគិរី ។ ', 'http://kohsantepheapdaily.com.kh/article/126685.html', 'http://i.kspg.co/2015/8/9bb6a7dd-90fb-4bda-88ba-207bc816aa38.jpg?w=285&h=170&mode=carve', 'ថ្ងៃសុក្រ ទី១៤ ខែសីហា ឆ្នាំ២០១៥ ម៉ោង ០៩:១៣', '0', '0', '0', 't', '2015-08-14', 'f');
