INSERT INTO "tbnews" VALUES ('753', 'B020102', 'B020000', 'សមត្ថកិច្ច​ចម្រុះ​ឡោម​ព័ទ្ធ​ភូមិ​គ្រឹះ​២​កន្លែង​ដើម្បី​ចាប់​លោក ហុង សុខ​ហួរ', 'រាជធានី​ភ្នំពេញ ៖ ប្រភព​ព័ត៌មាន​ជំហាន​ដំបូង​បានឱ្យ​ដឹង​ថា ខណៈ​ពេល​នេះ​សមត្ថកិច្ច​ចម្រុះ​កំពុង​ឡោម​ភូមិ​គ្រឹះ​២​កន្លែង​ដែល​១​កន្លែង​នៅ​សង្កាត់​ចោម​ចៅ និង​មួយ​កន្លែង​ទៀត​នៅ​សង្កាត់​ភ្នំពេញ​ថ្មី ដែល​សង្ស័យ​ថា លោក ហុង សុខ​ហួរ សមាជិក​ព្រឹទ្ធសភា​កំពុង​សម្ងំ​លាក់ខ្លួន ។', 'http://kohsantepheapdaily.com.kh/article/126767.html', 'http://i.kspg.co/2015/8/afefb656-eaf1-4677-a118-1f41e4ec1f76.jpg?w=285&h=170&mode=carve', 'ថ្ងៃសុក្រ ទី១៤ ខែសីហា ឆ្នាំ២០១៥ ម៉ោង ២២:៥០', '0', '0', '0', 't', '2015-08-14', 'f');
INSERT INTO "tbnews" VALUES ('754', 'B020102', 'B020000', 'ខណៈ​ពេល​នេះ សមត្ថកិច្ច​កំពុង​ព័ទ្ធ​ចាប់​លោក ហុង សុខ​ហួរ', 'រាជធានី​ភ្នំពេញ ៖ ខណៈ​ពេល​នេះ​ម៉ោង​១០ និង​១០​នាទី​យប់ ថ្ងៃ​ទី​១៤ សីហា ២០១៥ សមត្ថកិច្ច​ចម្រុះ​បាន​នឹង​កំពុង​ព័ទ្ធ​ភូមិ​គ្រឹះ​មួយ​កន្លែង លេខ​១០៦A ផ្លូវ​លេខ​២០១១ ស្ថិត​ក្នុងភូមិ​គោក​ឃ្លាង សង្កាត់​ភ្នំពេញ​ថ្មី ខណ្ឌ​សែន​សុខ', 'http://kohsantepheapdaily.com.kh/article/126766.html', 'http://i.kspg.co/2015/8/978375d5-b2fa-41d6-8335-b1e7fa574198.jpg?w=285&h=170&mode=carve', 'ថ្ងៃសុក្រ ទី១៤ ខែសីហា ឆ្នាំ២០១៥ ម៉ោង ២២:២៤', '0', '0', '0', 't', '2015-08-14', 'f');
INSERT INTO "tbnews" VALUES ('755', 'B020102', 'B020000', 'តូចចិត្ត​នឹង​ម្តាយ​សង្សារ ផឹកថ្នាំ​ពុល​សញ្ញា​ឆ្កែ​អង្គុយ​ប៉ង​ធ្វើ​អត្តឃាត', 'ខេត្ត​ប៉ៃលិន ៖ យុវជន​ម្នាក់​ត្រូវ​ក្រុម​គ្រួសារ​ដឹក​តាម​រថយន្តឈ្នួល​យក​ទៅ​សង្គ្រោះ​នៅ​មន្ទីរពេទ្យ​បង្អែក ខេត្ត ហើយ​បាន​ក្រុម​គ្រូពេទ្យ​ដាក់​ទឹក​លាង​ក្រពះ​ជម្រះ​ជាតិពុល​ឱ្យ​រួច​ផុត​ជីវិត ដោយសារ​តែ​ខឹង​នឹង​ម្តាយ​សង្សារ​ហាម​មិន​ឱ្យ​អង្គុយ​និយាយ​លេង​ជាមួយ​កូនស្រី​គាត់​នា​ពេល​យប់ ', 'http://kohsantepheapdaily.com.kh/article/126765.html', 'http://i.kspg.co/2015/8/44234cf3-c038-4a34-841e-c214541de07c.jpg?w=285&h=170&mode=carve', 'ថ្ងៃសុក្រ ទី១៤ ខែសីហា ឆ្នាំ២០១៥ ម៉ោង ២១:០៩', '0', '0', '0', 't', '2015-08-14', 'f');
