INSERT INTO "tbnews" VALUES ('758', 'B020102', 'B020000', 'សមត្ថកិច្ច​វាយ​ខ្នោះ​​ជន​សង្ស័យ២នាក់​ជាប់​ចោទ ​លួច ឆក់​ យ៉ាង​សកម្ម​​តាម​មាត់សមុទ្រ ​​', 'ខេត្ត​ព្រះ​សីហ​នុ ៖ យុវជន​សង្ស័យ​​២​នាក់ ដែល​ជាប់​ពាក់ព័ន្ធករណី​ឆក់​តាម​បណ្តោយ​មាត់សមុទ្រ ត្រូវ​បាន​នគរ​បាល​ប៉ុស្តិ៍រ​ដ្ឋា​បាល​សង្កាត់​លេខ​៤​ សហការ​​ជាមួយ​នគរ​បាល​ព្រហ្មទណ្ឌ​​​ខេត្ត​​ ​​​ចាប់​ឃាត់​ខ្លួន​​', 'http://kohsantepheapdaily.com.kh/article/126715.html', 'http://i.kspg.co/2015/8/cea43859-2e91-4e31-b31a-bae223e9f9b4.jpg?w=285&h=170&mode=carve', 'ថ្ងៃសុក្រ ទី១៤ ខែសីហា ឆ្នាំ២០១៥ ម៉ោង ១២:៤៦', '0', '0', '0', 't', '2015-08-14', 'f');
INSERT INTO "tbnews" VALUES ('759', 'B020102', 'B020000', 'ឡើង​រុក​រើ​លើ​ផ្ទះ​គេ​ពេល​យប់​បំរុង​លួច​​​​ទ្រព្យសម្បត្តិ​ ម្ចាស់ផ្ទះ​ទាន់​ចាប់​ប្រគល់​ឱ្យសមត្ថកិច្ច', 'ខេត្ត​កណ្តាល ៖ ជន​ស​ង្ស័យ​ម្នាក់​ត្រូវ​ម្ចាស់ផ្ទះ ​និង​ប្រជា​ពលរ​ដ្ឋ​ដេញ​ព័ទ្ធ​ចាប់​បាន​ប្រគល់​ឲ​សមត្ថកិច្ច​ បន្ទាប់​ពី​បុរស​រូប​នេះ​លប​ឡើង​រុក​រើ​កកូរ​កកាយ​អីវ៉ាន់​លើ​ផ្ទះ​ប្រជា​ពលរ​ដ្ឋ​បំរុង​លួច​ទ្រព្យសម្បត្តិ​ ', 'http://kohsantepheapdaily.com.kh/article/126713.html', 'http://i.kspg.co/2015/8/edffc979-6772-439f-a0d3-794d91da1544.jpg?w=285&h=170&mode=carve', 'ថ្ងៃសុក្រ ទី១៤ ខែសីហា ឆ្នាំ២០១៥ ម៉ោង ១១:០៣', '0', '0', '0', 't', '2015-08-14', 'f');
INSERT INTO "tbnews" VALUES ('760', 'B020102', 'B020000', 'ក្មេង​ទំនើង​២​ក្រុម​វាយ​គ្នា​នៅ​មុខ​ក្លិប​កម្សាន្ត​ហា​យ', 'រាជធានី​ភ្នំពេញ ៖ ក្មេង​ទំនើង​២​ក្រុម​បាន​វាយ​គ្នា​បន្ទាប់​ពី​ចេញពី​រាំ​លេង​កម្សាន្ត​រួច បង្ក​ការ​ភ្ញាក់ផ្អើល​យ៉ាង​អនាធិបតេយ្យ ដែល​ហេតុការណ៍​នេះ​បាន​កើតឡើង​កាលពី​វេលា​ម៉ោង​១១ និង​៤០​នាទី​យប់ ថ្ងៃ​ទី​១៣ ខែសីហា​ឆ្នាំ​២០១៥ នៅ​ចំណុច​មុខ​ក្លិប​កម្សាន្ត​ហា​យ', 'http://kohsantepheapdaily.com.kh/article/126675.html', 'http://i.kspg.co/2015/8/d024a5aa-d9df-4fdf-b735-a9124b8ec335.jpg?w=285&h=170&mode=carve', 'ថ្ងៃសុក្រ ទី១៤ ខែសីហា ឆ្នាំ២០១៥ ម៉ោង ០០:២៦', '0', '0', '0', 't', '2015-08-14', 'f');
INSERT INTO "tbnews" VALUES ('761', 'B020102', 'B020000', 'ទំនាស់​គ្នា​តិចតួច​ឈាន​ដល់​ការ​ប្រើ​អំពើ​ហិង្សា ត្រូវ​ឃាត់ខ្លួន​ទាំង​២​នាក់', 'រាជធានី​ភ្នំពេញ ៖ បុរស​ម្នាក់​បាន​យក​ខ្ទះ​ខ្លាញ់​កំពុង​ពុះ​ជះ​ទៅ​លើ​គូ​ជម្លោះ​របស់​ខ្លួន​តែ​មិន​ត្រូវ បែ​ជា​ត្រូវ​អ្នកដំណើរ​៤​នាក់ ដោយ​រង​របួសធ្ងន់​២​នាក់ ស្រាល​២​នាក់ ។ ហេតុការណ៍​នេះ​បាន​កើតឡើង​កាលពី​វេលា​ម៉ោង​៤​ល្ងាច ថ្ងៃ​ទី​១៣ សីហា ២០១៥', 'http://kohsantepheapdaily.com.kh/article/126674.html', 'http://i.kspg.co/2015/8/8872fc23-2e86-443d-b2ff-e4fc00e18dfb.jpg?w=285&h=170&mode=carve', 'ថ្ងៃសុក្រ ទី១៤ ខែសីហា ឆ្នាំ២០១៥ ម៉ោង ០០:០០', '0', '0', '0', 't', '2015-08-14', 'f');
INSERT INTO "tbnews" VALUES ('762', 'B020102', 'B020000', 'នារី​ម្នាក់​ត្រូវ​ចោរឆក់​ទូរស័ព្ទ iPhone 6 នៅ​ម្តុំ​នាគ​បាញ់​ទឹក​យក​ទៅ​បាត់​ដោយ​សុវត្ថិភាព', 'រាជធានី​ភ្នំពេញ ៖ នារី​ម្នាក់​ត្រូវ​បាន​ចោរឆក់​ទូរស័ព្ទ​១​គ្រឿង​ម៉ាក iPhone 6 ពណ៌​មាស​យក​ទៅ​បាត់​ ខណៈ​កំពុង​ជិះ​ម៉ូតូ​និយាយ​ទូរស័ព្ទ​កាលពី​វេលា​ម៉ោង​១០​យប់ ថ្ងៃ​ទី​១៣ សីហា ២០១៥ ត្រង់​ចំណុច​នាគ​បាញ់​ទឹក កែង​មហាវិថី​ព្រះ​សីហ​នុ និង​មហាវិថី​សុធារស', 'http://kohsantepheapdaily.com.kh/article/126672.html', 'http://i.kspg.co/2015/8/047bd278-617d-48a0-b851-2eb6dac8702f.jpg?w=285&h=170&mode=carve', 'ថ្ងៃព្រហស្បត្ត៏ ទី១៣ ខែសីហា ឆ្នាំ២០១៥ ម៉ោង ២២:៤៧', '0', '0', '0', 't', '2015-08-13', 'f');
INSERT INTO "tbnews" VALUES ('763', 'B020102', 'B020000', 'សមត្ថកិច្ច​នគរបាល​ក្រុង​កំពង់ឆ្នាំង​ឃាត់​ម្ចាស់​ម៉ូតូ​កែ​ច្នៃ​ខុស​បច្ចេកទេស​២​គ្រឿង​យក​មក​អប់រំ​ណែនាំ', 'ខេត្តកំពង់ឆ្នាំង ៖ ម៉ូតូ​កែ​ច្នៃ​ខុស​លក្ខណៈ​បច្ចេកទេស​២​គ្រឿង​ត្រូវ​បាន​កម្លាំង​សមត្ថកិច្ច​នគរបាល​ក្រុង ធ្វើការ​ចាប់​ឃាត់​យក​មក​កាន់​អធិការដ្ឋាន​នគរបាល​ក្រុង​កំពង់ឆ្នាំង​កាលពី​វេលា​ម៉ោង​១១ និង​៤៥​នាទី​យប់​ថ្ងៃ​ទី​១២ ខែសីហា ឆ្នាំ​២០១៥ នៅ​ចំណុច​ជិត​ផ្ទះសំណាក់ «​ដា​ឡា​ត់​»', 'http://kohsantepheapdaily.com.kh/article/126670.html', 'http://i.kspg.co/2015/8/f1ff98db-6946-41a8-aa29-adac99f1c968.jpg?w=285&h=170&mode=carve', 'ថ្ងៃព្រហស្បត្ត៏ ទី១៣ ខែសីហា ឆ្នាំ២០១៥ ម៉ោង ២០:៥៦', '0', '0', '0', 't', '2015-08-13', 'f');
INSERT INTO "tbnews" VALUES ('764', 'B020102', 'B020000', 'ចាប់ខ្លួន​ស្ត្រី​សង្ស័យ​ម្នាក់​បន្ទាប់​ពី​ច្របាច់ក​ស្ត្រី​ម្នាក់​ប្លន់​​ម៉ូតូ និង​កាបូប​លុយ​មិន​បាន​សម្រេច', 'រាជធានី​ភ្នំពេញ ៖ ស្ត្រី​ម្នាក់​ត្រូវ​បាន​កម្លាំង​នគរបាល​ខណ្ឌដង្កោ​ធ្វើការ​ឃាត់ខ្លួន​បាន​បន្ទាប់​ពី​បាន​រួមដៃគ្នា​ជាមួយ​បក្ខពួក​ដែល​ជា​បុរស​ម្នាក់​ទៀត​ធ្វើ​សកម្មភាព​ល្បួង​ឲ្យ​ស្ត្រី​ម្នាក់​ទៅ​ជួប រួច​បាន​ចាប់​ច្របាច់ក ប្លន់​យក​ម៉ូតូ និង​កាបូប​លុយ​មិន​បាន​សម្រេច', 'http://kohsantepheapdaily.com.kh/article/126668.html', 'http://i.kspg.co/2015/8/5c8fa52e-3093-48a7-9d45-805d4bcbbd6b.jpg?w=285&h=170&mode=carve', 'ថ្ងៃព្រហស្បត្ត៏ ទី១៣ ខែសីហា ឆ្នាំ២០១៥ ម៉ោង ១៩:៥២', '0', '0', '0', 't', '2015-08-13', 'f');
INSERT INTO "tbnews" VALUES ('765', 'B020102', 'B020000', '​​ចោរ​ដាក់​ថ្នាំ​សណ្តំ​​ម្ចាស់​ផ្ទះ​ចូល​លួច​មាស​១តម្លឹង១ជី និង​ប្រាក់មួយចំនួន ​​​នៅ​ក្រុងប៉ៃលិន', '​ខេត្ត​ប៉ៃលិន ៖ ស្ត្រី​ម្នាក់ត្រូវ​ក្រុម​ចោរ​ដាក់​ថ្នាំ​សណ្តំ​ចូល​​លួច​យក​​អស់​​មាស​១​តម្លឹង​ ១​ជី ​ប្រាក់​ថៃ១​ពាន់​បាត ​ប្រាក់​ខ្មែរ​៣០​ម៉ឺន​រៀល ខណៈ​ដេក​លង់លក់​នា​ពេល​យប់ និង​មិន​បាន​​ចាក់សោ​ទ្វារ​មុខ', 'http://kohsantepheapdaily.com.kh/article/126663.html', 'http://i.kspg.co/2015/8/e2b7efc9-4b99-447b-b7d1-6a93a7cf0555.jpg?w=285&h=170&mode=carve', 'ថ្ងៃព្រហស្បត្ត៏ ទី១៣ ខែសីហា ឆ្នាំ២០១៥ ម៉ោង ១៧:០៤', '0', '0', '0', 't', '2015-08-13', 'f');
INSERT INTO "tbnews" VALUES ('766', 'B020102', 'B020000', 'ទុក​ម៉ូតូ​មិន​ដក​សោដើរទៅ​បេះ​បន្លែ​ក្រោយ​ផ្ទះ​ ចោរ​ស្រី​ឃើញ​ជិះ​យក​ទៅ​បាត់​បាន​មួយ​ម៉ោង​ប៉ូ​លិស​ក្របួច​ជាប់​', 'ខេត្តពោធិ៍សាត់ ​៖ ​ចោរ​ស្រី​ជើងឆើត​ម្នាក់​បាន​លួច​ម៉ូតូ​អ្នកស្រុក​ដែល​ទុក​ក្បែរ​ផ្ទះ​មិន​ដក​សោ ​រួច​ហើយ​ជិះ​គេចខ្លួន​បាត់ ប៉ុន្តែ​បាន​តែ​មួយ​ម៉ោង​ប៉ុណ្ណោះ​ក៏ត្រូវ​សមត្ថកិច្ច​ដេញចាប់​បាន​មក​វិញ​។', 'http://kohsantepheapdaily.com.kh/article/126662.html', 'http://i.kspg.co/2015/8/5cb58275-1eab-4a84-b902-2c774563cda3.jpg?w=285&h=170&mode=carve', 'ថ្ងៃព្រហស្បត្ត៏ ទី១៣ ខែសីហា ឆ្នាំ២០១៥ ម៉ោង ១៦:៤៣', '0', '0', '0', 't', '2015-08-13', 'f');
INSERT INTO "tbnews" VALUES ('767', 'B020102', 'B020000', 'ប៉េ​អឹម​ខណ្ឌ​សែន​សុខ​ឃាត់ខ្លួន​អ្នក​ប្រើប្រាស់​ថ្នាំ​ញៀន​៦​នាក់​ទៀត​ហើយ​​​ក្នុងភូមិ​ត្រពាំង​ឈូក', 'រាជ​ធានីភ្នំពេញ ៖ មេបញ្ជាការ​កងរាជអាវុធហត្ថ​ខណ្ឌ​សែន​សុខ បាន​ដឹកនាំ​កម្លាំង​ចុះ​ឃាត់ខ្លួន​អ្នក​ប្រើប្រាស់​ថ្នាំ​ញៀនបាន​៦​នាក់ទៀត​ហើយ ដោយ​​ដកហូត​បាន​ថ្នាំ​ញៀន​២​កញ្ចប់​តូច​ ', 'http://kohsantepheapdaily.com.kh/article/126618.html', 'http://i.kspg.co/2015/8/9af751b2-61a8-4585-851c-fcf4a25a9fe4.jpg?w=285&h=170&mode=carve', 'ថ្ងៃព្រហស្បត្ត៏ ទី១៣ ខែសីហា ឆ្នាំ២០១៥ ម៉ោង ១៣:៣០', '0', '0', '0', 't', '2015-08-13', 'f');
INSERT INTO "tbnews" VALUES ('768', 'B020102', 'B020000', '​ថៃ​បញ្ជូន​​​ក្មេង​ស្រី​​​រងគ្រោះចំនួន​៨នាក់​​​​ប្រគល់​ឱ្យ​​​កម្ពុជា', 'ខេត្ត​បន្ទាយមានជ័យ​ ៖ ​​ក្មេង​ស្រី​ចំនួន​៨នាក់​​​ដែល​ត្រូវ​គេ​​កេង​ប្រវ័ញ្ច​​កម្លាំង​ពល​កម្ម​និង​បំពាន​​រំលោភ​ផ្លូវ​ភេទ​ ត្រូវ​ភាគី​ថៃ​បញ្ជូន​មក​​​​ដល់​កម្ពុជា​ប្រគល់​ឱ្យ​មជ្ឈមណ្ឌល​សុំ​ចត​របស់​ក្រសួង​សង្គម​កិច្ច​​ នៅក្រុង​ប៉ោយប៉ែត ', 'http://kohsantepheapdaily.com.kh/article/126617.html', 'http://i.kspg.co/2015/8/f6152a1c-5aa8-4cd9-ba96-ac22daaed095.jpg?w=285&h=170&mode=carve', 'ថ្ងៃព្រហស្បត្ត៏ ទី១៣ ខែសីហា ឆ្នាំ២០១៥ ម៉ោង ១៣:២៥', '0', '0', '0', 't', '2015-08-13', 'f');
INSERT INTO "tbnews" VALUES ('769', 'B020102', 'B020000', 'យាម​ម៉ូតូ​ឱ្យ​គេ​បាន​លុយ​តិច​ពេក​ បែកគំនិត​ចេញ​ឆក់ ​ត្រូវ​សមត្ថកិច្ច​ចាប់​បាន​ម្នាក់', 'រាជ​ធានីភ្នំពេញ​ ៖ នារី​ម្នាក់​បាន​ដើរ​ចេញពី​ធ្វើការ​ត្រលប់​ទៅ​ផ្ទះវិញត្រូវ​ចោរ២នាក់ឆក់កាបូប​បាន​សម្រេច ហើយ​ត្រូវសមត្ថកិច្ចជិះ​ម៉ូតូ​ប្រទះ​ឃើញបាន​ដេញ​តាមធាក់​ម៉ូតូ​ផ្តួលចាប់​បាន​ម្នាក់ ', 'http://kohsantepheapdaily.com.kh/article/126599.html', 'http://i.kspg.co/2015/8/a4edd497-fd4e-4a09-a387-c572d7ab8b52.jpg?w=285&h=170&mode=carve', 'ថ្ងៃព្រហស្បត្ត៏ ទី១៣ ខែសីហា ឆ្នាំ២០១៥ ម៉ោង ១០:២៥', '0', '0', '0', 't', '2015-08-13', 'f');
INSERT INTO "tbnews" VALUES ('770', 'B020102', 'B020000', 'បុរស​ម្នាក់​ត្រូវ​ក្រុម​ចោរ​យក​ស្នាប់​កាំភ្លើង​វាយ​បែកក្បាលប្លន់​យក​ម៉ូតូ​​​ទៅបាត់', 'រាជ​ធានី​ភ្នំពេញ​ ៖ បុរស​ម្នាក់​ ខណៈ​កំពុង​ជិះ​ម៉ូតូហុងដា​ឌ្រី​ម​មួយ​គ្រឿង ព​ណ៌ខ្មៅ នៅ​តាម​ផ្លូវត្រូវ​ក្រុម​ចោរប្លន់​៤​នាក់​ជិះ​ម៉ូតូ​២​គ្រឿងយក​ស្នាប់​កាំភ្លើង​វាយ​ក្បាល​ចំ​ពីក្រោយបណ្ដាល​ឱ្យ​​ដួល​ម៉ូតូ​បោក​', 'http://kohsantepheapdaily.com.kh/article/126576.html', 'http://i.kspg.co/2015/8/b8279184-9ef5-4ef6-b789-010c65b673e3.jpg?w=285&h=170&mode=carve', 'ថ្ងៃព្រហស្បត្ត៏ ទី១៣ ខែសីហា ឆ្នាំ២០១៥ ម៉ោង ០៩:០៦', '0', '0', '0', 't', '2015-08-13', 'f');
INSERT INTO "tbnews" VALUES ('771', 'B020102', 'B020000', 'យក​ទូរស័ព្ទ​ទើប​ឆក់​បាន​ទៅ​បញ្ចាំ ម្ចាស់​ហាង​ស្គាល់​ម្ចាស់​ទូរស័ព្ទ​ជា​ភេទ​ទី​៣​ដូច​គ្នា ត្រូវ​ជាប់​ខ្នោះ', 'ខេត្តបន្ទាយមានជ័យ ៖ ជនសង្ស័យ​ម្នាក់​ឆក់​ទូរស័ព្ទ​ដៃ​បាន​សម្រេច​រួច​យក​ទៅ​បញ្ចាំ​ត្រូវ​បាន​ម្ចាស់​ហាង​ប្តឹង​នគរបាល​ប៉ុស្តិ៍​សង្កាត់​ព្រះ​ពន្លា​ឱ្យ​ចាប់​ឃាត់ខ្លួន​ប្រគល់​ឲ្យ​នគរបាល​ការិយាល័យ​ព្រហ្មទណ្ឌ​កម្រិត​ស្រាល​ដើម្បី​ចាត់ការ​បន្ត​តាម​និ​តិ​វិធី​ច្បាប់​។', 'http://kohsantepheapdaily.com.kh/article/126573.html', 'http://i.kspg.co/2015/8/52168ac5-62a4-454b-9692-2a0abfbd954b.jpg?w=285&h=170&mode=carve', 'ថ្ងៃពុធ ទី១២ ខែសីហា ឆ្នាំ២០១៥ ម៉ោង ១៩:៣០', '0', '0', '0', 't', '2015-08-12', 'f');
INSERT INTO "tbnews" VALUES ('772', 'B020102', 'B020000', 'ជនសង្ស័យ​ប៉ុនប៉ង​សម្លាប់​ប្រពន្ធ​ខ្លួនឯង​នៅ​ស្រុក​សៀម​ប៉ា​ង​ត្រូវ​បាន​ចាប់ខ្លួន​ហើយ', 'ខេត្តស្ទឹងត្រែង ៖ ករណី​ប៉ុនប៉ង​ឃាតកម្ម​ទៅ​លើ​ប្រពន្ធ​ខ្លួនឯង​ដែល​បាន​កើតឡើង​កាលពី​ថ្ងៃ​ទី​១០ សីហា ២០១៥ នៅ​ស្រុក​សៀម​ប៉ា​ង ពេល​នេះ​ជនសង្ស័យ​ត្រូវ​បាន​កម្លាំង​សមត្ថកិច្ច​ឡាវ​ធ្វើការ​ចាប់ខ្លួន​បាន​នៅ​ភូមិ​តា​អួ​ក ស្រុក​មឿង​ខូង ខេត្ត​ចំប៉ា​សាក់', 'http://kohsantepheapdaily.com.kh/article/126572.html', 'http://i.kspg.co/2015/8/810f6739-badb-4d23-9615-4477f2f65c82.jpg?w=285&h=170&mode=carve', 'ថ្ងៃពុធ ទី១២ ខែសីហា ឆ្នាំ២០១៥ ម៉ោង ១៨:៥១', '0', '0', '0', 't', '2015-08-12', 'f');
INSERT INTO "tbnews" VALUES ('773', 'B020102', 'B020000', 'រាយការណ៍​ប្រាប់​វិទ្យុ​ថា​ចោរ​ភ្ជង់​ប្លន់​យក​ម៉ូតូ សមត្ថកិច្ច​ចុះ​ទៅ​ដល់​រត់គេច​អស់', 'រាជធានី​ភ្នំពេញ ៖ សង្សារ​មួយគូ​បាន​ទូរស័ព្ទ​ប្រាប់​វិទ្យុ​ថា​ត្រូវ​ចោរ​ភ្ជង់​ប្លន់​យក​ម៉ូតូ​មួយ​គ្រឿង​ទៅ​បាត់ ពេល​ដែល​សមត្ថកិច្ច​ដឹង​ចុះ​ទៅ​ដល់​ស្រាប់តែ​ជន​រង​គ្រោះ​ទាំង​២​នាក់​នាំ​គ្នា​ឡើង​ជិះ​ម៉ូតូ​របស់​សាច់ញាតិ​ចេញ​ទៅ​បាត់ បង្ក​ឱ្យ​មានការ​ភ្ញាក់ផ្អើល​កាលពី​វេលា​ម៉ោង​១​រំលង​អធ្រាត្រ', 'http://kohsantepheapdaily.com.kh/article/126533.html', 'http://i.kspg.co/2015/8/6a7b10bf-bfd1-4148-bf5c-6fb1147ba6c4.jpg?w=285&h=170&mode=carve', 'ថ្ងៃពុធ ទី១២ ខែសីហា ឆ្នាំ២០១៥ ម៉ោង ១៣:៥៤', '0', '0', '0', 't', '2015-08-12', 'f');
INSERT INTO "tbnews" VALUES ('774', 'B020101', 'B020000', 'លោក ហុង សុខ​ហួរ ប្រកាស​តាម​ហ្វេ​ស​ប៊ុ​ក​លោក​សម រ​ង្ស៊ី ថា​នឹង​ចូលខ្លួន​បំភ្លឺ​នៅ​ថ្ងៃ​ទី​១៧សីហា', 'រាជធានី​ភ្នំពេញ ៖ មុន​នេះ​បន្តិច លោក​ហុង សុខ​ហួរ សមាជិក​ព្រឹទ្ធសភា​មក​ពី​គណបក្ស​សង្គ្រោះ​ជាតិ បាន​ប្រកាស​តាម​ហ្វេ​ស​ប៊ុ​ករប​ស់លោក សម រ​ង្ស៊ី ថា នឹង​ចូលខ្លួន​បំភ្លឺ​ចំពោះ​មុខ​អាជ្ញា​ធរមាន​សមត្ថកិច្ច​នៅ​ថ្ងៃ​ចន្ទ ទី​១៧ ខែសីហា ឆ្នាំ​២០១៥ វេលា​ម៉ោង​១០​ព្រឹក', 'http://kohsantepheapdaily.com.kh/article/126768.html', 'http://i.kspg.co/2015/8/eeb88f27-1c89-4ff4-aa9a-4f599aa75495.jpg?w=285&h=170&mode=carve', 'ថ្ងៃសុក្រ ទី១៤ ខែសីហា ឆ្នាំ២០១៥ ម៉ោង ២៣:១១', '0', '0', '0', 't', '2015-08-14', 'f');
INSERT INTO "tbnews" VALUES ('775', 'B020101', 'B020000', 'ទំនាស់​រវាង​ពុទ្ធបរិស័ទ​និង​ចៅអធិការ​វត្ត​ឧទ្ទម្ពរ​បញ្ចប់​ដោយ​មិន​សប្បាយចិត្ត​ព្រោះ​គ្មាន​លោក​សម្រាប់​ធ្វើបុណ្យ', 'ខេត្តកំពង់ឆ្នាំង ៖ ទំនាស់​ដ៏​រាំ​រ៉ៃ​រវាង​ប្រជាពលរដ្ឋ និង​ចៅអធិការ​វត្ត​ឧទ្ទម្ពរ ស្ថិត​ក្នុងភូមិ​ឧទ្ទម្ពរ ឃុំ​ស្វាយ​ជ្រុំ ស្រុក​រលាប្អៀរ បាន​បញ្ជ​ប់​នៅ​ព្រឹក​ថ្ងៃ​ទី​១៤ ខែសីហា ឆ្នាំ​២០១៥ បន្ទាប់​ពី​មានការ​ប្រជុំ​រវាង​ប្រជាពលរដ្ឋ និង​មន្ត្រី​សង្ឃ​ខេត្ត ដែល​មាន​អាជ្ញាធរ​មូលដ្ឋាន​ចូលរួម​ជា​ច្រើន​នាក់​ផង​ដែរ ។', 'http://kohsantepheapdaily.com.kh/article/126764.html', 'http://i.kspg.co/2015/8/2eb742f1-4a13-403e-bf72-ac58d8b30aa8.jpg?w=285&h=170&mode=carve', 'ថ្ងៃសុក្រ ទី១៤ ខែសីហា ឆ្នាំ២០១៥ ម៉ោង ២០:៣៩', '0', '0', '0', 't', '2015-08-14', 'f');
INSERT INTO "tbnews" VALUES ('776', 'B020101', 'B020000', 'ភាគី​រោងចក្រ​និង​កម្មករ​-​កម្មការិនី​រោងចក្រ​កាត់ដេរ​តៃ​អ៊ី​ហ្ស៊ី​ទទួលយក​សំណូម​គ្នា​ទៅ​វិញ​ទៅ​មក', 'ខេត្តពោធិ៍សាត់ ៖ នៅ​ថ្ងៃ​ទី​១៤ ខែសីហា ឆ្នាំ​២០១៥ លោក អ៊ឹ​ង គឹ​ម​លាង អភិបាលរង តំណាង​ឲ្យ​អភិបាល នៃ​គណៈ​អភិបាលខេត្ត​ពោធិ៍សាត់ បាន​អញ្ជើញ​ទៅ​កាន់​រោងចក្រ​កាត់ដេរ​តៃ​អ៊ី​ហ្ស៊ី​នៅ​ស្រុក​ក្រគរ ដើម្បី​សម្របសម្រួល​ដោះស្រាយ​បញ្ហា និង​សំណូមពរ​មួយ​ចំនួន​ដែល​មាន​ភាព​មិន​ស៊ីសង្វាក់​គ្នា', 'http://kohsantepheapdaily.com.kh/article/126762.html', 'http://i.kspg.co/2015/8/e14e148a-baa8-4fa3-82c2-7c80a6f0ee7c.jpg?w=285&h=170&mode=carve', 'ថ្ងៃសុក្រ ទី១៤ ខែសីហា ឆ្នាំ២០១៥ ម៉ោង ១៩:៤១', '0', '0', '0', 't', '2015-08-14', 'f');
INSERT INTO "tbnews" VALUES ('777', 'B020101', 'B020000', 'សិក្ខាសាលា​ថ្នាក់​តំបន់ ស្តី​ពី​ការ​បង្កើត​មជ្ឈមណ្ឌល​តំបន់​អាស៊ាន​សម្រាប់​សកម្មភាព​មីន', 'ខេត្តសៀមរាប ៖ ក្រសួងការបរទេស និង​សហប្រតិបត្តិការ​អន្តរជាតិ​របស់​ប្រទេស​កម្ពុជា ក្រោម​កិច្ចសហការ​ជាមួយ សាធារណរដ្ឋ​កូរ៉េ និង​លេខាធិការ​អាស៊ាន បាន​ដំណើរការ​អង្គ​សិក្ខាសាលា​ថ្នាក់​តំបន់​លើក​ទី​២ ស្តី​ពី​ការ​បង្កើត​ម​ជ្ឈ មណ្ឌល​អាស៊ាន​សម្រាប់​សកម្មភាព​មីន ។', 'http://kohsantepheapdaily.com.kh/article/126760.html', 'http://i.kspg.co/2015/8/f1137062-751b-4419-9021-7800ffab4a85.jpg?w=285&h=170&mode=carve', 'ថ្ងៃសុក្រ ទី១៤ ខែសីហា ឆ្នាំ២០១៥ ម៉ោង ១៨:២៦', '0', '0', '0', 't', '2015-08-14', 'f');
INSERT INTO "tbnews" VALUES ('778', 'B020101', 'B020000', 'ជន​រង​គ្រោះ​ដោយ​គ្រោះ​ថ្នាក់​ចរាចរណ៍​នៅ​អន្លង់​វែង​៩នាក់​កំពុង​សម្រាក​ព្យា​បាល​នៅ​មន្ទីរ​ពេទ្យខេត្ត​សៀមរាប', 'ខេត្តសៀមរាប ​៖ ​តាម​ការ​បញ្ជាក់​ពី​​លោក​ឱស​ថ​ការី លឹ​ម ហេង អនុប្រធាន​មន្ទីរពេទ្យ​បង្អែក​ខេត្តសៀមរាប នៅ​ថ្ងៃទី១៤ សីហា ២០១៥​នេះ បានឱ្យដឹង​​ថា​​ ជន​រង​គ្រោះ​ដោយ​គ្រោះ​ថ្នាក់​ចរាចរណ៍​ដែល​បង្ក​ឡើង', 'http://kohsantepheapdaily.com.kh/article/126759.html', 'http://i.kspg.co/2015/8/a9fc84c7-4cd2-4cd8-9059-031f5e02260e.jpg?w=285&h=170&mode=carve', 'ថ្ងៃសុក្រ ទី១៤ ខែសីហា ឆ្នាំ២០១៥ ម៉ោង ១៨:០៣', '0', '0', '0', 't', '2015-08-14', 'f');
INSERT INTO "tbnews" VALUES ('779', 'B020101', 'B020000', 'សម្តេចតេជោ ​ហ៊ុន សែន ​ថ្លែងអំណរគុណ​វៀតណាមដែលជួយបំពាក់ប្រព័ន្ឋផ្សព្វផ្សាយ', 'រាជធានីភ្នំពេញ ៖ ​សម្តេចអគ្គមហាសេនាបតីតេជោ​ ​ហ៊ុន សែន ​​បានមានប្រសាសន៍​ថ្លែង​អំណរគុណដល់លោក​ ​​Nguyen Dang Tiln ​​សមាជិកមជ្ឈឹមបក្ស​និងជាអគ្គនាយក​', 'http://kohsantepheapdaily.com.kh/article/126755.html', 'http://i.kspg.co/2015/8/3e3ab7b1-c180-42a9-bdf7-818f7a6ec252.jpg?w=285&h=170&mode=carve', 'ថ្ងៃសុក្រ ទី១៤ ខែសីហា ឆ្នាំ២០១៥ ម៉ោង ១៧:៣៧', '0', '0', '0', 't', '2015-08-14', 'f');
INSERT INTO "tbnews" VALUES ('780', 'B020101', 'B020000', '​សាលាឧទ្ធរណ៍​គ្មាន​បេះដូង​​ បន្ត​តម្កល់​ការ​សម្រេចរបស់​សាលា​ដំបូងឃុំ​ខ្លួន​ម្តា​យ​កូន​ភ្លោះ​២នាក់​', 'រាជ​ធានី​ភ្នំពេញ​ ៖ សាលដីកាសាលាឧទ្ធរណ៍​ហាក់ដូចជា​គ្មាន​បេះដូងអាណិតកូន​ភ្លោះ​២​នាក់​អាយុ​ទើបបាន៨​ខែសោះ គឺបាន​ប្រកាស​តម្កល់​ការ​សម្រេច​ឃុំខ្លួន​ស្ត្រីឈ្មោះ​ហេង លី​លី ហៅ​ស្រី​អូន ជា​ម្តាយកូន​ភ្លោះ​២​', 'http://kohsantepheapdaily.com.kh/article/126754.html', 'http://i.kspg.co/2015/8/6b6a2eaa-a129-4142-bc3f-987b98a589f7.jpg?w=285&h=170&mode=carve', 'ថ្ងៃសុក្រ ទី១៤ ខែសីហា ឆ្នាំ២០១៥ ម៉ោង ១៧:៣៦', '0', '0', '0', 't', '2015-08-14', 'f');
INSERT INTO "tbnews" VALUES ('781', 'B020101', 'B020000', 'ប៉ូលិស​ស្រុក​អន្លង់​វែង​ម្នាក់​ក្លាយជា​ឈ្មួញ​ឈើ និង​នាំ​ធ្យូង​ចេញ​ទៅ​ថៃ', 'ខេត្តឧត្តរមានជ័យ ៖ លោក​អនុសេនីយ៍ត្រី ថា​ន់ ម៉ៅ ប៉ូលិស​ស្រុក​អន្លង់​វែង​បាន​ក្លាយ​ខ្លួន​ជា​ក្រុម​ឈ្មួញ​ឈើ និង​នាំ​ធ្យូង​ចូល​ទៅ​លក់​ឲ្យ​ក្រុម​ឈ្មួញ​ថៃ​យ៉ាង​គគ្រឹកគគ្រេង​កាលពី​រសៀល​ថ្ងៃ​ទី​១២ សីហា ២០១៥ ។', 'http://kohsantepheapdaily.com.kh/article/126753.html', 'http://i.kspg.co/2015/8/18f32ef6-fcd3-4b4f-8c41-0af4a2deddb4.jpg?w=285&h=170&mode=carve', 'ថ្ងៃសុក្រ ទី១៤ ខែសីហា ឆ្នាំ២០១៥ ម៉ោង ១៧:៣៥', '0', '0', '0', 't', '2015-08-14', 'f');
INSERT INTO "tbnews" VALUES ('782', 'B020101', 'B020000', 'អ្នក​ភូមិ​ចោទ​មន្ត្រី​អធិ​ការ​ដ្ឋាន​រដ្ឋ​បាល​ជល​ផល​ខាង​ជើង​បឹង​ទន្លេ​សាប​ជំរិត​ទារ​លុយ', 'ខេត្ត​កំពង់ធំ ៖ វេទិការ​សាធារណៈ​ស្តី​ពី​ការ​ទប់ស្កាត់​បទល្មើស​នេសាទ​ក្នុង​សហ​គម​ន៍​នៅ​រដូវ​បិទ​នេសាទ ត្រូវ​បាន​ប្រារព្ធ​ធ្វើ​នៅ​ព្រឹក​ថ្ងៃ​ទី​១៤ សីហា ​២០១៥ ក្នុង​ឃុំ​កំពង់​គោ ស្រុក​កំពង់ស្វាយ ក្រោម​ការ​សហការពី​អង្គការ​ហូ​ម', 'http://kohsantepheapdaily.com.kh/article/126752.html', 'http://i.kspg.co/2015/8/cdaf80e1-03b3-4c26-ac0b-a46b15e4cac2.jpg?w=285&h=170&mode=carve', 'ថ្ងៃសុក្រ ទី១៤ ខែសីហា ឆ្នាំ២០១៥ ម៉ោង ១៧:២៦', '0', '0', '0', 't', '2015-08-14', 'f');
INSERT INTO "tbnews" VALUES ('783', 'B020101', 'B020000', 'ជន​ជាតិ​បារាំង​ម្នាក់​ដួល​ស្លាប់ក្នុង​បន្ទប់​ទឹក​ផ្ទះ​សំណាក់​បារាយណ៍​អង្គរ', 'រាជ​ធានី​ភ្នំពេញ​ ៖ កាល​ពី​​​ម៉ោង​៤ និង​១៧​នាទី​រសៀល ថ្ងៃ​ទី​១៣​ សីហា​ នៅផ្ទះ​សំណាក់បា​រា​យណ៍អង្គរ​ ផ្ទះ​លេខ​៥០-៥២​ ផ្លូវ​១៣០ ​ភូមិ​១៣​ ក្រុម​២១​ សង្កាត់​ផ្សារ​កណ្ដាល​១​ មាន​ករណី​ជនបរទេស​ម្នាក់ដួល​សន្លប់​', 'http://kohsantepheapdaily.com.kh/article/126751.html', 'http://i.kspg.co/2015/8/91716249-7d1b-4d51-87ad-261f972de9ac.jpg?w=285&h=170&mode=carve', 'ថ្ងៃសុក្រ ទី១៤ ខែសីហា ឆ្នាំ២០១៥ ម៉ោង ១៧:២១', '0', '0', '0', 't', '2015-08-14', 'f');
INSERT INTO "tbnews" VALUES ('784', 'B020101', 'B020000', '​ក្រុម​កម្មករ​នាំគ្នា​មក​​សាលា​ដំបូង​​សុំ​ឱ្យ​ជួយ​​អន្តរាគមន៍​ ​រឿង​ថៅកែ​​​រោង​ចក្រ​​​​​​​​មិន​បើក​ប្រាក់​​ខែ', 'ខេត្តកណ្ដាល ​៖ ​ក្រុម​​កម្មករ-កម្មការិនី​​ចំនួន​៥០​នាក់ តំណាង​ឲ្យ១៦០​នាក់​ដែល​ធ្វើការ​នៅ​រោងចក្រកាត់ដេរ ធៀន ធៀន នៅ​ភូមិទួល​ត្នោត ឃុំ​ពើក ស្រុកអង្គស្នួល ​​​កាលពី​ម៉ោង​​​១១​និង​៣០​នាទី​ព្រឹកថ្ងៃ​ទី​១៤ សីហា', 'http://kohsantepheapdaily.com.kh/article/126750.html', 'http://i.kspg.co/2015/8/4c2c9208-c4a7-4bec-8f1d-ead237e83fee.jpg?w=285&h=170&mode=carve', 'ថ្ងៃសុក្រ ទី១៤ ខែសីហា ឆ្នាំ២០១៥ ម៉ោង ១៧:១៦', '0', '0', '0', 't', '2015-08-14', 'f');
INSERT INTO "tbnews" VALUES ('785', 'B020101', 'B020000', 'បុរសពោះម៉ាយ​ម្នាក់ស៊ីឈ្នួល​រើ​ផ្ទះ​ឱ្យគេមិន​ប្រយ័ត្ន​ធ្លាក់​​បាក់​.ក​ស្លាប់​ចោល​កូន​៣នាក់', 'ខេត្តបន្ទាយមានជ័យ​​ ៖ បុរស​ពោះម៉ាយម្នាក់​ស៊ីឈ្នួល​រើ​ផ្ទះឱ្យ​គេ​បាន​​របូត​ដៃ​ធ្លាក់ពី​លើ​ជញ្ជាំង​ផ្ទះ​មក​ដី​​​បាក់​ក​របួស​ធ្ងន់ ហើយ​បាន​ស្លាប់​ខណៈ​សង្គ្រោះ​នៅ​ក្នុងមន្ទីរ​ពេទ្យ​បាន​មួយ​យប់​។', 'http://kohsantepheapdaily.com.kh/article/126749.html', 'http://i.kspg.co/2015/8/1d129839-0df6-4fca-b443-32ee0fb031bd.jpg?w=285&h=170&mode=carve', 'ថ្ងៃសុក្រ ទី១៤ ខែសីហា ឆ្នាំ២០១៥ ម៉ោង ១៧:០២', '0', '0', '0', 't', '2015-08-14', 'f');
INSERT INTO "tbnews" VALUES ('786', 'B020101', 'B020000', 'គណ​បក្ស​ប្រជា​ធិបតេយ្យ​មូល​ដ្ឋាន​មិន​ពេញ​ចិត្ត​ក្រសួង​អប់រំ​ណែ​នាំ​ឱ្យ​ទប់​ស្កាត់ និង​ការ​ឃោសនា​នយោ​បាយ​តាម​សាលា​រៀន', 'គណ​បក្ស​ប្រជា​ធិបតេយ្យ​មូល​ដ្ឋាន​មិន​ពេញ​ចិត្ត​ក្រសួង​អប់រំ​ណែ​នាំ​ឱ្យ​ទប់​ស្កាត់ និង​ការ​ឃោសនា​នយោ​បាយ​តាម​សាលា​រៀន', 'http://kohsantepheapdaily.com.kh/article/126748.html', 'http://i.kspg.co/2015/8/0c0b610d-297b-4187-a182-bebde2f5df1b.jpg?w=285&h=170&mode=carve', 'ថ្ងៃសុក្រ ទី១៤ ខែសីហា ឆ្នាំ២០១៥ ម៉ោង ១៧:០១', '0', '0', '0', 't', '2015-08-14', 'f');
INSERT INTO "tbnews" VALUES ('787', 'B020101', 'B020000', 'បុរស​ម្នាក់​ត្រូវ​​​ចរន្ត​អគ្គិស​នី​ឆក់​របួស​ធ្ងន់​ទើ​លើ​បង្គោលភ្លើង​​​​', 'រាជ​ធានីភ្នំពេញ​ ៖ បុរស​ម្នាក់​ត្រូវ​​ចរន្ត​អគ្គិស​នី​ឆក់​​​ទើ​នៅ​លើបង្គោលភ្លើង​រង​របួស​ធ្ងន់ បង្ក​ឲ្យ​មានការ​ភ្ញាក់ផ្អើល កាល​ពី​វេលា​ម៉ោង៤រសៀល​ ថ្ងៃ​ទី​១៤ ​សីហា ​​២០១៥ នៅ​ក្នុងភូមិ​រោល​ជ្រូក សង្កាត់​ពងទឹក ខណ្ឌដង្កោ ​​។', 'http://kohsantepheapdaily.com.kh/article/126747.html', 'http://i.kspg.co/news_pic_kh.png?w=285&h=170&mode=carve', 'ថ្ងៃសុក្រ ទី១៤ ខែសីហា ឆ្នាំ២០១៥ ម៉ោង ១៦:៤២', '0', '0', '0', 't', '2015-08-14', 'f');
INSERT INTO "tbnews" VALUES ('788', 'B020101', 'B020000', '​​វីដេ​អូ​ឃ្លីបជន​ជាតិ​ភាគ​តិច​ចារ៉ាយ​កប់​សាក​សព​ជាមួយ​ម៉ូតូផ្អើល​ពេញ Facebook', 'ខេត្ត​រតន​គិរី​ ៖ ទំ​លៀម​ទំលាប់​ប្រពៃណី​បងប្អូន​ជនជាតិ​ដើម​ភាគតិច​ចារ៉ាយ​នៅ​តំបន់​ភាគ​ឦសាន នៅ​គោរព​ប្រពៃណី​របស់​ខ្លួន​តាំងពី​ដូនតា​យូរលង់​ណាស់​មក​ហើយ', 'http://kohsantepheapdaily.com.kh/article/126743.html', 'http://i.kspg.co/2015/8/1efc4480-ba66-432c-aa37-fd41f90e074e.jpg?w=285&h=170&mode=carve', 'ថ្ងៃសុក្រ ទី១៤ ខែសីហា ឆ្នាំ២០១៥ ម៉ោង ១៦:២១', '0', '0', '0', 't', '2015-08-14', 'f');
INSERT INTO "tbnews" VALUES ('789', 'B020101', 'B020000', '​មន្ត្រីនគរបាល​​ក្រុង​កំពត ​ចុះ​​​ធ្វើ​អត្តសញ្ញាណ​ប័​ណ្ណ​ថ្មីជូន​ពលរដ្ឋ​ជាង២.០០០​នាក់ ក្នុង​សង្កាត់​អណ្តូង​ខ្មែរ​​', 'ខេត្តកំពត ​៖ យោង​តាម​ប្រសាសន៍លោក​អនុសេនីយ៍ឯក ឈុ​ន សំណាង អធិការ​នគរ​បាល​រង​ក្រុង​កំពត ទទួល​បន្ទុក​អត្ត​ស​ញ្ញាណ​កម្ម ​បាន​ឲ្យ​ដឹង​ថា​ តាម​អនុសាសន៍​ក្នុង​ស្មារតី​​អង្គ​ប្រជុំ​របស់​គណៈអភិបាលខេត្ត​', 'http://kohsantepheapdaily.com.kh/article/126741.html', 'http://i.kspg.co/2015/8/65a0e01a-65c3-4bbc-a38f-dc17dabeb423.jpg?w=285&h=170&mode=carve', 'ថ្ងៃសុក្រ ទី១៤ ខែសីហា ឆ្នាំ២០១៥ ម៉ោង ១៦:១០', '0', '0', '0', 't', '2015-08-14', 'f');
INSERT INTO "tbnews" VALUES ('790', 'B020101', 'B020000', 'លោក ហង់ ជួនណារ៉ុន​៖ សិស្សប្រឡង​ឆមាសធ្លាក់​និងឈប់អត់ច្បាប់​លើសពីបីដង​មិនឲ្យ​ប្រឡងបាក់ឌុប', 'រាជធានីភ្នំពេញ ​៖ រដ្ឋមន្រ្តីក្រសួងអប់រំ ​យុវជននិងកីឡា ​លោកបណ្ឌិត្យ ​ហង់ ជួនណារ៉ុន​ បានមានប្រសាសន៍ថា ​សិស្សដែលប្រឡង​ឆមាសធ្លាក់ ​​និងឈប់អត់ច្បាប់​លើស​', 'http://kohsantepheapdaily.com.kh/article/126740.html', 'http://i.kspg.co/2015/8/5e077280-840d-47b9-bc87-00d546101ae6.jpg?w=285&h=170&mode=carve', 'ថ្ងៃសុក្រ ទី១៤ ខែសីហា ឆ្នាំ២០១៥ ម៉ោង ១៦:០២', '0', '0', '0', 't', '2015-08-14', 'f');
INSERT INTO "tbnews" VALUES ('791', 'B020101', 'B020000', 'ក្មេង​ប្រុស​២​នាក់​លង់ទឹក​ស្រះ​ស្លាប់ ព្រោះតែ​ចុះ​ទៅ​រើស​បាល់', 'ខេត្តបាត់ដំបង ៖ មាន​ហេតុការណ៍​ភ្ញាក់ផ្អើល​មួយ កាលពី​វេលា​ម៉ោង​០៥ និង​២០​នាទី​ល្ងាច​ថ្ងៃ​ទី​១៣ សីហា ២០១៥ ខណៈ​ក្មេង​ប្រុស​តូច​២​នាក់​បងប្អូន​ជីដូនមួយ បាន​លង់ទឹក​ស្រះ​ស្លាប់​ទាំង​២ បន្ទាប់​ពី​រត់​លេង​បាល់ ហើយ​បាល់​នោះ​បាន​ធ្លាក់​ចូល​ក្នុង​ទឹក​ស្រះ មួយ​កន្លែង​ស្ថិត​ក្នុងភូមិ​ថ្មី ឃុំ​ក្រាំង ស្រុក​កំ​រៀង ក៏​នាំ​គ្នា​ចុះ​រើស ក៏​បណ្តាល​ឱ្យ​លង់​តែ​ម្តង ។', 'http://kohsantepheapdaily.com.kh/article/126735.html', 'http://i.kspg.co/2015/8/7037f669-3161-4cec-b3c0-83a14842f423.jpg?w=285&h=170&mode=carve', 'ថ្ងៃសុក្រ ទី១៤ ខែសីហា ឆ្នាំ២០១៥ ម៉ោង ១៥:៣១', '0', '0', '0', 't', '2015-08-14', 'f');
INSERT INTO "tbnews" VALUES ('792', 'B020101', 'B020000', 'រដ្ឋមន្រ្តីក្រសួងអប់រំ ​៖ បេក្ខជនដែល​យកទូរស័ព្ទដៃចូល​ក្នុងបន្ទប់ប្រឡង​ត្រូវធ្លាក់ដោយស្វ័យប្រវត្តិ', 'រាជធានីភ្នំពេញ ​៖ បេក្ខជន ឬបេក្ខនារី ​ដែលយកទូរស័ព្ទដៃ​ចូលក្នុងបន្ទប់ប្រឡង ​នឹងត្រូវធ្លាក់ដោយស្វ័យប្រវត្តិ ។ ', 'http://kohsantepheapdaily.com.kh/article/126734.html', 'http://i.kspg.co/2015/8/83267716-abae-420e-97a4-890f19f45dff.jpg?w=285&h=170&mode=carve', 'ថ្ងៃសុក្រ ទី១៤ ខែសីហា ឆ្នាំ២០១៥ ម៉ោង ១៥:២៩', '0', '0', '0', 't', '2015-08-14', 'f');
INSERT INTO "tbnews" VALUES ('793', 'B020101', 'B020000', '​មន្ត្រីកាំកុងត្រូល ៖ ​តំ​ណាប់​ដូង​ «ថា​ញ​និ​ញ» ​ផលិត​នៅ​​​វៀតណាម​​​​ធ្វើ​ឱ្យ​​ប៉ះពាល់​សុខ​ភាព', 'ខេត្តបន្ទាយមានជ័យ ៖ លោក លន់ ស៊ី​ថា អនុប្រធាន​សាខា​កាំកុងត្រូល​ខេត្ត​​ ​បាន​ឱ្យ​ដឹង​ថា ដោយ​មានការ​ណែនាំ​ពី​នាយកដ្ឋាន​កាំកុងត្រូល ​លោក សៀន ស៊ី​ណាត ប្រធាន​សាខា​កាំកុងត្រូល​ខេត្ត​ បានឱ្យ​​មន្ត្រី​កាំកុងត្រូល​សហការ​', 'http://kohsantepheapdaily.com.kh/article/126732.html', 'http://i.kspg.co/2015/8/236a20de-e0ba-4ee3-bf8a-a92789201efd.jpg?w=285&h=170&mode=carve', 'ថ្ងៃសុក្រ ទី១៤ ខែសីហា ឆ្នាំ២០១៥ ម៉ោង ១៥:២៤', '0', '0', '0', 't', '2015-08-14', 'f');
INSERT INTO "tbnews" VALUES ('794', 'B020101', 'B020000', '​​ចិន​អញ្ជើញ​ប្រតិភូ​សាលា​រាជធានី​ទៅ​ចូលរួម​ពិធី​តាំង​ពិ​ព័រណ៌​អន្តរជាតិ​​នៅ​ក្រុង​ណា​នី​ង', 'រាជធានី​ភ្នំពេញ ៖ លោក​ឃួ​ង ស្រេង​ អភិបាលរង​រាជធានី​ភ្នំពេញ បាន​ទទួល​ជួប​សំណេះសំណាល​ជាមួយ​គណៈប្រតិភូ​ទីក្រុង​ណា​នី​ង ខេត្ត​គ​ង័​ស៊ី សាធារណរដ្ឋ​ប្រជា​មា​និតចិន​', 'http://kohsantepheapdaily.com.kh/article/126731.html', 'http://i.kspg.co/2015/8/6c25e7bf-9eeb-4d1b-8092-106fd2731e72.jpg?w=285&h=170&mode=carve', 'ថ្ងៃសុក្រ ទី១៤ ខែសីហា ឆ្នាំ២០១៥ ម៉ោង ១៥:២៣', '0', '0', '0', 't', '2015-08-14', 'f');
INSERT INTO "tbnews" VALUES ('795', 'B020101', 'B020000', '​ប្រកាស​ទទួលស្គាល់​សមាសភាព​អនុ​សាខា​សមាគមនារី​កម្ពុជា​ដើម្បី​សន្តិភាព​​ និង​អភិវឌ្ឍន៍', 'រាជធានី​ភ្នំពេញ ៖ នៅ​មន្ទីរ​សាធារណការ និង​ដឹក​ជញ្ជូន​រាជធានី​ភ្នំពេញ នា​ព្រឹក​ថ្ងៃ​ទី​១៤ សីហា​ ២០១៥ បាន​ធ្វើ​ពិធី​ប្រកាស​ទទួលស្គាល់​សមាសភាព​អនុ​សាខា​សមាគមនារី​កម្ពុជា​ដើម្បី​សន្តិភាព', 'http://kohsantepheapdaily.com.kh/article/126729.html', 'http://i.kspg.co/2015/8/46dfb13e-3b2c-4312-be20-f98a5370dd43.jpg?w=285&h=170&mode=carve', 'ថ្ងៃសុក្រ ទី១៤ ខែសីហា ឆ្នាំ២០១៥ ម៉ោង ១៥:១៣', '0', '0', '0', 't', '2015-08-14', 'f');
INSERT INTO "tbnews" VALUES ('796', 'B020101', 'B020000', 'ផឹក​ស៊ី​ទាស់​សំដី​ ​ទាញ​ដំបង​ដេញ​វាយ​គ្នា មិន​ត្រូវ​គូទំនាស់​ បែរ​ជា​ត្រូវ​អ្នក​ដទៃ​បែក​ក្បាល', 'ខេត្ត​កណ្តាល​ ៖ បុរស​ម្នាក់​ត្រូវ​សមត្ថកិច្ច​នគរបាល​ប៉ុ​ស្តិ៍រ​ដ្ធ​បាល​ឃុំឈើទាលចាប់​ឃាត់ខ្លួន​ កាលពី​វេលា​ម៉ោង៨ និង​៤០​នាទី​យប់​ ថ្ងៃ​ទី​១៣ ​សីហា​ ​២០១៥ ​នៅផ្ទះ​ជន​រង​គ្រោះ​ស្ថិត​ក្នុងភូមិ​ឫស្សីស្រុក', 'http://kohsantepheapdaily.com.kh/article/126725.html', 'http://i.kspg.co/2015/8/35d1fe1d-cc14-4dee-866d-607de3041253.jpg?w=285&h=170&mode=carve', 'ថ្ងៃសុក្រ ទី១៤ ខែសីហា ឆ្នាំ២០១៥ ម៉ោង ១៤:៥២', '0', '0', '0', 't', '2015-08-14', 'f');
INSERT INTO "tbnews" VALUES ('797', 'B020101', 'B020000', 'ស្ត្រី​​​ម្ចាស់​​តូប​លក់​​​កាសែត​​សុំ​​ឱ្យ​អាជ្ញាធរ​ក្រុង​​ផ្តល់​ទីតាំង​​​លក់​កាសែត​​​ជាប់​របង​​មន្ទីរ​​ពេទ្យ​​ខេត្ត​​​មក​​​​វិញ', 'ខេត្តសៀមរាប  ៖ ស្ត្រី​ម្ចាស់​តូប​​លក់​កាសែត​មួយ​កន្លែង ដែល​មា​​នទី​តាំងនៅ​ពី​ខាងត្បូង​​ផ្សារ​​កណ្តាល​ជាប់​របង​មន្ទីរ​ពេទ្យ​បង្អែក​​ខេត្ត ស្ថិតក្នុង​ភូមិ​មណ្ឌល​​១ សង្កាត់​ស្វាយ​​ដង្គំ ក្រុង​សៀមរាប បាន​ទទូច​​សុំ​ឱ្យ​​អាជ្ញា​ធរ​​ក្រុង​ផ្តល់​ទីតាំង​លក់​​កាសែត​​នៅ​កន្លែង​​ដើម​​​វិញ បន្ទាប់​ពី​​អាជ្ញាធរ​បាន​​ប្រាប់​ឱ្យ​រុះរើ​មួយ​​រយៈ​ ដើម្បី​ឱ្យ​ខាង​មន្ទីរ​​សាធារណ​កា​រដាក់​ប្រ​ព័ន្ធ​​លូ​ ដើម្បី​រំដោះ​​ទឹក​កុំឱ្យ​ជន​លិច​​ក្នុង​', 'http://kohsantepheapdaily.com.kh/article/126721.html', 'http://i.kspg.co/2015/8/c522fd83-73ca-4f2a-9a47-deeb9eae74c2.jpg?w=285&h=170&mode=carve', 'ថ្ងៃសុក្រ ទី១៤ ខែសីហា ឆ្នាំ២០១៥ ម៉ោង ១៤:០៩', '0', '0', '0', 't', '2015-08-14', 'f');
INSERT INTO "tbnews" VALUES ('798', 'B020101', 'B020000', 'បណ្តេញជន​ជាតិ​វៀតណាម​ខុស​ច្បាប់​​​៤នាក់ ​​ចេញពី​កម្ពុជា​តាម​ច្រក​ព្រំដែន​អន្តរជាតិ​ត្រពាំង​ស្រែ', 'ខេត្តក្រចេះ​ ៖ ​​ជនជាតិវៀតណាម​ជា​ជនអន្តោប្រវេសន៍​​​លួច​ចូល​មក​រស់នៅ​​ក្នុង​ខេត្ត​​​ក្រចេះចំនួន​៤នាក់​ ត្រូវ​បាន​​នគរបាល​ការិយាល័យ​ជនអន្តោប្រវេសន៍​​ខេត្ត​ចាប់​ឃាត់ខ្លួន​បញ្ជូន​ទៅ​ប្រទេស​កំណើត​វិញ', 'http://kohsantepheapdaily.com.kh/article/126720.html', 'http://i.kspg.co/2015/8/2516f2c8-2bca-4f33-a03a-2ba26f127b9a.jpg?w=285&h=170&mode=carve', 'ថ្ងៃសុក្រ ទី១៤ ខែសីហា ឆ្នាំ២០១៥ ម៉ោង ១៤:០៦', '0', '0', '0', 't', '2015-08-14', 'f');
INSERT INTO "tbnews" VALUES ('799', 'B020103', 'B020000', 'កម្លំាងសមត្ថកិច្ច​នៅតែចំាចាប់ខ្លួន​លោក ហុង សុខហួរ ​​មុខស្ថាន​បារំាងទោះបីមន្រ្តីបដិសេធ', 'រាជធានីភ្នំពេញ ៖ ​បើតាមអ្នកឆ្លើយឆ្លងព័ត៌មាន​កោះសន្តិភាពបានបញ្ជាក់ឲ្យ​ដឹងនៅប៉ុន្មាននាទីមុននេះថា​ កងកម្លំាងសមត្ថកិច្ច​ចម្រុះនៅតែឈរ​និង​អង្គុយ​មុខស្ថានទូតបារំាង​', 'http://kohsantepheapdaily.com.kh/article/126758.html', 'http://i.kspg.co/2015/8/2dfdd760-9b4c-4f9d-a8e3-648c0e3990a5.jpg?w=285&h=170&mode=carve', 'ថ្ងៃសុក្រ ទី១៤ ខែសីហា ឆ្នាំ២០១៥ ម៉ោង ១៧:៥៥', '0', '0', '0', 't', '2015-08-14', 'f');
INSERT INTO "tbnews" VALUES ('800', 'B020103', 'B020000', 'ក្រុមស្រាវជ្រាវ​ព្រំដែនច្រាន​ចោលផែនទី២ផ្ទំាងរបស់ CNRP', 'រាជធានីភ្នំពេញ ៖ ​ផែនទីព្រំដែនរបស់​គណបក្សសង្គ្រោះជាតិ​ (CNRP)ចំនួន២៦ផ្ទំាង​ដែលប្រគល់ជូនក្រុម​ស្រាវជ្រាវព្រំដែន​នៃរាជបណ្ឌិត្យសភាកម្ពុជានោះ ​បានត្រូវក្រុម​', 'http://kohsantepheapdaily.com.kh/article/126714.html', 'http://i.kspg.co/2015/8/ec301f31-b70e-4eb2-9360-3df5bd6e983f.jpg?w=285&h=170&mode=carve', 'ថ្ងៃសុក្រ ទី១៤ ខែសីហា ឆ្នាំ២០១៥ ម៉ោង ១១:៣០', '0', '0', '0', 't', '2015-08-14', 'f');
INSERT INTO "tbnews" VALUES ('801', 'B020103', 'B020000', 'រាជរដ្ឋាភិបាល​ប្រកាស​យ៉ាង​ម៉ឹង​ម៉ាត់​ចាត់វិធានការ​ច្បាប់ ប្រឆាំង​លោក ហុង សុខ​ហួរ សមាជិក​ព្រឹទ្ធសភា​បក្ស សម រ​ង្ស៊ី', 'រាជធានី​ភ្នំពេញ ៖ សម្តេច​អគ្គមហាសេនាបតី​តេ​ជោ ហ៊ុន សែន នាយក​រដ្ឋមន្ត្រី​នៃ​ព្រះរាជាណាចក្រ​កម្ពុជា បាន​មាន​ប្រតិកម្ម​យ៉ាង​ខ្លាំង​ចំពោះ​សមាជិក​ព្រឹទ្ធសភា​មួយ​រូប​ដែល​បង្ហោះ​តាម​ហ្វេ​ស​ប៊ុ​ក​នូវ​សន្ធិសញ្ញា​ក្លែងក្លាយ​... ជា​រឿង​ធ្ងន់ធ្ងរ​មួយ​ដែល​បាន​កើត', 'http://kohsantepheapdaily.com.kh/article/126688.html', 'http://i.kspg.co/2015/8/bcb8a307-10e7-46db-95da-6f5026e84b93.jpg?w=285&h=170&mode=carve', 'ថ្ងៃសុក្រ ទី១៤ ខែសីហា ឆ្នាំ២០១៥ ម៉ោង ០៩:២១', '0', '0', '0', 't', '2015-08-14', 'f');
INSERT INTO "tbnews" VALUES ('802', 'B020103', 'B020000', 'តំណាងរាស្រ្ត ​CNRP​​ ៖ អត់ទាន់​មានការចាប់ខ្លួន​​លោក ហុង សុនហួរ​ នោះទេ', 'រាជធានីភ្នំពេញ ៖​ ​ប្រភពពីតំណាងរាស្រ្ត​​គណបក្សសង្គ្រោះជាតិ ​​លោក ជា ប៉ូច​ ​បានឲ្យដឹងថា ​​មកដល់ម៉ោង១១និង៤៥នាទី​​ថ្ងៃត្រង់ថ្ងៃទី១៣​ ​សីហានេះ ​មិនទាន់មាន​', 'http://kohsantepheapdaily.com.kh/article/126613.html', 'http://i.kspg.co/2015/8/61538624-5b6c-4efc-b592-cc3d05298362.jpg?w=285&h=170&mode=carve', 'ថ្ងៃព្រហស្បត្ត៏ ទី១៣ ខែសីហា ឆ្នាំ២០១៥ ម៉ោង ១១:៥៩', '0', '0', '0', 't', '2015-08-13', 'f');
INSERT INTO "tbnews" VALUES ('803', 'B030101', 'B030000', 'រហូតដល់ ម៉ោង២យប់, កម្លាំងសមត្ថកិច្ច នៅបន្តឡោមព័ទ្ធ ផ្ទះចំនួន២ កន្លែង ដើម្បីចាប់ខ្លួន លោក ហុង សុនហួរ', 'null', 'http://www.dap-news.com/2011-06-14-02-39-55/97154-2015-08-14-23-45-41.html', 'img/dapnews.png', 'Saturday, 15 August 2015 06:42', '0', '0', '0', 't', '2015-08-15', 'f');
INSERT INTO "tbnews" VALUES ('804', 'B030101', 'B030000', 'លោក ហុង សុខហួរ ប្រកាសចូលបំភ្លឺ ចំពោះអាជ្ញាធរ ស្របពេល កម្លាំងជាច្រើននាក់ ឡោមព័ទ្ធផ្ទះ២កន្លែង ដើម្បីចាប់ខ្លួន', 'null', 'http://www.dap-news.com/2011-06-14-02-39-55/97153-2015-08-14-16-40-35.html', 'img/dapnews.png', 'Friday, 14 August 2015 23:36', '32', '0', '0', 't', '2015-08-14', 'f');
INSERT INTO "tbnews" VALUES ('805', 'B030101', 'B030000', 'សមត្ថកិច្ច កំពុងឡោមព័ទ្ធ ភូមិគ្រឹះមួយកន្លែង នៅខណ្ឌសែនសុខ ចាប់ខ្លួន លោក ហុង សុខហួរ', '<strong>ភ្នំពេញ៖</strong> នៅម៉ោងប្រមាណ១០យប់ ថ្ងៃទី១៤ ខែសីហានេះ កម្លាំងសមត្ថកិច្ច ប្រមាណជាង៥០នាក់ បាននឹងកំពុង ឡោមព័ទ្ធភូមិគ្រឹះមួយកន្លែង ស្ថិតនៅក្នុងភូមិគ្រឹះលេខ១០៦A ផ្លូវ២០១១ ភូមិគោកឃ្លាង សង្កាត់ភ្នំពេញថ្មី ខណ្ឌសែនសុខ ដែលត្រូវបានគេដឹងថា លោក ហុង សុខហួរ សមាជិកព្រឹទ្ធសភា កំពុងលាក់ខ្លួននៅទីនោះ នេះបើយោងតាមភ្នាក់ងារ របស់មជ្ឈមណ្ឌលព័ត៌មានដើមអម្ពិល កំពុងស្ថិតនៅកន្លែងនោះ៕ សូមរង់ចាំព័ត៌មានបន្ត !', 'http://www.dap-news.com/2011-06-14-02-39-55/97152-2015-08-14-15-28-19.html', 'http://image.dap-news.com:81/dap-news/dap-images/2015/August/id-067/CH-00452.jpg', 'Friday, 14 August 2015 22:24', '0', '0', '0', 't', '2015-08-14', 'f');
INSERT INTO "tbnews" VALUES ('806', 'B030101', 'B030000', 'មន្ត្រី កាំកុងត្រូល ខេត្តព្រះសីហនុ រកឃើញដំណាប់ដូង ធ្វើពីប្លាស្ទិច ចរាចរលើទីផ្សារ', '<strong>ព្រះសីហនុៈ</strong> មន្រ្តីកាំកុងត្រូល សាលាខេត្ត ព្រះសីហនុ រយៈពេល២ថ្ងៃគឺ ថ្ងៃទី១៣-១៤ ខែសីហា ឆ្នាំ២០១៥ បានចុះត្រួតពិនិត្យ ទំនិញតាមសាលា<wbr>រៀន និងទីផ្សារនានា នៅទូទាំងខេត្តព្រះសីហនុ បន្ទាប់ពីលេចឮ ព័ត៌មានថាចំណីមួ<wbr>យចំនួន មានភាពមិនប្រក្រតី ។ </wbr></wbr>', 'http://www.dap-news.com/2011-06-14-02-39-55/97151-2015-08-14-13-53-00.html', 'http://image.dap-news.com:81/dap-news/dap-images/2015/August/id-067/CH-00448.jpg', 'Friday, 14 August 2015 20:17', '0', '0', '0', 't', '2015-08-14', 'f');
INSERT INTO "tbnews" VALUES ('807', 'B030101', 'B030000', 'ក្រុមការងារ យុវជន ដើម្បីចលនាការងារ សង្គមនិងមនុស្សធម៌ សហការជាមួយ សង្កាត់ ប៉ោយប៉ែត នាំយកថវិកា ជូនយុវជន កំពុងសម្រាកព្យាបាល', '<strong>បន្ទាយមានជ័យ៖</strong> ព្រឹកថ្ងៃទី១៤ ខែសីហា ឆ្នាំ២០១៥ក្រុមការងារ យុវជនដើម្បី ចលនា ការងារសង្គម និងមនុស្សធម៌ សង្កាត់ប៉ោយប៉ែត សហការគាំទ្រ ពីអាជ្ញាធរសង្កាត់ប៉ោយប៉ែត បាននាំយកថវិកា ចំនួន៣៧ ម៉ឺនរៀល ទៅឧបត្ថម្ភ យុវជន គាង សុភាង អាយុ ៣៥ ឆ្នាំ មានទីលំនៅភូមិក្បាលកោះ សង្កាត់ប៉ោយប៉ែត ក្រុងប៉ោយប៉ែត ខេត្តបន្ទាយមានជ័យ ដែលកំពុង សម្រាកព្យាបាល ជំងឺបំពង់ក នៅមន្ទីរពេទ្យបង្អែកស្រុក ប្រតិបត្តិប៉ោយប៉ែត ។', 'http://www.dap-news.com/2011-06-14-02-39-55/97150-2015-08-14-13-08-56.html', 'http://image.dap-news.com:81/dap-news/dap-images/2015/August/id-067/CH-00440.jpg', 'Friday, 14 August 2015 20:06', '0', '0', '0', 't', '2015-08-14', 'f');
INSERT INTO "tbnews" VALUES ('808', 'B030101', 'B030000', 'អភិបាលខេត្តព្រះសីហនុ បញ្ជាអោយការដ្ឋាន សំណង់សណ្ឋារគារ លំដាប់ផ្កាយ៥ ក្រុមហ៊ុន XI-HU International Hotel Co., Ltd កាយដីក្រហម ចេញពីឆ្នេរវិញ', '<strong>ព្រះសីហនុ៖</strong> លោក ឈិត សុខុន អភិបាលខេត្តព្រះសីហនុ បានចេញបញ្ជា អោយការដ្ឋានសំណង់ សណ្ឋារគារ លំដាប់ផ្កាយ៥ ក្រុមហ៊ុន XI-HU International Hotel Co., Ltd ដែលវិនិយោគដោយ ជនជាតិចិន ស្ថិតនៅក្នុងសង្កាត់លេខ៣ ក្រុងខេត្ត ព្រះសីហនុ ត្រូវកាយដីក្រហម ចេញពីឆ្នេរវិញ បន្ទាប់ពីរងការ រិះគន់ពីប្រជា<wbr>ពលរដ្ឋ ។ </wbr>', 'http://www.dap-news.com/2011-06-14-02-39-55/97149------xi-hu-international-hotel-co-ltd--.html', 'img/dapnews.png', 'Friday, 14 August 2015 19:59', '0', '0', '0', 't', '2015-08-14', 'f');
INSERT INTO "tbnews" VALUES ('809', 'B030101', 'B030000', 'រដ្ឋមន្រ្តីអប់រំឲ្យ គបម ស្នើទៅរដ្ឋសភា កែប្រែច្បាប់ ហាមឃាត់ ឃោសនានយោបាយ តាមសាលារៀន', '<strong>ភ្នំពេញ៖</strong> លោក ហង់ ជួនណារ៉ុន រដ្ឋមន្រ្តីក្រសួងអប់រំ យុវជន និងកីឡា បានស្នើទៅកាន់ គណបក្សប្រជាធិបតេយ្យមូលដ្ឋាន (គបម/GDP)ថា ឲ្យសុំរដ្ឋសភាជាតិកម្ពុជា កែប្រែច្បាប់ហាមឃាត់ការឃោសនាគណបក្សនយោបាយ នៅតាមគ្រឹះស្ថានសិក្សារដ្ឋនិងឯកជន។', 'http://www.dap-news.com/2011-06-14-02-39-55/97148-2015-08-14-11-54-38.html', 'img/dapnews.png', 'Friday, 14 August 2015 18:54', '0', '0', '0', 't', '2015-08-14', 'f');
INSERT INTO "tbnews" VALUES ('810', 'B030101', 'B030000', 'នគរបាលវរទាំង៤ នៃស្នងការដ្ឋាន ខេត្តបន្ទាយមានជ័យ បន្តហ្វឹកហាត់ ពង្រឹងសមត្ថភាព និងជំនាញការងារ', '<strong>បន្ទាយមានជ័យ ៖</strong> កម្លាំងនគរបាល វរសេនាតូច ការពារព្រំដែន នៃស្នងការដ្ឋាន នគរបាល ខេត្តបន្ទាយមានជ័យ នៅថ្ងៃទី១៤ ខែសីហា ឆ្នាំ២០១៥នេះ បានបន្តហ្វាត់ហាត់ ជំនាញ របស់ខ្លួន ដើម្បីពង្រឹងសមត្ថភាព និងជំនាញបច្ចេក ទេសការងារ កាន់តែស្ទាត់ជំនាញលើការងារ ប្រចាំថ្ងៃរបស់ខ្លួន ។', 'http://www.dap-news.com/2011-06-14-02-39-55/97147-2015-08-14-11-46-33.html', 'http://image.dap-news.com:81/dap-news/dap-images/2015/August/id-067/CH-00430.jpg', 'Friday, 14 August 2015 18:45', '0', '0', '0', 't', '2015-08-14', 'f');
INSERT INTO "tbnews" VALUES ('811', 'B030101', 'B030000', 'ក្រសួងអប់រំ ក្រើនរំលឹកជាថ្មី ដល់បេក្ខជន ដែលត្រូវប្រឡងក្នុងឆ្នាំនេះ', '<strong>ភ្នំពេញ៖</strong> ខណៈដែលការប្រឡង មធ្យមសិក្សាទុតិយភូមិ នៅសល់តែរយៈពេល ១០ថ្ងៃទៀតប៉ុណ្ណោះ នឹងឈានមកដល់នោះ រដ្ឋមន្ត្រីក្រសួងអប់រំ យុជន និងកីឡា លោក ហង់ ជួនណារ៉ុន នៅថ្ងៃទី១៤ ខែសីហា ឆ្នាំ២០១៥នេះ បានក្រើនរំលឹកទៅបេក្ខជន ដែលត្រូវប្រឡងទាំង ត្រូវខិតខំរៀនសូត្រ និងត្រូវប្រកាន់ខ្ជាប់នូវវិន័យ ដែលក្រសួងបានដាក់។', 'http://www.dap-news.com/2011-06-14-02-39-55/97146-2015-08-14-11-32-40.html', 'http://image.dap-news.com:81/dap-news/dap-images/2015/August/id-014/that-0367.jpg', 'Friday, 14 August 2015 18:32', '0', '0', '0', 't', '2015-08-14', 'f');
INSERT INTO "tbnews" VALUES ('812', 'B030101', 'B030000', 'EU ផ្តល់ជំនួយ ជាង១០លានដុល្លារ ដល់សាលាក្តីខ្មែរក្រហម', '<strong>ភ្នំពេញ៖</strong> សហភាពអឺរ៉ុប (EU) នៅថ្ងៃទី១៤ ខែសីហា ឆ្នាំ២០១៥នេះ បានសម្រេច ផ្តល់ជំនួយឥតសំណងបន្ថែមចំនួន ១០ លានអឺរ៉ូស្មើជាង១០លានដុល្លារ ដើម្បីគាំទ្រដល់អង្គជំនុំជម្រះវិសាមញ្ញក្នុងតុលាការកម្ពុជា និងការអប់រំស្តីពីរបបប្រល័យពូជសាសន៍។', 'http://www.dap-news.com/2011-06-14-02-39-55/97145-eu---.html', 'img/dapnews.png', 'Friday, 14 August 2015 18:18', '0', '0', '0', 't', '2015-08-14', 'f');
INSERT INTO "tbnews" VALUES ('813', 'B030102', 'B030000', 'អាមេរិកប្រគល់ រូបគំនូរ ពីកាសូ តម្លៃ ១៥លានដុល្លារ ទៅឲ្យបារាំងវិញ', '<strong>វ៉ាស៊ីនតោន៖</strong> ទីភ្នាក់ងារព័ត៌មានស៊ិនហួ ចេញផ្សាយថ្ងៃទី ១៤ ខែ សីហា ឆ្នាំ ២០១៥បានឲ្យដឹងថា អាមេរិកបានប្រគល់ រូបគំនូរ ពីកាសូ "La Coiffeuse" តម្លៃ ១៥ លានដុល្លារ ដែលត្រូវបានជនទុច្ចរិតលួចទៅឲ្យបារាំងវិញ បន្ទាប់ពីបានរកឃើញនៅកន្លែងលក់ FedEx package អស់រយៈពេល ៨ខែក្រោយមក។', 'http://www.dap-news.com/2011-06-14-02-40-26/97119-2015-08-14-07-08-20.html', 'http://image.dap-news.com:81/dap-news/dap-images/2015/August/id-014/that-0356.jpg', 'Friday, 14 August 2015 14:07', '0', '0', '0', 't', '2015-08-14', 'f');
INSERT INTO "tbnews" VALUES ('814', 'B030102', 'B030000', 'មនុស្សស្លាប់កើនដល់ ៥០នាក់ បន្ទាប់ពីផ្ទុះ ឃ្លាំងស្តុកទំនិញគីមី នៅកំពង់ផែចិន', '<strong>ធានជិន ៖</strong> ចំនួនមនុស្សស្លាប់ ដែលបង្កឡើងដោយការផ្ទុះឃ្លាំងស្តុកទំនិញនៃតំបន់ កំពង់ផែ ទីក្រុងធានជិន របស់ប្រទេសចិននោះ បានកើនឡើងដល់ ៥០នាក់ហើយ ស្របពេលដែល មនុស្សជាង ៧០០នាក់ទៀត ត្រូវបានក្រុមសង្គ្រោះបញ្ជូនទៅមន្ទីរពេទ្យ។ នេះបើតាមការ ផ្សាយរបស់ទីភ្នាក់ងារព័ត៌មានចិនស៊ិនហួ នៅថ្ងៃសុក្រ ទី១៤ ខែសីហា ឆ្នាំ២០១៥។', 'http://www.dap-news.com/2011-06-14-02-40-26/97104-2015-08-14-04-12-24.html', 'http://image.dap-news.com:81/dap-news/dap-images/2015/AUGUST/id-011/Hong-0521.jpg', 'Friday, 14 August 2015 11:11', '0', '0', '0', 't', '2015-08-14', 'f');
INSERT INTO "tbnews" VALUES ('815', 'B030102', 'B030000', 'លោកស្រី ផាក នឹងបំពេញ ទស្សនកិច្ចនៅចិន តែមិនចូលរួម ព្យុហយាត្រាយោធា', '<strong>សេអ៊ូល ៖</strong> លោកស្រី ប្រធានាធិបតីកូរ៉េ ខាងត្បូង ផាក គឺនហេ បានសម្រេចចិត្តថា នឹង ទៅបំពញទស្សនកិច្ចនៅទីក្រុងប៉េកាំង នៅដើមខែកញ្ញា ខាងមុខនេះ ប៉ុន្តែមេដឹកនាំជាស្ត្រី ទីមួយរូបនេះ នឹងមិនចូលរួមនៅក្នុងព្យុហយាត្រាយោធា នៅថ្ងៃទី០៣ ខែកញ្ញា ដើម្បីប្រារព្ធ ខួបលើកទី ៧០ នៃការទទួលជោគជ័យលើសង្គ្រាមលោកលើកទីពីរនោះឡើយ។', 'http://www.dap-news.com/2011-06-14-02-40-26/97098-2015-08-14-03-48-49.html', 'http://i.telegraph.co.uk/multimedia/archive/02491/Park-Geun-Hye_2491647b.jpg', 'Friday, 14 August 2015 10:48', '0', '0', '0', 't', '2015-08-14', 'f');
INSERT INTO "tbnews" VALUES ('816', 'B030102', 'B030000', 'តើភពផែនដី ផ្ទុកចំនួនមនុស្ស បានប៉ុន្មាននាក់?', '<strong>វិទ្យាសាស្ត្រ៖</strong> នៅក្នុងឆ្នាំ២០១៥ នេះចំនួនប្រជាជន ទូទាំងពិភពលោក មានចំនួន ៧ ពាន់ លាននាក់។ ជាមួយគ្នានេះដែរ ក្រុមអ្នកវិទ្យាសាស្រ្ត បានធ្វើការរំពឹងទុកថាចំនូនប្រជាជន ទូទាំងពិភពលោកនឹង អាចកើនឡើងដល់ ១១ពាន់លាននាក់នៅក្នុងឆ្នាំ ២០៥០។', 'http://www.dap-news.com/2011-06-14-02-40-26/97093-2015-08-14-02-43-40.html', 'https://latimesphoto.files.wordpress.com/2012/04/171732-ca-0420-coachella-073-aas.jpg', 'Friday, 14 August 2015 09:39', '0', '0', '0', 't', '2015-08-14', 'f');
INSERT INTO "tbnews" VALUES ('817', 'B030102', 'B030000', 'ផ្ទុះឃ្លាំងស្តុក ទំនិញនៅចិន បណ្តាលឲ្យមនុស្ស ៤៤នាក់ស្លាប់ និង របួស ៤០០នាក់', '<strong>ធានជីន ៖</strong> ការផ្ទុះបង្កជាអគ្គីភ័យកើតឡើង នៅឯឃ្លាំងស្តុកទំនិញមួយកន្លែង ក្នុងទីក្រុងធានជីន ភាគខាង ជើងប្រទេសចិន បណ្តាលឲ្យទីក្រុង កំពុងផេះនេះស្ទើរតែខ្ទេចខ្ទាំទំាងស្រុង។ នេះបើ តាមព័ត៌មាន ដែលចាប់ផ្តើមផ្សាយ កាលពីវេលាម៉ោង ៣ទៀបភ្លឺ ថ្ងៃព្រហស្បតិ៍ ទី១៣ ខែសីហា ឆ្នាំ២០១៥នេះ។', 'http://www.dap-news.com/2011-06-14-02-40-26/97052-2015-08-13-08-09-16.html', 'http://image.dap-news.com:81/dap-news/dap-images/2015/AUGUST/id-011/Hong-0464.jpg', 'Thursday, 13 August 2015 15:08', '0', '0', '0', 't', '2015-08-13', 'f');
INSERT INTO "tbnews" VALUES ('818', 'B030102', 'B030000', 'ប្រសិនបើដាយណូស័រ មិនទាន់ផុតពូជ តើមនុស្ស អាចមានវត្តមាន ឬទេ?', '<strong>&nbsp;(វិទ្យាសាស្ត្រ)</strong>៖ កាលពី ៦៥លានឆ្នាំមុន ប្រភេទដាយណូស័រ គ្មានស្លាប បានរងគ្រោះថ្នាក់ បណ្តាលឲ្យផុត ពូជហិនហោចគ្មានសល់ ។ មិនតែប៉ុណ្ណោះ ពពួកដាយណូស័រ mosasaurs និង plesiosaurs នៅក្នុងសមុទ្រ ព្រមទាំង ដាយណូស័រ បក្សីក៏បានផុតពូជផងដែរ។ នេះបើ យោងតាមគេហទំព័រ National Geographic។', 'http://www.dap-news.com/2011-06-14-02-40-26/97048-2015-08-13-07-39-05.html', 'http://news.bbc.co.uk/olmedia/1610000/images/_1610461_dinosaurstroke300b.jpg', 'Thursday, 13 August 2015 14:37', '0', '0', '0', 't', '2015-08-13', 'f');
INSERT INTO "tbnews" VALUES ('819', 'B030102', 'B030000', 'អតីតនាយករដ្ឋ មន្ត្រី ជប៉ុន ៥រូប បង្ហាញក្តី ព្រួយបារម្ភ ចំពោះច្បាប់ សន្តិសុខជប៉ុនថ្មី', '<strong>តូក្យូ ៖</strong> អតីតនាយករដ្ឋមន្ត្រី ជប៉ុនចំនួន ៥រូប គឺលោក ម៉ូរីហ៊ីរ៉ូ ហូសូកាវ៉ា, ត្សូតូមុ ហាតា, តូមីអ៊ីជី មួរ៉ាយ៉ាម៉ា, យូគីយ៉ូ ហាតូយ៉ាម៉ា និង ណាអូតូ ខាន់ បានសម្តែងការព្រួយបារម្ភដូចៗគ្នា ចំពោះ សេចក្តីព្រាង ច្បាប់ថ្មីស្តីសន្តិសុខជាតិ ហើយអតីតមេដឹកនាំទាំងអស់នេះ បានស្នើឲ្យលោក នាយករដ្ឋមន្ត្រីបច្ចុប្បន្ន ស៊ីហ្សូ អាបេ ដកចេញនូវច្បាប់នេះវិញជាបន្ទាន់។', 'http://www.dap-news.com/2011-06-14-02-40-26/97020-2015-08-13-04-07-55.html', 'http://cdn.itar-tass.com/width/744_b12f2926/tass/m2/en/uploads/i/20150306/1085510.jpg', 'Thursday, 13 August 2015 11:07', '0', '0', '0', 't', '2015-08-13', 'f');
INSERT INTO "tbnews" VALUES ('820', 'B030102', 'B030000', 'ណាសា៖ មិនមាន​ ដុំថ្មអវកាសមក បុកជាមួយ នឹងភពផែនដី នៅឆ្នាំ ២០៤០ទេ', '<strong>កាលីហ្វញ៉ា ៖</strong> ទីភ្នាក់ងារអវកាសណាសា សហរដ្ឋអាមេរិកបាននិយាយថា ការគណនា ជាលើកចុង ក្រោយបង្អស់បង្ហាញឲ្យឃើញថា ដុំថ្មអវកាស ឬ អាចម៍ផ្កាយ ដែលកំពុងតែធ្វើ ដំណើរឆ្ពោះ ទៅកាន់ប្រព័ន្ធ ព្រះអាទិត្យ នឹងមិនមានការ គំរាមកំហែងដលភពផែនដី ដូចការលើកឡើងពីវែបសាយអវកាស មួយចំនួនថានឹងបុកផែនដីនោះទេ ។', 'http://www.dap-news.com/2011-06-14-02-40-26/97016-2015-08-13-03-27-50.html', 'http://i.ytimg.com/vi/yQkmr3D3qMg/maxresdefault.jpg', 'Thursday, 13 August 2015 10:27', '0', '0', '0', 't', '2015-08-13', 'f');
INSERT INTO "tbnews" VALUES ('821', 'B030302', 'B030000', 'សហភាព​សហព័ន្ធ​​យុវជន​​កម្ពុជា ប្រកាស​​បើក​​ការ​​ប្រកួត​ពាន​រង្វាន់ Soccer League លើក​ទី៣', '<strong>ភ្នំពេញ៖</strong> សហភាពសហព័ន្ធយុវជនកម្ពុជា សហភាពសហព័ន្ធយុវជនកម្ពុជា បានប្រកាសបើកជាផ្លូវការ នៃការប្រកួត បាល់ទាត់មិនអាជីពពានរង្វាន់ Soccer League លើកទី៣ ឆ្នាំ២០១៥ ដែលចាប់ផ្តើមប្រកួតវគ្គបឋម នៅថ្ងៃទី០១-១៥ ខែវិច្ឆិកា ឆ្នាំ២០១៥ ខាងមុខនេះតទៅ មុនពេលវគ្គផ្តាច់ព្រ័ត្រ នឹងធ្វើឡើងនៅថ្ងៃទី០១-៣១ ខែធ្នូ ឆ្នាំ២០១៥ ។', 'http://www.dap-news.com/morokatmagazine/97051--soccer-league-.html', 'http://image.lookingtoday.com:81/lookingtoday/looking-images/news/2015/August/ID-141/MrMSP-0483.jpg', 'Thursday, 13 August 2015 14:59', '0', '0', '0', 't', '2015-08-13', 'f');
