INSERT INTO "tbnews" VALUES ('849', 'B010201', 'B010000', '​ACT 2015 ៖ U-18 ភ្នំពេញ​ក្រោន ស្មើ​ Frenz United B ១-១ ​', 'ក្លិប​បាល់ទាត់ U-18 ​ភ្នំពេញ​ក្រោន​ បាន​ត្រឹម​ស្មើ​ជា​មួយ​ក្រុម Frenz United B ដោយ​លទ្ធផល ១-១ នៅ​​​កីឡដ្ឋាន​ RSN របស់​ខ្លួន​​សម្រាប់​ការ​ប្រកួត​បាល់ទាត់​​ពាន​រង្វាន់​ U-18 Asia Champions Trophy 2015។ &nbsp; ការ​ប្រកួត​រវាង​ក្រុម​ទាំង​ពីរ​បាន​ទម្លាយ​សំណាញ់​ទី​គ្នា​ម្នាក់​១​គ្រាប់​នៅ​តង់​ទីមួយ ប៉ុន្តែ​នៅ​តង់​ទី​ពីរ​មិន​អាច​ទម្លាយ​សំណាញ់​​ទីគ្នា​បាន​ទេ។ ក្រុម​យុវជន​ភ្នំ​ពេញ​ក្រោន​គ្រប់​គ្រង​បាល់​បាន​​ល្អ និង​វាយ​សម្រុក​បាន​ច្រើន។ ក្រុម​ភ្ញៀវមាន​ឱកាស​បាល់​ដាច់មាត់​​មួយ​លើក​ជា​ពីរលើក តែ​​ធ្វើ​មិន​បាន​សម្រេច។ ចង់​តង់​ទី​ពីរ​សមាជិក Frenz United ម្នាក់​ត្រូវ​បាន​កាត​ក្រហម​បណ្តេញ​ចេញ​​ពី​ទី​លាន​ដោយ​សល់​គ្នា​តែ​១០​នាក់។ យ៉ាង​ណា ក្រុម​ម្ចាស់​ផ្ទះ​មិន​អាច​រក​គ្រាប់​បាល់​ជ័យជម្នះ​បាន​ឡើយ​៕ &nbsp;   អត្ថបទ៖ ជាន ចាន់រ៉ា', 'http://news.sabay.com.kh/article/451359', 'http://cdn.sabay.com/cdn/news.sabay.com.kh/wp-content/uploads/2015/08/Featuredcrownv-285x170.jpg?ebb82d', '14 August, 2015', '0', '0', '0', 't', '2015-08-14', 'f');
