INSERT INTO "tbnews" VALUES ('884', 'B020302', 'B020000', 'តម្លៃ​ខ្លួន​សម្តែង​របស់​ឱ​ក សុគន្ធ​កញ្ញា​ និង​មាស សុខ​សោភា​ មួយ​ណា​ខ្ពស់​ជាង ?', 'រាជធានី​ភ្នំពេញ ៖ តារាចម្រៀង​២​ដួង​នៅ​ផលិតកម្ម​ផ្សេង​គ្នា គឺ​ហង្ស​មាស និង​ថោ​ន ត្រូវ​មហាជន​កត់សម្គាល់​ឃើញ​ថា មាន​ប្រជាប្រិយភាព​ប្រដេញ​គ្នា ។ ស្ថិត​ក្នុង​លំដាប់​កំពូល​ដូច​គ្នា ដូច្នេះ​តារាចម្រៀង​ស្រី​២​ដួង​នេះ', 'http://kohsantepheapdaily.com.kh/article/126967.html', 'http://i.kspg.co/2015/8/16d15080-924a-405b-af58-773251b7232b.jpg?w=285&h=170&mode=carve', 'ថ្ងៃអង្គារ ទី១៨ ខែសីហា ឆ្នាំ២០១៥ ម៉ោង ០៩:៣១', '0', '0', '2121', 't', '2015-08-18', 'f');
