INSERT INTO "tbnews" VALUES ('898', 'B020102', 'B020000', 'អាវុធហត្ថ​ខណ្ឌទួលគោក​ចាប់ខ្លួន​ជនសង្ស័យ​ពាក់ព័ន្ធ​គ្រឿងញៀន​៥​នាក់', 'រាជធានី​ភ្នំពេញ ៖ កម្លាំង​អាវុធហត្ថ​ខណ្ឌទួលគោក​បាន​ឃាត់ខ្លួន​ជនសង្ស័យ​៥​នាក់​ពី​បទ​ពាក់ព័ន្ធ​នឹង​គ្រឿងញៀន កាលពី​វេលា​ម៉ោង​៩ និង​១០​នាទី​យប់ ថ្ងៃ​ទី​១៧ ខែសីហា ឆ្នាំ​២០១៥ នៅ​ចំណុច​ផ្លូវ​លេខ​១៨២ សង្កាត់​ទឹកល្អក់​៣ ខណ្ឌទួលគោក រាជធានី​ភ្នំពេញ ។', 'http://kohsantepheapdaily.com.kh/article/126958.html', 'http://i.kspg.co/2015/8/bc8f3905-ba70-4552-bb56-91765d62f3d6.jpg?w=285&h=170&mode=carve', 'ថ្ងៃចន្ទ ទី១៧ ខែសីហា ឆ្នាំ២០១៥ ម៉ោង ២២:៥៩', '12', '123', '0', 't', '2015-08-17', 'f');
INSERT INTO "tbnews" VALUES ('899', 'B020102', 'B020000', 'បុរស​ម្នាក់​ត្រូវ​គេ​សម្លាប់​នៅ​ស្រុក​បរិបូរណ៍ សមត្ថកិច្ច​អំពាវនាវ​រក​ក្រុម​គ្រួសារ​ជន​រង​គ្រោះ', 'ខេត្តកំពង់ឆ្នាំង ៖ សាកសព​បុរស​មិន​ស្គាល់​អត្តសញ្ញាណ​ម្នាក់​ត្រូវ​បាន​ប្រជាពលរដ្ឋ​ប្រទះ​ឃើញ​នៅ​ក្បែរ​គុម្ពោត​ព្រៃ​មួយ​កន្លែង នៅ​ចំណុច​ស្តុំ ស្ថិត​ក្នុងភូមិ​ក្រោល​ជី ឃុំ​ពពេល ស្រុក​បររិបូរណ៍ ។', 'http://kohsantepheapdaily.com.kh/article/126955.html', 'http://i.kspg.co/2015/8/42ac8390-02ef-4250-b999-5100156f6209.jpg?w=285&h=170&mode=carve', 'ថ្ងៃចន្ទ ទី១៧ ខែសីហា ឆ្នាំ២០១៥ ម៉ោង ២១:០៤', '0', '13', '0', 't', '2015-08-17', 'f');
INSERT INTO "tbnews" VALUES ('900', 'B020102', 'B020000', 'ចាប់ខ្លួន​ជនសង្ស័យ​ម្នាក់​ពី​បទ​ប្លន់​សម្លាប់​អ្នក​រត់​ម៉ូតូឌុប', 'ខេត្តព្រះវិហារ ៖ ជនសង្ស័យ​ម្នាក់​ក្នុង​ចំណោម​២​នាក់​ត្រូវ​បាន​សមត្ថកិច្ច​​នគរ​បាល​ស្នង​ការ​ដ្ឋាន​ខេត្ត​ព្រះ​វិហារ​ចាប់ខ្លួន​កាលពី​វេលា​ម៉ោង​១២​ថ្ងៃត្រង់ ថ្ងៃ​ទី​១៧ ខែសីហា ឆ្នាំ​២០១៥​នេះ​នៅ​ក្រុង​ព្រះ​វិហារ ពាក់ព័ន្ធ​ករណី​ប្លន់​សម្លាប់​អ្នក​រត់​ម៉ូតូឌុប​ម្នាក់ ។', 'http://kohsantepheapdaily.com.kh/article/126953.html', 'http://i.kspg.co/2015/8/ccb44959-ea38-430e-971d-58e0892cae87.jpg?w=285&h=170&mode=carve', 'ថ្ងៃចន្ទ ទី១៧ ខែសីហា ឆ្នាំ២០១៥ ម៉ោង ២០:០៥', '12', '0', '1', 't', '2015-08-17', 'f');
INSERT INTO "tbnews" VALUES ('901', 'B020102', 'B020000', 'ព្រហ្មទណ្ឌ​កម្រិត​ស្រាល​ខេត្តសៀមរាប​បង្ហាញមុខ​សញ្ញា​ក្រុម​ចោរលួច​-​ឆក់​មួយ​សំបុក​ធំ', 'ខេត្តសៀមរាប ៖ កម្លាំង​ការិយាល័យ​នគរបាល​ព្រហ្មទណ្ឌ​កម្រិត​ស្រាល នៃ​ស្នងការដ្ឋាន​នគរបាល​ខេត្ត​បាន​បើក​ប្រតិបត្តិការ​ចុះ​បង្ក្រាប​បាន​មុខសញ្ញា​ចោរឆក់ និង​លួច​មួយ​សំបុក​ធំ រហូត​ឈាន​ឃាត់ខ្លួន​បានជា​បន្តបន្ទាប់ ដែល​មាន​ប្រុស​-​ស្រី​ចំនួន​១១​នាក់​កាលពី​ថ្ងៃ​ទី​១៥ ខែសីហា ឆ្នាំ​២០១៥ ។', 'http://kohsantepheapdaily.com.kh/article/126951.html', 'http://i.kspg.co/2015/8/05f00448-ab63-4a93-9f79-8f778bf009e1.jpg?w=285&h=170&mode=carve', 'ថ្ងៃចន្ទ ទី១៧ ខែសីហា ឆ្នាំ២០១៥ ម៉ោង ១៩:១១', '1', '0', '0', 't', '2015-08-17', 'f');
