INSERT INTO "tbnews" VALUES ('903', 'B020102', 'B020000', '​ស្រវឹងស្រា​ខ្លាំង​ពេក​ ឈប់​ម៉ូតូ​ដេក​​លក់​​នៅ​​លើ​ចិញ្ចើម​ផ្លូវ​ធ្វើ​មិនដឹង', '​រាជ​ធានី​ភ្នំពេញ​ ៖​ បុរស​ម្នាក់​ស្រវឹងស្រា​ជោកជាំ ឈប់​ម៉ូតូ​ដេក​លង់លក់​នៅ​លើ​ម៉ូតូ​តាម​ចិញ្ចើម​ផ្លូវ សំណាង​ជួប​មនុស្ស​ល្អ កុំ​​អី​ស៊យ​ធំ កាលពី​វេលា​ម៉ោង​១​និង​១៥​នាទី​រំលង​​អធ្រាត្រ​ឈាន​ចូល​ថ្ងៃ​ទី​១៧ ​សីហា ​២០១៥', 'http://kohsantepheapdaily.com.kh/article/126931.html', 'http://i.kspg.co/2015/8/1c6c7df9-0c1b-4952-8937-15050b6d38de.jpg?w=285&h=170&mode=carve', 'ថ្ងៃចន្ទ ទី១៧ ខែសីហា ឆ្នាំ២០១៥ ម៉ោង ១៥:២៤', '12', '13', '0', 't', '2015-08-17', 'f');
INSERT INTO "tbnews" VALUES ('904', 'B020102', 'B020000', 'វាយ​បងស្រី​បង្កើត​របួសធ្ងន់ ត្រូវ​សមត្ថកិច្ច​ចាប់ខ្លួន', 'ខេត្តតាកែវ ៖ ជនសង្ស័យ​ម្នាក់​ត្រូវ​បាន​ប៉ូលិស​ស្រុក​គី​រី​វង់​ចាប់ខ្លួន​រួច​បញ្ជូន​បន្ត​មក​ការិយាល័យ​ព្រហ្មទណ្ឌ​កម្រិត​ស្រាល​ខេត្ត​ដើម្បី​ចាត់ការ​បន្ត បន្ទាប់​ពី​ជនសង្ស័យ​បាន​វាយ​បងស្រី​បង្កើត​របស់​ខ្លួន​ឱ្យ​រង​របួសធ្ងន់ ហើយ​ត្រូវ​បាន​ក្រុម​គ្រួសារ​បញ្ជូន​មក​ព្យាបាល​នៅ​មន្ទីរពេទ្យ​បង្អែក​ស្រុក ។', 'http://kohsantepheapdaily.com.kh/article/126851.html', 'http://i.kspg.co/2015/8/2c4f4343-034e-4968-8795-38e5bd6e81bf.jpg?w=285&h=170&mode=carve', 'ថ្ងៃអាទិត្យ ទី១៦ ខែសីហា ឆ្នាំ២០១៥ ម៉ោង ១៤:៥៨', '12', '2', '0', 't', '2015-08-16', 'f');
INSERT INTO "tbnews" VALUES ('905', 'B020102', 'B020000', 'ប្រជា​ពលរដ្ឋ​ដេញ​ចាប់​ចោរ​លួច​ទូរ​ស័ព្ទ​បាន​ម្នាក់ ម្នាក់​ទៀត​រត់​រួច​', 'រាជ​ធានីភ្នំពេញ ៖ ជនសង្ស័យ​២នាក់​បាន​ធ្វើ​សកម្មភាព​ចូល​លួច​​​ទូរស័ព្ទ​ក្នុងផ្ទះ​ជនជាតិ​ចិន​ ខ​ណៈលួច​បាន​ត្រូវ​​ម្ចាស់គេ​ឃើញ​ស្រែក​ឱ្យអ្នក​ជិតខាង​ជួយ​អត្ត​រាគ​មន៍​ដេញចាប់​បាន​ម្នាក់ ​រត់​រួច​ម្នាក់​។', 'http://kohsantepheapdaily.com.kh/article/126840.html', 'http://i.kspg.co/2015/8/879fa1dc-5d14-4b8c-9f05-f7bac0d44072.jpg?w=285&h=170&mode=carve', 'ថ្ងៃអាទិត្យ ទី១៦ ខែសីហា ឆ្នាំ២០១៥ ម៉ោង ១៤:២១', '21', '0', '0', 't', '2015-08-16', 'f');
INSERT INTO "tbnews" VALUES ('906', 'B020102', 'B020000', 'ឆក់​អាយ​ផេត​គេ​បាន​ហើយ​គេច​មិន​រួច​ត្រូវ​សមត្ថ​កិច្ច​ក្របួ​ច​ជាប់​​​២នាក់', 'រាជ​ធានី​ភ្នំពេញ​ ៖ តាម​សេចក្តី​រាយការណ៍​បាន​ឱ្យ​ដឹង​ថា ជន​សង្ស័យ​ជា​ចោរ​ឆក់​២​នាក់​ត្រូវ​នគរ​បាល​ខណ្ឌ​ទួល​គោក​ចាប់​ខ្លួន​បាន​ជា​បន្ត​បន្ទាប់​កាល​ពី​វេលា​ម៉ោង​១២ និង​៣០​នាទី​ថ្ងៃ​ត្រង់ ', 'http://kohsantepheapdaily.com.kh/article/126837.html', 'http://i.kspg.co/2015/8/41dac1c3-02b3-42a7-b0a1-26350c966b2f.jpg?w=285&h=170&mode=carve', 'ថ្ងៃអាទិត្យ ទី១៦ ខែសីហា ឆ្នាំ២០១៥ ម៉ោង ១៤:០៦', '0', '0', '0', 't', '2015-08-16', 'f');
INSERT INTO "tbnews" VALUES ('907', 'B020102', 'B020000', 'ជិះ​ម៉ូតូ​រត់គេច​ពី​សមត្ថកិច្ច ត្រូវ​ឃាត់ខ្លួន​២​នាក់ រត់​រួច​២​នាក់', 'រាជធានី​ភ្នំពេញ ៖ យុវជន​៤​នាក់​ជីះ​ម៉ូតូឌុប​គ្នា ខណៈ​ឃើញ​សមត្ថកិច្ច​បាន​ព្យាយាម​បើក​ម៉ូតូ​រត់​គេ​តែ​ត្រូវ​បាន​សមត្ថកិច្ច​ដេញ​តាម ហើយ​ឃាត់ខ្លួន​បាន​២​នាក់ រត់​រួច​២​នាក់ ។ ករណី​ខាងលើ​នេះ​កើតឡើង​កាលពី​វេលា​ម៉ោង​១១ និង​២០​នាទី​យប់​ថ្ងៃ​ទី​១៥ សីហា ២០១៥', 'http://kohsantepheapdaily.com.kh/article/126827.html', 'http://i.kspg.co/2015/8/cf004240-ec9c-4d52-9d01-a5979ba0cc1a.jpg?w=285&h=170&mode=carve', 'ថ្ងៃអាទិត្យ ទី១៦ ខែសីហា ឆ្នាំ២០១៥ ម៉ោង ១០:៥៣', '2', '0', '0', 't', '2015-08-16', 'f');
INSERT INTO "tbnews" VALUES ('908', 'B020102', 'B020000', 'សមត្ថកិច្ច​ខេត្តកំពង់ចាម​តាម​ក្របួច​បាន​ក្រុម​ចោរ​៣​នាក់​ក្នុង​ចំណោម​៥​នាក់​ដល់​ភ្នំពេញ', 'ខេត្តកំពង់ចាម ៖ លោក​ឧត្តមសេនីយ៍ត្រី បែន រ័​ត្ន ស្នងការ​នគរបាល​ខេត្ត បាន​ចាត់​ឲ្យ​កម្លាំង​ក្រោម​ឱវាទ​ជា​ច្រើន​នាក់​តាម​ប្រមាញ់​ក្រុម​ចោរ​ជើងឆើត​ចំនួន​៥​នាក់ ដែល​បាន​ល្បួង​បុរស​ចំណាស់​ម្នាក់​ទៅ​កន្លែង​ស្ងាត់​ហើយ​ឆក់​យក​លុយ​១០០​ដុល្លារ', 'http://kohsantepheapdaily.com.kh/article/126799.html', 'http://i.kspg.co/2015/8/a67abe42-a40b-4b42-89e5-f1cd45cee7c0.jpg?w=285&h=170&mode=carve', 'ថ្ងៃសៅរ៏ ទី១៥ ខែសីហា ឆ្នាំ២០១៥ ម៉ោង ១៨:៥៦', '3', '0', '0', 't', '2015-08-15', 'f');
INSERT INTO "tbnews" VALUES ('909', 'B020101', 'B020000', '​លោក​អភិបាល​​ស្រុក​ព្រៃនប់ ​បញ្ជា​ឱ្យ​ដោះ​ច្រវាក់​​​ច្រកចេញ-​ចូល​ផ្សារ​វាលរេញ​ចេញ​​ជា​បណ្តោះ​​អាសន្ន', 'ខេត្ត​ព្រះ​សីហនុ ៖ ​នៅ​​ព្រឹក​ថ្ងៃទី​១៨ សីហា ២០១៥ លោក​ ទិត វុទ្ធី ​អភិ​បាល​ស្រុក​ព្រៃនប់ បាន​​ធ្វើ​ដំណើរ​ចូល​ផ្សារ​វាល​រេញ​​ដើម្បី​រក​ដំណោះស្រាយ​ជូន​​​ក្រុម​អាជីវករ បន្ទាប់​ពី​មាន​​ក្រុម​អាជីវករ​មួយ​ចំនួន​បាន​នាំ​គ្នា​ផ្តិត​​​មេដៃ​ប្តឹង​​អ្នក​​ម៉ៅការ​​ភាស៊ី​ ', 'http://kohsantepheapdaily.com.kh/article/127042.html', 'http://i.kspg.co/2015/8/9009403d-6e96-4843-bdcd-e9f227a43a0f.jpg?w=285&h=170&mode=carve', 'ថ្ងៃអង្គារ ទី១៨ ខែសីហា ឆ្នាំ២០១៥ ម៉ោង ១៧:៥៩', '313', '0', '0', 't', '2015-08-18', 'f');
