INSERT INTO "tbnews" VALUES ('912', 'B020101', 'B020000', '​​នាំ​យក​អំណោយ​ទៅ​ជូន​​មន្ត្រី​នគរបាល​ម្នាក់​កំពុង​សម្រាក​ព្យាបាល​នៅផ្ទះ', 'ខេត្តសៀមរាប ៖ លោក​ឧ​ត្តម​សេនីយ៍​ត្រី ញ៉ឹ​ក កែវ ស្នងការ​​​រង​ខេត្ត​ ​​​និង​លោក​វរសេនីយ៍ទោ ហួត សុធី នាយការិយាល័យ​សន្តិសុខ​​​ខេត្ត​ ​បាន​ដឹកនាំ​កម្លាំង​ក្នុង​អង្គភាព​​ទៅ​សួរ​សុខ​ទុក្ខ​ ​លោក ឡុ​ង ឌី​សា​រ៉េ​ត', 'http://kohsantepheapdaily.com.kh/article/127038.html', 'http://i.kspg.co/2015/8/eb3a4e96-8b8e-44c5-a28d-7fdafadd9da3.jpg?w=285&h=170&mode=carve', 'ថ្ងៃអង្គារ ទី១៨ ខែសីហា ឆ្នាំ២០១៥ ម៉ោង ១៧:១៥', '31', '0', '0', 't', '2015-08-18', 'f');
