INSERT INTO "tbnews" VALUES ('914', 'B020101', 'B020000', 'អភិបាល​ខណ្ឌ​សែន​សុខ​ជួប​សំណេះសំណាល និង​ចែក​អំណោយ​ដល់​​នគរបាល​បង្ការ​ពន្លត់​អគ្គិភ័យ​ និង​សង្គ្រោះ', 'រាជធានី​ភ្នំពេញ ៖ លោក​លី សា​វ៉េ​ត អភិបាល​ខណ្ឌ​សែន​សុខ បាន​អញ្ជើញ​ចុះ​ជួប​សំណេះសំណាល​ជាមួយ​កម្លាំង​នគរបាល​បង្ការ​ពន្លត់​អគ្គិភ័យ និង​សង្គ្រោះ ដែល​មាន​លោក​វរសេនីយ៍ទោ ព្រំ យ៉​ន ប្រធាន​បង្ការ​ពន្លត់​អគ្គិភ័យ​កម្ពុជា កាលពី​រសៀល​ថ្ងៃ​ទី​១៨ សីហា ២០១៥ នេះ នៅ​សាលាសង្កាត់​ភ្នំពេញ​ថ្មី ។', 'http://kohsantepheapdaily.com.kh/article/127036.html', 'http://i.kspg.co/2015/8/016fcfb7-cbf1-43f1-b5f3-6c2cdd7afbc6.jpg?w=285&h=170&mode=carve', 'ថ្ងៃអង្គារ ទី១៨ ខែសីហា ឆ្នាំ២០១៥ ម៉ោង ១៧:០៥', '321', '0', '0', 't', '2015-08-18', 'f');
INSERT INTO "tbnews" VALUES ('915', 'B020101', 'B020000', 'ការិយាល័យ​ប្រឆាំង​ការ​ជួញដូរ​មនុស្ស​និង​ការពារ​អ​និ​តិ​ជន​​ខេត្ត​​​ ទទួល​អំណោយ​ពី​សប្បុរសជន', 'ខេត្តសៀមរាប ៖ ​កាល​ពី​​ថ្ងៃ​ទី​១៧​ សីហា​​ ​២០១៥ លោកស្រីឩ​ត្តម​សេនីយ៍​ត្រី សុខ សំ ស្នងការ​រង​ទទួល​ការងារ​ប្រឆាំង​ការ​ជួញដូរ​មនុស្ស​ និង​ការពារ​អនីតិជន ​និង​លោកស្រី​វរសេនីយ៍ទោ ដួង ថាវ​រី ', 'http://kohsantepheapdaily.com.kh/article/127034.html', 'http://i.kspg.co/2015/8/9f1df7c9-f3a7-449b-b073-084c05db40da.jpg?w=285&h=170&mode=carve', 'ថ្ងៃអង្គារ ទី១៨ ខែសីហា ឆ្នាំ២០១៥ ម៉ោង ១៦:៥៣', '1', '0', '0', 't', '2015-08-18', 'f');
INSERT INTO "tbnews" VALUES ('916', 'B020101', 'B020000', 'ចោរ​ជំនាញ​កាច់​សោ​.​ក​ម៉ូតូ​ពី​ខេត្តកំពង់ចាម​ ជាប់អន្ទាក់​ប៉េ​អឹមខេត្ត​​សៀមរាប', 'ខេត្តសៀមរាប ​៖ កម្លាំង​ការិយាល័យ​បទល្មើស​ព្រហ្មទណ្ឌ​កងរាជអាវុធហត្ថ​ខេត្តសៀមរាប​សហការ​ជាមួយ​អាវុធហត្ថ​ខេត្តកំពង់ចាម​ និង​នគរបាល​ខេត្តកំពង់ចាម​បាន​ចុះ​ប្រតិបត្តិការអនុវត្ត​​ដី​កា​បង្គា​ប់​ឱ្យ​ចាប់ខ្លួន', 'http://kohsantepheapdaily.com.kh/article/127033.html', 'http://i.kspg.co/2015/8/29efa85d-8e68-421b-abc7-3df7c51221d8.jpg?w=285&h=170&mode=carve', 'ថ្ងៃអង្គារ ទី១៨ ខែសីហា ឆ្នាំ២០១៥ ម៉ោង ១៦:៤៨', '31', '0', '0', 't', '2015-08-18', 'f');
INSERT INTO "tbnews" VALUES ('917', 'B020101', 'B020000', 'ឯកអគ្គរាជទូត​ថៃ​ប្រាប់​ពី​បំណង​ចង់​ធ្វើ​កិច្ច​សហប្រតិបត្តិការ​អភិវឌ្ឍ​ន៍​វិស័យ​កសិកម្ម​ជាមួយ​កម្ពុជា', 'រាជធានី​ភ្នំពេញ ​៖ ​មន្ត្រី​ស្ថានទូត​ថៃ​បាន​ប្រាប់​មន្ត្រីជាន់ខ្ពស់​នៃ​រាជរដ្ឋាភិបាល​កម្ពុជា​អំពី​បំណង​ពង្រីកកិច្ច​សហប្រតិបត្តិការ​អភិវឌ្ឍ​ន៍​វិស័យ​កសិកម្ម​នៅ​កម្ពុជា', 'http://kohsantepheapdaily.com.kh/article/127030.html', 'http://i.kspg.co/2015/8/a0327faf-3f6b-4c86-bcbc-79efd515a2af.jpg?w=285&h=170&mode=carve', 'ថ្ងៃអង្គារ ទី១៨ ខែសីហា ឆ្នាំ២០១៥ ម៉ោង ១៦:៣៧', '21', '3', '0', 't', '2015-08-18', 'f');
INSERT INTO "tbnews" VALUES ('918', 'B020101', 'B020000', 'បើកវេទិកា​សាធារណៈ​ជាមួយប្រជាពលរដ្ឋ​ក្នុង​បុរី​​ពិភព​ថ្មី​', 'រាជធានី​ភ្នំពេញ ៖ នៅ​ក្នុង​បុរី​ពិភព​ថ្មី នា​វេលា​រសៀល​ថ្ងៃ​ទី​១៨ សីហា ២០១៥ នេះ ស្ថិត​ក្នុងភូមិ​បា​យ៉ាប សង្កាត់​ភ្នំពេញ​ថ្មី ខណ្ឌ​សែន​​សុខ មាន​រៀបចំពិធី​​បើក​វេទិកាសាធារណៈ​មួយ ដោយ​មានការ​អញ្ជើញ​ចូលរួម​​ពី​លោក​ម៉​ម ប៊ុ​ន​ហេង រដ្ឋមន្ត្រី​ក្រសួង​សុខា​ភិ​បាល​, លោក​លី សា​វ៉េ​ត អភិបាល​ខណ្ឌ​, លោក​ជុំ សា​រ៉ា​យ ចៅសង្កាត់​ភ្នំពេញថ្មី​ ព្រមទាំង​ប្រជាពលរដ្ឋ​ជា​ច្រើន​នាក់​ទៀត​ផង​', 'http://kohsantepheapdaily.com.kh/article/127025.html', 'http://i.kspg.co/2015/8/fb9577ea-bd3a-460a-b74e-006fbd47e6bd.jpg?w=285&h=170&mode=carve', 'ថ្ងៃអង្គារ ទី១៨ ខែសីហា ឆ្នាំ២០១៥ ម៉ោង ១៥:៣៦', '1', '0', '0', 't', '2015-08-18', 'f');
INSERT INTO "tbnews" VALUES ('919', 'B020101', 'B020000', 'ព្រះរាជអាជ្ញា​រង​សាលាដំបូង​០១​រូប ​កំពុង​នៅ​ពីក្រោយ​សំណុំរឿង​ដែល​គ្មាន​ធាតុ​ផ្សំ​បទល្មើស​គ្រប់គ្រាន់', 'រាជធានី​ភ្នំពេញ ៖ តាម​ប្រភព​ព័ត៌មាន​មួយ​បាន​ទម្លាយ​ឱ្យ​ដឹង​ថា ព្រះរាជអាជ្ញារ​រង​សាលាដំបូង​រាជធានី​ភ្នំពេញ មួយ​រូប​សុំ​មិន​បញ្ចេញ​ឈ្មោះ​កំពុង​នៅ​ពីក្រោយ​សំណុំរឿង​ព្រហ្មទណ្ឌ​តូចតាច​មួយ​ដែល​គ្មាន​ធាតុ​ផ្សំ​បទល្មើស​គ្រប់គ្រាន់ ', 'http://kohsantepheapdaily.com.kh/article/127021.html', 'http://i.kspg.co/2015/8/67041324-0e25-4350-a1cb-1d8dde39c6e1.jpg?w=285&h=170&mode=carve', 'ថ្ងៃអង្គារ ទី១៨ ខែសីហា ឆ្នាំ២០១៥ ម៉ោង ១៥:០៦', '2', '0', '0', 't', '2015-08-18', 'f');
