INSERT INTO "tbnews" VALUES ('922', 'B020101', 'B020000', 'ព្រះ​គ្រូ​អនុ​គណ​ក្រុង​ស្វាយ​រៀង​នាំ​អំណោយ​ជូន​លោក​យាយគ្មាន​ទី​ពឹងនៅ​សង្កាត់​ចេក', 'ខេត្តស្វាយរៀង ៖ ​កាល​ពី​ព្រឹក​ថ្ងៃ​ទី១៨ ​សីហា ​២០១៥ នេះ ព្រះ​គ្រូ​ធម្មបាល​វង្សា អ៊ុ​ក សារី ព្រះ​គ្រូ​អនុគណ​ក្រុង​ស្វាយ​រៀង និង​លោក រស់ សំ​អន នាយករង​ប្រតិបត្តិ​អង្គការ​សន្តិ​សេនា​ប្រចាំ​ខេត្តស្វាយរៀង', 'http://kohsantepheapdaily.com.kh/article/127007.html', 'http://i.kspg.co/2015/8/df9b3781-210f-49c0-9e4d-33f01797b358.jpg?w=285&h=170&mode=carve', 'ថ្ងៃអង្គារ ទី១៨ ខែសីហា ឆ្នាំ២០១៥ ម៉ោង ១៤:០៤', '3', '0', '0', 't', '2015-08-18', 'f');
INSERT INTO "tbnews" VALUES ('923', 'B020101', 'B020000', 'បុរស​ចិត្តខ្លាំង​យក​ដាវ​គំរាមប្រពន្ធ-​កូន និង​ចាក់ទឹក​ក្តៅ​លើ​ជើង​ម្តាយក្មេក ត្រូវ​សមត្ថ​កិច្ច​ឃាត់​ខ្លួន​', 'រាជធានី​ភ្នំពេញ ​៖ បុរស​ម្នាក់​ត្រូវ​ ​កម្លាំង​ប៉ូលិស​ប៉ុស្តិ៍​រដ្ឋបាល​ស្វាយប៉ាកឃាត់ខ្លួន ​កាលពី​វេលា​យប់​ ថ្ងៃ​ទី​១៦​ សីហា​ ​២០១៥​​ នៅ​ចំណុចក្រុម​១ ភូមិ​លូ សង្កាត់​ ​ស្វាយប៉ាក ​ខណ្ឌ​ឬ​ស្សី​កែវ', 'http://kohsantepheapdaily.com.kh/article/127005.html', 'http://i.kspg.co/2015/8/b0309cba-d159-4e52-bb80-8e1d72e1b8bf.jpg?w=285&h=170&mode=carve', 'ថ្ងៃអង្គារ ទី១៨ ខែសីហា ឆ្នាំ២០១៥ ម៉ោង ១៣:៤៥', '3', '0', '0', 't', '2015-08-18', 'f');
INSERT INTO "tbnews" VALUES ('924', 'B020101', 'B020000', 'សមត្ថកិច្ច​ចុះ​ធ្វើ​រដ្ឋបាល​តាម​ហាង​អ៊ី​ន​ធើ​រ​ណេ​ត «69 Net 24h» រក​ល្បែង​ស៊ីសង', 'ខេត្តកំពង់ចាម ៖ ដោយ​មានការ​ណែនាំ​ពី​លោកស្នងការ​នគរបាល​ខេត្ត​នោះ កម្លាំង​ប៉ុស្តិ៍​រដ្ឋបាល​សង្កាត់​វាលវង់ កាលពី​វេលា​ព្រឹក​ថ្ងៃ​ទី​១៨ សីហា ២០១៥ បាន​ចុះ​ត្រួតពិនិត្យ​ហាង «69 Net 24h» ដែល​មាន​ទីតាំង​ស្ថិត​នៅ​ភូមិ​ទី​៤ សង្កាត់​វាលវង់ ក្រុង​កំពង់ចាម ដើម្បី​ធ្វើ​រដ្ឋបាល​ចំហ​រក​ច្បាប់​ធ្វើ​អាជីវកម្ម តែ​ភ្លាម​ៗ​នោះ​មិនឃើញ​ម្ចាស់ផ្ទះ​យក​ច្បាប់​មក​បង្ហាញ​នោះ​ទេ  ។', 'http://kohsantepheapdaily.com.kh/article/127004.html', 'http://i.kspg.co/2015/8/14b42092-76be-433d-b727-053916f68440.jpg?w=285&h=170&mode=carve', 'ថ្ងៃអង្គារ ទី១៨ ខែសីហា ឆ្នាំ២០១៥ ម៉ោង ១៣:៤៣', '23', '0', '0', 't', '2015-08-18', 'f');
