INSERT INTO "tbnews" VALUES ('928', 'B020401', 'B020000', 'តម្លៃ​ទំនិញប្រើប្រាស់​ប្រចាំ​ថ្ងៃ​ស​ប្តា​ហ៍​ទី​3​នៃ​ខែសីហា​2015', 'រា​ជ​ធានី​ភ្នំពេញ ៖ តម្លៃ​ទំនិញប្រើប្រាស់​ប្រចាំ​ថ្ងៃ របស់​ក្រសួងពាណិជ្ជកម្ម សម្រាប់ ថ្ងៃ​ទី​១៧ សីហា ២០១៥ នេះ មុខទំនិញ សាច់​ជ្រូក​-​គោ និង​បន្លែ មានការ ប្រែប្រួល តម្លៃ​តិចតួច ។ សូម​មើល​តារាង​ខាង  ក្រោម ៖', 'http://kohsantepheapdaily.com.kh/article/126994.html', 'http://i.kspg.co/2015/8/f42125e5-8ce7-4832-9f16-dfda3c9027bc.jpg?w=285&h=170&mode=carve', 'ថ្ងៃអង្គារ ទី១៨ ខែសីហា ឆ្នាំ២០១៥ ម៉ោង ១០:៤៧', '23', '0', '0', 't', '2015-08-18', 'f');
