INSERT INTO "tbnews" VALUES ('930', 'B020401', 'B020000', 'វិនិយោគិន​ជប៉ុន​មាន​សុទិដ្ឋិនិយម​ចំពោះ​បរិយាកាស​វិនិយោគ​នៅ​កម្ពុជា', 'រាជធានី​ភ្នំពេញ ៖ បន្ទាប់​ពី​បោះទុន​សាងសង់​ផ្សារ​ទំនើប​អុ​ី​អន​នៅ​កម្ពុជា​ទទួល​បាន​ជោគជ័យ​លើស​ពី​ការ​រំពឹង​ទុក​នៅ​រយៈពេល​មួយ​ឆ្នាំ​កន្លង​មកនេះ ក្រុមហ៊ុន​ផ្សារ​ទំនើប​អីិុ​អនបាន​ប្រកាស​បើក​គម្រោង​សាងសង់​ផ្សារ​ទំនើប​', 'http://kohsantepheapdaily.com.kh/article/126991.html', 'http://i.kspg.co/2015/8/57bc067f-0bf3-4ddf-8cb5-f7f9a8139649.jpg?w=285&h=170&mode=carve', 'ថ្ងៃអង្គារ ទី១៨ ខែសីហា ឆ្នាំ២០១៥ ម៉ោង ១០:៤១', '3', '0', '0', 't', '2015-08-18', 'f');
INSERT INTO "tbnews" VALUES ('931', 'B020401', 'B020000', 'ចំណូល​ពី​ឧស្សាហកម្ម​កាស៊ីណូ​កម្ពុជា​កើនឡើង​ខ្ពស់​ក្នុង​ឆមាស​ទី​1​ឆ្នាំ​នេះ', 'រាជធានី​ភ្នំពេញ ៖ ស្រប​ពេល​ដែល​រាជរដ្ឋាភិបាល​កម្ពុជា​បាន​ដាក់​ចេញ​នូវ​ផែនការ​រឹត​បន្តឹង និង​បង្កើន​ប្រសិទ្ធភាព​នៃ​ការ​កៀរគរ​ចំណូល​រយៈពេល​មធ្យម ចំណូល​បាន​មក​ពី​វិស័យ​កាស៊ីណូ​បាន​កើនឡើង​យ៉ាង​គំហុក​', 'http://kohsantepheapdaily.com.kh/article/126988.html', 'http://i.kspg.co/2015/8/a3e39c36-48d4-48ce-86ff-39c9d6a42791.jpg?w=285&h=170&mode=carve', 'ថ្ងៃអង្គារ ទី១៨ ខែសីហា ឆ្នាំ២០១៥ ម៉ោង ១០:៣៤', '3', '0', '0', 't', '2015-08-18', 'f');
INSERT INTO "tbnews" VALUES ('932', 'B020103', 'B020000', 'តំណាងរាស្រ្ត​ CNRP លោក អ៊ុំ សំអាន ​ចាកចេញទៅអាមេរិកស្ងាត់ៗ', 'រាជធានីភ្នំពេញ ៖ ​តំណាងរាស្រ្ត​មណ្ឌលសៀមរាប​មកពី​គណបក្សសង្គ្រោះជាតិ ( CNRP ) ​លោក អ៊ុំ សំអាន ​បានចាកចេញពី​ប្រទេសកម្ពុជា​ហើយកាលពីថ្ងៃទី១៦ សីហា ', 'http://kohsantepheapdaily.com.kh/article/126916.html', 'http://i.kspg.co/2015/8/6db04f2c-f7ac-46ba-b129-2bd20c70ba6d.jpg?w=285&h=170&mode=carve', 'ថ្ងៃចន្ទ ទី១៧ ខែសីហា ឆ្នាំ២០១៥ ម៉ោង ១៤:៣៦', '0', '0', '0', 't', '2015-08-17', 'f');
INSERT INTO "tbnews" VALUES ('933', 'B020103', 'B020000', 'លោក​សម រង្សី ថា ការ​ចាប់ខ្លួន និង​ដោះ​លែង​សកម្មជន​ខ្លួន​ជា​រឿង​ដដែល​ៗ', 'រាជធានី​ភ្នំពេញ ៖ ប្រធាន​គណបក្ស​សង្គ្រោះ​ជាតិ​លោក សម រង្សី បាន​ថ្លែង​ថា ករណី​ចាប់ចង និង​ដោះ​លែង​មន្ត្រីជាន់ខ្ពស់​គណបក្ស និង​សកម្មជន​របស់​ខ្លួន គឺជា​រឿង​ដដែល​ៗ ។ លោក​សមាជិក​ព្រឹ​ទ្ឋ​សភា ហុង សុខ​ហួរ និង​សកម្មជន​គណបក្ស​មួយ​ចំនួន​ទៀត', 'http://kohsantepheapdaily.com.kh/article/126904.html', 'http://i.kspg.co/2015/8/c25eeae5-b1e3-4817-b270-90536763cf35.jpg?w=285&h=170&mode=carve', 'ថ្ងៃចន្ទ ទី១៧ ខែសីហា ឆ្នាំ២០១៥ ម៉ោង ១២:៣៤', '31', '0', '0', 't', '2015-08-17', 'f');
INSERT INTO "tbnews" VALUES ('934', 'B020103', 'B020000', 'មុននេះបន្តិច​ អនុរក្សពន្ឋនាគារ​ព្រៃសអនុញ្ញាត​ឲ្យលោក សម រង្ស៊ី ​ចូលសួរសុខទុក្ខសកម្មជនខ្លួន', 'រាជធានីភ្នំពេញ ​៖ ប្រធានគណបក្សសង្គ្រោះជាតិ ​លោក សម រង្ស៊ី ​និង មន្រ្តីជាន់ខ្ពស់​បក្សប្រមាណ៤រូបផ្សេងទៀត ​នៅម៉ោង៩និង៣៧នាទី​ព្រឹក​ថ្ងៃទី១៧ ​សីហា ២០១៥នេះ ', 'http://kohsantepheapdaily.com.kh/article/126887.html', 'http://i.kspg.co/news_pic_kh.png?w=285&h=170&mode=carve', 'ថ្ងៃចន្ទ ទី១៧ ខែសីហា ឆ្នាំ២០១៥ ម៉ោង ០៩:៤៧', '0', '0', '0', 't', '2015-08-17', 'f');
INSERT INTO "tbnews" VALUES ('935', 'B020103', 'B020000', 'លោក​សម រ​ង្ស៊ី វិល​វិញ ផ្ទៃមេឃ​អួអាប់​ដោយសារ​ក្រុម​សកម្មជន​និង​សមាជិក​ព្រឹទ្ធសភា​ត្រូវ​ចាប់ខ្លួន', 'រាជធានី​ភ្នំពេញ ៖ ថ្នាក់ដឹកនាំ​គណបក្ស​ប្រជាជន​កម្ពុជា​និង​គណបក្ស​សង្គ្រោះ​ជាតិ បាន​ជជែក​ពិភាក្សា​បញ្ហា​វិសោធនកម្ម​រដ្ឋធម្មនុញ្ញ​មាត្រា​២ ដើម្បី​ដោះស្រាយ​បញ្ហា​ព្រំដែន​ជាមួយ​វៀតណាម​។ នេះ​បើ​តាម​លោក សម រ​ង្ស៊ី ប្រាប់​អ្នក​សារព័ត៌មាន', 'http://kohsantepheapdaily.com.kh/article/126886.html', 'http://i.kspg.co/2015/8/cb0cf360-8490-43b1-a360-d8e4d948bef6.jpg?w=285&h=170&mode=carve', 'ថ្ងៃចន្ទ ទី១៧ ខែសីហា ឆ្នាំ២០១៥ ម៉ោង ០៩:៤៦', '1', '0', '0', 't', '2015-08-17', 'f');
INSERT INTO "tbnews" VALUES ('936', 'B020103', 'B020000', 'បន្តិចទៀតនេះ​ លោក សម រង្ស៊ី ​នឹងទៅសួរសុខទុក្ខ​សកម្មជនខ្លួន​នៅពន្ឋនាគារព្រៃស', 'រាជធានីភ្នំពេញ ​៖ បើគ្មានអ្វីប្រែប្រួលនោះទេ​ នៅម៉ោង៩ព្រឹកនេះ​ ​ប្រធានគណបក្សសង្គ្រោះជាតិ ​លោក សម រង្ស៊ី ​នឹងដឹកនំាមន្រ្តីជាន់ខ្ពស់​បក្សនិងសកម្មជន​ទៅសួរសុខទុក្ខ', 'http://kohsantepheapdaily.com.kh/article/126871.html', 'http://i.kspg.co/2015/8/66581680-03db-451f-9edd-7e1efd375051.jpg?w=285&h=170&mode=carve', 'ថ្ងៃចន្ទ ទី១៧ ខែសីហា ឆ្នាំ២០១៥ ម៉ោង ០៨:៤០', '1', '0', '0', 't', '2015-08-17', 'f');
INSERT INTO "tbnews" VALUES ('937', 'B030101', 'B030000', 'ខ្យល់បក់ខ្លាំង មុនពេលភ្លៀង បណ្តាលឲ្យ បាក់ជញ្ជាំង អគារសាងសង់ផ្ទះល្វែង នៅសង្កាត់ បឹងកេងកងទី៣ សង្កត់ម៉ូតូកង់បី និងម៉ូតូឌុប', '<strong>ភ្នំពេញ :</strong> ដោយសារតែ ខ្យល់បក់ខ្លាំង មុនពេលមានភ្លៀងធ្លាក់ បានបណ្តាលឲ្យបាក់ជញ្ជាំងជាន់ទី៣ នៃអគារផ្ទះល្វែង ដែលកំពុងសាងសង់ មួយកន្លែង ស្ថិតនៅតាមផ្លូវលេខ៣៧៦ សង្កាត់បឹងកេងកងទី៣ ខណ្ឌចំការមន នៅល្ងាចថ្ងៃទី១៨ ខែសីហា ឆ្នាំ២០១៥នេះ បណ្តលឲ្យ សង្កត់ម៉ូតូក់បី និងម៉ូតូឌុប ដែលនៅខាងក្រោមសំណង់ ប៉ុន្តែសំណាងល្អមិនបណ្តាលឲ្យប៉ះពាល់ និងរងរបួសដល់មនុស្សនោះឡើយ ។', 'http://www.dap-news.com/2011-06-14-02-39-55/97378-2015-08-18-11-01-57.html', 'http://image.dap-news.com:81/dap-news/dap-images/2015/August/id-014/that-0446.jpg', 'Tuesday, 18 August 2015 18:01', '123', '0', '0', 't', '2015-08-18', 'f');
INSERT INTO "tbnews" VALUES ('938', 'B030101', 'B030000', 'មន្ទីរធនធានទឹក និងឧតុនិយម ខេត្តតាកែវ ចុះជួយអន្តរាគមន៍ បូមទឹក ចែកជូន ប្រជាកសិករ ស្រោចស្រព ដំណាំស្រូវ', '<strong>តាកែវ៖</strong> នៅថ្ងៃទី១៨ ខែសីហា ឆ្នាំ២០១៥ មន្ទីរធនធានទឹក និងឧតុនិយមខេត្តតាកែវ បានចុះពិនិត្យស្ថានភាពទឹក និងការងារបង្កបង្កើនផល របស់ប្រជាកសិករ នៅក្នុងឃុំព្រៃរំដេង ស្រុកគិរីវង់ ខេត្តតាកែវ។', 'http://www.dap-news.com/2011-06-14-02-39-55/97377-2015-08-18-10-54-12.html', 'http://image.dap-news.com:81/dap-news/dap-images/2015/August/id-014/that-0439.jpg', 'Tuesday, 18 August 2015 17:51', '0', '0', '0', 't', '2015-08-18', 'f');
INSERT INTO "tbnews" VALUES ('939', 'B030101', 'B030000', 'អភិបាល ខណ្ឌសែនសុខ : ប្រជាពលរដ្ឋ ប្រមាណ ៩៥ភាគរយ ដែលរស់នៅក្នុងបុរី គ្មានឯកសារគ្រួសារ', '<strong>ភ្នំពេញ :</strong> អភិបាលខណ្ឌសែនសុខ លោក លី សាវេត បានលើកឡើងថា ប្រជាពលរដ្ឋ ប្រមាណ៩៥ភាគរយ ដែលរស់នៅក្នុងបុរី គ្មានឯកសារ សម្រាប់ការស្នាក់នៅ និងរស់នៅនោះឡើយ ដូចជា សៀវភៅគ្រួសារ អត្តសញ្ញណប័ណ្ណនិងអត្រានុកូលដ្ឋានជាដើម ដែលបញ្ហានេះ បានធ្វើឲ្យមានការលំបាកដល់អាជ្ញាធរ ក៏ដូចជាសមត្ថកិច្ចនៅពេលកើតមានហេតុការណ៍ណានោះ ។', 'http://www.dap-news.com/2011-06-14-02-39-55/97376-2015-08-18-10-20-12.html', 'http://image.dap-news.com:81/dap-news/dap-images/2015/August/id-014/that-0433.jpg', 'Tuesday, 18 August 2015 17:19', '6', '0', '0', 't', '2015-08-18', 'f');
INSERT INTO "tbnews" VALUES ('940', 'B030101', 'B030000', 'រថយន្តកូរ៉េដឹកដែក ក្រឡាប់ ស្លាប់ម្នាក់របួសធ្ងន់ ស្រាល៣នាក់', '<strong>&nbsp;ភ្នំពេញ ៖</strong> រថយន្តកូរ៉េចំណុះ ១តោន កន្លះ ដឹកដែក ក្រុមហ៊ុន កម្ពុជា ដូនពេញ កំពុងស្ថាប នាផ្លូវ៦០ម៉ែត្រ បាន ក្រឡាប់ បណ្តាលឲ្យស្លាប់ម្នាក់ និងមនុស្ស៣នាក់ផ្សេងទៀត រងរបួសធ្ងន់ និងស្រាល កាលពីវេលាម៉ោងជាង ៨ ព្រឹក ថ្ងៃទី១៨ ខែសីហា ឆ្នាំ២០១៥ នៅចំណុចតាមបណ្តោយផ្លូវ៦០ម៉ែត្រ ស្ថិតក្នុងភូមិព្រែកថ្លឹង សង្កាត់ជើងឯក ខណ្ឌដង្កោ ។', 'http://www.dap-news.com/2011-06-14-02-39-55/97373-2015-08-18-09-25-27.html', 'http://image.dap-news.com:81/dap-news/dap-images/2015/AUGUST/id-011/Hong-0710.jpg', 'Tuesday, 18 August 2015 16:24', '0', '0', '0', 't', '2015-08-18', 'f');
INSERT INTO "tbnews" VALUES ('941', 'B030101', 'B030000', 'សមត្ថកិច្ច ឃាត់ខ្លួនពលករខ្មែរ ១៣នាក់ ប្រុងឆ្លងដែន ខុសច្បាប់ ចូលប្រទេសថៃ នៅស្រុកម៉ាឡៃ', '<strong>បន្ទាយមានជ័យ</strong> : កម្លាំងនគរបាលវរការពារព្រំដែនគោក នៃស្នងការដ្ឋាន នគរបាល ខេត្តបន្ទាយមានជ័យ នៅរសៀលថ្ងៃទី១៨ ខែសីហា ឆ្នាំ២០១៥ នេះ ដែលឈរជើង នៅតំបន់ច្រកម៉ាឡៃ បានឃាត់ខ្លួនពលករខ្មែរ ស្រីប្រុស ១៣ នាក់ ដែលបំរុង ឆ្លងដែនខុសច្បាប់ ចូលទៅកាន់ ប្រទេសថៃ ។', 'http://www.dap-news.com/2011-06-14-02-39-55/97371-2015-08-18-09-02-25.html', 'http://image.dap-news.com:81/dap-news/dap-images/2015/AUGUST/id-011/Hong-0707.jpg', 'Tuesday, 18 August 2015 16:01', '1', '0', '0', 't', '2015-08-18', 'f');
INSERT INTO "tbnews" VALUES ('942', 'B030101', 'B030000', 'អតិថិជន សែលកាត៖ ទឹកប្រាក់ កន្លះសែន ដុល្លារអាមេរិកនេះ នឹងយកទៅពង្រីក មុខរបរឲ្យធំជាងមុន', '<strong>ភ្នំពេញ៖</strong> នៅថ្ងៃទី១៨ ខែសីហានេះ តំណាងក្រុមហ៊ុន សែលកាត រួមនឹងអ្នកសារព័ត៌មាន បាននាំយកទឹកប្រាក់ ៥ម៉ឺនដុល្លារអាមេរិក ទៅប្រគល់ជូនលោក ឡេង តុងសង់ រស់នៅភូមិជ្រៃកោង សង្កាត់ចោមចៅ ខណ្ឌពោធ៍សែនជ័យ រាជធានីភ្នំពេញ ដែលបានឈ្នះរង្វាន់កាលពីថ្ងៃទី១៧ ខែសីហា។', 'http://www.dap-news.com/2011-06-14-02-39-55/97370-2015-08-18-09-01-30.html', 'http://image.lookingtoday.com:81/lookingtoday/looking-images/news/2015/August/ID-068/lay-045.jpg', 'Tuesday, 18 August 2015 16:00', '31', '0', '0', 't', '2015-08-18', 'f');
INSERT INTO "tbnews" VALUES ('943', 'B030101', 'B030000', 'សង្វៀនជល់មាន់ មួយកន្លែងនៅក្នុង ឃុំស្តុកប្រវឹក ជាឆ្នាំងបាយ របស់សមត្ដកិច្ច', '<strong>បាត់ដំបង ៖</strong> មានសង្វៀនជល់ មាន់១កន្លែង នៅក្នុងភូមិ.ឃុំស្តុកប្រវឹក ស្រុករុក្ខគីរី ដែលជាឆ្នាំងបាយ របស់មេប៉ុស្តិ៍ ទើបគ្មានការបង្រ្កាប ខណៈលោកមេប៉ុស្តិ៍ និងលោកអធិការ ស្រុកឆ្លើយថា មិនបានដឹងរឿង ។', 'http://www.dap-news.com/2011-06-14-02-39-55/97368-2015-08-18-08-56-39.html', 'http://image.dap-news.com:81/dap-news/dap-images/2015/AUGUST/id-011/Hong-0700.jpg', 'Tuesday, 18 August 2015 15:56', '1', '0', '0', 't', '2015-08-18', 'f');
INSERT INTO "tbnews" VALUES ('944', 'B030101', 'B030000', 'មន្ត្រីកាំកុងត្រូល សាខាខេត្តប៉ៃលិន លទ្ធផល តេស្តគីត លើផលិតផល ចំនួន បីប្រភេទ', '<strong>បន្ទាយមានជ័យ ៖</strong> នៅព្រឹកថ្ងៃទី១៨ ខែសីហា ឆ្នាំ២០១៥នេះ មន្រ្តីសា ខាកាំកុងត្រូល ខេត្តប៉ៃលិន សហការ ជាមួយមន្ទីរពាណិជ្ជកម្មខេត្ត ចុះត្រួតពិនិត្យគុណភាព សុវត្ថិភាពម្ហូបអាហារនៅតាមគេហដ្ឋាន និងផ្សារ សាលាក្រៅ ស្ថិតក្នុងភូមិទួលខ្ពស់ ឃុំអូរអណ្តូង ស្រុកសាលាក្រៅ ខេត្តប៉ៃលិន។', 'http://www.dap-news.com/2011-06-14-02-39-55/97367-2015-08-18-08-51-43.html', 'http://image.dap-news.com:81/dap-news/dap-images/2015/AUGUST/id-011/Hong-0695.jpg', 'Tuesday, 18 August 2015 15:46', '2', '0', '0', 't', '2015-08-18', 'f');
INSERT INTO "tbnews" VALUES ('945', 'B030101', 'B030000', 'អស់កង្វល់​ជា​មួយ​ប្រព័ន្ធ​ការពារ​សុវត្ថិភាព​ដ៏​ទំនើប​របស់ ESS', 'ភ្នំពេញ៖ ដើម្បីចូលរួមចំណែកក្នុងការការពារសុវត្ថិភាព ទាំងអាយុជីវិត និងទ្រព្យសម្បត្តិរបស់លោកអ្នក ក៏ដូចជាជួយសម្រួលដល់អាជីវកម្ម របស់លោកអ្នក ឱ្យកាន់តែមានភាពងាយស្រួល បន្ថែមទៀតនោះ យើងខ្ញុំមានលក់នូវឧបករណ៏អេឡិចត្រូនិក ទំនើបៗជាច្រើនប្រភេទរួមមានដូចខាងក្រោមៈ', 'http://www.dap-news.com/2011-06-14-02-39-55/96591--ess.html', 'http://image.lookingtoday.com:81/lookingtoday/looking-images/news/2015/August/ID-141/MrMSP-0381.jpg', 'Tuesday, 18 August 2015 15:15', '3', '0', '0', 't', '2015-08-18', 'f');
INSERT INTO "tbnews" VALUES ('946', 'B030101', 'B030000', 'អត្ថបទស្រាវជ្រាវ៖ ទោះបីជា ស្ថិតនៅក្នុង ស្ថានភាពណាមួយ ក៏ដោយ ក៏ Argentina ដាច់ខាតនៅតែ ទាមទារប្រជុំកោះ Malvinas មកជូនមាតុភូមិវិញ្!', 'null', 'http://www.dap-news.com/2011-06-14-02-39-55/97364-------argentina---malvinas-.html', 'http://image.dap-news.com:81/dap-news/dap-images/2015/June/id-009/ST-087.jpg', 'Tuesday, 18 August 2015 15:04', '1', '0', '0', 't', '2015-08-18', 'f');
INSERT INTO "tbnews" VALUES ('947', 'B030102', 'B030000', 'សាកសពទាំង ៥៤នាក់ និង ប្រអប់ខ្មៅ ត្រូវបានរកឃើញ ក្រោយធ្លាក់យន្តហោះ នៅឥណ្ឌូណេស៊ី', '<strong>ហ្សាការតា៖</strong> ទីបំផុតសាកសព មនុស្សចាស់ក្មេង ទាំង ៥៤នាក់ នៅលើយន្តហោះ ឥណ្ឌូណេស៊ី ដែលបានធ្លាក់ នៅតំបន់ព្រៃនៃក្រុង អុកស៊ីប៊ីល របស់ខេត្តប៉ាពួ ភាគខាងកើតប្រទេសនោះ ត្រូវបានក្រុមសង្គ្រោះ ប្រមូល បានហើយ រហូតដល់ល្ងាចថ្ងៃអង្គារ ទី១៨ ខែសីហា ឆ្នាំ២០១៥។', 'http://www.dap-news.com/2011-06-14-02-40-26/97374-2015-08-18-09-32-30.html', 'http://image.dap-news.com:81/dap-news/dap-images/2015/August/ID-068/Lay-026.jpg', 'Tuesday, 18 August 2015 16:29', '1', '0', '0', 't', '2015-08-18', 'f');
INSERT INTO "tbnews" VALUES ('948', 'B030102', 'B030000', 'វិដេអូ បង្ហាញមុខ ជនសង្ស័យ បំផ្ទុះគ្រាប់បែកនៅបាងកក កាលពីយប់មិញ', '<strong>បាងកក ៖</strong> បន្ទាប់ពីទទួលបាន វីដេអូ នៅកន្លែងបំផ្ទុះគ្រាប់ បែកកណ្ដាលទីក្រុងបាងកក, សមត្ថកិច្ចបង្ហាញវិដេអូ ជនសង្ស័យ ស្ពាយសាក់កាដូ និងដាក់កៅអី ហើយបំផ្ទុះគ្រាប់បែកករណីលើកទី១។ ជនសង្ស័យស្ពាយ សាក់កាដូនេះត្រូវប៉ូលិសកំពុងតាមប្រមាញ់ចាប់ខ្លួន។', 'http://www.dap-news.com/2011-06-14-02-40-26/97366-2015-08-18-08-35-39.html', 'http://image.dap-news.com:81/dap-news/dap-images/2015/AUGUST/id-011/Hong-0692.jpg', 'Tuesday, 18 August 2015 15:34', '0', '0', '0', 't', '2015-08-18', 'f');
INSERT INTO "tbnews" VALUES ('949', 'B030102', 'B030000', 'មនុស្សយន្ត កំពុងជួយ ក្នុងវិស័យ កសិកម្ម អូស្រ្តាលីយ៉ាងសកម្ម', '<strong>ស៊ីដនី ៖</strong> ទីភ្នាក់ងារព័ត៌មានចិន ស៊ិនហួចេញផ្សាយ នៅថ្ងៃអង្គារ ទី ១៨ ខែសីហា ឆ្នាំ ២០១៥ ឲ្យដឹងថា មនុស្ស យន្តជាច្រើនគ្រឿង កំពុងត្រូវគេប្រើប្រាស់ នៅក្នុងប្រទេសអូស្រ្តាលី ដើម្បី ជំនួសមនុស្សធម្មតា ក្នុងការងារ ការដាំដុះ ការប្រមូលផល ការវិចខ្ចប់ បន្លែ និង ផ្លែឈើនៅ ក្នុងប្រទេសឲ្យកាន់តែប្រសើរឡើង។', 'http://www.dap-news.com/2011-06-14-02-40-26/97365-2015-08-18-08-12-50.html', 'http://sydney.edu.au/engineering/research/robotics/images/agricultural-robotics3.jpg', 'Tuesday, 18 August 2015 15:12', '12', '0', '0', 't', '2015-08-18', 'f');
INSERT INTO "tbnews" VALUES ('950', 'B030102', 'B030000', 'ផ្ទុះគ្រាប់បែក លើកទីពីរ នៅបាងកក តែមិនបង្កឲ្យ មនុស្សស្លាប់ និង របួស (មានវីដេអូ)', '<strong>បាងកក ៖</strong>គ្រាប់បែក ត្រូវបានគប់ទៅកាន់តំបន់ផែ សាថន នៅក្នុង ទីក្រុងបាងកក នៅថ្ងៃអង្គារ ទី១៨ ខែសីហា ឆ្នាំ ២០១៥ នេះ ប៉ុន្តែវាមិនបាន បង្កឲ្យមាន មនុស្សស្លាប់ និង របួស ឬបង្កការខូចខាតអ្វីនោះឡើយ។', 'http://www.dap-news.com/2011-06-14-02-40-26/97362-2015-08-18-07-56-20.html', 'http://image.dap-news.com:81/dap-news/dap-images/2015/August/id-009/ST-057.jpg', 'Tuesday, 18 August 2015 14:55', '2', '0', '0', 't', '2015-08-18', 'f');
INSERT INTO "tbnews" VALUES ('951', 'B030102', 'B030000', 'នាយករដ្ឋមន្ត្រី៖ ជនជាតិម៉ាឡេស៊ី ២រូប ស្លាប់ក្នុងការ ផ្ទុះគ្រាប់បែក នៅបាងកក', '<strong>គូឡាឡាំពួរ៖</strong> រដ្ឋាភិបាលម៉ាឡេស៊ី បានប្រកាសនៅ ថ្ងៃអង្គារ ទី១៨ ខែសីហា ឆ្នាំ២០១៥ នេះថា ជនជាតិរបស់ខ្លួន ២នាក់ ក៏បានស្លាប់នៅក្នុងការ បំផ្ទុះគ្រាប់បែក ជាប្រភេទច្នៃចេញពី ម្សៅ TNT កណ្តាលទីក្រុង បាងកក កាលពី យប់ថ្ងៃចន្ទ នោះដែរ។ លោកនាយករដ្ឋមន្ត្រី ណាជីប រ៉ាហ្សាក់ បានហៅទង្វើខាងលើ នេះថា “អំពើដ៏គួរឲ្យ ស្អប់ខ្ពើមបំផុត”។', 'http://www.dap-news.com/2011-06-14-02-40-26/97361-2015-08-18-07-31-43.html', 'http://image.dap-news.com:81/dap-news/dap-images/2015/August/ID-068/Lay-024.jpg', 'Tuesday, 18 August 2015 14:28', '3', '0', '0', 't', '2015-08-18', 'f');
INSERT INTO "tbnews" VALUES ('952', 'B030102', 'B030000', 'កាណាដា នឹងសាងសង់ ជណ្តើរយន្ត អវកាស ខ្ពង់ជាងអគារ នៅឌូបៃ ២០ដង', '<strong>អូតាវ៉ា៖</strong> ទីភ្នាក់ងារព័ត៌មាន វិទ្យាសាស្រ្ត Space ចេញផ្សាយ នៅថ្ងៃចន្ទ ទី១៨ ខែសីហា ឆ្នាំ ២០១៥បានឲ្យដឹងថា ក្រុមហ៊ុនបច្ចេកវិទ្យាអវកាស Thoth Technology របស់ប្រទេស កាណាដា មួយ មានគំរោងចង់សាង សង់ប៉ម លេចត្រដែត នៅក្នុងលំហអាកាស ដែលក្រុមអាកាសយានិក អាចធ្វើដំណើរតាមជណ្តើរ យន្តទៅកាន់គោចរ លំហអាកាស។', 'http://www.dap-news.com/2011-06-14-02-40-26/97357-2015-08-18-07-10-20.html', 'http://l1.yimg.com/bt/api/res/1.2/DDxHSRjVYPVfL9lDwmJIpg--/YXBwaWQ9eW5ld3M7Zmk9ZmlsbDtoPTM3NztpbD1wbGFuZTtweG9mZj01MDtweW9mZj0wO3E9NzU7dz02NzA-/http://media.zenfs.com/en_us/News/afp.com/Part-WAS-Was8952848-1-1-0.jpg', 'Tuesday, 18 August 2015 14:08', '0', '0', '0', 't', '2015-08-18', 'f');
INSERT INTO "tbnews" VALUES ('953', 'B030102', 'B030000', 'បំណែកយន្តហោះ ឥណ្ឌូណេស៊ី ត្រូវបានរកឃើញ ជាមួយសាកសព ៣៨នាក់', '<strong>ហ្សាយ៉ាភូរ៉ា៖</strong> តាមការផ្សាយ របស់សារព័ត៌មាន បាងកកប៉ុស្តិ៍ របស់ថៃ បានឲ្យដឹងនៅថ្ងៃអង្គារ ទី១៨ ខែសីហា ឆ្នាំ ២០១៥ នេះថា យន្តហោះដឹកអ្នកដំណើរ ក្នុងស្រុកមួយគ្រឿង របស់ ឥណ្ឌូណេស៊ី ដែលបានបាត់ខ្លួន តាំងពី រសៀលថ្ងៃអាទិត្យ សប្តាហ៍មុននេះ ត្រូវបានក្រុម សង្គ្រោះរកឃើញហើយ ស្ថិតនៅខេត្តប៉ាពួ ភាគខាងកើតប្រទេស ដោយស្ថានភាពនៃ យន្តហោះនោះ គឺខ្ទេចខ្ទីអស់រលីងតែម្តង។', 'http://www.dap-news.com/2011-06-14-02-40-26/97346-2015-08-18-04-42-53.html', 'http://image.dap-news.com:81/dap-news/dap-images/2015/August/ID-068/Lay-021.jpg', 'Tuesday, 18 August 2015 11:38', '321', '0', '0', 't', '2015-08-18', 'f');
INSERT INTO "tbnews" VALUES ('954', 'B030102', 'B030000', 'នាវាពេទ្យ នៃកងទ័ពជើងទឹក អាមេរិក Mercy ធ្វើដំណើរមកដល់ វៀតណាម', '<strong>ហាណូយ៖</strong> ទីភ្នាក់ងារព័ត៌មាន ចិនស៊ិនហួ ចេញផ្សាយនៅថ្ងៃអង្គារ ទី ១៨ ខែសីហា ឆ្នាំ ២០១៥នេះថា នាវាមន្ទីរពេទ្យរបស់ កងទ័ពជើងទឹកអាមេរិកឈ្មោះ Mercy បានចូលមកចត នៅកំពង់ផែ ទៀន សា នៅទីក្រុង ដា ណាង ភាគកណ្តាលប្រទេស វៀតណាម កាលពីថ្ងៃទី ១៧ ខែសីហា ឆ្នាំ ២០១៥ ម្សិលមិញនេះ។', 'http://www.dap-news.com/2011-06-14-02-40-26/97342----mercy---.html', 'http://image.dap-news.com:81/dap-news/dap-images/2015/August/ID-068/Lay-020.jpg', 'Tuesday, 18 August 2015 10:48', '0', '0', '0', 't', '2015-08-18', 'f');
INSERT INTO "tbnews" VALUES ('955', 'B030302', 'B030000', 'មន្ទីរពេទ្យ «វេជ្ជថាវី» ត្រូវបានគេស្គាល់ថា ជា «អធិរាជឆ្អឹង» ជំនាញផ្នែកវះកាត់ជំងឺសន្លាក់ឆ្អឹង (Video Inside)', '<strong>ភ្នំពេញ៖</strong> មជ្ឈមណ្ឌលផ្នែកស្តារ នីតិសម្បទា សម្រាប់ជំងឺរលាកសន្លាក់ឆ្អឹង (TJR) របស់មន្ទីរពេទ្យ «វេជ្ជថាវី» ក៏ត្រូវបានគេទទួលស្គាល់ យ៉ាងពេញលេញ នូវភាពប៉ិនប្រសប់ និងបច្ចេកវិទ្យាក្នុងការព្យាបាល។ គ្រូពេទ្យវះកាត់ ជួសជុលកែលំអសន្លាក់ របស់យើងផ្តល់នូវការវះកាត់ព្យាបាល ទៅលើ arthroplasty រួមមាន: High Tibial Osteotomy, Unicompartment knee arthroplasty, Total knee arthroplasty (knee replacement surgery), និងការវះជាច្រើនទៀត ។', 'http://www.dap-news.com/morokatmagazine/97254--lr-lr-video-inside.html', 'img/dapnews.png', 'Monday, 17 August 2015 10:24', '0', '0', '0', 't', '2015-08-17', 'f');
INSERT INTO "tbnews" VALUES ('956', 'B030302', 'B030000', 'ការពិនិត្យរកជំងឺ មហារីកក្រពះពោះវៀន ពីសំណាក់វេជ្ជបណ្ឌិត ជំនាញមកពីសឹង្ហបុរី', '<strong style="line-height: 2;">ភ្នំពេញ៖</strong><span style="line-height: 2;"> របាយការណ៍នានា បានបង្ហាញថា ជំងឺមហារីកក្រពះ និងជំងឺមហារីក ពោះវៀនធំ និងចុងពោះវៀនធំ ត្រូវបានចាត់ថ្នាក់ទី៣ និងទី៤ រៀងគ្នាថាជាជំងឺមហារីក ដែលបណ្ដាលឱ្យស្លាប់ ក្នុងចំណោម បុរសកម្ពុជា។ ដោយសារជំងឺ មហារីកក្រពះពោះវៀន ទាំងនេះមិនមានរោគ សញ្ញាច្បាស់លាស់ អ្នកជំងឺរហូតដល់ ៧០% ជាធម្មតាត្រូវបានធ្វើរោគ វិនិច្ឆ័យដឹងនៅដំណាក់កាលទី៣ ឬទី៤។</span>', 'http://www.dap-news.com/morokatmagazine/97099-2015-08-14-03-55-32.html', 'http://image.lookingtoday.com:81/lookingtoday/looking-images/news/2015/August/ID-140/hour-01193.jpg', 'Friday, 14 August 2015 10:50', '0', '0', '0', 't', '2015-08-14', 'f');
