INSERT INTO "tbnews" VALUES ('1001', 'B020302', 'B020000', 'ជ័យ​លា​ភី​មេដាយ​ប្រាក់​ផ្នែក​កុំព្យូទ័រ​ថ្នាក់​ពិភពលោកបង្ហាញ​បទ​ពិសោធន៍​នាំ​ឱ្យ​ជោគជ័យ​ប្រកបដោយ​មោទនភាព', 'រាជធានី​ភ្នំពេញ ៖ យុវជន​គង់ ឌី​វី​ន ជា​ម្ចាស់​មេដាយ​ប្រាក់​ដំបូង​បង្អស់​របស់​កម្ពុជា ក្នុង​ការ​ប្រកួត​វិញ្ញាសា​កុំព្យូទ័រ MOSWC 2015 នៅ​រដ្ឋ​តិច​សាស់ សហរដ្ឋអាមេរិក បាន​ត្រឡប់​មក​ដល់​មាតុភូមិ​វិញ​ហើយ​កាលពី​រាត្រី​ថ្ងៃ​ទី​១៤ សីហា ឆ្នាំ​២០១៥ ។ ', 'http://kohsantepheapdaily.com.kh/article/126883.html', 'http://i.kspg.co/2015/8/5d70a8b1-1821-4cc5-a2ba-1e783d17d3d9.jpg?w=285&h=170&mode=carve', 'ថ្ងៃចន្ទ ទី១៧ ខែសីហា ឆ្នាំ២០១៥ ម៉ោង ០៩:៣១', '0', '0', '0', 't', '2015-08-17', 'f');
INSERT INTO "tbnews" VALUES ('1002', 'B020505', 'B020000', 'ជំងឺ​ច្រាល់​ទឹក​អាស៊ី​ដ​ក្នុង​ក្រពះ​អាច​នឹង​វិ​វ​ឌ្ឍ​ទៅ​ជា​ជំងឺមហារីក​បំពង់​អាហារ', 'កាលពី​ពេល​ថ្មី​ៗ​កន្លង​ទៅ​នេះ លោក​វេជ្ជបណ្ឌិត អ៊ុ​ង ចក្រាវុធ ប្រធាន​ផ្នែក​ជំងឺថ្លើម ក្រពះ​-​ពោះវៀន និង​ឫសដូងបាត​នៃ​មន្ទីរពេទ្យ​កាល់​ម៉ែ​ត បាន​ថ្លែងប្រាប់​កោះសន្តិភាព​ឱ្យ​ដឹង​ថា ជំងឺ​ច្រាល់​ទឹក​អាស៊ីត​ក្នុង​ក្រពះ', 'http://kohsantepheapdaily.com.kh/article/126900.html', 'http://i.kspg.co/2015/8/3dc92db2-5352-49d1-8046-4c12d91723f9.jpg?w=285&h=170&mode=carve', 'ថ្ងៃចន្ទ ទី១៧ ខែសីហា ឆ្នាំ២០១៥ ម៉ោង ១០:៤៥', '0', '0', '0', 't', '2015-08-17', 'f');
